INSERT INTO `translation` VALUES (1,10,'en','[{\"line\": 0, \"text\": \"Lain! Why didn\'t you wait for us?\", \"actor\": \"Girl\"}]'),(2,10,'ru','[{\"line\": 0, \"text\": \"Лэйн! Почему ты не подождала нас?\", \"actor\": \"Девочка\"}, {\"line\": 1, \"text\": \"Э?\", \"actor\": \"Лэйн\"}]'),(3,11,'en','[{\"line\": 0, \"text\": \"You said we could visit you after school.\", \"actor\": \"Girl-1\"}, {\"line\": 1, \"text\": \"Really?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Listen, Lain!\", \"actor\": \"Girl-2\"}, {\"line\": 3, \"text\": \"You play games all the time.\", \"actor\": \"Girl-2\"}, {\"line\": 4, \"text\": \"You never listen during classes… and then you get 100% on tests.\", \"actor\": \"Girl-2\"}, {\"line\": 5, \"text\": \"It\'s not relevant.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"It is! It is!\", \"actor\": \"Girl\"}, {\"line\": 7, \"text\": \"Lain, your dad is cool, isn\'t he?\", \"actor\": \"Girl-2\"}, {\"line\": 8, \"text\": \"Will we see him today?\", \"actor\": \"Girl-2\"}, {\"line\": 9, \"text\": \"I\'m sorry.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I really can\'t today.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Sorry.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"I\'m sure she\'s free…\", \"actor\": \"Girl-1\"}, {\"line\": 13, \"text\": \"Yeah…\", \"actor\": \"Girl-2\"}]'),(4,13,'en','[{\"line\": 0, \"text\": \"“LAIN”\", \"actor\": \"Sign on the door\"}, {\"line\": 1, \"text\": \"That\'s what I don\'t get.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"If we use packet filtering here… then debugging won\'t be necessary.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"No, not that…\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wanted…\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Don\'t enter without permission!\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"My friend is with me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"We are always together.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"All right.\", \"actor\": \"Lain\"}]'),(5,17,'en','[{\"line\": 0, \"text\": \"By the way, I recently found a super tasty cake!\", \"actor\": \"Girl-1\"}, {\"line\": 1, \"text\": \"Really? I love chocolate.\", \"actor\": \"Girl-2\"}, {\"line\": 2, \"text\": \"Believe me, it’s really good.\", \"actor\": \"Girl-1\"}]'),(6,18,'en','[{\"line\": 0, \"text\": \"Computer?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"A friend?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I never thought about it that way.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"We were talking about it before.\", \"actor\": \"Lain\"}]'),(7,19,'en','[{\"line\": 0, \"text\": \"What\'s that?\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"This?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Bike-chan.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"A puppy.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"My stuffed toy.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I wouldn\'t call him a friend, but…\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"He was already there when I was born.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Right, Bike-chan?\", \"actor\": \"Lain\"}]'),(8,20,'en','[{\"line\": 0, \"text\": \"I wouldn\'t say I like meetings with Touko-sensei…\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Sure, it\'s nice.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"But I dislike it here.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Why is that?\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"I just do.\", \"actor\": \"Lain\"}]'),(9,25,'en','[{\"line\": 0, \"text\": \"I\'m going back to you.\", \"actor\": \"Lain\"}]'),(10,26,'en','[{\"line\": 0, \"text\": \"… and so…\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"… when we want to increase the processing speed\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"… we must take into account the fact that the processor can overheat.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"You can keep the CPU cool by using a Peltier element…\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"but it\'s still a problem…\", \"actor\": \"Lain\"}]'),(11,27,'en','[{\"line\": 0, \"text\": \"Savant syndrome?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Am I sick?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I didn\'t say it\'s about you, Lain.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Only that there are people who suffer from that sickness.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"If it\'s not about me…\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"then why are talking about it?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"There are other children who may learn from your example.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"I\'m only myself.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Not other children.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"That\'s true!\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"I\'m sorry, Lain.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"What\'s wrong, Lain?\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"Why are you calling me Lain?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Who are you?\", \"actor\": \"Lain\"}]'),(12,31,'en','[{\"line\": 0, \"text\": \"When we are not connected to the network…\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"…we are all alone.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Do you understand that?\", \"actor\": \"Lain\"}]'),(13,33,'en','[{\"line\": 0, \"text\": \"Stop it!\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"You have to be still!\", \"actor\": \"Lain\"}]'),(14,34,'en','[{\"line\": 0, \"text\": \"Don’t do that. You have to stay calm... It finally came.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"...\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Lain, concerning your inquiry, I think your approach is the correct one.\", \"actor\": \"Computer\"}, {\"line\": 3, \"text\": \"...\", \"actor\": \"Computer\"}, {\"line\": 4, \"text\": \"The concept of a neural network is a relatively old idea, but the arrangement of neurons by any system may not necessarily make the system work...\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"It won’t work.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Ha, ha.\", \"actor\": \"Lain\"}]'),(15,35,'en','[{\"line\": 0, \"text\": \"But even if it is assumed to be complete, there will definitely be an unknown network that connects people.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It might be one of many things, like Jung’s ideas of the subconscious.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"There are many ways to say it, but no one can prove its existence now...\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"But thinking about a person like you who has an interest in this sort of thing, I wonder, what are you trying to research?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"What is your goal?\", \"actor\": \"Lain\"}]'),(16,37,'en','[{\"line\": 0, \"text\": \"Wave interference.\", \"actor\": \"Lain\"}]'),(17,38,'en','[{\"line\": 0, \"text\": \"I don\'t believe it\'s you.\", \"actor\": \"Guy\"}, {\"line\": 1, \"text\": \"How old are you?\", \"actor\": \"Guy\"}, {\"line\": 2, \"text\": \"Oh, I\'m sorry.\", \"actor\": \"Guy\"}, {\"line\": 3, \"text\": \"You shouldn\'t ask women about their age.\", \"actor\": \"Guy\"}, {\"line\": 4, \"text\": \"I don\'t normally get a chance to talk with girls your age.\", \"actor\": \"Guy\"}, {\"line\": 5, \"text\": \"And to be honest, it\'s making me a bit nervous.\", \"actor\": \"Guy\"}, {\"line\": 6, \"text\": \"Do you have what you promised?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"The actuator motor.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"It\'s a rare thing.\", \"actor\": \"Guy\"}, {\"line\": 9, \"text\": \"Watch out for electricity.\", \"actor\": \"Guy\"}, {\"line\": 10, \"text\": \"No worries.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"So sure?\", \"actor\": \"Guy\"}, {\"line\": 12, \"text\": \"What are you building anyway?\", \"actor\": \"Guy\"}, {\"line\": 13, \"text\": \"Myself.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"And something that gave me a body.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"But it\'s only temporary.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"So there\'s no point in developing it.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"I don\'t think I quite understand…\", \"actor\": \"Guy\"}, {\"line\": 18, \"text\": \"Things are born when you build them.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"And when they are destroyed, they transform into something else.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Something completely new.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Do you know what\'s going on?\", \"actor\": \"Guy\"}]'),(18,42,'en','[{\"line\": 0, \"text\": \"Stop!\", \"actor\": \"Guy\"}]'),(19,44,'en','[{\"line\": 0, \"text\": \"Lain?\", \"actor\": \"Guy\"}, {\"line\": 1, \"text\": \"Lain!\", \"actor\": \"Guy\"}]'),(20,45,'en','[{\"line\": 0, \"text\": \"What\'s going on?\", \"actor\": \"Touko\"}]'),(21,46,'en','[{\"line\": 0, \"text\": \"Lain!\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"When you appeared in a laboratory two years ago…\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"…all data about you was entered into a database.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"You know that.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Archives of movies recorded by your family.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"The contents of your personal computer.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"We had it all in data.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"But there\'s something strange…\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"The last data update…\", \"actor\": \"Touko\"}]'),(22,47,'en','[{\"line\": 0, \"text\": \"…is about your future!\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"It\'s a record from a day two years in the future!\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Who are you?\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Talk to me!\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Why isn\'t there any data prior to three years ago…?\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"No!\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"You were born in your current form…\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"It doesn\'t matter in this world.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"You know that.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Stay away!\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"I want to connect with the entire world.\", \"actor\": \"Lain\"}]'),(23,50,'en','[{\"line\": 0, \"text\": \"Lain.\", \"actor\": \"Guy\"}, {\"line\": 1, \"text\": \"You…\", \"actor\": \"Guy\"}, {\"line\": 2, \"text\": \"I finished.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"What are you talking about?\", \"actor\": \"Guy\"}, {\"line\": 4, \"text\": \"About my dad.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"What?\", \"actor\": \"Guy\"}, {\"line\": 6, \"text\": \"Lain!\", \"actor\": \"Guy\"}, {\"line\": 7, \"text\": \"Lain!\", \"actor\": \"Guy\"}, {\"line\": 8, \"text\": \"Lain?\", \"actor\": \"Guy\"}]'),(24,54,'en','[{\"line\": 0, \"text\": \"It\'s over.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"For me it\'s the beginning.\", \"actor\": \"Lain2\"}, {\"line\": 2, \"text\": \"From now on we will always be together.\", \"actor\": \"Lain2\"}]'),(25,57,'en','[{\"line\": 0, \"text\": \"So… Mrs. Iwakura, please wait here in the antechamber.\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Lain, you don’t have to worry so much.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Please sit down there.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Ah… do you want some juice or something else to drink?\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"It’s OK if you don’t worry so much.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"Let\'s see.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"I\'m thirsty, so I\'m going for tea… mind to follow me?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Because it will look funny if only I drink it.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Thank you.\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"Which one should it be?\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"We have many to choose from.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"The same as you.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Green tea?\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"Got it.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Just a moment…\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"You put in the sugar and milk by yourself, OK?\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"Here you go.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"So where should we start?\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"How is this…? Earl Grey tea, I really love it.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Earl Grey?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Right.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"Even with tea there are lots of kinds, with different smell and taste.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"Does it taste good?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Yes.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"It suits my taste.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"But I\'m not sure about you Lain.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"If you want to, you can drink a little bit.\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"If it’s not to your taste, I can make something else for you.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"Here…\", \"actor\": \"Touko\"}]'),(26,58,'en','[{\"line\": 0, \"text\": \"That smells good.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Here you go.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"How is it?\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Looks as if you don’t like it, huh?\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Too bitter?\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"What if you add some sugar?\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"Well, how about if you add some milk?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"It might take away the bitterness.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Or I can get you something else?\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"I don’t like milk.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Ah, sorry. I didn’t know... Well, how about if I get some juice for you?\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"I’m sorry...\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"That’s all right; I probably couldn’t have had green tea either at your age.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"You said that there are lots of different kinds of tea right?\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"If I don’t like it, I don’t like it.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"Of course, that doesn’t mean I can’t drink it, but different kinds of tea have different flavors right?\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"What do you like, Lain?\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"Why don’t you like milk?\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"It upsets my stomach.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Ha, ha, truth be told, when I was a child I had the same problem.\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"Especially when the school lunch included milk, I can drink milk now though.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"I can’t drink it, but I really love it...\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"How about milk that doesn’t upset your stomach? Would that be all right?\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"What’s it called?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"I forgot what it’s called, but it’s milk that doesn’t upset your stomach.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"I have it for kids like you Lain, who are like me as a kid and can’t drink milk.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"If you talk about it with your mother, I’m sure she’ll look for it for you.\", \"actor\": \"Touko\"}]'),(27,59,'en','[{\"line\": 0, \"text\": \"Are there different kinds of milk as well?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"That’s right; although they’re basically all they same, depending on how they’re prepared there are different kinds.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Different kinds?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Yes, in tea too.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Depending on the way the leaves are dried and the way the tea is poured, the same leaves can be very different.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"People too?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Hmm?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Are there different kinds of people as well?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Hmm, well, rather than kinds, each person is different in his or her own way.\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"For example, you don’t like tea, but there are also people like me who love it.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"There are also people whose stomach gets upset when they drink milk, but there also those whose stomach doesn’t get upset right?\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"However, there also people who when they grow up, learn to drink it.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"Lain, you’ll probably will learn to drink tea you know.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"It’s not a good thing, nor a bad thing.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"It’s normal that there are different kinds of people.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"That’s why you can just ignore rather you should be proud of your uniqueness I think!\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"Be proud of it?\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"I’m sorry... That’s right, you worry, don’t you?\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"You don’t have to boast about it, but there’s no need to mind what other people think is what I meant to say.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"I have weird dreams, is that different too?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"You may think its different, but in fact that’s normal.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"If you’re worried about that, I might be able to help.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"Not to help you stop having those dreams, but rather helping you to explain things you don’t understand.\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"If you come to me with your problems, I’d be really happy.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"You know, I’ve studied a lot to help you answer those kinds of questions; I’m still studying actually.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"If you’re ever in trouble or something, and should you want to come and talk to me, that’d be OK. I’d like to know more about you Lain.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"Would that be OK?\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"I’m so glad!\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"Starting today you and I are friends!\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"Please come and play sometime!\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"Promise?\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"I’d like to ask you things as well!\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"You can ask me anything you want.\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"If I don’t know the answer I’ll look up the answer for you.\", \"actor\": \"Touko\"}, {\"line\": 34, \"text\": \"I’ll even prepare your favorite drink.\", \"actor\": \"Touko\"}, {\"line\": 35, \"text\": \"What’s that??\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"Milk that doesn’t upset your stomach!\", \"actor\": \"Touko\"}]'),(28,60,'en','[{\"line\": 0, \"text\": \"7th of July, 2pm, 2nd session\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Come on in.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Touko, Touko, hello.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Hello, Lain\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Hello.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"It’s almost the summer holidays! Are you looking forward to them?\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"Not especially.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Let’s see here, today, today I’d like to play a game with you Lain, that’s why I brought this.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"What’s that?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"It’s oil painting set. Do you like picture Lain?\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"I don’t like paintings... Maybe...\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"I see… Well, do you know tic-tac-toe?\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"I know it, but I don’t want to play that\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I… I’m sorry. I’m a bit frazzled today. Well, do you have anything you want to do Lain?\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"I’d like to talk some more\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"What do you want to talk about?\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"Whatever. About you.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"About me? Well… I’m not that interesting you know.\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"Yes, you are.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"No really, I’m not that interesting.\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"But look, I’m already 27, if we talked about me we’d have to start all the way at the beginning and it’d take forever!\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"And really, it’s not a very interesting story at that.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"What kind of doctor are you?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"What kind of doctor you ask?\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"Well, I help people like you who worry about certain things, and listen to their stories.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"And I give them advice to help fix their problems.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"You understand?\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"Ah… I thought so\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"What’s wrong?\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"If you’re not feeling well, or you’re not comfortable please tell me, you can be honest with me.\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"I should be more careful really.\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"You shouldn’t worry so much.\", \"actor\": \"Touko\"}, {\"line\": 32, \"text\": \"Just relax, come and have a nice talk with me once a month.\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"Okay…\", \"actor\": \"Lain\"}]'),(29,61,'en','[{\"line\": 0, \"text\": \"So, how’s school?\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Normal.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"By normal you mean? Not interesting, not boring, just normal?\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Yes…\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"How about friends?\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"I have friends.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Do you often play with them?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Yes, but everyone’s busy with cram school. The only place I go is here…\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Don’t you want to learn something? Calligraphy, or take swimming lesso… ooohhh…\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"It’s ok; don’t worry about it.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I’m sorry…\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"I’d rather stay at home than go out and do something.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"What do you do at home?\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"That’s a secret.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Aw, that’s mean. You won’t tell me.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"But I’m ashamed.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Are you doing things you’re ashamed of?\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"Maybe…\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Now I want to know! Ah, so be it… But I’d really like to know…\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"If I don’t tell you is that bad?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"I’d be glad if you did tell me.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"I talk to my stuffed animals.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Ha, ha, is that it? That’s completely normal. I knew it.\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"Knew what?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Ha, ha, never mind! I used to have a stuffed animal I loved…\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"What kind of stuffed animal?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"A dragon I got at the bank. Weird huh?\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"Why is that weird?\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"Well, aren’t girls supposed to play with cute stuffed animals?\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"It wasn’t even a stuffed animal… it was a piggy bank dragon.\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"But I still treasured it.\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"I called it Ta-chan and talked to it all the time.\", \"actor\": \"Touko\"}, {\"line\": 32, \"text\": \"Me too. I talk to my animals. Is that normal?\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"That’s normal! Do I look weird to you??\", \"actor\": \"Touko\"}, {\"line\": 34, \"text\": \"No…\", \"actor\": \"Lain\"}, {\"line\": 35, \"text\": \"You’re the same as me, Lain, we’re not weird at all!\", \"actor\": \"Touko\"}]'),(30,62,'en','[{\"line\": 0, \"text\": \"Hello Lain, have you been well?\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Yes, and you?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Yes, I’ve been fine. I’m a bit tired though.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Tired?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yes, I’ve been pretty busy.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"With what?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Hmm, well... At least talking with you like this is something I can look forward to.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Really?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Really! What shall we talk about today… What do you want to talk about Lain?\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"I want to talk about you, doctor. We weren’t able to last month.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I’d rather talk about you, Lain; not about myself, though.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"But you’re right, it’s only fair that we both get to talk about what we want, so this time we’ll talk about me, but next time we’ll talk about you Lain, is that all right?\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"OK!\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I’m not sure where I should start though... My name is Younera Touko, I’m 27, I’m a doctor at the Tokyo Research Institute for Psychotherapy… That’s this place.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Ah, we won’t get anywhere like this.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"You know, I’m used to listening people talk, not used to talking myself.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"Is there anything you’d like to know Lain?\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"How did you become a doctor?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"I studied a lot at a place called a university.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"I wrote something called a thesis, and with that I received my degree to become a doctor.\", \"actor\": \"Touko\"}]'),(31,63,'en','[{\"line\": 0, \"text\": \"What is college like?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"You’re in junior high school now aren’t you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"So if you’re in junior high and want to keep studying, you go to high school, right?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"College is where people go if they want to keep studying after high school.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Do you like to study, Sensei?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I didn’t used to.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"But I went to America as an exchange student when I was in junior high school, and I started to feel differently then.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"You were an exchange student. Wow!\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"At the time, I just went because my parents wanted me to.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"But people have different ways of seeing things in other countries, and I learned a lot from that.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Looking back, it was a lot of work, and at first, I didn\'t feel good about it, but now I’m really grateful that I was able to be an exchange student.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I learned English, too.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"That’s amazing, Toko Sensei!\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Oh, it’s not so amazing.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"There are lots of people who have studied much more than I have.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I’m really a pretty lazy person.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I liked to fool around, really.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"It’s boring to just study, isn’t it?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Do you like to study, Lain?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Not really.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"What subjects don’t you like?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Japanese and physical education.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"I see. So you don’t like exercise.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"No, I don’t like physical education.\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"You like exercise, then?\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"What kind of exercise do you like?\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"The metal beam.\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"Wow!\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"I used to like running, but I was never good at the metal beam.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"I would spin around and get too dizzy.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"I like to spin around.\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"Both frontwards and backwards?\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"Yes, both.\", \"actor\": \"Lain\"}, {\"line\": 34, \"text\": \"What don’t you like?\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"(silence)\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"You don’t have to answer if you don’t want to.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"But, what kind of study do you like?\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"I\'m sorry.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"Are you tired?\", \"actor\": \"Toko\"}, {\"line\": 40, \"text\": \"Why don\'t we end early today?\", \"actor\": \"Toko\"}]'),(32,64,'en','[{\"line\": 0, \"text\": \"I’m sorry about last time… but I’m relieved.\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"I was worried that you didn’t like me and wouldn\'t come anymore.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"…I’m sorry.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"There’s no reason to apologize.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"I was the one who asked you a bad question.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"Can you forgive me?\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"…thank you.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"So I thought we could work with this today. Can we talk about this?\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"They had that at the last hospital.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Really? I wanted to play with this.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"What kind of test is it?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Amazing Lain! Do you know about it?\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"They call this a Rorschach Test.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"This isn’t really a test.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Think of it as a game.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"But that’s boring.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Hmm… I guess so.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"If you think it\'s boring, we\'ll stop the test.\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"How’s that?\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Okay.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Okay. What do you see?\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"A splotch.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Um, right.\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"But does it look like anything?\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"What do you see?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Me? Hmm… a person’s face, I think.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"Is it angry? Is it also crying?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"It looks a little angry.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"This is boring.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"Lain…\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"Right. A promise is a promise. Let’s stop.\", \"actor\": \"Touko\"}]'),(33,65,'en','[{\"line\": 0, \"text\": \"Do you want to talk about something fun? Do you like to take a bath or a shower?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I like showers.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Really?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I prefer baths, but I don’t get a chance to take them much at home.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"It’s a bit of a hassle to prepare for a bath, too.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I usually take showers, but I take a bath on my days off.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Don’t you like the way a bath feels?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I’m afraid of being in hot water.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"You don\'t like to get wet?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"No.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Then, what are you afraid of?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I’m not really sure. It feels like I’m being touched all over by something.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Hmm… the water does touch the skin.\", \"actor\": \"Toko\"}]'),(34,66,'en','[{\"line\": 0, \"text\": \"It’s because I’m weird…\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Wait a second Lain.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"You’re not a weird kid at all.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Everyone thinks they’re weird when they’re kids...\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"But you’re wrong.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Do you dislike being different from others?\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"I’m not sure.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Okay?\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Lain is Lain because she is different.\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"You have a very strong imagination, and that’s a good thing.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"So don’t say that you\'re weird.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"I don’t think that at all.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"But… the test…\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I’m sorry… but you’re troubled, right?\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"I want you to not to be troubled.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"In order to help you, I have to know more about you.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"I don’t want to make you uncomfortable.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"What should I do?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Right now you don\'t need to do anything.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Since you and I are friends, if we keep talking, I’ll slowly start to understand more and more about you.\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"So, if I say, “I think you should come over,” it would be good for you to think, “That sounds nice, okay.”...\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"So right now, it’d be best if you tell me all about yourself.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"You won’t tell anyone?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Nope, it’ll be our secret.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"Not even your friends, or your mother?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"I promise. If I lie, one thousand needles!\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"One thousand needles?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Don’t kids your age know that?\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"When I was a kid, whenever we made a promise, we said, “If I break the promise, I will drink 1000 needles.”\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"Wouldn’t that hurt?\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"A lot!!... Right! How many needles are in my stomach right now? Miss Iwakura?...\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"… five thousand?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"Wrong! Yesterday, since I went to the bathroom and flushed everything out, there’s zero!\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"Sensei, that\'s gross!...\", \"actor\": \"Lain\"}]'),(35,67,'en','[{\"line\": 0, \"text\": \"Hello, Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"(in a soft voice) Hello.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"You don’t sound so energetic.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Maybe you don’t feel up for this.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Why don’t we quit now?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Ok?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"But since you came all the way out here, how would you like a cup of tea or something?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"What would you like?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Don’t be shy.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I want to talk.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"You don’t have to force yourself. That might make you feel even worse. Take my word for it.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I want to be cured.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Cured…but you’re not sick, Lain.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But this is a hospital isn’t it?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Well, this place can function as a hospital, but I see patients at my research room as well.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Right now, I don’t know if you are really sick or not.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"My hunch is that you are probably not sick, Lain.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Still, if you aren’t feeling well or are worried about something, I’d like to be able to help you.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I’m not sick?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Right now, I’m not completely sure, but you’re probably just fine.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Even if you are sick, I’ll make sure that you are cured.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"You’re absolutely sure you can cure me?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Absolutely. I will not let you be sick.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Sensei, why are you so kind to me? Is it because it is your job?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Hmm… You’re right that this is a part of my work.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"But it’s a type of work that’s hard to see as just work.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Most of the time, I see patients, diagnose them as objectively as possible, and give them psychological treatment, but it isn’t enough to make them better.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I’m a researcher as well as a doctor.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"If I see someone suffering, I must try and help that person.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"I want to do everything I can to help that person.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"You would do that for anybody?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"To be specific, if I see someone suffering from something for which the cause is physical and obvious, I refer that person to a specialist.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"If there is a car accident or a poisoning, that\'s what I would do.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Do put it simply, I’m not the kind of doctor who treats people who have something wrong with them physically.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"So I have…\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"I can’t tell for sure without doing a close examination, but I know that you had an examination before you came here and that no problems were found.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"And just by talking to you here, Lain, you don’t seem to have anything wrong with you at all.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"But I have to have you talk to me and tell me much more about what’s going on with you I can’t really help you with what’s bothering you.\", \"actor\": \"Toko\"}]'),(36,68,'en','[{\"line\": 0, \"text\": \"What bothers me is that I have nightmares, I see strange things, I feel that someone is touching me when no one is there.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I have symptoms of schizophrenia.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Schizophrenia? Where did you hear a word like that? Who said that to you?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I looked it up in a book.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Lain, it’s a good things to study hard, but it’s dangerous to go digging around and diagnosing yourself when you aren’t sick.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Have you heard the expression “a lot of how sick you feel depends on what you think about it?”\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Actually I haven’t.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Oh?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Anyway, if you start diagnosing yourself, you’re going to start believing that you have all sorts of things wrong with you.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"But I wanted to know what’s wrong with me. I wanted to understand.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I see.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I understand.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I think you’re intentions are right.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But I’d like to ask something of you.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Please don’t go around imagining and concocting… searching, for things that you think are wrong with you.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Can I ask that of you?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Then will you help me find what’s wrong?\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Of course I will.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"So let’s make a promise.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I will tell you everything I understand about you.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I won’t hide anything from you.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"If you lie can I cut off your finger?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Hmm… you can do something even worse. You can hammer a nail into my head.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"You’re goofy, Sensei.\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Seriously, though, I really want to help you.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"I’m an only child, and I always wished I had a younger sister—someone like you, Lain.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"You’re my big sister then?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"I’d be happy if you saw it that way.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I will.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"Thank you. Anyway, let’s end for today. You’re tired aren’t you?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Uh, uh. But I’ll do what you asked me to do, Sensei.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"Great! Hee, hee. Actually, I’m the one who’s tired.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"You’re tired of me?\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"No, no!\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Don’t think that.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"I’ve had a lot of other work to do today, too.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"I like you and I’ll miss you until we meet again, Lain.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"That makes me feel better.\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"Let’s end now, then. It’s getting late and we don’t want your mother to worry.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"Goodbye, Toko Sensei.\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"Goodbye, Lain-chan.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"Just call me Lain. It’s a bit weird for a “big sister” to call me Lain-chan.\", \"actor\": \"Lain\"}, {\"line\": 42, \"text\": \"Ok then, goodbye, Lain.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"Goodbye.\", \"actor\": \"Lain\"}]'),(37,69,'en','[{\"line\": 0, \"text\": \"Hello, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Hello, Lain.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Ok, from today we’re going to really tackle what’s bothering you, Lain-chan.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I’m sorry.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I mean Lain.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"We’re going to try to figure things out so you don’t see nightmares anymore.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Uh, huh. I haven’t seen nightmares much lately, though. Sometimes I hear things, though.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Hear things? Like what?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I don’t know.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"It sounds like someone talking.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I don’t like hearing it, so I try my best to ignore it.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Hmm. Do you know whose voice you hear?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I don’t know. It can be a man’s voice or a woman’s voice.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Where do the voices seem to come from?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"From the sky.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"The sky? From what part of the sky?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"From the electric lines. Somehow…\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"The electric lines… Do you know what’s in side of electric lines?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Wiring that carries electricity.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Right. They carry electricity that comes from power plants. Do feel like you sense electricity?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I don\'t really know. Sometimes it seems to come from the sky; not the electric lines.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Are you bothered by it right now?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"No. I’m not scared now. But…\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"When you told your mother, did she tell you not to tell anyone else about these things?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Uh huh.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"People who don’t hear these things would think strange things about you if you told them, huh?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"You don’t have to worry about telling me anything you want to.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"You don’t have to worry.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Ok?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"I’m not going to think strange things about you.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Because I’m sick?\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"No, Lain. Let’s see. You know about “kanashibari?”\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"When a ghost comes out and you can’t move your body?\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"Right. Have you ever had that happen to you?\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Many times.\", \"actor\": \"Lain\"}, {\"line\": 35, \"text\": \"It was frightening, wasn’t it?\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"Uh, huh.\", \"actor\": \"Lain\"}, {\"line\": 37, \"text\": \"Do you know why kanashibari happens?\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"Why?\", \"actor\": \"Lain\"}, {\"line\": 39, \"text\": \"The human body is very mysterious.\", \"actor\": \"Toko\"}, {\"line\": 40, \"text\": \"When you’re sleeping, there are times when your mind is awake, times when your body is awake.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"Do you understand?\", \"actor\": \"Toko\"}, {\"line\": 42, \"text\": \"Sort of…\", \"actor\": \"Lain\"}, {\"line\": 43, \"text\": \"Well, that’s good enough for now. What parts of you are awake right now?\", \"actor\": \"Toko\"}, {\"line\": 44, \"text\": \"My body and my mind.\", \"actor\": \"Lain\"}, {\"line\": 45, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 46, \"text\": \"Suppose I said something really scary right now and you feel scared enough to run out of this room.\", \"actor\": \"Toko\"}, {\"line\": 47, \"text\": \"What would you do?\", \"actor\": \"Toko\"}, {\"line\": 48, \"text\": \"I think I would run out of this room.\", \"actor\": \"Lain\"}, {\"line\": 49, \"text\": \"Ok.\", \"actor\": \"Toko\"}, {\"line\": 50, \"text\": \"Suppose that you feel so scared that you want to run out of this room, but your body is asleep and you can’t move.\", \"actor\": \"Toko\"}, {\"line\": 51, \"text\": \"That’s what kanashibari is.\", \"actor\": \"Toko\"}, {\"line\": 52, \"text\": \"So if you’re having a nightmare but your body is asleep and you can’t move, you are having kanashibari.\", \"actor\": \"Toko\"}, {\"line\": 53, \"text\": \"You see?\", \"actor\": \"Toko\"}, {\"line\": 54, \"text\": \"If you know what’s involved, it’s not so scary, is it?\", \"actor\": \"Toko\"}, {\"line\": 55, \"text\": \"No. It isn’t.\", \"actor\": \"Lain\"}]'),(38,70,'en','[{\"line\": 0, \"text\": \"There are many people who have had experienced kanashibari, but we don’t call them “sick.”\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"But…\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Hmm… there might be only a few people who have experienced what you have.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Still, I don’t think you should be called “sick” just because of your experiences.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"A lot of things can happen to a person while she’s young and impressionable.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"But I’m scared and I hate this.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"What are the scariest parts of your dreams?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I start to feel that I don’t need myself. I feel that I am an unneeded thing.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"You “don’t need” yourself?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I feel that I am being told to come back because I’m unneeded.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Come back? Come back where?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"To myself.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Hmm. This is hard to understand. You want you to come back to yourself?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I don’t know. But for a person like me to be me, and to touch body, feels like I’m being torn…\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"(interrupting) And it’s painful?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"It isn’t painful, but it’s frightening.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"I see. Does this happen when you’re asleep?\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Probably.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Probably? This happens sometimes when you’re not dreaming?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I don\'t know.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Sometimes it happens when I’m just sitting by myself, and sometimes when I’m talking to my friends.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Tell me what happens when you’re not alone. This happens when you’re just having conversations?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Yes. While I’m just talking, it suddenly…\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"How do you notice it? Do your friends call out to you?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Suddenly…\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Is there something that triggers it?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I was afraid, and I felt better after staying motionless for a while.\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Can you explain what it felt like, or what you saw? Could you draw a picture?\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"No, I can’t.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"I see… Oh!\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"We have to end.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"I’m sorry, Lain.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"I have to go out and take care of some things today.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"I’m sorry to have to stop right in the middle of our appointment.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"But it’s good for now that you can’t see these things.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"I think that what you experience probably has something to do with kanashibari.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"I’ll do some research on it until next month.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"That’ll be my homework.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"Your homework will be keeping a diary.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"A diary?\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"I want you to write about the things you did during the day, and what you thought about.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"You don’t have to show it to me.\", \"actor\": \"Toko\"}, {\"line\": 42, \"text\": \"Just write what you want.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"I used to keep a diary when I was your age.\", \"actor\": \"Toko\"}, {\"line\": 44, \"text\": \"But…\", \"actor\": \"Lain\"}, {\"line\": 45, \"text\": \"You don\'t want to?\", \"actor\": \"Toko\"}, {\"line\": 46, \"text\": \"It can be really interesting to look back on the things you write in your diary.\", \"actor\": \"Toko\"}, {\"line\": 47, \"text\": \"We can be forgetful, so it can help you remember things, too.\", \"actor\": \"Toko\"}, {\"line\": 48, \"text\": \"That will cure me?\", \"actor\": \"Lain\"}, {\"line\": 49, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 50, \"text\": \"That probably doesn’t have anything to do with it.\", \"actor\": \"Toko\"}, {\"line\": 51, \"text\": \"It can’t hurt, though.\", \"actor\": \"Toko\"}, {\"line\": 52, \"text\": \"I’ll explain more carefully next month.\", \"actor\": \"Toko\"}, {\"line\": 53, \"text\": \"I don’t mind if you don’t want to keep a diary.\", \"actor\": \"Toko\"}, {\"line\": 54, \"text\": \"I went and bought you a diary book, though.\", \"actor\": \"Toko\"}, {\"line\": 55, \"text\": \"See?\", \"actor\": \"Toko\"}, {\"line\": 56, \"text\": \"It’s cute, isn’t it?\", \"actor\": \"Toko\"}, {\"line\": 57, \"text\": \"I found it when I went shopping last week, and I thought it would be a nice present for you.\", \"actor\": \"Toko\"}, {\"line\": 58, \"text\": \"Thank you.\", \"actor\": \"Lain\"}, {\"line\": 59, \"text\": \"But don’t force yourself.\", \"actor\": \"Toko\"}, {\"line\": 60, \"text\": \"You don\'t have to keep a diary just because I bought you one.\", \"actor\": \"Toko\"}, {\"line\": 61, \"text\": \"Feel free to write in the diary if you want to, though.\", \"actor\": \"Toko\"}, {\"line\": 62, \"text\": \"Well I guess we’ll wrap it up, then.\", \"actor\": \"Toko\"}, {\"line\": 63, \"text\": \"Make sure you’re not forgetting anything.\", \"actor\": \"Toko\"}, {\"line\": 64, \"text\": \"I’ll walk out to the corner with you.\", \"actor\": \"Toko\"}]'),(39,71,'en','[{\"line\": 0, \"text\": \"How are you feeling, Lain?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I’m doing ok. How about you, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Pretty much as always. I could use a little more time to relax a little, though.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"You’re very busy?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yes… Ha, ha. Now our roles are reversed. I feel like you are counseling me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Ha, ha\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Ha, ha. By the way, what did you decide to do about the diary?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I decided to keep a diary.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Wow, that’s great! I didn’t waste my money right before payday.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I’ll pay you for the diary.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I’m joking, I’m joking.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"But I get paid well anyway.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"For the money I earn, the diary may have been a bit cheap.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Oh, no.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"This isn’t good.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I’m just yacking on about myself.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"But you’re funny, Sensei. You’re a lot of fun.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Ha, ha.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Well, why don’t we begin?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Let’s pick up where we left off last time.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Have you learned the word “ego” at school?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Uh huh. I looked into it. It’s about knowing that you are yourself?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Right.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"People around your age can get unsure about that.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"They ask themselves “what kind of person am I?” Boys sometimes think that they are heroes and that they will accomplish some superhuman feat someday.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Girls sometimes imagine that they don\'t belong with their own families.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"They think instead that they are members of a rich elite family, and that a prince on a white steed will come one day to take them away.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Kids your age sometimes believe these things for real.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I don’t feel that way. I think that I’m just me.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"But It’s hard to tell what you are just by yourself, isn’t it?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"When I was younger, I thought that there was a different me, and that somehow I wasn\'t the person I was at the time.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"A different me?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"For example, if there was something happening that I didn’t like, I wondered why I had to be the one dealing with it.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"I thought there had to be some kind of mistake.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"I thought that someday I would return to the real me.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"I also thought things like, if there was a big earthquake, everyone would die, but I would be the only survivor.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"I thought that I was someone special; someone “chosen.” In another words, I believed in a “me” that could not be.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"It’s funny, isn’t it?\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"I don’t believe that I’m anyone chosen. I’m ok with being ordinary.\", \"actor\": \"Lain\"}]'),(40,72,'en','[{\"line\": 0, \"text\": \"I see.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"That’s probably because you are a strong person.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I think I was probably weak and that I was trying to run away because of it.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Do you think I’m trying to run away from myself?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I don’t think so. But you want to change yourself, don’t you?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I just don’t want to see any more bad dreams. That’s how I feel.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Could it be that you don’t want to be the person who sees the bad dreams?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Maybe you feel that way somewhere deep inside yourself?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Could it be that you are subconsciously trying to construct a “me” that isn’t “me”?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"What should I do?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Do you think you can accept your dreams?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Accept?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Right.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I mean facing the “you” that you don’t want to meet or hear, and then trying to become friends with them.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I can’t.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"I think that all you have to do is listen to the voices.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"See what they are saying and see what they want to do.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Face them until you understand them and expose them.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I’m scared.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"But you’ve made it so far.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"You’re still you.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"You’ve never had a ghost punch you or had a voice act violently with you, right?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Maybe these ghosts and voices are trying to be friends with you.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Ha, ha.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"If you don’t see the ghosts again, great.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"But I if you do see them, I think that it’s important that you have a good attitude and know how to brace yourself.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"If the ghosts show up, just shake it off your shoulders and say, “oh, here they are again.”\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"I’m leaving. I’m going home.\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"Why? Did I say something I shouldn’t have?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"I didn’t lie to you. I didn’t see any ghosts or phantoms. You’re a big jerk!\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"Lain!\", \"actor\": \"Toko\"}]'),(41,73,'en','[{\"line\": 0, \"text\": \"I’m sorry ma’am.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I can handle it from here.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"(pause) Lain, I’m sorry.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I thought that if I didn’t come here personally, you would never see me again.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I came to apologize.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I think I was arrogant about my assumptions about you.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Still, I wanted you to understand and to be comfortable.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I just thought that if you could face your dreams and understand where they were coming from, that they would stop.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I’m sorry, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Why?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I lied to you… I didn’t keep the diary.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"That’s ok. I told you that it was all up to you.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"You were trying to be helpful to me.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Thank you. I’m grateful that you feel that way. Can you forgive me, Lain?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"I’m so glad.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I was so sad when you walked away from our session.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I cried.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I felt so bad.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I though you didn’t like me anymore.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Toko-san, don’t cry.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Oh.. It’s so silly of me to cry over this. I’m sorry, Lain.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Don’t cry.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"These are tears of joy, Lain. I’m so glad.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Joy?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Yes. Tears of joy.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I’m really happy, too. I feel so close to you.\", \"actor\": \"Lain\"}]'),(42,74,'en','[{\"line\": 0, \"text\": \"Let’s skip counseling today and just relax instead.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"You came all the way out here.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Do you want something to drink?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I cried so much that I’m thirsty.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Toko-san, I’ll start keeping my diary. Honestly, this time.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Don’t force yourself. You’re a kind girl.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I’m not forcing myself.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"The diary is for me, after all.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"And the next time I hear the voices, I’ll listen to see what they are saying.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Then, I’ll tell you what they said, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Thank you, Lain.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Happy birthday, by the way, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Huh! My birthday?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I made this for you with my mom.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Really? I’m so happy! Can I open it?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Sure.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Cookies! Wow. There’s some tea ready right now. Why don’t we have the cookies with some tea?\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Is it good?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Mmm. It tastes like tea.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"You said that you like tea, so we made the cookies that way.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Thank you. But then we better have a different kind of tea.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"It won’t go with your tea?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"No, not that it wouldn’t go with it, but it would be too bad if would couldn’t enjoy the tea and cookies as much as possible.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Should I have made cookies with a different flavor?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Oh, no, no.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Not at all!\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"But you took all the trouble to make such nice cookies, and I want to enjoy them as much as possible.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"That’s all.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Really?\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"Really. Thanks so much, Lain.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Sensei, it hurts.\", \"actor\": \"Lain\"}]'),(43,75,'en','[{\"line\": 0, \"text\": \"Hello, Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I write in my diary everyday. Sometimes I don’t have much to write, though.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Don’t worry about it.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"If you force yourself you’ll just want to give up.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I don’t know how many diaries I’ve had where I missed entries.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Have you seen any of them since then?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I’ve been ok.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Really? That’s good. There’s something I want to confirm with you today.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"What?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"You have really good eyesight, don’t you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Really good?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"You’ve had eye exams at school, haven’t you? What’s your score?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"One-point-five.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Your eyesight is really even better than that, isn’t it?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But I wanted to have the same score as all the other kids.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Well there’s no point to having the examination then.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"At the last hospital where we used to meet, they examined your eyes, too.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But they did it differently than at your school.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"They had a machine that measures eyesight automatically.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"The machine only measures as far as three-point-zero, and your score was three-point-zero.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"There are some kids with extremely good eyesight.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"The doctor in charge of your eye-exam spoke with a cancer specialist, who said he saw no problems.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Oh.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Lain, what can you see outside the window?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Trees and a gateway to a shrine in the distance.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"And beyond that?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Buildings.\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"And beyond that?\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Mountains.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"And beyond that?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"I can’t see anything further.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"Nothing at all?\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"A purplish color that looks like a curtain.\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"Thank you.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"I’m sorry to pepper you with all these questions.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"I think that you probably see the world in a different way than I do.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"Lain, don’t look so glum.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"It’s nothing to worry about.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"It’s not such a bad thing… I’m sorry for saying that.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"I guess it is bad thing for you.\", \"actor\": \"Toko\"}, {\"line\": 40, \"text\": \"To see things so differently.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"I don’t know.\", \"actor\": \"Lain\"}, {\"line\": 42, \"text\": \"But I think that I’m probably different from everybody else.\", \"actor\": \"Lain\"}, {\"line\": 43, \"text\": \"I feel like I’m the only person who can hear and see some things.\", \"actor\": \"Lain\"}, {\"line\": 44, \"text\": \"It makes me very uncomfortable.\", \"actor\": \"Lain\"}, {\"line\": 45, \"text\": \"I think that I will be disliked.\", \"actor\": \"Lain\"}, {\"line\": 46, \"text\": \"Is that the reason?\", \"actor\": \"Lain\"}, {\"line\": 47, \"text\": \"It might be. I think that you’d have to be seen by a specialist.\", \"actor\": \"Toko\"}, {\"line\": 48, \"text\": \"No. I want to see you. I don’t want to see any of the other doctors here. They all scare me.\", \"actor\": \"Lain\"}, {\"line\": 49, \"text\": \"Why don’t we just keep this as a secret?\", \"actor\": \"Toko\"}, {\"line\": 50, \"text\": \"A secret?\", \"actor\": \"Lain\"}, {\"line\": 51, \"text\": \"Um hm. It will be our secret until you aren’t worried about it anymore.\", \"actor\": \"Toko\"}, {\"line\": 52, \"text\": \"Is that ok?\", \"actor\": \"Lain\"}, {\"line\": 53, \"text\": \"I’m your big sister, remember? I’m not going to take you anywhere that you will be scared.\", \"actor\": \"Toko\"}, {\"line\": 54, \"text\": \"Toko-san.\", \"actor\": \"Lain\"}]'),(44,76,'en','[{\"line\": 0, \"text\": \"Good morning, Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Good morning, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Have you seen anything since then?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"No, I haven’t. Honestly.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Have you heard anything?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Sometimes I hear something very confused.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Very confused? One person? More than one?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"More than one. Men and women.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Are they people you know?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Probably not.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"You said that you heard things from the sky. What do you think is in the sky?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"There’s air.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Uh, huh. And aside from that?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Clouds. The sun. The stars.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Do you know about the transmission of radio wavelengths for TV and cell phones?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Yes. The information travels wirelessly?\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Right. The information flies through the sky.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Am I hearing that information?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Hmm. It might be that you imagine you are hearing that information.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"There’s a sickness like that?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"There is.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"It’s called delusion.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"It’s a symptom of schizophrenia and neurosis.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Sometimes, if you are focusing much too much on something or are getting very stressed, it can bring out those kind of symptoms.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"But…\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"But?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Usually, when people say they hear things, what they hear is more closely associated with themselves.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"For instance, they hear voices that bad-mouth them.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Delusion of persecution?\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"Right.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"You’ve been studying up on this stuff.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Ok. I’ll fill you in a little more on this.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"The definition of “delusion” is taking a mistaken idea and believing in your own mind that it is correct.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"But for you, Lain, you say that you might have felt or sensed something.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"A person who is really delusional tends to have conviction that his delusions are real.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"But you don’t have that.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"You have thoughts, considerations.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"Considerations?\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"You can forget that word if you’d like.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"As I mentioned before, a delusion is generally something intertwined with one’s self.\", \"actor\": \"Toko\"}, {\"line\": 40, \"text\": \"There really aren’t any delusions that have nothing to do with someone personally.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"I’m not delusional?\", \"actor\": \"Lain\"}, {\"line\": 42, \"text\": \"Fundamentally, no.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"Still, there are all sorts of delusions, and if you try to “pin” one of them onto someone, you may start pinning other ones on that person, too.\", \"actor\": \"Toko\"}]'),(45,77,'en','[{\"line\": 0, \"text\": \"What could happen?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"There is a delusion some people have that they are being influenced by something.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"The delusion that the radio or TV or cell phones are trying to influence them somehow.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"The delusion that TV and cell phones have the power to touch them physically.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"You said that you thought you were being touched by something, didn’t you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Uh, huh.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"It might not even be a delusion.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"There is an actual medical condition that makes people very sensitive physically.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"But you said that you don’t feel like you are yourself, didn’t you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"That makes me think that you have the delusion that nothing exists.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"That you think you aren’t real and that existence isn’t real.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"This can come as the result of poor self-esteem.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Poor self-esteem?\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"It’s the feeling that one is worthless.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"It’s similar to the delusion of persecution we were talking about.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I’m really sick.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"There are other kinds of delusions.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I don’t want to listen anymore.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Some people have the delusion that they have a special, unique sickness.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"It’s the sickness of thinking they have a sickness, when they really aren’t sick.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"I think that what you have, Lain, is mostly to do with this kind of delusion.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"The sickness of thinking that I am sick when I’m not sick?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"I think that you have poor self-esteem, and that a small insecurity manifests itself in nightmares, and makes you think that you are sick.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Can I be cured?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Sure!\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"After all, you’re not sick in the first place.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I’m sorry to have alarmed you.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Still, if you don’t understand psychiatry well, you can use it to make all sorts of misguided conclusions.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"You can diagnose yourself as being sick.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Most people go to the doctor when they or someone they know can’t carry on their day-to-day activities.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"They come in to see a doctor and they are diagnosed or treated, and they are diagnosed sometimes as sick.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"So for someone like you, who can continue her own day-to-day activities without a problem, we can’t really say that you are sick unless something’s bad enough that you to need hospitalization.\", \"actor\": \"Toko\"}]'),(46,78,'en','[{\"line\": 0, \"text\": \"Am I sick because I came to the hospital?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Well, you can carry on with your life, but you’re troubled, aren’t you Lain?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"It’s the job of a counselor to help with that.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Are you a counselor, Toko-san? You said before that you’re a doctor.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I’m a counselor and a doctor.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"How are the two different?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Well, both jobs have to do with helping people with psychological problems.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"They’re similar in the sense that they have the same goals.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"But the difference is that as a counselor I care for patients differently than I do as a doctor, and that each job has different qualifications.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Each job has its strengths and weaknesses, and I think it’s best when I can make their strengths compliment each other.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"That’s why I’m a doctor and a counselor.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Are you a doctor or a counselor when you see me?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"I’m a counselor. I think it’s better for me to be a counselor when I see you.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I’m not mentally ill? You said that the things I see are hallucinations.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"It’s true that hallucinations and delusions are symptoms of mental illness, and this is just my hunch, but you don’t seem to be mentally ill to me.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Do I pay too much attention to things?\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Yes, I think that’s probably right.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I think your tendency to observe things and listen to thing very carefully might be stressing you out.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"What can I do about it, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Well… even if I told you not to mind things too much, you’re going to mind things.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I don\'t mind anymore. I’m getting used to this. Still, I can\'t get used to that.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"“That”?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"I’m not the me that I am right now.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"What kind of Lain are you?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"I’m me. That person is, too.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Why do you feel that way?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I don’t know. Have you ever thought that you aren’t you, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Looking back on myself in the past, I’ve wondered what I was doing sometimes.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"But I’ve never thought at the time that I wasn’t me.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Do you feel that there are two of you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"No. I’m me and I’m not me.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"That’s mysterious.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"But if you feel that way, that must be true for you.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"You don’t have to feel that you’re mistaken.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"But it isn’t usual.\", \"actor\": \"Lain\"}]'),(47,79,'en','[{\"line\": 0, \"text\": \"You’re really yearning all the time, aren’t you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Probably…\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I’m envious.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Human perception is a wild thing.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"If you start feeling something, you start believing it.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I don\'t know how to explain to anyone that I am really me.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"But being able to question that sense of self is a special talent.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I don’t want a talent like that.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Yeah.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I’m sure you feel really strongly that way right now.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"But I feel that it can be a good thing for you too.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I don\'t understand it well right now, but I think that if you can include and accept that as part of yourself, it would help you in the long run.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I won’t change?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I don’t know.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"You might change naturally and stop having nightmares.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"If that happens, you’d be like most other kids.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"But I won’t know if I’ve gotten better. I might have nightmares again.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Yeah. But things will change from now on.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"What do you mean?\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"I mean that you’re not alone. I’m here and you can talk to me about things.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"That’s true, Toko-san.\", \"actor\": \"Lain\"}]'),(48,80,'en','[{\"line\": 0, \"text\": \"Good morning, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Good morning. How are you? Have you been feeling healthy?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Uh huh. I thought about the things you talked about last month and I’ve somehow been feeling better.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Oh, that’s great.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Actually, though, it’s against the rules to talk about the things we talked about last month.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"In some people it can trigger something irreversible.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Irreversible?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Their sickness could worsen, and sometimes, patients can hurt themselves physically.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Suicide?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Well, that would be the worst-case scenario.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I don’t want to die.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Of course not!\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Far from it.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But when people become troubled emotionally, they start to see things and act differently than you or I would.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"See things differently?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"They can lose their fear of dying or hurting themselves.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"In fact they start to feel that dying or getting hurt are very good things.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"You can’t understand that, can you?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I am afraid of those things.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Sure! Me too. We’re the same way. That’s normal.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Ok.\", \"actor\": \"Lain\"}]'),(49,81,'en','[{\"line\": 0, \"text\": \"Have you seen or heard anything since then?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"No.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"See!\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"It was a state of mind, Lain.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I think that you probably won’t see them again, but if you do, don’t get discouraged.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"You’ll get over it gradually.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I won’t get better right away?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Hmm. I don’t know. How old were you when you started having problems with them?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I was eleven.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"So it’s been over a year now. During that time, how often did it happen?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I don’t remember… once.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Sometimes, even if there doesn\'t seem to be an explanation for it immediately, these things may go away.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"But if you decide that they’ve gone away completely and become complacent, there’s a chance that they may come back.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I can’t say for certain that that will happen with you, but even if you don’t see them for a while, I’d like you to continue what you’ve been doing up till now.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Do you want to stop coming here, Lain?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"No. I like seeing you.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Thank you.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I guess that there’s no problem, then.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"You’re doing well, but you’ll promise me that you will keep coming here?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Yes. In exchange, I want you to teach me some things.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Why?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"For homework?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Sure.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"You won’t believe it, but I was pretty sharp in junior high school.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Ha, ha.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Learning to read and write was tough, though.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I don’t mean that. I want to know more about your work. Is that ok?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I don\'t think it’s a very good idea, but if you’re really curious, ok. Learning about things that make you curious is a great way to study.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"But you have to make time for your schoolwork, too.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"I’ll promise to teach you about my work if you promise to do your schoolwork.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Can you promise me that?\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"Yes, I promise.\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"And if you break your promise…\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"I’ll whack you with a blowfish!\", \"actor\": \"Lain and Toko together\"}]'),(50,82,'en','[{\"line\": 0, \"text\": \"You are a counselor and a psychiatrist, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"And a psychiatrist is someone who heals mental illnesses?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Uh huh. Put simply, I’m a doctor of the mind. As I mentioned before, mental illness is very complex.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"What is mental illness?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Basically, a person’s mind becomes disturbed for some reason, and the person is unable to continue functioning.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"What sort of reason, for instance?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"There can be many reasons.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Problems with a living environment or work environment can be reasons.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Sudden, psychological shock can also be a reason.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"The sudden death of a well-loved pet can trigger mental illness.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Sometimes people become mentally ill because of something that happens to them physically.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"A car accident or some other kind of physical trauma can be a cause.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Also, people who drink too much can damage the function of their brains.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Alcoholism?\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Exactly.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"You’ve been studying!\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"If you drink too much the alcohol content in your blood will rise.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"You’ve seen drunk people wobble around without balance, haven’t you?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"They’re in an alcoholic stupor.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"But normal people aren’t that way, are they?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"No, they can be.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"It takes about 24 hours for alcohol to leave the human body.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"If you drink a lot everyday for a year or so—if you stay drunk long enough—you’ll your perception will start to change.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Your brain will start to deteriorate.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"That’s scary.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"It is scary. I still have some sake now and then, though.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Isn’t that dangerous?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Ha, ha.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Oh, a little bit.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"And smoking is bad too.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"But having a drink with friends every now and then can help relieve stress.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Relieving stress is very important for the body’s health.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"I don’t understand what stress is.\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"Remember when you didn’t like coming here at first?\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 35, \"text\": \"Stress is what we call the cause of that discomfort.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"You’re glad that the discomfort is gone, aren’t you?\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"Yes. What other kinds of mental illness are there?\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"Could you excuse me? I’ve been talking so much and I’m a little thirsty.\", \"actor\": \"Toko\"}]'),(51,83,'en','[{\"line\": 0, \"text\": \"I wanted to ask last time, but why do have that kind of hairstyle?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"It’s weird?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"No, it’s great! You really look good that way. Is it a popular look at your school?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"No. It’s just my own style.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"You mentioned that you want to be like other kids. Why do you have a unique look?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"It comes from the right, and probably, that’s why it goes out of the left. That’s why...\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"It works a little bit like a magic charm, then. I used have a magic charm, too.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"You did, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Sure.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"It didn’t have to do with hairstyle, but I used to have a lucky rabbit’s foot.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I used to believe that it would keep me safe and happy, and I tied it to my backpack.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Looking back, it seems so ghastly.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"To tie a little bunny’s foot to my backpack.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But then again, believing in the rabbit’s foot really made me feel safer.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"A magic charm?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Yeah.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Your hair looks really good, though, Lain.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"If you had a hairstyle like everyone else’s, it wouldn’t be like you.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I think you look great the way you are.\", \"actor\": \"Toko\"}]'),(52,84,'en','[{\"line\": 0, \"text\": \"You know about neurosis? Wow, Lain. You’re going to have to start counseling me.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I know the word, not its meaning.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"What kind of image?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"The mind changes and becomes distorted?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yes. You’ve got the general idea.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"That’s awful. You won’t teach me anymore?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"No.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I’m really telling you that you got the general idea.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Neurosis is a state in which there is nothing wrong with a person physically, yet the person has irrational fears and perceives threats.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"This places a lot of stress on the person and makes her feel sick.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"It’s only inside of their heads?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"We used to think so.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"But after a lot of research, we think that an imbalance of the autonomic nerves in the brain is related to it.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"What I mean is, something is wrong in the body, too.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"We used to categorize physical symptoms as psychosomatic.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"But we’ve been finding more and more that conditions we thought were only of the mind involve the body itself.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Psychosomatic?\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Oh, Lain, if ask so many questions, your head is going to explode!\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"You’ve heard about people who lose a lot of weight because of stress, or go bald?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"That’s psychosomatic.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Lain, you don’t, by any chance…\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"No, I don’t have that kind of problem.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Oh, I’m relieved. That was hasty of me!\", \"actor\": \"Toko\"}]'),(53,85,'en','[{\"line\": 0, \"text\": \"Who decides the names of diseases?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Various doctors.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"But we have general criterions.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"We have the ICD and the DSM, which are international standards for the definition of mental diseases.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"We know that if a person has a certain set of symptoms, we can consult these standards and find the exact name of the disease the person is suffering from.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"As you know, the mind can be a difficult thing to understand.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"For that reason, psychiatrists have publications, and we communicate all the time, sharing our data and our understanding of mental illness.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"We accumulate data and categorize it so that we can diagnose patients as correctly as possible.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"There’s mental illness you don\'t understand? Not even you?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"To tell you the truth, we don’t understand much of this at all.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"If there has been a successful treatment method for a patient with a certain disease in the past, we can apply that treatment method toward a patient suffering from the same disease in the present.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Much of this has to do with experience.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"You do this even if you don’t know if a person can be cured?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Right.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"You can’t tell if a person will be cured.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"We can only try our best to heal and help with our experience from the past.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"That’s usually the case.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But from the point of view of the patient, knowing if one will become better is very important.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"In fact, the understanding of the mechanisms behind getting better is an area of study of its own.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I see. It would be so much better if we could understand everything.\", \"actor\": \"Lain\"}]'),(54,86,'en','[{\"line\": 0, \"text\": \"Do you have many memories from your childhood?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Memories? Do you mean records?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Well, memories and records aren’t exactly the same.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Don\'t think about it too much.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"If you’ve traveled somewhere during summer vacation…\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I haven’t traveled anywhere during summer vacation.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I’m sorry.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"In my youth, both of my parents worked, so I didn’t get to travel very much.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I didn’t like it when I went back to school after vacation and other kids would ask me where I went or what I did during the summer.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"What’s the difference between a memory and a record?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"From a medical standpoint, a record is something documented that lists exactly what happened, when and where.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"A memory is more subjective.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"It’s what you remember personally.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"It’s something in your mind that you can access from time to time.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But Lain, doesn’t all this stuff bore you?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"No. How does a memory enter the brain?\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"How? That’s a pretty complicated subject. Are you sure you want to get into it?\", \"actor\": \"Toko\"}]'),(55,87,'en','[{\"line\": 0, \"text\": \"There are different kinds of memories.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"First of all, there’s one’s memory of oneself.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"You know what self- understanding is?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"You know that you are a girl named Lain with a certain face and height and weight?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"That’s all recorded at a molecular level.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Aside from the brain, there are places throughout the body that carry residues of things that have happened.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You know that if you get cut, you bleed, but if you wait a while, it gets scab and heals?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"That has to do with information stored in cells going to work.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Take a look at athletes and people who have gone through intense physical training.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"They seem to do things effortlessly.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"That’s because their bodies have their own recollection or memory.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"If you generate a recording, you have a memory?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"You might be able to say that sometimes, but not always.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Humans are haphazard and unpredictable, aren’t they?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I don’t remember everything that happened to me as a kid precisely.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I have happy memories of times I spent with my family, but my family might not have happy memories of those times at all.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I may have had misperceptions in the moment, or I may have remembered things incorrectly and by mistake.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But we all walk around with memories, thinking that what we remember is what actually happened.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Suppose you meet an old friend that you haven’t seen in a long time.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Don’t you remember the things you used to do with that person right away?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Your memory has a way of linking things together.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"If you can’t link things together, you’re sick?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Well, there is something called memory impediment.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"People who have this can’t remember things that happened only moments ago.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"They have no idea of how things in the past took place.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"They don’t know what happened and in what order.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Since they don’t want anyone to know that they don’t remember things, they often make up stories about the past to fool people.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Humans are complex.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"We do all sorts of things.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"You’ve seen senile old people on TV shows, haven’t you?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"As humans, there are some things we can’t do anything about.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Things we can’t do anything about?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"I’m not saying that you’re sick, Lain.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"There are things we can’t do anything about, aren’t there?\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"We can’t flap our arms and fly, can we?\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"There are memories in the body and the brain?\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"There are still many things we don’t understand.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"The human brain is made up of many different things.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"When something is recorded there, there is a change that takes place that becomes the foundation for memories.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"I don’t understand.\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"Yeah.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"When you go to high school and college, you can study up on all this stuff.\", \"actor\": \"Toko\"}, {\"line\": 42, \"text\": \"But it’s good that you have a healthy curiosity.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"I don’t think you’re sick, Lain.\", \"actor\": \"Toko\"}, {\"line\": 44, \"text\": \"I feel that my memories aren’t my memories.\", \"actor\": \"Lain\"}, {\"line\": 45, \"text\": \"Sure! I feel that way sometimes, too.\", \"actor\": \"Toko\"}, {\"line\": 46, \"text\": \"Even when it’s me. But I don’t remember anything else.\", \"actor\": \"Lain\"}, {\"line\": 47, \"text\": \"You’ve just forgotten something, that’s all.\", \"actor\": \"Toko\"}, {\"line\": 48, \"text\": \"Didn’t I say that human beings are haphazard and unpredictable?\", \"actor\": \"Toko\"}, {\"line\": 49, \"text\": \"We remember things the way we want to remember them and often forget what we want to.\", \"actor\": \"Toko\"}, {\"line\": 50, \"text\": \"That’s being human.\", \"actor\": \"Toko\"}, {\"line\": 51, \"text\": \"That’s normal.\", \"actor\": \"Toko\"}, {\"line\": 52, \"text\": \"Normal?\", \"actor\": \"Lain\"}, {\"line\": 53, \"text\": \"Yes. You dwell on things too much, Lain. All those things are normal.\", \"actor\": \"Toko\"}]'),(56,88,'en','[{\"line\": 0, \"text\": \"I wondered if your name has anything to do with R. D. Laing. Oh, it couldn’t, could it?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"R.D. Laing?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"It’s a person’s name.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"An English psychiatrist.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"He was an unusual person.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"The titles of some of his books are very interesting.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"One of them is: “I Love, Love, Really Love.”\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"“I Love, Love, Really Love”?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I read it when I was in college and I don’t remember much of it, but it’s about two lovers—a man and a woman—and the woman asks the man if he loves her.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"And?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Well of course, the man replies that he loves her.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"But the woman isn\'t satisfied, and asks more and more questions.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I don’t understand.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"OK. Let’s see. Do you like me, Lain?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Yes, I do.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"More than your dad? More than your mom?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"That makes me happy. Do you mean it?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Yes I do.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Are you happy when you’re here?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I don’t really understand, but I probably am.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Do you like talking to me?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"What do you like about me?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"You’re kind and you listen to me.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Which do you like more? That I’m kind, or that I listen?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Which?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Yes, which?\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I like you because you’re kind.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"How am I kind to you?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"You don’t get angry and you’re polite.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"But there are lots of people besides me who don’t get angry at you.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"There are lots of people who are polite to you.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"Aren’t there?\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Do you really like me?\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"I don’t really know.\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"Do I have to think about all this stuff?\", \"actor\": \"Lain\"}, {\"line\": 37, \"text\": \"Let’s stop this.\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"I feel like I might start to not like you.\", \"actor\": \"Lain\"}]'),(57,89,'en','[{\"line\": 0, \"text\": \"I’m sorry. I just was trying to explain this issue to you.\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"I like you too Lain, though I’m not sure why.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"I suppose I just enjoy talking to you. That’s it.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"It’s probably just because I like my job!\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"I just thought it had to do with you, but I guess not.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"It is enough for me to be able to understand my feelings.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"I can never read your mind, so I can never know that you like me as much as I like you.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Sounds lonely…\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I’m sorry, but you know, this is one of the ways for me to know that.\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"I’m so happy with you saying that you like me.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"Are you happy with me saying I like you?\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"Yes, indeed.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Yes, that is enough. Lain, I like you.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"But still, as you can never comprehend all of me, it is impossible for me to know everything about you.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"If we could know everything about each other, I could be you and vice versa.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"I mean you do not have to be Lain unless we are different people.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"I really like Lain even though Lain is hard to understand sometimes.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"You like the mysterious Lain?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Well, I like Lain who I don’t know very well. Of course, that is not all I like.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"I want to know everything about you!!\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"So do I! But it’s impossible.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"Impossible?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Yes. That is because even I don’t know who I am!\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"Well, I don’t know how to explain this to you. Lain is Lain and I am me.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"Actually it might be still impossible even if we became the same person.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"The same person?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Yes, Lain and I are exactly the same!\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"We see the same things, have the same ideas and take same actions.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"Everything.\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"Even our looks are the same!\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"In this situation, there is only an identity that you KEEP, isn\'t there?\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"Yet even if such an identification is gone.\", \"actor\": \"Touko\"}, {\"line\": 32, \"text\": \"If you thought “Here is Lain,” then I cannot exist there.\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"The same thing can happen to you!\", \"actor\": \"Touko\"}, {\"line\": 34, \"text\": \"That is why it’s impossible to understand each other at the same time.\", \"actor\": \"Touko\"}, {\"line\": 35, \"text\": \"But what if you would answer every question I ask.\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"Still, it would be impossible?\", \"actor\": \"Lain\"}, {\"line\": 37, \"text\": \"Right, but they are not all possible questions, are they?\", \"actor\": \"Touko\"}, {\"line\": 38, \"text\": \"I need time to answer those questions and those answers could be changed eventually, such as I used to think one thing, but now I have changed my mind — something like that.\", \"actor\": \"Touko\"}, {\"line\": 39, \"text\": \"It\'s impossible after all, isn’t it?\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"Yes, I wish I could go inside your mind and answer everything you want to know…\", \"actor\": \"Touko\"}]'),(58,90,'en','[{\"line\": 0, \"text\": \"Are you happy at your school?\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Yes, I am!\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"What makes you so happy!\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"You know, playing and talking with my best friend.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"That’s good! It is a good thing to like school, isn’t it?\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"Were you happy?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Not really, because I didn’t have many friends there.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"You feel sad without friends at school.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Yeah… I’m sorry\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"About what?\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"That story reminds you of a bad memory.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Well, it certainly wasn’t good memory, but that’s OK.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"I actually feel sorry for my past, but my current job makes me think that was a good experience for me.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"Good experience?\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Yes.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"For example, such an experience is sometimes so useful to analyze the psychology of those who doesn’t want to go to school.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"It sounds difficult.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"I’m sorry, I’m talking about myself now! Whenever I talk about my job, I tend to get lost in it.\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"I guess I would make a better patient.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Ok, let’s change the subject. Well, what is your best friend like?\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"She is really cheerful and nice to me. I’m really happy around her.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"What\'s her name?\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"Misato.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"OK… Misato. What do you usually do together?\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"We read books together at her place and I listen to her playing violin sometimes.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Wow, she can play the violin.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"Awesome!\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"She must be a good friend.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"Why don’t you come over with her next time?\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"But she is so shy.\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"So shy?\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"Maybe… I’ll ask her anyway. I’m guessing she wouldn’t want to.\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"Well, I wish she could.\", \"actor\": \"Touko\"}]'),(59,91,'en','[{\"line\": 0, \"text\": \"Touko-san, deja vu isn’t an illness, is it?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"No.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"You may also feel it’s the first time you’ve seen a scene which you’ve seen before.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Both situations have nothing to do with a disease.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Even normal people sometimes have an experience of deja vu.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"What a relief! I feel better now.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Do you feel anything else when you experience deja vu?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Time is strange for some reason. I can’t figure out if time is fast or slow.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Do you feel it’s real or kind of ordinary?\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"Well, it could be… or could not be… I don’t know… maybe not…\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Is it nebular? Or you just can’t comprehend…\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"I just feel something is different.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Even though I recognize that I am playing in my house, I feel like it isn’t my house.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"And even though I don’t know where it is, when I play at a park that I’ve never seen, for me it seems like the place I usually use.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Strange… Do you feel comfortable or uneasy when you are there?\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"I don’t know, but Lain was laughing when it happened.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Not yourself but Lain?\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"I can’t explain very well but it was Lain\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Where were you at that time?\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Let’s see… I don’t know, because I was just watching her…\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"I mean, where from did you observe her?\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"Like, were you standing on the ground, or you could you see her from the sky…?\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"Well… I don’t know… It was like I saw myself on the TV… I guess…\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Lain, are you all right? You don’t have to bring it back by force. I’m sorry.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"I feel compelled to ask questions…. OK, that’s all for today!\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"How do you express it in technical jargon?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"No, let’s stop for now.\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"I really want to know about myself!\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"OK, so this is the last lesson.\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"In technical jargon, we call it “alienation of perception.” It means that you experience something as if somebody else experienced it.\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"It’s an illness, isn’t it?\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"There is one called “depersonalization” which is that you cannot feel yourself, but you couldn’t have it.\", \"actor\": \"Touko\"}, {\"line\": 32, \"text\": \"So stop worrying about it.\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"You can’t prove that I don’t.\", \"actor\": \"Lain\"}, {\"line\": 34, \"text\": \"Yes, but it doesn’t worry you. So I can say that you’re not sick.\", \"actor\": \"Touko\"}, {\"line\": 35, \"text\": \"Well, I shouldn’t have said that; it just makes you worried.\", \"actor\": \"Touko\"}, {\"line\": 36, \"text\": \"I guess I made a mistake…\", \"actor\": \"Touko\"}, {\"line\": 37, \"text\": \"No, you didn’t.\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"You didn’t make any mistakes because I insisted that you should answer my question.\", \"actor\": \"Lain\"}, {\"line\": 39, \"text\": \"Still, I want to study a lot in order to be like you, Touko-san.\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"Lain…\", \"actor\": \"Touko\"}]'),(60,92,'en','[{\"line\": 0, \"text\": \"The ego is your sense of who you are, isn’t it?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Yes, but more technically phrased, the ego is a part of the mind you use to experience as yourself and to recognize yourself.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Do you get it?\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Not at all.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Ok, so you have an idea that you are Lain.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"In such the structural element, the “EGO” is right there.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"Linguistically, “myself” describes all of you, right?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"“Ego” is, however, one of the parts of your mind you use to recognize that all of experiences I have had were mine.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Saying “it’s just to realize that you are yourself” is somewhat better to understand?\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"I can get the last part, but it is still beyond my understanding.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I like your honesty.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"So the girl, Lain, has various emotions, such as kindness, loneliness and things like that.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"Now the EGO is what is left when you don’t feel any emotions.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"I guess I understand a little…\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"For example, the Lain who woke up this morning and what Lain is now here are the same, right?\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"Right.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"What Lain will be tomorrow should be the same as those.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"I guess so.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"I mean if your ego works properly, you can recognize that you exist continuously and confirm that that existence is “myself.” If your ego doesn’t work properly…\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"If it doesn’t?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"We call such a situation “identity disorder” in the medical field.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"On the other hand, there is also an “isolation disorder” which means you think that you are not yourself.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"He, he, they sound like bad diseases, don’t they?\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"Yet anyone your age usually doesn’t complete his or her ego until later.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"You just can gradually recognize yourself with age.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"So is it wrong to think that I understand myself at my age?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"It just happens to be that such things happen all the time.\", \"actor\": \"Touko\"}]'),(61,93,'en','[{\"line\": 0, \"text\": \"If I became sick, what would you do for it?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"If you were sick?\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"I mean if…\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Well, since you are still young, I wouldn’t use any medical treatment for such minor symptoms… the way one uses medicine to cure disease.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Why wouldn’t you use them?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Lain, you already have the power to recover your health from disease.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"If you depend on using those medicines, however, your body becomes weaker.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Usually children shouldn’t take medicine too much anyway.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"So what are you going to do?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Let’s see… I will use psychotherapy to help you, like talking with you as we do now.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"Just talking?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Hmm, it’s difficult to explain it. For example, we have a program to recover people from alcoholism.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"In this program, they discuss and present what make them drink so much and listen to each other’s stories.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"We call it “group therapy.” I don’t like it though.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Why you don’t like it?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"I think people who regain their health from those group therapies would make the same mistakes later.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"They think that they can recover themselves from alcoholism simply by group conversation, but when they are alone, they will start drinking again.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"I’ve seen this happen a lot.\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"So what would you do?\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"I try to talk one-on-one as I do with you, Lain.\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"And?\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"And then, I suggest those patients to think about themselves.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"I have nothing particular to support them but I try to analyze what they think on neutral ground.\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"This helps them to recognize what is wrong with themselves, and then they try to recover their own health.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"We call it “non-supportive psychotherapy”.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"If they start recognizing there is a problem, it means they are already fine.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"Is that enough to recover them from diseases? Sound simple!\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Right, it sounds so simple.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"In fact, however, those things are more complicated and difficult to do than you think.\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"Difficult?\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"(yawn) I’m sorry. I need more sleep.\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"You must be tired. Should you take a break?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"(yawn)\", \"actor\": \"Touko\"}]'),(62,94,'en','[{\"line\": 0, \"text\": \"When you get sick in the head, your head is really sick?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"The brain isn’t the only part of the body involved in mental illness, but it is very important.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"We know that many types of disease occur because of trauma to the brain.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"When the brain is injured, something is broken?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"The human brain is very complex, and there are many things we don’t understand about it.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"We know that some parts of the brain seem to have a certain function.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"We know a little about where and when things take place in the brain.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Memories are stored in the hippocampus.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"There is the frontal lobe and the occipital lobe, and there are many other parts of the brain that have different functions.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Many chemical processes take place in the brain.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"When they do, a lot of information is exchanged.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"That’s how the brain gets sick?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Sometimes the brain does get sick that way.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"If there is an abnormal change in the brain’s chemical process, or if the parts of the brain responsible for processing information are physically damaged and not working properly, there could be disease.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"We call this kind of disease organic psychosis.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Is this hard to understand?\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I understand. People have their brainwaves tested and have CAT scans to check for it.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Are they teaching you that at school already? Did you teach yourself?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I checked it out on the Web. I had a test done before, and I was curious.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Lain, if you’re doing all this research, you don’t have to see me.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"No, it’s not like that. I learn a lot from you. It isn’t like the things I learn from books.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"I’m sorry, Lain. I’m not feeling very well.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Is there brain research at this research center?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"What kind of research?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"I’m not a specialist and I don’t work in that area, so I can’t say anything specifically.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"But it’s on the Web. All sorts of research takes place.\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"Lain, can I ask you a favor? I’m not feeling very well. Can we end for today?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"I’m sorry, Toko-san.\", \"actor\": \"Lain\"}]'),(63,95,'en','[{\"line\": 0, \"text\": \"What kind of research do you do?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Research? I never mentioned that…\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"But this is a research center, isn’t it?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I’m still an apprentice. I haven’t done any of my own research.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"You’re an apprentice after being here for 3 years?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"How did you know…\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You told me yourself.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I did?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I’m sorry.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Somehow, I haven’t been feeling very good lately.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Could you ask me fewer questions today?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Either that, or can we just not talk quite so much?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"You’re tired, Sensei. Maybe you should sleep a little.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Tired? I guess I look that way. You’re right. I must look that way.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I feel bad for you Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"You can’t do your own research because you’re busy with miscellaneous tasks.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"I’m not doing any miscellaneous tasks!\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I’m sorry.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I’m sorry for raising my voice like that.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I was so shocked.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Did something happen? Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"No, nothing.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"You’re busy with miscellaneous tasks and can’t do your own research. Does that bother you?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"How do you know about… ?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"I found the homepage for this research center.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"I saw the list of researchers and published dissertations, but I couldn\'t find you.\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"That’s right. I haven’t published any dissertations since I came here. But I mean to very soon.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"You’re OK now. You have time for your own research now?\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"What are you saying? What do you mean, Lain?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"I’m a research subject, aren’t I?\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"That’s why I’m not useful to you.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"We can end now if you’d like.\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"I can’t see them anymore.\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"I’m wasting your time.\", \"actor\": \"Lain\"}, {\"line\": 34, \"text\": \"Lain.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"You’re like a sister to me.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"I’m the one who said that we could just talk.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"I don’t think of you as a nuisance.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"And…\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"There’s nothing for you to worry about, Toko-san. It\'s about suicide.\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"How do you know about that?\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"I read it in the paper. It was on the bulletin board here, too.\", \"actor\": \"Lain\"}, {\"line\": 42, \"text\": \"I see. You’re right.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"What kind of research do you do?\", \"actor\": \"Lain\"}, {\"line\": 44, \"text\": \"As I said before, I’m trying to develop a new treatment system using my background as a counselor and psychiatrist.\", \"actor\": \"Toko\"}, {\"line\": 45, \"text\": \"But it looks like I’m failing as a counselor and a psychiatrist right now.\", \"actor\": \"Toko\"}, {\"line\": 46, \"text\": \"I’m sorry.\", \"actor\": \"Toko\"}, {\"line\": 47, \"text\": \"Why?\", \"actor\": \"Lain\"}, {\"line\": 48, \"text\": \"I think I’m becoming mentally unstable, now. Just a few moments ago, I…I’m sorry.\", \"actor\": \"Toko\"}, {\"line\": 49, \"text\": \"Toko-san… are you ok?\", \"actor\": \"Lain\"}]'),(64,96,'en','[{\"line\": 0, \"text\": \"Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Good morning.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I’m sorry.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I stayed up all night preparing my paper.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Everything is scattered everywhere.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Could you give me a second?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Maybe I should go home?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"It’s all right. Don’t worry. Oh, shoot, I’m out of tea.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I can go buy you some.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Thanks.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"But please don’t.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I appreciate your feelings.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Have a seat, Lain.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I thought that maybe I ought to take a break, too.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Are you very busy?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"A little.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"After that thing happened to Professor Takashima.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Now I have to do a lot of his work, too.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I’ve had a lot of new clients, to boot.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Can you do your research?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Lain, don’t mind that stuff too much.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"It’ll be tough for a while, but I have things to do.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"For now, I’m just happy just concentrating on my work.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"You aren’t sad?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"You’ll understand one day Lain. It’s a wonderful thing to have work that’s fulfilling.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"I don’t think I would understand. Instead of something like that…\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Instead of... what do you mean? Did you do something fun lately?\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Yes. I think everything is going to work out well.\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"What will work out well?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Things about me.\", \"actor\": \"Lain\"}]'),(65,97,'en','[{\"line\": 0, \"text\": \"Lain, I’d like to confirm something with you.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"What were the names of your friends that you talked about?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Kyoko-chan.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I see. And you have another friend?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Misato-chan?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Right. Misato-chan.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Why do you ask?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Never mind.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I was just gathering information for a report, and part of it asks about friends you may have.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I was double-checking it because I was a bit unsure of myself.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Oh. Misato-chan. Is she going to be in your charts, too?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"No, just her name. Nothing else. Does she live in your neighborhood?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Yes. But I can’t walk there.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"You have to go by car?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Yes. When I go to Misato-chan’s home, I always have my mom or dad drive me there.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"What does your dad do?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"He goes to work. He’s not around home much.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"He’s very busy, then. Is he nice to you?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"He is. He brings me gifts when he goes to work.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"That’s great, Lain.\", \"actor\": \"Toko\"}]'),(66,98,'en','[{\"line\": 0, \"text\": \"Lain, I’d like for you to tell me the truth.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"The truth?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"You don’t have any friend named Misato-chan, do you?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Ha, ha. What do you mean? That’s silly of you.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I asked Kyoko-chan.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"And?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"She said that there is no Misato-chan. She said, “isn’t that Minako’s mistake?”\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Is that what you believe?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I’m not accusing you, but this is very important.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"For you to write your report?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I won’t deny that. But I’m concerned about you personally.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"About me personally?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I’m concerned that you might be creating imaginary friends and running away into your own little world.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"That is a sickness.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"You said that kids often have that, and that there’s nothing wrong with it, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Misato-chan isn\'t an imaginary friend, either.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Then where is she? Can you call her right now? Can you give her a call right now, in front of me?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"She can’t be contacted. She moved away.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Can you do anything to prove to me that she exists?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"That she exists?\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Yes. Can you prove that?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"My memory…\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Look, Lain, I want to believe you, but I can’t read your mind or tell what your memories are.\", \"actor\": \"Toko\"}]'),(67,99,'en','[{\"line\": 0, \"text\": \"It’s not a memory. It’s a record.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Well, they’re vaguely similar. But it’s no time to be playing word-games.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"You would understand if you were connected.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Connected to what?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Lain, you say that Misato-chan exists, but you are the only person who says so.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"How can you expect anyone else to understand?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"There is something wrong with this.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"How can anyone believe you?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"You don\'t believe me, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Unfortunately, no.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"But that’s because I’ve looked very closely into this personally, and all the facts point against it.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"If you could show me some objective proof that that I am mistaken, that would be different.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"How would I prove that she exists?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"How?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Well show me other people who’ve known her.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Show me her identification or birth certificate or something.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"More than anything else, show me that she’s a human being that exists and walks around in a human body.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"She doesn’t exist if she doesn’t have a human body?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Of course not! She’s not a ghost, is she?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Professor Takashima didn’t exist, then?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"But he did exist.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"He had a body before he died.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"He lived and walked around in a human body.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"There is ample evidence that he existed.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Many people who are alive now knew him.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"There are records of his existence.\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Let’s cool it, Lain.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"I’m not here to have a question and answer session with you.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I’m here to treat you.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"I do not acknowledge Misato-chan’s existence.\", \"actor\": \"Toko\"}]'),(68,100,'en','[{\"line\": 0, \"text\": \"I’m sorry about last session, Lain. I don’t know myself why I became so angry.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"It’s ok, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"It’s just that aside from this issue with Misato-chan, I can’t see anything wrong with you.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"We’ll have to work on it slowly.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Will you finish your paper in time?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I’m not putting anything about you in my paper.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I’ll use information from other clients.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"It’s not your problem anyway, Lain.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"You’re gentle, Lain.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I’m not gentle.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I have to apologize.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I should have apologized right away, but I’ve done some things behind your back.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I lied and told some of your friends that I’m a private tutor.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I asked your friends about you.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I needed the information for my report.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I should have these things with more benevolent intentions, but… are you angry?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I’m sorry, Lain.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But I did it for y… No, I didn’t do it for you.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I did it for my work.\", \"actor\": \"Toko\"}]'),(69,101,'en','[{\"line\": 0, \"text\": \"Who did you see, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Kyoko-chan and Kaori-chan.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I know Kyoko-chan, but who is Kaori-chan?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"She’s your classmate.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"You aren’t going to ask me to prove her existence now, are you?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I’ll feel bad.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Please don’t be hard on me that way, Lain.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"You’re confident, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"About what?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"You’re confident that that person exists. I can’t prove that she exists.\", \"actor\": \"Lain\"}]'),(70,102,'en','[{\"line\": 0, \"text\": \"You seem to be busy lately, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"The deadline for my paper is coming up, and although I’ve been waiting for a long time for someone to relieve me with some of my work, it hasn’t happened.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"That means more work for me.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Sure, it’s a little hard.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Is it tiresome?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I’m ok, Lain. You don’t have to worry about it.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I’m worried about you, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"About me? Well thank you. I appreciate your feelings.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Will you continue to like me, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Of course! You’re my little sister, remember? I don’t have any reasons not to like you.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"But I made you mad.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"That’s true.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But that was just because you’re growing intellectually, we were having a discussion and we had a difference of opinion.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But let’s leave that alone.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"There’s no reason for me to dislike you because we had a difference of opinion.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Do you have important people in your life aside from me?\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Sure.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I have friends, too.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"You have people in your life that are important to you aside from me, don’t you?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I do. But if I had to choose one person, I would choose you.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"That makes me happy. Then, I’ll pick you, too.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Really? But is that ok? What about the other important people in your life?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"If you chose me and I didn’t choose you, won’t you feel sad? I don’t want you to feel sad.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Won’t your lover be sad?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Huh? Um, well, yes. Are you interested in my private life, Lain?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I want to know about you.\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Things having to do about my lover are a secret. You’re a bit young for that kind of thing, anyway.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Too young?\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"You may eventually have a love life of your own. But love is a pretty complicated thing.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Complicated?\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"I have by doubts and worries about it, too.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"I’m human.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"I make mistakes and have regrets.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"Even now I have feelings that I can’t figure out.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"See?\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"It’s complicated.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"Complicated…\", \"actor\": \"Lain\"}]'),(71,103,'en','[{\"line\": 0, \"text\": \"You’re Lain, aren’t you?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Sure I am. You’re being silly, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Oh, I’m sorry.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"You’re right.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I’m a little tired.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I’m sorry for asking goofy questions.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You seem to be really doing well lately, Lain.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I haven’t changed.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"We’ve been having counseling sessions for a long time.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"A long time? Do you mean many times? Have I been seeing you too much?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Maybe. What do you think?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"There seem to be many areas left.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"What do you mean? Don\'t tell me things I can’t understand.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"What’s wrong? Are you angry? You can’t control your emotions?\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Why are you asking me this! Look, Lain. Are you playing with me? Are you mocking me?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"No.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"This counseling session is for YOU. You come here to see me. Not the other way around.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But you said that I could talk. I want to know about you.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"How is finding out about me going to help you? Do you just want to irritate me?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"You really didn’t like my questions?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"No, I didn’t!\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"I want you to leave things alone.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"I don’t want you trying to get inside my head.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"That’s what I think.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Aren’t you sad to be alone?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"I’m not alone!\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I am not you!\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"You are Lain, aren’t you?\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"You’re the proper, reserved, sad girl without much self-confidence, aren’t you?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"No, that’s not me. I think my personality is much different from that.\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"Then who are you?\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"I’m… Lain.\", \"actor\": \"Lain\"}]'),(72,104,'en','[{\"line\": 0, \"text\": \"I felt really good today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"That’s really great, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I’m starting to feel worse, though. I can’t tell what’s bothering me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Is it something vague? Something about feeling dormant?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"When you’re feeling happy, don’t you sometimes feel worried about when it might come to an end?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Is that a symptom of an intermittent flow of thought?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I can’t imagine myself being happy.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"You can imagine yourself being unhappy?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I suppose. But I can’t get rid of the worry.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Is there something I can do?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"What would I want to seek for from you? Why me from you?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Are you too proud to tell me?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"It has nothing to do with pride. Why don’t you tell me what you think is going on with me.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"You’re just Toko-san. You’re yourself.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"That’s right. I better get to my reports. If you want, you can stay around here, but I have to go.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Why?\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Because there is something I have to do right now.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"There is something that I can only do now.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I have to continue my research.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"That makes you happy?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Yes, it does. My number one goal is to continue doing the research that I enjoy.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"So that’s number one?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"No.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"That’s not what I mean.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Seeing you is very important, too.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"It’s very important to me that everything goes well.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"But I have to do things in order.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"I’m glad.\", \"actor\": \"Lain\"}]'),(73,105,'en','[{\"line\": 0, \"text\": \"Are you troubled about anything?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"No, I’m not. How about you, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Sometimes. I feel like something bad might happen.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"You’re feeling delusional?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yes, but unlike with you, it isn’t a strange delusional problem with perception.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I wonder if that’s true.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"But how about the feelings you have about that man?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Aren’t those feelings delusional?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Who is “that man”?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"The man you’re thinking about right now.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"You can tell what I’m thinking.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Wow!\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"You have super powers?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Is that why you poke fun at me sometimes?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But I’m connected to you, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"“Connected”? In what way?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"It probably wouldn’t make sense if I tried to explain it to you.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"That’s called psychosis, Lain.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Believing yourself to be a unique “chosen” person or that you are God.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"That’s a serious disease.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I’m sick. How about you?\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"I’m not sick. I’m…\", \"actor\": \"Toko\"}]'),(74,106,'en','[{\"line\": 0, \"text\": \"You don’t have much energy.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I’m tired.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Are you tired of doing miscellaneous chores?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Is someone having you do waste your time with small tasks?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yeah. I do feel that way sometimes.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Do you think you have some kind of psychological condition?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Do you think you might have caught something from me?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Ha, ha.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Why do you think I’m like this? I know and understand the things I am talking about.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"You’re ok, Toko-san. You’ll be better by tomorrow.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I’ll be “better”?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"You’ll be in a world where you don’t suffer.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Am I myself when I am by myself or when I’m with you?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"You’re always yourself. Even when you doubt my existence.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"I doubt your existence?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Yes. You don’t want to believe that I exist.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Why would I have a problem like that because I treat you?\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"There is information you wouldn’t understand now. You aren’t doing well psychologically.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"“Information I wouldn’t understand now?” But I want to know now.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Yes. But it would be too difficult for you to understand now.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"You’re thinking about what’s best for me, aren’t you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Of course, I am. I like you.\", \"actor\": \"Lain\"}]'),(75,107,'en','[{\"line\": 0, \"text\": \"A rumor?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Does it bother you?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Someone is spreading rumors about you?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Is that a delusion of persecution?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Some other kind of delusion?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Well, it might just be a lack of self-confidence.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Why do you have so much confidence, even when you’re sick, Lain?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I’m sick. What’s the name of my disease? Schizophrenia? Manic depression? Something else?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"You aren’t any of those things.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"You have pathomimesis.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"That’s what you call the imitation of disease.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Then I’m not really sick?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"You don’t exist.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I just think that you’re here.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Everyone has been tricked.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"We’ve all been shown some glimpse of your existence.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"That’s why no one really remembers you.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"It’s not like you to be mad. Some of us don’t seem to exist, though. Which are you?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"I think that you think I’m here.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"You’re a delusion that created a delusion.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"All these things, your constructed diseases and your voices, are delusions.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"How about me? Am I here?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"That’s why I have to heal you!\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"You’re so serious. But you’re an elite. You’ve been chosen.\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"That’s right.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"I’m trying hard to be here.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I would rather have been out playing and fooling around, but I’ve gone to school and studied and worked hard to get here.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"I feel sorry for you, Toko-san.\", \"actor\": \"Lain\"}]'),(76,108,'en','[{\"line\": 0, \"text\": \"Are you ok, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Yes. I’m hanging in there. Sometimes I don’t feel so good.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"You think too much, Toko-san. You pay too much attention to what’s going on around you.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I have things to think about. Work. Romance.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Is a love life necessary?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I think it’s a very important part of life.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Does a woman need a man?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Not just because she’s a woman. If that were true, a man would need a woman.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"It would be obsessive to think that a woman must have a man, Toko-san. You can live on your own.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"But wouldn’t that be sad? I can’t stand to be sad. How about you? How about you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I would get sad, too.\", \"actor\": \"Lain\"}]'),(77,109,'en','[{\"line\": 0, \"text\": \"You don’t want to do anything? How about your research?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"No one will want to read my paper anyway.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"What’s wrong? It’s not like you to play a victim.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Not like me! What am I?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"You’re just Toko-san, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I’ve been severed from society. I’m no longer connected.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"That’s not true. I’m connected to you. I’m connected to society. That’s why you’re connected, too.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Something’s wrong with me, isn’t there?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"The times when I talk to you and the time I spend with everything else are separate.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"From your point of view, my life must be a contradiction.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"My point of view?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"From your point of view, this world doesn’t exist.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"No. That’s not true. I’m connected to this world.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I can’t take it anymore.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I can’t even stand this moment.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"That’s not all.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"My memories of tomorrow…the memories I’ll have in the future are in disarray.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Is this a delusion caused by worrying?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Am I experiencing something I shouldn’t be experiencing?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"That’s you, isn’t it?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"It is me! Because it is me!\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Wouldn’t you be better off if you were just being yourself?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"I can’t understand my sense of self from moment to moment!\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"The judgment you have exactly at that moment is you, Toko-san. The rest is…\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"The rest is…\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Ordinary data.\", \"actor\": \"Lain\"}]'),(78,110,'en','[{\"line\": 0, \"text\": \"Concerning diagnosis: The client lives in a nuclear family with her mother and father.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Both parents are middle to upper class and financially well-to-do.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"The parents have a stable relationship.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"They do not seem to have mental health problems.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Concerning family history of illness, according to the parents as well as the database at our center, there was no neurosis, mental illness, suicidality or consanguineous marriage in her family.\", \"actor\": \"Toko\"}]'),(79,111,'en','[{\"line\": 0, \"text\": \"She hasn’t had any significant problems from the time of birth.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Even at this point, she doesn’t have any bad habits or problems with truancy.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She does not seem to have a troubled history.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Of course, at this research center, accompanying illnesses must be treated.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I wanted to ask her about her illness history when I started counseling her, but she was very shy.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I decided that I would try to get her to open up.\", \"actor\": \"Toko\"}]'),(80,112,'en','[{\"line\": 0, \"text\": \"According to her mother at home, Lain has very frightening dreams.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"They are not ordinary dreams.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"They are very realistic and almost hallucinatory.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She visited the hospital for this reason.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Regarding the nature of the dreams, it seems that Lain doesn\'t elaborate much about their contents, but she says that she sometimes hears incomprehensible voices out of nowhere, that she is suddenly awakened and forced to watch a video.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She sometimes plays in the park, and suddenly finds herself alone when she thought other kids were around.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"These seem to be childhood imaginary fears.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"In the future we will address these symptoms.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I hope for a recovery.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"It might be possible.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Fundamentally, even if that does not occur, she may be able to manage life at home.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"However, what her mother said that concerned me was her hallucinations.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I will try to pinpoint the cause of these symptoms in our counseling sessions.\", \"actor\": \"Toko\"}]'),(81,113,'en','[{\"line\": 0, \"text\": \"The physical appearance of the client at the first session.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her figure is short and thin.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"The only feature that stood out was her hairstyle.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She has grown out the tuft of her hair on just one side of her face and tied it in a bundle.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I don’t think it’s a popular style at school, but it seems to be her individual style.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"According to her mother, she enjoys having her hair that way.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Understanding the reason for her hairstyle may guide me in understanding her.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I will look into this in the future.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"She has a calm demeanor, and her skin is fair.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"There is otherwise nothing left to mention.\", \"actor\": \"Toko\"}]'),(82,114,'en','[{\"line\": 0, \"text\": \"Although her affect is flat, she appears like any other healthy child when she smiles.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She sometimes gets tongue-tied, but not to the point where communication with her becomes impossible.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She seems calm and reserved.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She understands my questions without a problem, and is capable of answering them.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She doesn’t seem to have problems with her attention span, concentration, orientation, clarity of thought, and expression of emotions, but hinders when asked about her symptoms.\", \"actor\": \"Toko\"}]'),(83,115,'en','[{\"line\": 0, \"text\": \"At this stage, I thought it would be a bad idea to ask her about her symptoms in a teasing fashion.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I was unable to find details concerning her symptoms.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"However, she generally seems to be a clever and relaxed girl.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Her mother showed me a painting she did in school.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"It was very well painted, and showed a high level of artistic sensibility.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"However, I could not find any specific points that might assist it analyzing her psychologically.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"At this stage, she appears to be at a delusional state, but there seems be a very good chance that she is a normal child.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"One point from her physical examination struck me.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Her vision is excellent.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Her standard light reflex is lower than the standard range.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"However, I have not seen in her the type of dermatological or anemic problems that are common for her age.\", \"actor\": \"Toko\"}]'),(84,116,'en','[{\"line\": 0, \"text\": \"Clinical notices from last session: First, concerning this case, I want to treat her as a therapist as opposed to a psychiatrist.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Therefore, I will treat Iwakura Lain as my client from now on.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"One of the reasons for this is that she is still 12 years old and is emotionally sensitive.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I do not want to make her feel that she is sick.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"In fact, from the results of her last clinical visit, the extent of what she is troubled from is nightmares and psychological damage associated with it.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Although during sessions with her, psychological discomfort can be detected, she is able to ask and answer questions without a problem.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I see no reason to advocate objections to my conclusion that she is sick, but at this point, I would like to treat her as someone without a mental illness.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"One more thing: Because I am both a psychiatrist and a counselor, my job is selecting the best position possible for my patients.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"When counseling her in the future, if I detect symptoms of mental illness, I would have to treat her as a psychiatrist.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Of course, there’s a good chance that I myself will continue to treat her.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"In order to honor my research and keep it objective, I may want to ask a third party to continue to treat her clinically.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"In the meanwhile, I will continue to work with her in a research setting.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"This may be an exceptional situation at this research center, but I would like to proceed with it.\", \"actor\": \"Toko\"}]'),(85,117,'en','[{\"line\": 0, \"text\": \"The client is very cooperative and our sessions have been going smoothly.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"However, somewhere within her, she still is very cautious around me, and it is difficult to discuss her problems.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"We had some extra time, so I proposed that we play a simple game.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She turned me down, and we went directly into our session.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She takes much curiosity in me, an outside observer, but I think this has to do with her cautiousness around me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She plays with dolls at home, and seems to have a complex associated with them.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"She has few friends, and playing with dolls is her way of dealing with her loneliness.\", \"actor\": \"Toko\"}]'),(86,118,'en','[{\"line\": 0, \"text\": \"In the client’s response, a good understanding of language could be found.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"However, this is not related to disease.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"It displays the client’s strong ability to use language.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"The client differentiated physical activity from physical education, stating that the latter is an “undertaking.” In school and group activities, the two can be compared quite conspicuously.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"The fact that she says she likes physical exercise but not physical education may have to do with her lack of self confidence.\", \"actor\": \"Toko\"}]'),(87,119,'en','[{\"line\": 0, \"text\": \"Matters for special mention: The client’s tendencies disturb me.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her light reflexes were tested at the hospital.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She may have Adie’s syndrome.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Together with active auditory hallucinations and paranoia, she may have early onset general paralysis, although very unlikely.\", \"actor\": \"Toko\"}]'),(88,120,'en','[{\"line\": 0, \"text\": \"Making a hasty diagnosis is taboo, but thinking of the worst, I think I should obtain cerebrospinal fluids as soon as possible.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"May I have your permission, please?\", \"actor\": \"Toko\"}]'),(89,121,'en','[{\"line\": 0, \"text\": \"My diagnosis at the hospital is affecting my client.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She was emotionally hurt when she found out that the Rorschach’s test was in fact a tool used to examine her.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"This wasn’t severe to the extent of a “mind-rape,” but I did hurt her in our session today.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"This has made me realize that I must be discrete when bringing up the theme of disease.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I explained to her my role as a counselor, and my hopes to be helpful to her, and regained some trust from her.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I hope that in our future sessions, she will develop some goals of her own concerning counseling.\", \"actor\": \"Toko\"}]'),(90,122,'en','[{\"line\": 0, \"text\": \"She seems to be cautious about with mother.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"And as far as she’s concerned, her hallucinations are real.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She may be mistrustful of her mother because her mother does not accept that her hallucinations are ‘real.’ At this point, I don’t know if the hallucinations are due to some warped sense of perception, or if they are entirely imaginary.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"There are times when it is difficult to tell.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Concerning the ‘vision’ the client saw floating in mid-air, they were most likely imaginary, opposed to being shapes of monsters formed by the shadows on the walls and curtains at night, etc.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Could it be that she has developed a system of sensing without perceiving?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I would like permission to examine her cerebrospinal fluid.\", \"actor\": \"Toko\"}]'),(91,123,'en','[{\"line\": 0, \"text\": \"My client has found her own diagnosis and has been studying it on her own.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She must have a considerable amount of fear concerning her illness.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Having the ‘label’ of mental illness pasted on you causes you to be stigmatized by society.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She may need to deal with this.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I am rethinking the how the mentally ill are viewed by society.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"In our last session, I tried to eliminate her concerns about feeling that she is mentally ill. As a result, I think I was able to secure some trust in our relationship.\", \"actor\": \"Toko\"}]'),(92,124,'en','[{\"line\": 0, \"text\": \"Mainly, we talked about auditory hallucinations.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She believes that she hears voices from electric lines.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She says that the voices she hears are not related to her.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"This could be categorized as a delusion of physical influences, but the symptoms didn’t seem serious enough to be called full-blown delusions.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Her being “able to hear from phone lines and electric lines” seemed instead to be childlike theories.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She’s an intelligent and sensitive girl, and probably creates her own hallucinations.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"However, regarding the fact that she has hallucinations only during the day makes her very unusual.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Although I don’t think my client is lying, but she says that she very rarely hallucinates while she is talking to her friends.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Most likely, references to time and place become fuzzy in her mind, and she is either confusing separate experiences, or she may have just been talking to her friends in her dreams.\", \"actor\": \"Toko\"}]'),(93,125,'en','[{\"line\": 0, \"text\": \"The relationship with my client has deteriorated.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"My own lack of consideration is to blame.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I told here that her hallucinations are an imaginary enemy.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I tried to ease her worries by being lighthearted, but this backfired and hurt my client more.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I must visit the client’s home to apologize to her and give her parents a status report concerning our sessions.\", \"actor\": \"Toko\"}]'),(94,126,'en','[{\"line\": 0, \"text\": \"I was somehow able to reconnect with my client.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her symptoms haven’t particularly deteriorated, so I will try my best to slowly become more trustworthy.\", \"actor\": \"Toko\"}]'),(95,127,'en','[{\"line\": 0, \"text\": \"I spoke to my client about her vision.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"There is a good chance that she may develop a troubling complex by comparing herself to others, thinking that she is uniquely—and abnormally—able to see and hear better than them.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"At the same time, instead of just saying that she has hyperesthesia, I believe she is dealing with the kind of déjà vu that neurotic people sometimes experience.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I will exclude the details, but it seems that what she perceives comes from a poor sense of reality, and her true perceptions are abandoned.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Regarding her perception of time, if the client is telling the truth, there isn’t much of a problem.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I don’t know what she actually sees, or whether it’s due to a physical abnormality or an abnormality of perception, but as long as her underestimation of herself persists, it will be difficult to reach any fundamental solutions.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I cannot diagnose her with depersonalization neurosis just yet, but when I consider her fuzzy recollections of hallucinations, I do think she may be developing depersonalization symptoms.\", \"actor\": \"Toko\"}]'),(96,128,'en','[{\"line\": 0, \"text\": \"In order to alleviate her tendency to underestimate herself, I spoke to my client about ways to increase her self-confidence.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her facial expression has been hard and cold, but her personality has been easy to relate to.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Because she is an intelligent client, I will have to work hard psychologically.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"But because she understands quickly, I expect that our sessions will be effective.\", \"actor\": \"Toko\"}]'),(97,129,'en','[{\"line\": 0, \"text\": \"My client’s curiosities are less to do with me than they are with my profession.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Clients who inquire about the psychiatric field usually do so out of their own discomfort about their illness.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"With this client, however, her inquiries may help in her own rehabilitation.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"There is an undeniable chance that learning about her own condition may intensify her worries, but I think that can be avoided in our relationship.\", \"actor\": \"Toko\"}]'),(98,130,'en','[{\"line\": 0, \"text\": \"I was able to ask my client about her hairstyle for the first time today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her hair looks like a good-luck charm.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I suppose she was hearing voices even with that hairstyle, but I think that she may feel that discontinuing it would cause her to have auditory hallucinations that are even worse.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"It isn’t much different from the small good luck charms we put in our cars to avoid accidents.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"A person can have an overconfidence in good luck charms, of course, but I have no intention of asking her to change her hairstyle.\", \"actor\": \"Toko\"}]'),(99,131,'en','[{\"line\": 0, \"text\": \"As usual, my client’s interest is entirely about psychiatry.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Although she doesn’t have a complete lack of interest in everything else, she shows very little interest.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I’ve grown a bit weary of her probing questions, but being a specialist, I’m able to answer those questions.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I think that letting her ask will help her eliminate her lack of self-esteem, however.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Also, comparing her case to that of a similar third party client I think that she will be able to see herself more objectively by the asking and answering of questions.\", \"actor\": \"Toko\"}]'),(100,132,'en','[{\"line\": 0, \"text\": \"We are able to have completely relaxed talks now.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I’ve assumed that she still has some problems having to do with memory, but she can’t accept today the memories and experiences she had of herself in the past.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"From that information, I think she probably has an identity disorder and/or a disorder with self-unity.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"However, these identity problems are still in development, and there’s a chance they may naturally go away as she strengthens her sense of self in therapy.\", \"actor\": \"Toko\"}]'),(101,133,'en','[{\"line\": 0, \"text\": \"Communication with the client has been going satisfactorily.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"In our previous session, I was irritated by her many questions and our talk and became half-teasing.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"This definitely narrowed my relationship with her.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"In future sessions, we will focus on the growth of her sense of self and I will attempt to further establish aid to her.\", \"actor\": \"Toko\"}]'),(102,134,'en','[{\"line\": 0, \"text\": \"She has lightened up a great deal.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Much of it seems to do with a new friend she’s developed in the last few months.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Additionally, she has had a change in her living arrangement, which she seems to be happy with.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Having now found a friend her own age, she will probably develop a relationship with that person that is more intimate than the one she has with me.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I wonder if she still asks me questions because she doesn’t know what else to talk about.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I will see how things go for the next few months, and then I would like to discontinue examinations.\", \"actor\": \"Toko\"}]'),(103,135,'en','[{\"line\": 0, \"text\": \"She won’t stop pressing me with questions.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Concerning this case, it may simply be an issue with her personality.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She still has worries about when she was hallucinating in the past when she was losing her sense of reality.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"We focused on that issue.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Her current symptoms involve excessive underestimation of herself.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She is objectifying the adoration she has for her ‘other’ self, and because her current self and her past self are completely different, it seems that she has been developing these personification symptoms on her own for some time.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"However, there is a very small chance that that will happen now.\", \"actor\": \"Toko\"}]'),(104,136,'en','[{\"line\": 0, \"text\": \"We had our talk.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"There was no problem from beginning to end.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She is getting better.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"As far as I can see, she is just an ordinary healthy junior high schooler.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"There is no reason why she should continue to come here.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Professor Takashima, in order that I can submit my paper to the academic conference in the fall, I would like to apply to start seeing a different client.\", \"actor\": \"Toko\"}]'),(105,137,'en','[{\"line\": 0, \"text\": \"Professor Takashima, regarding my request last month to see a different client, how has that progressed?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"If you could introduce me to the person in charge of the outpatients, I could arrange things from there.\", \"actor\": \"Toko\"}]'),(106,138,'en','[{\"line\": 0, \"text\": \"Professor Takashima, I have prepared the final reports for my current client.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I thought that it might be a good idea to keep an appointment history, but because there seem to be have been so few problems, I did not keep one up till now.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"By next month I will prepare a final report with the appointment history.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I don’t have much time until the academic conference this fall.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"May I please request this of you?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"As it is, I will not finish in time.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Are you harassing me?\", \"actor\": \"Toko\"}]'),(107,139,'en','[{\"line\": 0, \"text\": \"To the chief administrative secretary: who should I submit my report to before there is a new head of the laboratory?\", \"actor\": \"Toko\"}]'),(108,140,'en','[{\"line\": 0, \"text\": \"To the chief administrative secretary: Thank you for sending me a new client.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"The status with my current client can be found in my report.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I will send you my final report after I organize it.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"If it is not a problem, I would like to terminate the counseling of my current client.\", \"actor\": \"Toko\"}]'),(109,141,'en','[{\"line\": 0, \"text\": \"To the chief administrative secretary: In order to confirm my client’s history, I spoke to one of her classmates.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I found a problem.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I am terribly sorry, but may I resume counseling this client?\", \"actor\": \"Toko\"}]'),(110,142,'en','[{\"line\": 0, \"text\": \"As I had feared, my client has created an imaginary friend, and has taken a rebellious stance toward my opinions.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"At the same time, her speech is remarkably lacking in logicality, and is almost like that of a schizophrenic.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I cannot, of course, diagnose her immediately.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Considering her loss of touch with reality in the past, her anthropomorphic symptoms, her age, and gender, she may have depersonalization neurosis.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Also, her personality disorder is extremely light, and considering her hallucinations, she may be suffering from paranoid schizophrenia.\", \"actor\": \"Toko\"}]'),(111,143,'en','[{\"line\": 0, \"text\": \"As much as possible in the future, I would like to assist her as a neutral third party, but it will be difficult because of the relationship I’ve developed with her.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I would like someone else to take over her case.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Either that, or I would like to stop seeing her after our next session.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Mr. Chief Administrative Secretary, I apologize for being selfish.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I will follow through with all of my remaining work.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Please allow me to move ahead in this fashion.\", \"actor\": \"Toko\"}]'),(112,144,'en','[{\"line\": 0, \"text\": \"I tried my best to repair the relationship with my client, but failed.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Somehow, after I negated the existence of her imaginary friend, she’s withdrawn into her own world.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She has become withdrawn like a patient with autism.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"We speak very little, but because she has become so distant, I’m afraid there is a good chance of sending her into a panic if I speak to her carelessly.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I suppose I just need to wait and see what happens, but I’m afraid that through psychotherapy, there is the danger that I may get wrapped up in her paranoid delusions.\", \"actor\": \"Toko\"}]'),(113,145,'en','[{\"line\": 0, \"text\": \"To the chief administrative secretary: Concerning future correspondences: The patient herself doesn’t believe that she is very sick, and this has made therapy much more difficult.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I think the top priority should be to have her see a different therapist and allow her to find a different environment.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"However, if changing therapists is not possible, I think she would need to start a small dosage of anti-psychotic medication.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Perhaps Contom or Neoparidol might be effective.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"It may be well worth trying.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Will I need to submit a request to the overseeing authorities for this?\", \"actor\": \"Toko\"}]'),(114,146,'en','[{\"line\": 0, \"text\": \"The client had a pleasant demeanor in our session from beginning to end.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Perhaps there was no need to do anything abruptly.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"The client hasn’t changed her feelings about me from the past, and our casual conversations go well as long as I avoid talking about her imaginary friend.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"What bothers me, though, are the prying personal questions asks.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I sometimes think that she means to intentionally work me up.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"But that may just be due to my impatience.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Somehow I want to have an intimate relationship with her again.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I want to gradually redevelop objectivity and lead our sessions.\", \"actor\": \"Toko\"}]'),(115,147,'en','[{\"line\": 0, \"text\": \"The client has changed so much that she doesn’t seem to be the same person anymore.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Is that true, or am I being subjective?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Could something have changed due to the progression of her symptoms?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"As we talked, she made remarks that I couldn\'t understand.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She also made some inciteful remarks.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"During our session, I forgot myself and became hysterical.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I can’t handle it anymore.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I can do no more.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Please have someone else take charge of this case.\", \"actor\": \"Toko\"}]'),(116,148,'en','[{\"line\": 0, \"text\": \"Overall, she doesn’t seem very youthful.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She appears older than her age.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Even when we talk at a relaxed tone, she moves her eyes about irratically.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She also moves her wrists about oddly, and appears anxious.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She probably is very conscious about her bodily sensations, and doesn’t admit to being ill because of her pride.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She blames what is happening to her on fatigue and lack of sleep.\", \"actor\": \"Toko\"}]'),(117,149,'en','[{\"line\": 0, \"text\": \"During our talk, she said that she couldn’t imagine herself being happy.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 1, \"text\": \"She said that she has no hope for her future.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 2, \"text\": \"She has lost her sense of confidence, and has slipped into a pessimistic mood.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 3, \"text\": \"She would go into hysterics if stimulated.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 4, \"text\": \"The client is scrupulous and diligent.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 5, \"text\": \"She has anxiety about having harmonious interpersonal relationships.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 6, \"text\": \"She is transformational and melancholy.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 7, \"text\": \"From now on, I will continue trying to relieve her psychological burdens and to provide her guidance.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}]'),(118,150,'en','[{\"line\": 0, \"text\": \"The client accidentally poured scalding water on her hand during our session.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"She went about cleaning up afterwards as if nothing had happened.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"There is a chance that there is something wrong with her brain organically.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"We may need to perform CT scans and some other tests to determine if this is so.\", \"actor\": \"Toko and Lain together\"}]'),(119,151,'en','[{\"line\": 0, \"text\": \"My client is not opening up to me.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"Some kind of drastic treatment may be necessary.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"When I intentionally rubbed her the wrong way, she showed her combative side.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"I think that in the future, lowering of mental efficiency, deterioration of memory, and deterioration of healing will take place, and an inferiority complex with self-reproach will take over.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 4, \"text\": \"As a result, impulsive behavior will come about, among other symptoms.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 5, \"text\": \"There is a good chance that her sense of reality will be troubling to her.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 6, \"text\": \"She may want to commit suicide.\", \"actor\": \"Toko and Lain together\"}]'),(120,152,'en','[{\"line\": 0, \"text\": \"Her personality speedily continues to decline.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"Her emotions are flattened.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"She is indifferent and lethargic.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"She doesn’t show a feminine demeanor as she used to.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 4, \"text\": \"Until now, I’ve always served tea for visitors, but I didn’t get around to it today.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 5, \"text\": \"The person one imagines oneself to be and that person’s difference in ability compared to her…The client’s ego is collapsing.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 6, \"text\": \"Even in my presence, she exposes lack of confidence and appears sick.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 7, \"text\": \"Sometimes, I don’t know about me…\", \"actor\": \"Toko and Lain together\"}]'),(121,153,'en','[{\"line\": 0, \"text\": \"Her responses to my questions have become sluggish.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"Her lack of self-confidence seems to be altering her self-assessment.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"Her intellect is affected.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"The assessment of her character has not taken place. She has completely lost sense of herself.\", \"actor\": \"Toko and Lain together\"}]'),(122,154,'en','[{\"line\": 0, \"text\": \"Patients with melancholia, it is good to treat them right at the onset of the disease.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"In addition to the neurosis, symptoms of depression and schizophrenia can be seen.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"She may have atypical psychosis.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"Her brainwaves should be tested at the hospital, along with some other tests.\", \"actor\": \"Toko and Lain together\"}]'),(123,155,'en','[{\"line\": 0, \"text\": \"She doesn’t understand what I am saying.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"She responds to technical psychiatric language, but not to ordinary speech.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"I’ve decided to diagnose her with verbal auditory agnosia.\", \"actor\": \"Toko and Lain together\"}]'),(124,156,'en','[{\"line\": 0, \"text\": \"The client has an indirect reason for going to the trouble of surrounding herself with love.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"Using a continuous approach, I think I can regain her psyche.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"I will discontinue medicating her, and resume psychotherapy and milieu therapy.\", \"actor\": \"Toko and Lain together\"}]'),(125,157,'en','[{\"line\": 0, \"text\": \"The client has discovered the connection between herself and reality, and has completely recovered from her ego disturbance.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"The results from the PPRS and HMB tests showed no problems whatsoever.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"I completed my first treatment successfully.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"Thank you, my valuable client.\", \"actor\": \"Lain alone\"}]'),(126,158,'en','[{\"line\": 0, \"text\": \"February 7th. Interview for testimony from a witness to the suicide of a high school girl.\", \"actor\": \"Cop\"}, {\"line\": 1, \"text\": \"Before we begin with questions, I’d like to confirm your identity.\", \"actor\": \"Cop\"}, {\"line\": 2, \"text\": \"Shinji Makino 29 years old.\", \"actor\": \"Cop\"}, {\"line\": 3, \"text\": \"Office worker.\", \"actor\": \"Cop\"}, {\"line\": 4, \"text\": \"Is that correct?\", \"actor\": \"Cop\"}, {\"line\": 5, \"text\": \"Could you remind me of your address?\", \"actor\": \"Cop\"}, {\"line\": 6, \"text\": \"Sure. Shinagawa ward, Kamiosaki, 30-19-105, Meguro-residence 504.\", \"actor\": \"Shinji\"}, {\"line\": 7, \"text\": \"Thank you.\", \"actor\": \"Cop\"}, {\"line\": 8, \"text\": \"This interview will be recorded, by the way, but it will be kept confidential, so please don’t worry.\", \"actor\": \"Cop\"}, {\"line\": 9, \"text\": \"Alright.\", \"actor\": \"Cop\"}, {\"line\": 10, \"text\": \"Let’s get started.\", \"actor\": \"Cop\"}, {\"line\": 11, \"text\": \"Could you tell me what you saw at the scene of the suicide in as much detail as possible?\", \"actor\": \"Cop\"}, {\"line\": 12, \"text\": \"Where do you want me to start? From the morning of that day?\", \"actor\": \"Shinji\"}, {\"line\": 13, \"text\": \"No. Just what you remember from around the time of the suicide. Relax. Don’t feel pressured.\", \"actor\": \"Cop\"}, {\"line\": 14, \"text\": \"Uhh.\", \"actor\": \"Shinji\"}]'),(127,159,'en','[{\"line\": 0, \"text\": \"You were there as a twosome.\", \"actor\": \"Cop\"}, {\"line\": 1, \"text\": \"Yeah. So?\", \"actor\": \"Shinji\"}, {\"line\": 2, \"text\": \"Well, according to a storekeeper who saw you, the girl you were with was very young.\", \"actor\": \"Cop\"}, {\"line\": 3, \"text\": \"Yeah. And?\", \"actor\": \"Shinji\"}, {\"line\": 4, \"text\": \"Well, you see, prostitution takes place at that store.\", \"actor\": \"Cop\"}, {\"line\": 5, \"text\": \"Sorry to tell you, but I don’t have any hobbies like that. She’s just a girl I knew.\", \"actor\": \"Shinji\"}, {\"line\": 6, \"text\": \"Is that right? Well, I’d also like to ask her a few questions too.\", \"actor\": \"Cop\"}, {\"line\": 7, \"text\": \"I haven’t had any contact with her, and why do I have to do all this for you anyway?\", \"actor\": \"Shinji\"}, {\"line\": 8, \"text\": \"Because we’d like to have as much information as we can to understand why a high school student is dead.\", \"actor\": \"Cop\"}, {\"line\": 9, \"text\": \"We haven’t even been able to retrieve the gun used in the crime.\", \"actor\": \"Cop\"}, {\"line\": 10, \"text\": \"And you think she took it with her? You’ve got to be kidding. She’s in junior high school!\", \"actor\": \"Shinji\"}, {\"line\": 11, \"text\": \"We’re not certain, we’re just not ruling out the possibility.\", \"actor\": \"Cop\"}, {\"line\": 12, \"text\": \"Among the people at that store at that time, the only person who we haven’t been able to identify is the girl you were with.\", \"actor\": \"Cop\"}, {\"line\": 13, \"text\": \"Could you help us out with more detailed information?\", \"actor\": \"Cop\"}]'),(128,160,'en','[{\"line\": 0, \"text\": \"I don\'t want to talk if my testimony is going to harm her.\", \"actor\": \"Shinji\"}, {\"line\": 1, \"text\": \"No, nothing has been decided yet.\", \"actor\": \"Cop\"}, {\"line\": 2, \"text\": \"We’d just like to be able to contact her.\", \"actor\": \"Cop\"}, {\"line\": 3, \"text\": \"There’s nothing for you to worry about.\", \"actor\": \"Cop\"}, {\"line\": 4, \"text\": \"But is there something…\", \"actor\": \"Cop\"}, {\"line\": 5, \"text\": \"No, nothing.\", \"actor\": \"Shinji\"}, {\"line\": 6, \"text\": \"I did meet her through the Internet.\", \"actor\": \"Shinji\"}, {\"line\": 7, \"text\": \"But she wasn’t the kind of kid that would reveal an e-mail address.\", \"actor\": \"Shinji\"}, {\"line\": 8, \"text\": \"She was good at computers.\", \"actor\": \"Shinji\"}, {\"line\": 9, \"text\": \"What do you mean?\", \"actor\": \"Cop\"}, {\"line\": 10, \"text\": \"She tapped into computers.\", \"actor\": \"Shinji\"}, {\"line\": 11, \"text\": \"You mean she was a hacker?\", \"actor\": \"Cop\"}, {\"line\": 12, \"text\": \"No doubt about it.\", \"actor\": \"Shinji\"}, {\"line\": 13, \"text\": \"Then you wouldn’t be able to contact her?\", \"actor\": \"Cop\"}, {\"line\": 14, \"text\": \"I don’t mean that I couldn’t.\", \"actor\": \"Shinji\"}, {\"line\": 15, \"text\": \"I reached her once.\", \"actor\": \"Shinji\"}, {\"line\": 16, \"text\": \"But I can’t guarantee that she still even has that address.\", \"actor\": \"Shinji\"}, {\"line\": 17, \"text\": \"I see.\", \"actor\": \"Cop\"}, {\"line\": 18, \"text\": \"Then there’s little we can do.\", \"actor\": \"Cop\"}, {\"line\": 19, \"text\": \"We’ll handle it from here, then.\", \"actor\": \"Cop\"}, {\"line\": 20, \"text\": \"Could you give us her address?\", \"actor\": \"Cop\"}, {\"line\": 21, \"text\": \"Sure, I don’t mind.\", \"actor\": \"Shinji\"}, {\"line\": 22, \"text\": \"If you don’t have it, we can have some of our specialists look into it.\", \"actor\": \"Cop\"}, {\"line\": 23, \"text\": \"If you do hear from her, could you keep our little meeting here a secret?\", \"actor\": \"Cop\"}, {\"line\": 24, \"text\": \"It would be important for you, too, that you keep this a secret.\", \"actor\": \"Cop\"}, {\"line\": 25, \"text\": \"Is that a threat?\", \"actor\": \"Shinji\"}, {\"line\": 26, \"text\": \"Oh, not at all.\", \"actor\": \"Cop\"}, {\"line\": 27, \"text\": \"We have better things to do than make threats.\", \"actor\": \"Cop\"}, {\"line\": 28, \"text\": \"As long as we aren’t interfered with, things will be just fine.\", \"actor\": \"Cop\"}, {\"line\": 29, \"text\": \"That’s what I mean.\", \"actor\": \"Cop\"}]'),(129,161,'en','[{\"line\": 0, \"text\": \"Hello, Makino-san.\", \"actor\": \"Cop\"}, {\"line\": 1, \"text\": \"It’s been a while.\", \"actor\": \"Cop\"}, {\"line\": 2, \"text\": \"You don’t seem to be doing very well, so I’ll keep this short.\", \"actor\": \"Cop\"}, {\"line\": 3, \"text\": \"Can you take responsibility for the things you say to us from now on?\", \"actor\": \"Cop\"}, {\"line\": 4, \"text\": \"Makino-san?\", \"actor\": \"Cop\"}, {\"line\": 5, \"text\": \"I haven’t been able to sleep. I feel better when I’m looking at the computer.\", \"actor\": \"Shinji\"}, {\"line\": 6, \"text\": \"Smoking isn’t really allowed here, but if it relaxes you, go ahead.\", \"actor\": \"Cop\"}, {\"line\": 7, \"text\": \"Let’s begin.\", \"actor\": \"Cop\"}, {\"line\": 8, \"text\": \"The reason you’re here…\", \"actor\": \"Cop\"}, {\"line\": 9, \"text\": \"It’s about Lain.\", \"actor\": \"Shinji\"}, {\"line\": 10, \"text\": \"Yes it is. Did you meet her after the shooting?\", \"actor\": \"Cop\"}, {\"line\": 11, \"text\": \"Probably.\", \"actor\": \"Shinji\"}, {\"line\": 12, \"text\": \"Probably?\", \"actor\": \"Cop\"}, {\"line\": 13, \"text\": \"I don’t know anymore.\", \"actor\": \"Shinji\"}, {\"line\": 14, \"text\": \"Detective, could you contact a psychiatrist for me?\", \"actor\": \"Shinji\"}, {\"line\": 15, \"text\": \"I can’t take it anymore.\", \"actor\": \"Shinji\"}, {\"line\": 16, \"text\": \"I don’t want to die, but all of a sudden, I want to die now!\", \"actor\": \"Shinji\"}, {\"line\": 17, \"text\": \"Look at these scars!\", \"actor\": \"Shinji\"}, {\"line\": 18, \"text\": \"There’s nowhere left to cut!\", \"actor\": \"Shinji\"}, {\"line\": 19, \"text\": \"This is interrogation room number 7.\", \"actor\": \"Cop\"}, {\"line\": 20, \"text\": \"Bring a doctor here quickly.\", \"actor\": \"Cop\"}, {\"line\": 21, \"text\": \"This one’s no good, either.\", \"actor\": \"Cop\"}, {\"line\": 22, \"text\": \"Hey, cut it out!\", \"actor\": \"Cop\"}, {\"line\": 23, \"text\": \"Sorry.\", \"actor\": \"Cop\"}, {\"line\": 24, \"text\": \"Forget about the doctor.\", \"actor\": \"Cop\"}, {\"line\": 25, \"text\": \"Bring a cleaner.\", \"actor\": \"Cop\"}, {\"line\": 26, \"text\": \"And a cup of coffee, too.\", \"actor\": \"Cop\"}]'),(130,162,'en','[{\"line\": 0, \"text\": \"First of all, regarding the suicides of two researchers at our center, there have been rumors that their deaths had to do with them exploring some strange area of research, like something out of a science fiction novel.\", \"actor\": \"Research center representative\"}, {\"line\": 1, \"text\": \"I reassure you that that is completely untrue.\", \"actor\": \"Research center representative\"}, {\"line\": 2, \"text\": \"This center specializes in the study of communication and mental illness.\", \"actor\": \"Research center representative\"}, {\"line\": 3, \"text\": \"Fundamentally, this is not a facility for clinical medicine.\", \"actor\": \"Research center representative\"}, {\"line\": 4, \"text\": \"Our staff sometimes visits hospitals at their invitation, but basically, all we do here is research.\", \"actor\": \"Research center representative\"}, {\"line\": 5, \"text\": \"I don’t know how to apologize to her parents of those researchers.\", \"actor\": \"Research center representative\"}, {\"line\": 6, \"text\": \"I am quite overcome.\", \"actor\": \"Research center representative\"}, {\"line\": 7, \"text\": \"I’ve heard that the female researcher who took her life had some patients.\", \"actor\": \"Reporter\"}, {\"line\": 8, \"text\": \"She was unique.\", \"actor\": \"Representative\"}, {\"line\": 9, \"text\": \"She treated people clinically at this research center.\", \"actor\": \"Representative\"}, {\"line\": 10, \"text\": \"But this was her own decision.\", \"actor\": \"Representative\"}, {\"line\": 11, \"text\": \"I’ve heard rumors that one of the researchers who died was mentally ill. Is that true?\", \"actor\": \"Reporter\"}, {\"line\": 12, \"text\": \"We can’t really comment on that right now.\", \"actor\": \"Representative\"}, {\"line\": 13, \"text\": \"But seeing that she took her own life, she may have had some type of mental illness.\", \"actor\": \"Representative\"}, {\"line\": 14, \"text\": \"There are many things a person may call it, but there’s no doubt that she was suffering deeply.\", \"actor\": \"Representative\"}, {\"line\": 15, \"text\": \"Suicide is very extreme, but she must have been under a lot of stress.\", \"actor\": \"Representative\"}, {\"line\": 16, \"text\": \"A psychiatrist is still just a human being.\", \"actor\": \"Representative\"}, {\"line\": 17, \"text\": \"Did she catch her mental illness from her patients?\", \"actor\": \"Reporter\"}, {\"line\": 18, \"text\": \"Mental illness isn’t a contagious disease, So no, she didn’t “catch” anything from her patients.\", \"actor\": \"Representative\"}, {\"line\": 19, \"text\": \"We wouldn’t use that expression.\", \"actor\": \"Representative\"}, {\"line\": 20, \"text\": \"She may have been influenced by the patients to a degree.\", \"actor\": \"Representative\"}, {\"line\": 21, \"text\": \"You may call that a sort of contagion if you’d like.\", \"actor\": \"Representative\"}, {\"line\": 22, \"text\": \"It’s a matter of semantics.\", \"actor\": \"Representative\"}, {\"line\": 23, \"text\": \"But don’t let anyone trick you about this sort of thing.\", \"actor\": \"Representative\"}, {\"line\": 24, \"text\": \"All of this doctor’s patients were healthy.\", \"actor\": \"Representative\"}, {\"line\": 25, \"text\": \"I can’t get into specifics because of privacy issues.\", \"actor\": \"Representative\"}, {\"line\": 26, \"text\": \"Are there other things that might explain her behavior?\", \"actor\": \"Reporter\"}, {\"line\": 27, \"text\": \"Did the researcher who took her life may have had devious intentions?\", \"actor\": \"Reporter\"}, {\"line\": 28, \"text\": \"We’re in the middle of an investigation, and I can’t comment too much on that out of respect for her friends and family.\", \"actor\": \"Representative\"}, {\"line\": 29, \"text\": \"However, I’ve been informed that that has absolutely nothing to do with it.\", \"actor\": \"Representative\"}, {\"line\": 30, \"text\": \"I would rather…\", \"actor\": \"Representative\"}, {\"line\": 31, \"text\": \"You would rather?\", \"actor\": \"Reporter\"}, {\"line\": 32, \"text\": \"Anyway, I’ve heard that that has nothing to do with it.\", \"actor\": \"Representative\"}, {\"line\": 33, \"text\": \"I’ll say this for our researchers that they are very fine human beings that are very seriously committed about what they do.\", \"actor\": \"Representative\"}, {\"line\": 34, \"text\": \"What was the likelihood of this kind of thing happening?\", \"actor\": \"Reporter\"}, {\"line\": 35, \"text\": \"Well, it may have been less likely if we had caught it earlier.\", \"actor\": \"Representative\"}, {\"line\": 36, \"text\": \"On the other hand, we may have not paid enough attention because our researchers really have it together and are professional and hardworking.\", \"actor\": \"Representative\"}, {\"line\": 37, \"text\": \"We would like to help avoid this is the future by making psychological assessments of our staff.\", \"actor\": \"Representative\"}]'),(131,163,'en','[{\"line\": 0, \"text\": \"Hey, why don’t you walk home with me? Let’s hang out on the way there!\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Sure… if you want to…\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I want to buy lipstick!\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"Teacher will scold you for using lipstick again…\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Lain… you are too stiff. Lets both use it! I think it will look good on you.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"No… It’s needless for me. It’s no use for me to try to be cute.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"You should have confidence in yourself! I think you are cute! So… come shopping with me.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"Ok…\", \"actor\": \"Lain\"}]'),(132,164,'en','[{\"line\": 0, \"text\": \"Lain, do you have a crush on anyone?\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"No… not really…\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"How about you?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I love Nakahara in Class 3. He looks cool.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"I will try to get along with him.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"I hope it works out…\", \"actor\": \"Lain\"}]'),(133,165,'en','[{\"line\": 0, \"text\": \"What do you do to kill time?\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Cleaning…?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Oh, you’re tidy.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"I have a trouble because I have many unnecessary things in my room.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yeah, my room is like that. I often go impulse shopping, so I have lots of lipstick I don’t use.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"How about trading makeup and accessories?\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"But… Lain, you don’t use makeup.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"Are you using the lipstick we bought together?\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"No…\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"You should use a little makeup. I think it suits you!\", \"actor\": \"Kyoko\"}]'),(134,166,'en','[{\"line\": 0, \"text\": \"I hope something great will happen like in a movie.\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Many people will get killed, but I don’t die.\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"And I resolve it with a very cool detective.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"For that, I’ll be a big star!!!\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"Sorry… I’m saying such strange things.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"Hey, don’t look at me with such cold eyes…\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"My eyes are always like this…\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"What? Are you trying to make me laugh?\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"Did I fail?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Aha, ha, ha, yes, you failed!\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"It’s just like you to say something like that.\", \"actor\": \"Kyoko\"}]'),(135,167,'en','[{\"line\": 0, \"text\": \"Hey, Lain. Do you think that you’re a special person?\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Special?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Everyone says, “Lain is not a normal girl” and “Isn’t she dangerous?”\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"I’ve known you since we were children, so I say to them “No, she is normal.”\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"Also, I’m a little similar to you.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"It makes me sad that I’m not special like other people!\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"Do you think so? I don’t care if I’m an ordinary person.\", \"actor\": \"Lain\"}]'),(136,168,'en','[{\"line\": 0, \"text\": \"Kyoko, you don’t look too good.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I can’t do anything for you… I’m so sorry Kyoko…\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Nakahara turned me down. He has another girl who he has a crush on…\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"Kyoko…\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I’m too stupid. He likes the quiet girl with short hair.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"I wish I were like Lain…\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"No! You are far cuter than I am, and you are popular in our class.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Those are useless if he doesn’t like them.\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"You can’t understand my feeling!\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"That’s enough! I’m miserable…\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"Kyoko…\", \"actor\": \"Lain\"}]'),(137,169,'en','[{\"line\": 0, \"text\": \"Say, do you like me? If you do, come to my new URL site.\", \"actor\": \"Girl\"}]'),(138,170,'en','[{\"line\": 0, \"text\": \"But I can only see him. Lain…die.\", \"actor\": \"Woman’s voice\"}]'),(139,171,'en','[{\"line\": 0, \"text\": \"I don’t know!\", \"actor\": \"Woman’s voice\"}, {\"line\": 1, \"text\": \"I don’t care about the test anyway.\", \"actor\": \"Woman’s voice\"}, {\"line\": 2, \"text\": \"If I knew that the world would end today, I would forget about study and just do what I felt like doing.\", \"actor\": \"Woman’s voice\"}]'),(140,172,'en','[{\"line\": 0, \"text\": \"You are a chosen fighter.\", \"actor\": \"Man’s voice\"}, {\"line\": 1, \"text\": \"Fight with me.\", \"actor\": \"Man’s voice\"}, {\"line\": 2, \"text\": \"The only thing that can change this world is your lofty willpower.\", \"actor\": \"Man’s voice\"}, {\"line\": 3, \"text\": \"I know about you.\", \"actor\": \"Man’s voice\"}, {\"line\": 4, \"text\": \"No.\", \"actor\": \"Man’s voice\"}, {\"line\": 5, \"text\": \"I must die.\", \"actor\": \"Man’s voice\"}, {\"line\": 6, \"text\": \"Because you seek after me.\", \"actor\": \"Man’s voice\"}]'),(141,173,'en','[{\"line\": 0, \"text\": \"The woman you walked past at the intersection today is my clone.\", \"actor\": \"Man’s voice\"}, {\"line\": 1, \"text\": \"I can understand because I grew her from a tissue.\", \"actor\": \"Man’s voice\"}, {\"line\": 2, \"text\": \"She is me.\", \"actor\": \"Man’s voice\"}, {\"line\": 3, \"text\": \"She probably has noticed.\", \"actor\": \"Man’s voice\"}, {\"line\": 4, \"text\": \"I understand.\", \"actor\": \"Man’s voice\"}, {\"line\": 5, \"text\": \"If only we knew this about each other, this world would be…\", \"actor\": \"Man’s voice\"}]'),(142,174,'en','[{\"line\": 0, \"text\": \"I ceased to exist again, today.\", \"actor\": \"Man’s voice\"}, {\"line\": 1, \"text\": \"I was so sure that I was me when I woke up.\", \"actor\": \"Man’s voice\"}, {\"line\": 2, \"text\": \"Somehow, something seems to have fallen.\", \"actor\": \"Man’s voice\"}, {\"line\": 3, \"text\": \"I remember becoming sleepy, sitting in the dark and looking around.\", \"actor\": \"Man’s voice\"}, {\"line\": 4, \"text\": \"When he fell asleep, I awoke.\", \"actor\": \"Man’s voice\"}, {\"line\": 5, \"text\": \"I took the sheets off my shoulders, and noticed blood on my hands.\", \"actor\": \"Man’s voice\"}, {\"line\": 6, \"text\": \"The floor was covered in a sea of blood.\", \"actor\": \"Man’s voice\"}, {\"line\": 7, \"text\": \"There was something written on my computer.\", \"actor\": \"Man’s voice\"}]'),(143,175,'en','[{\"line\": 0, \"text\": \"I believed it.\", \"actor\": \"Girl’s voice\"}, {\"line\": 1, \"text\": \"I thought you were terrible, Lain.\", \"actor\": \"Girl’s voice\"}, {\"line\": 2, \"text\": \"But he likes you more than me.\", \"actor\": \"Girl’s voice\"}, {\"line\": 3, \"text\": \"I didn’t know why he liked you and not me.\", \"actor\": \"Girl’s voice\"}, {\"line\": 4, \"text\": \"Lain, you don’t really care if he likes you or not, do you?\", \"actor\": \"Girl’s voice\"}]'),(144,176,'en','[{\"line\": 0, \"text\": \"You are all a clump.\", \"actor\": \"Man’s voice\"}, {\"line\": 1, \"text\": \"A clump that doesn’t exist.\", \"actor\": \"Man’s voice\"}, {\"line\": 2, \"text\": \"I eat the flesh of humans and shed blood.\", \"actor\": \"Man’s voice\"}, {\"line\": 3, \"text\": \"Look at your moronic face!\", \"actor\": \"Man’s voice\"}, {\"line\": 4, \"text\": \"Everyone who mocks me is a stupid asshole!\", \"actor\": \"Man’s voice\"}, {\"line\": 5, \"text\": \"Look, your construction is nearly complete…Oh, I want to laugh right now…did someone enter this room?\", \"actor\": \"Man’s voice\"}]'),(145,177,'en','[{\"line\": 0, \"text\": \"...\", \"actor\": \"Voices\"}]'),(146,178,'en','[{\"line\": 0, \"text\": \"I don’t know.\", \"actor\": \"Girl\"}, {\"line\": 1, \"text\": \"I don’t care about the test.\", \"actor\": \"Girl\"}, {\"line\": 2, \"text\": \"I know that the world will come to an end.\", \"actor\": \"Girl\"}, {\"line\": 3, \"text\": \"I could forget about studying and do what I feel like doing.\", \"actor\": \"Girl\"}, {\"line\": 4, \"text\": \"Look at your stupid face! Anyone who mocks me is a stupid asshole!\", \"actor\": \"Man\"}, {\"line\": 5, \"text\": \"But I can only see him. Lain.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"It is your lofty willpower. I know about you. No. I must die.\", \"actor\": \"Man\"}]'),(147,179,'en','[{\"line\": 0, \"text\": \"I’m so sorry to be late when I’m the person who asked to meet.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"It’s ok. What do you want to talk about?\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"Um, I’ll have Earl Grey without milk. Would you like more coffee, Kyoko-chan?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Sure.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"So you’re a private tutor.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"People like you are rare nowadays, aren’t you?\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"You get this involved about your students?\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"Enough to ask other kids about them?\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"I’ve been a bit distracted and out of focus lately, and I was concerned that that might have been rubbing off on Lain and that I haven’t been teaching her properly.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Either that, or maybe things haven’t been going well for her at school.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I think she’s doing ok.\", \"actor\": \"Kyoko\"}, {\"line\": 11, \"text\": \"From your point of view, is she doing well in school?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I don\'t really know.\", \"actor\": \"Kyoko\"}, {\"line\": 13, \"text\": \"We used to be friends, but since we entered junior high school, we haven’t talked to each other that much.\", \"actor\": \"Kyoko\"}, {\"line\": 14, \"text\": \"Is this a test to see if she’s been behaving herself?\", \"actor\": \"Kyoko\"}, {\"line\": 15, \"text\": \"I don’t know what other kids are doing outside of school.\", \"actor\": \"Kyoko\"}, {\"line\": 16, \"text\": \"Wouldn’t you get your answers more quickly by consulting an advisor at school or something?\", \"actor\": \"Kyoko\"}, {\"line\": 17, \"text\": \"Oh, no, no.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"It’s nothing serious like that.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Not at all.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"But she’s a calm girl, isn’t she?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"She doesn’t open up so easily?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"I was just curious about how she’s doing in school.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Hmm.\", \"actor\": \"Kyoko\"}, {\"line\": 24, \"text\": \"Well, I have nothing to lose by talking to you, so ask me whatever you want.\", \"actor\": \"Kyoko\"}, {\"line\": 25, \"text\": \"I haven’t talked to Lain much recently.\", \"actor\": \"Kyoko\"}, {\"line\": 26, \"text\": \"She doesn’t have that many friends.\", \"actor\": \"Kyoko\"}, {\"line\": 27, \"text\": \"She used to be a little somber.\", \"actor\": \"Kyoko\"}, {\"line\": 28, \"text\": \"I guess you could say that she’s become more somber lately.\", \"actor\": \"Kyoko\"}]'),(148,180,'en','[{\"line\": 0, \"text\": \"We were in the same class in 5th and 6th grade.\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"We were in the same group because we sat close to each other.\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"She was quiet, so she didn’t talk to many people other than me.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"She seemed like a sister to me.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"We didn’t play together much, though.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"When would you say that Lain started to change?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"She wasn’t so somber until about a year ago.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"When I see her these days, we don’t have much to say.\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"It’s too bad, but lately, she’s just another classmate to me.\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"Have you talked to her at all recently?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Recently? I seem to remember something. What was it, I wonder.\", \"actor\": \"Kyoko\"}, {\"line\": 11, \"text\": \"Can you remember?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"How weird. I usually have a good memory.\", \"actor\": \"Kyoko\"}, {\"line\": 13, \"text\": \"Do you remember talking about shopping or boys?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"No. I don’t think we talked about that.\", \"actor\": \"Kyoko\"}]'),(149,181,'en','[{\"line\": 0, \"text\": \"What was it?\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Something about my memory.\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"I remember now.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"I remember that we were in biology class.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"Lain was asking the teacher after class about the brain and how memories are stored.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"I thought that that was pretty impressive.\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"I asked her what she had asked the teacher, and she said she asked the teacher how to recollect memories.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"Weird.\", \"actor\": \"Kyoko\"}]'),(150,182,'en','[{\"line\": 0, \"text\": \"I don’t dislike Lain. But lately she’s been hard to approach.\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"I know what you mean.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Don’t you, though?\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"I think a lot of the kids see her that way.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"No one is bullying her, now, but most of us are pretty indifferent to her.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"Yeah.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You ought to talk to her. Tell her to laugh the way she used to.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"The way she used to?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"In the old memories I have of her, she was always smiling.\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"I remember that when we were together, she would always laugh.\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"I see. I sort of had that impression. That’s why I was concerned.\", \"actor\": \"Toko\"}]'),(151,183,'en','[{\"line\": 0, \"text\": \"Yes. are you a friend of Misato-chan, Kyoko?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Who’s Misato-chan?\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"A friend of Lain’s.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Misato? We had a class with a girl named Minako. Who is Misato?\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"She was in a class with you in junior high.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Huh!\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"No way.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"There was definitely nobody by that name in my junior high school class.\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"You must mean Minako.\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"But Minako wasn’t friendly with Lain at all.\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"Oh. I must have been misinformed. Never mind.\", \"actor\": \"Toko\"}]'),(152,184,'en','[{\"line\": 0, \"text\": \"I guess it sounds like I’m bad-mouthing her.\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"There are some strange things about Lain, but she’s an ordinary girl.\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"She could be much attractive and popular if she would lighten up.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"She’s gentle, too.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"Gentle.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"So what subjects do you tutor?\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"All subjects.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Wow, I’m jealous.\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"I’m no good at all at math.\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"Lain’s grades are perfect.\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"I wish I had a private tutor.\", \"actor\": \"Kyoko\"}]'),(153,185,'en','[{\"line\": 0, \"text\": \"Nice to meet you. Order whatever you want.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Lain doesn’t go out much, and it’s hard to tell if she’s going out with someone.\", \"actor\": \"Kaori\"}, {\"line\": 2, \"text\": \"Sometimes she helps me with my schoolwork, and I offer to take her out to have some fun to thank her, but she doesn’t come.\", \"actor\": \"Kaori\"}, {\"line\": 3, \"text\": \"A royal chocolate parfait, please.\", \"actor\": \"Kaori\"}, {\"line\": 4, \"text\": \"And who did you say you were?\", \"actor\": \"Kaori\"}, {\"line\": 5, \"text\": \"I’m her tutor.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Oh…a tutor.\", \"actor\": \"Kaori\"}, {\"line\": 7, \"text\": 