\": \"Girl2\"}, {\"line\": 5, \"text\": \"Это не так.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Так! Так!\", \"actor\": \"Girl\"}, {\"line\": 7, \"text\": \"Лейн, твой папа классный, не так ли?\", \"actor\": \"Girl2\"}, {\"line\": 8, \"text\": \"Мы его сегодня увидим?\", \"actor\": \"Girl2\"}, {\"line\": 9, \"text\": \"Мне жаль.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Я сегодня не могу.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Извините.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Я уверена, что она свободна ...\", \"actor\": \"Girl\"}, {\"line\": 13, \"text\": \"Да...\", \"actor\": \"Girl2\"}]');
