INSERT INTO `translation` VALUES (1,10,'en','[{\"line\": 0, \"text\": \"Lain! Why didn\'t you wait for us?\", \"actor\": \"Girl\"}]'),(2,10,'ru','[{\"line\": 0, \"text\": \"!     ?\", \"actor\": \"\"}, {\"line\": 1, \"text\": \"?\", \"actor\": \"\"}]'),(3,11,'en','[{\"line\": 0, \"text\": \"You said we could visit you after school.\", \"actor\": \"Girl-1\"}, {\"line\": 1, \"text\": \"Really?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Listen, Lain!\", \"actor\": \"Girl-2\"}, {\"line\": 3, \"text\": \"You play games all the time.\", \"actor\": \"Girl-2\"}, {\"line\": 4, \"text\": \"You never listen during classes and then you get 100% on tests.\", \"actor\": \"Girl-2\"}, {\"line\": 5, \"text\": \"It\'s not relevant.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"It is! It is!\", \"actor\": \"Girl\"}, {\"line\": 7, \"text\": \"Lain, your dad is cool, isn\'t he?\", \"actor\": \"Girl-2\"}, {\"line\": 8, \"text\": \"Will we see him today?\", \"actor\": \"Girl-2\"}, {\"line\": 9, \"text\": \"I\'m sorry.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I really can\'t today.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Sorry.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"I\'m sure she\'s free\", \"actor\": \"Girl-1\"}, {\"line\": 13, \"text\": \"Yeah\", \"actor\": \"Girl-2\"}]'),(4,13,'en','[{\"line\": 0, \"text\": \"LAIN\", \"actor\": \"Sign on the door\"}, {\"line\": 1, \"text\": \"That\'s what I don\'t get.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"If we use packet filtering here then debugging won\'t be necessary.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"No, not that\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wanted\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Don\'t enter without permission!\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"My friend is with me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"We are always together.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"All right.\", \"acto