INSERT INTO `translation_history` VALUES (1,'new','2020-02-13 22:55:04','2020-02-26 17:16:17',1,10,'en','[{\"line\": 0, \"text\": \"Lain! Why didn\'t you wait for us?\", \"actor\": \"Girl\"}]'),(2,'new','2020-02-13 22:55:04','2020-02-26 17:46:20',2,10,'ru','[{\"line\": 0, \"text\": \"!     ?\", \"actor\": \"\"}, {\"line\": 1, \"text\": \"?\", \"actor\": \"\"}]'),(3,'new','2020-02-13 22:55:04','2020-03-16 10:16:26',3,11,'en','[{\"line\": 0, \"text\": \"You said we could visit you after school.\", \"actor\": \"Girl\"}, {\"line\": 1, \"text\": \"Really?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Listen, Lain!\", \"actor\": \"Girl2\"}, {\"line\": 3, \"text\": \"You play games all the time.\", \"actor\": \"Girl2\"}, {\"line\": 4, \"text\": \"You never listen during classes and then you get 100% on tests.\", \"actor\": \"Girl2\"}, {\"line\": 5, \"text\": \"It\'s not relevant.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"It is! It is!\", \"actor\": \"Girl\"}, {\"line\": 7, \"text\": \"Lain, your dad is cool, isn\'t he?\", \"actor\": \"Girl2\"}, {\"line\": 8, \"text\": \"Will we see him today?\", \"actor\": \"Girl2\"}, {\"line\": 9, \"text\": \"I\'m sorry.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I really can\'t today.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Sorry.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"I\'m sure she\'s free\", \"actor\": \"Girl\"}, {\"line\": 13, \"text\": \"Yeah\", \"actor\": \"Girl2\"}]'),(4,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',4,13,'en','[{\"line\": 0, \"text\": \"LAIN\", \"actor\": \"Sign on the door\"}, {\"line\": 1, \"text\": \"That\'s what I don\'t get.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"If we use packet filtering here then debugging won\'t be necessary.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"No, not that\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wanted\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Don\'t enter without permission!\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"My friend is with me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"We are always together.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"All right.\", \"actor\": \"Lain\"}]'),(5,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',5,17,'en','[{\"line\": 0, \"text\": \"By the way, I recently found a super tasty cake!\", \"actor\": \"Girl-1\"}, {\"line\": 1, \"text\": \"Really? I love chocolate.\", \"actor\": \"Girl-2\"}, {\"line\": 2, \"text\": \"Believe me, its really good.\", \"actor\": \"Girl-1\"}]'),(6,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',6,18,'en','[{\"line\": 0, \"text\": \"Computer?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"A friend?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I never thought about it that way.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"We were talking about it before.\", \"actor\": \"Lain\"}]'),(7,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',7,19,'en','[{\"line\": 0, \"text\": \"What\'s that?\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"This?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Bike-chan.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"A puppy.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"My stuffed toy.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I wouldn\'t call him a friend, but\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"He was already there when I was born.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Right, Bike-chan?\", \"actor\": \"Lain\"}]'),(8,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',8,20,'en','[{\"line\": 0, \"text\": \"I wouldn\'t say I like meetings with Touko-sensei\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Sure, it\'s nice.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"But I dislike it here.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Why is that?\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"I just do.\", \"actor\": \"Lain\"}]'),(9,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',9,25,'en','[{\"line\": 0, \"text\": \"I\'m going back to you.\", \"actor\": \"Lain\"}]'),(10,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',10,26,'en','[{\"line\": 0, \"text\": \" and so\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \" when we want to increase the processing speed\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \" we must take into account the fact that the processor can overheat.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"You can keep the CPU cool by using a Peltier element\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"but it\'s still a problem\", \"actor\": \"Lain\"}]'),(11,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',11,27,'en','[{\"line\": 0, \"text\": \"Savant syndrome?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Am I sick?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I didn\'t say it\'s about you, Lain.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Only that there are people who suffer from that sickness.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"If it\'s not about me\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"then why are talking about it?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"There are other children who may learn from your example.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"I\'m only myself.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Not other children.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"That\'s true!\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"I\'m sorry, Lain.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"What\'s wrong, Lain?\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"Why are you calling me Lain?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Who are you?\", \"actor\": \"Lain\"}]'),(12,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',12,31,'en','[{\"line\": 0, \"text\": \"When we are not connected to the network\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"we are all alone.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Do you understand that?\", \"actor\": \"Lain\"}]'),(13,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',13,33,'en','[{\"line\": 0, \"text\": \"Stop it!\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"You have to be still!\", \"actor\": \"Lain\"}]'),(14,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',14,34,'en','[{\"line\": 0, \"text\": \"Dont do that. You have to stay calm... It finally came.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"...\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Lain, concerning your inquiry, I think your approach is the correct one.\", \"actor\": \"Computer\"}, {\"line\": 3, \"text\": \"...\", \"actor\": \"Computer\"}, {\"line\": 4, \"text\": \"The concept of a neural network is a relatively old idea, but the arrangement of neurons by any system may not necessarily make the system work...\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"It wont work.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Ha, ha.\", \"actor\": \"Lain\"}]'),(15,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',15,35,'en','[{\"line\": 0, \"text\": \"But even if it is assumed to be complete, there will definitely be an unknown network that connects people.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It might be one of many things, like Jungs ideas of the subconscious.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"There are many ways to say it, but no one can prove its existence now...\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"But thinking about a person like you who has an interest in this sort of thing, I wonder, what are you trying to research?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"What is your goal?\", \"actor\": \"Lain\"}]'),(16,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',16,37,'en','[{\"line\": 0, \"text\": \"Wave interference.\", \"actor\": \"Lain\"}]'),(17,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',17,38,'en','[{\"line\": 0, \"text\": \"I don\'t believe it\'s you.\", \"actor\": \"Guy\"}, {\"line\": 1, \"text\": \"How old are you?\", \"actor\": \"Guy\"}, {\"line\": 2, \"text\": \"Oh, I\'m sorry.\", \"actor\": \"Guy\"}, {\"line\": 3, \"text\": \"You shouldn\'t ask women about their age.\", \"actor\": \"Guy\"}, {\"line\": 4, \"text\": \"I don\'t normally get a chance to talk with girls your age.\", \"actor\": \"Guy\"}, {\"line\": 5, \"text\": \"And to be honest, it\'s making me a bit nervous.\", \"actor\": \"Guy\"}, {\"line\": 6, \"text\": \"Do you have what you promised?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"The actuator motor.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"It\'s a rare thing.\", \"actor\": \"Guy\"}, {\"line\": 9, \"text\": \"Watch out for electricity.\", \"actor\": \"Guy\"}, {\"line\": 10, \"text\": \"No worries.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"So sure?\", \"actor\": \"Guy\"}, {\"line\": 12, \"text\": \"What are you building anyway?\", \"actor\": \"Guy\"}, {\"line\": 13, \"text\": \"Myself.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"And something that gave me a body.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"But it\'s only temporary.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"So there\'s no point in developing it.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"I don\'t think I quite understand\", \"actor\": \"Guy\"}, {\"line\": 18, \"text\": \"Things are born when you build them.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"And when they are destroyed, they transform into something else.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Something completely new.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Do you know what\'s going on?\", \"actor\": \"Guy\"}]'),(18,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',18,42,'en','[{\"line\": 0, \"text\": \"Stop!\", \"actor\": \"Guy\"}]'),(19,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',19,44,'en','[{\"line\": 0, \"text\": \"Lain?\", \"actor\": \"Guy\"}, {\"line\": 1, \"text\": \"Lain!\", \"actor\": \"Guy\"}]'),(20,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',20,45,'en','[{\"line\": 0, \"text\": \"What\'s going on?\", \"actor\": \"Touko\"}]'),(21,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',21,46,'en','[{\"line\": 0, \"text\": \"Lain!\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"When you appeared in a laboratory two years ago\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"all data about you was entered into a database.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"You know that.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Archives of movies recorded by your family.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"The contents of your personal computer.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"We had it all in data.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"But there\'s something strange\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"The last data update\", \"actor\": \"Touko\"}]'),(22,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',22,47,'en','[{\"line\": 0, \"text\": \"is about your future!\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"It\'s a record from a day two years in the future!\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Who are you?\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Talk to me!\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Why isn\'t there any data prior to three years ago?\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"No!\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"You were born in your current form\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"It doesn\'t matter in this world.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"You know that.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Stay away!\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"I want to connect with the entire world.\", \"actor\": \"Lain\"}]'),(23,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',23,50,'en','[{\"line\": 0, \"text\": \"Lain.\", \"actor\": \"Guy\"}, {\"line\": 1, \"text\": \"You\", \"actor\": \"Guy\"}, {\"line\": 2, \"text\": \"I finished.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"What are you talking about?\", \"actor\": \"Guy\"}, {\"line\": 4, \"text\": \"About my dad.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"What?\", \"actor\": \"Guy\"}, {\"line\": 6, \"text\": \"Lain!\", \"actor\": \"Guy\"}, {\"line\": 7, \"text\": \"Lain!\", \"actor\": \"Guy\"}, {\"line\": 8, \"text\": \"Lain?\", \"actor\": \"Guy\"}]'),(24,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',24,54,'en','[{\"line\": 0, \"text\": \"It\'s over.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"For me it\'s the beginning.\", \"actor\": \"Lain2\"}, {\"line\": 2, \"text\": \"From now on we will always be together.\", \"actor\": \"Lain2\"}]'),(25,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',25,57,'en','[{\"line\": 0, \"text\": \"So Mrs. Iwakura, please wait here in the antechamber.\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Lain, you dont have to worry so much.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Please sit down there.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Ah do you want some juice or something else to drink?\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Its OK if you dont worry so much.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"Let\'s see.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"I\'m thirsty, so I\'m going for tea mind to follow me?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Because it will look funny if only I drink it.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Thank you.\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"Which one should it be?\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"We have many to choose from.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"The same as you.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Green tea?\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"Got it.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Just a moment\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"You put in the sugar and milk by yourself, OK?\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"Here you go.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"So where should we start?\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"How is this? Earl Grey tea, I really love it.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Earl Grey?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Right.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"Even with tea there are lots of kinds, with different smell and taste.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"Does it taste good?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Yes.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"It suits my taste.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"But I\'m not sure about you Lain.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"If you want to, you can drink a little bit.\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"If its not to your taste, I can make something else for you.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"Here\", \"actor\": \"Touko\"}]'),(26,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',26,58,'en','[{\"line\": 0, \"text\": \"That smells good.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Here you go.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"How is it?\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Looks as if you dont like it, huh?\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Too bitter?\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"What if you add some sugar?\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"Well, how about if you add some milk?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"It might take away the bitterness.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Or I can get you something else?\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"I dont like milk.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Ah, sorry. I didnt know... Well, how about if I get some juice for you?\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"Im sorry...\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Thats all right; I probably couldnt have had green tea either at your age.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"You said that there are lots of different kinds of tea right?\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"If I dont like it, I dont like it.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"Of course, that doesnt mean I cant drink it, but different kinds of tea have different flavors right?\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"What do you like, Lain?\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"Why dont you like milk?\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"It upsets my stomach.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Ha, ha, truth be told, when I was a child I had the same problem.\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"Especially when the school lunch included milk, I can drink milk now though.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"I cant drink it, but I really love it...\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"How about milk that doesnt upset your stomach? Would that be all right?\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"Whats it called?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"I forgot what its called, but its milk that doesnt upset your stomach.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"I have it for kids like you Lain, who are like me as a kid and cant drink milk.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"If you talk about it with your mother, Im sure shell look for it for you.\", \"actor\": \"Touko\"}]'),(27,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',27,59,'en','[{\"line\": 0, \"text\": \"Are there different kinds of milk as well?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Thats right; although theyre basically all they same, depending on how theyre prepared there are different kinds.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Different kinds?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Yes, in tea too.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Depending on the way the leaves are dried and the way the tea is poured, the same leaves can be very different.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"People too?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Hmm?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Are there different kinds of people as well?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Hmm, well, rather than kinds, each person is different in his or her own way.\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"For example, you dont like tea, but there are also people like me who love it.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"There are also people whose stomach gets upset when they drink milk, but there also those whose stomach doesnt get upset right?\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"However, there also people who when they grow up, learn to drink it.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"Lain, youll probably will learn to drink tea you know.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"Its not a good thing, nor a bad thing.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Its normal that there are different kinds of people.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"Thats why you can just ignore rather you should be proud of your uniqueness I think!\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"Be proud of it?\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Im sorry... Thats right, you worry, dont you?\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"You dont have to boast about it, but theres no need to mind what other people think is what I meant to say.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"I have weird dreams, is that different too?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"You may think its different, but in fact thats normal.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"If youre worried about that, I might be able to help.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"Not to help you stop having those dreams, but rather helping you to explain things you dont understand.\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"If you come to me with your problems, Id be really happy.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"You know, Ive studied a lot to help you answer those kinds of questions; Im still studying actually.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"If youre ever in trouble or something, and should you want to come and talk to me, thatd be OK. Id like to know more about you Lain.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"Would that be OK?\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"Im so glad!\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"Starting today you and I are friends!\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"Please come and play sometime!\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"Promise?\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"Id like to ask you things as well!\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"You can ask me anything you want.\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"If I dont know the answer Ill look up the answer for you.\", \"actor\": \"Touko\"}, {\"line\": 34, \"text\": \"Ill even prepare your favorite drink.\", \"actor\": \"Touko\"}, {\"line\": 35, \"text\": \"Whats that??\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"Milk that doesnt upset your stomach!\", \"actor\": \"Touko\"}]'),(28,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',28,60,'en','[{\"line\": 0, \"text\": \"7th of July, 2pm, 2nd session\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Come on in.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Touko, Touko, hello.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Hello, Lain\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Hello.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Its almost the summer holidays! Are you looking forward to them?\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"Not especially.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Lets see here, today, today Id like to play a game with you Lain, thats why I brought this.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Whats that?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Its oil painting set. Do you like picture Lain?\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"I dont like paintings... Maybe...\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"I see Well, do you know tic-tac-toe?\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"I know it, but I dont want to play that\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I Im sorry. Im a bit frazzled today. Well, do you have anything you want to do Lain?\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Id like to talk some more\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"What do you want to talk about?\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"Whatever. About you.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"About me? Well Im not that interesting you know.\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"Yes, you are.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"No really, Im not that interesting.\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"But look, Im already 27, if we talked about me wed have to start all the way at the beginning and itd take forever!\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"And really, its not a very interesting story at that.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"What kind of doctor are you?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"What kind of doctor you ask?\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"Well, I help people like you who worry about certain things, and listen to their stories.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"And I give them advice to help fix their problems.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"You understand?\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"Ah I thought so\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"Whats wrong?\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"If youre not feeling well, or youre not comfortable please tell me, you can be honest with me.\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"I should be more careful really.\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"You shouldnt worry so much.\", \"actor\": \"Touko\"}, {\"line\": 32, \"text\": \"Just relax, come and have a nice talk with me once a month.\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"Okay\", \"actor\": \"Lain\"}]'),(29,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',29,61,'en','[{\"line\": 0, \"text\": \"So, hows school?\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Normal.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"By normal you mean? Not interesting, not boring, just normal?\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Yes\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"How about friends?\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"I have friends.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Do you often play with them?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Yes, but everyones busy with cram school. The only place I go is here\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Dont you want to learn something? Calligraphy, or take swimming lesso ooohhh\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"Its ok; dont worry about it.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Im sorry\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"Id rather stay at home than go out and do something.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"What do you do at home?\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"Thats a secret.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Aw, thats mean. You wont tell me.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"But Im ashamed.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Are you doing things youre ashamed of?\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"Maybe\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Now I want to know! Ah, so be it But Id really like to know\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"If I dont tell you is that bad?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Id be glad if you did tell me.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"I talk to my stuffed animals.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Ha, ha, is that it? Thats completely normal. I knew it.\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"Knew what?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Ha, ha, never mind! I used to have a stuffed animal I loved\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"What kind of stuffed animal?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"A dragon I got at the bank. Weird huh?\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"Why is that weird?\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"Well, arent girls supposed to play with cute stuffed animals?\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"It wasnt even a stuffed animal it was a piggy bank dragon.\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"But I still treasured it.\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"I called it Ta-chan and talked to it all the time.\", \"actor\": \"Touko\"}, {\"line\": 32, \"text\": \"Me too. I talk to my animals. Is that normal?\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"Thats normal! Do I look weird to you??\", \"actor\": \"Touko\"}, {\"line\": 34, \"text\": \"No\", \"actor\": \"Lain\"}, {\"line\": 35, \"text\": \"Youre the same as me, Lain, were not weird at all!\", \"actor\": \"Touko\"}]'),(30,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',30,62,'en','[{\"line\": 0, \"text\": \"Hello Lain, have you been well?\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Yes, and you?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Yes, Ive been fine. Im a bit tired though.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Tired?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yes, Ive been pretty busy.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"With what?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Hmm, well... At least talking with you like this is something I can look forward to.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Really?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Really! What shall we talk about today What do you want to talk about Lain?\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"I want to talk about you, doctor. We werent able to last month.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Id rather talk about you, Lain; not about myself, though.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"But youre right, its only fair that we both get to talk about what we want, so this time well talk about me, but next time well talk about you Lain, is that all right?\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"OK!\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Im not sure where I should start though... My name is Younera Touko, Im 27, Im a doctor at the Tokyo Research Institute for Psychotherapy Thats this place.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Ah, we wont get anywhere like this.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"You know, Im used to listening people talk, not used to talking myself.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"Is there anything youd like to know Lain?\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"How did you become a doctor?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"I studied a lot at a place called a university.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"I wrote something called a thesis, and with that I received my degree to become a doctor.\", \"actor\": \"Touko\"}]'),(31,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',31,63,'en','[{\"line\": 0, \"text\": \"What is college like?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Youre in junior high school now arent you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"So if youre in junior high and want to keep studying, you go to high school, right?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"College is where people go if they want to keep studying after high school.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Do you like to study, Sensei?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I didnt used to.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"But I went to America as an exchange student when I was in junior high school, and I started to feel differently then.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"You were an exchange student. Wow!\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"At the time, I just went because my parents wanted me to.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"But people have different ways of seeing things in other countries, and I learned a lot from that.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Looking back, it was a lot of work, and at first, I didn\'t feel good about it, but now Im really grateful that I was able to be an exchange student.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I learned English, too.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Thats amazing, Toko Sensei!\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Oh, its not so amazing.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"There are lots of people who have studied much more than I have.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Im really a pretty lazy person.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I liked to fool around, really.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Its boring to just study, isnt it?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Do you like to study, Lain?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Not really.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"What subjects dont you like?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Japanese and physical education.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"I see. So you dont like exercise.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"No, I dont like physical education.\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"You like exercise, then?\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"What kind of exercise do you like?\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"The metal beam.\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"Wow!\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"I used to like running, but I was never good at the metal beam.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"I would spin around and get too dizzy.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"I like to spin around.\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"Both frontwards and backwards?\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"Yes, both.\", \"actor\": \"Lain\"}, {\"line\": 34, \"text\": \"What dont you like?\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"(silence)\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"You dont have to answer if you dont want to.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"But, what kind of study do you like?\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"I\'m sorry.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"Are you tired?\", \"actor\": \"Toko\"}, {\"line\": 40, \"text\": \"Why don\'t we end early today?\", \"actor\": \"Toko\"}]'),(32,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',32,64,'en','[{\"line\": 0, \"text\": \"Im sorry about last time but Im relieved.\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"I was worried that you didnt like me and wouldn\'t come anymore.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Im sorry.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Theres no reason to apologize.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"I was the one who asked you a bad question.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"Can you forgive me?\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"thank you.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"So I thought we could work with this today. Can we talk about this?\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"They had that at the last hospital.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Really? I wanted to play with this.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"What kind of test is it?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Amazing Lain! Do you know about it?\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"They call this a Rorschach Test.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"This isnt really a test.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Think of it as a game.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"But thats boring.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Hmm I guess so.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"If you think it\'s boring, we\'ll stop the test.\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"Hows that?\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Okay.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Okay. What do you see?\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"A splotch.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Um, right.\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"But does it look like anything?\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"What do you see?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Me? Hmm a persons face, I think.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"Is it angry? Is it also crying?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"It looks a little angry.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"This is boring.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"Lain\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"Right. A promise is a promise. Lets stop.\", \"actor\": \"Touko\"}]'),(33,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',33,65,'en','[{\"line\": 0, \"text\": \"Do you want to talk about something fun? Do you like to take a bath or a shower?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I like showers.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Really?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I prefer baths, but I dont get a chance to take them much at home.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Its a bit of a hassle to prepare for a bath, too.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I usually take showers, but I take a bath on my days off.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Dont you like the way a bath feels?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Im afraid of being in hot water.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"You don\'t like to get wet?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"No.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Then, what are you afraid of?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Im not really sure. It feels like Im being touched all over by something.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Hmm the water does touch the skin.\", \"actor\": \"Toko\"}]'),(34,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',34,66,'en','[{\"line\": 0, \"text\": \"Its because Im weird\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Wait a second Lain.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Youre not a weird kid at all.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Everyone thinks theyre weird when theyre kids...\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"But youre wrong.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Do you dislike being different from others?\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"Im not sure.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Okay?\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Lain is Lain because she is different.\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"You have a very strong imagination, and thats a good thing.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"So dont say that you\'re weird.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"I dont think that at all.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"But the test\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Im sorry but youre troubled, right?\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"I want you to not to be troubled.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"In order to help you, I have to know more about you.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"I dont want to make you uncomfortable.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"What should I do?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Right now you don\'t need to do anything.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Since you and I are friends, if we keep talking, Ill slowly start to understand more and more about you.\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"So, if I say, I think you should come over, it would be good for you to think, That sounds nice, okay....\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"So right now, itd be best if you tell me all about yourself.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"You wont tell anyone?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Nope, itll be our secret.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"Not even your friends, or your mother?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"I promise. If I lie, one thousand needles!\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"One thousand needles?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Dont kids your age know that?\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"When I was a kid, whenever we made a promise, we said, If I break the promise, I will drink 1000 needles.\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"Wouldnt that hurt?\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"A lot!!... Right! How many needles are in my stomach right now? Miss Iwakura?...\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \" five thousand?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"Wrong! Yesterday, since I went to the bathroom and flushed everything out, theres zero!\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"Sensei, that\'s gross!...\", \"actor\": \"Lain\"}]'),(35,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',35,67,'en','[{\"line\": 0, \"text\": \"Hello, Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"(in a soft voice) Hello.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"You dont sound so energetic.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Maybe you dont feel up for this.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Why dont we quit now?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Ok?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"But since you came all the way out here, how would you like a cup of tea or something?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"What would you like?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Dont be shy.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I want to talk.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"You dont have to force yourself. That might make you feel even worse. Take my word for it.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I want to be cured.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Curedbut youre not sick, Lain.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But this is a hospital isnt it?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Well, this place can function as a hospital, but I see patients at my research room as well.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Right now, I dont know if you are really sick or not.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"My hunch is that you are probably not sick, Lain.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Still, if you arent feeling well or are worried about something, Id like to be able to help you.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Im not sick?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Right now, Im not completely sure, but youre probably just fine.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Even if you are sick, Ill make sure that you are cured.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Youre absolutely sure you can cure me?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Absolutely. I will not let you be sick.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Sensei, why are you so kind to me? Is it because it is your job?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Hmm Youre right that this is a part of my work.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"But its a type of work thats hard to see as just work.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Most of the time, I see patients, diagnose them as objectively as possible, and give them psychological treatment, but it isnt enough to make them better.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Im a researcher as well as a doctor.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"If I see someone suffering, I must try and help that person.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"I want to do everything I can to help that person.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"You would do that for anybody?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"To be specific, if I see someone suffering from something for which the cause is physical and obvious, I refer that person to a specialist.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"If there is a car accident or a poisoning, that\'s what I would do.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Do put it simply, Im not the kind of doctor who treats people who have something wrong with them physically.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"So I have\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"I cant tell for sure without doing a close examination, but I know that you had an examination before you came here and that no problems were found.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"And just by talking to you here, Lain, you dont seem to have anything wrong with you at all.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"But I have to have you talk to me and tell me much more about whats going on with you I cant really help you with whats bothering you.\", \"actor\": \"Toko\"}]'),(36,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',36,68,'en','[{\"line\": 0, \"text\": \"What bothers me is that I have nightmares, I see strange things, I feel that someone is touching me when no one is there.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I have symptoms of schizophrenia.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Schizophrenia? Where did you hear a word like that? Who said that to you?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I looked it up in a book.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Lain, its a good things to study hard, but its dangerous to go digging around and diagnosing yourself when you arent sick.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Have you heard the expression a lot of how sick you feel depends on what you think about it?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Actually I havent.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Oh?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Anyway, if you start diagnosing yourself, youre going to start believing that you have all sorts of things wrong with you.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"But I wanted to know whats wrong with me. I wanted to understand.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I see.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I understand.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I think youre intentions are right.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But Id like to ask something of you.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Please dont go around imagining and concocting searching, for things that you think are wrong with you.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Can I ask that of you?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Then will you help me find whats wrong?\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Of course I will.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"So lets make a promise.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I will tell you everything I understand about you.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I wont hide anything from you.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"If you lie can I cut off your finger?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Hmm you can do something even worse. You can hammer a nail into my head.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Youre goofy, Sensei.\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Seriously, though, I really want to help you.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Im an only child, and I always wished I had a younger sistersomeone like you, Lain.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Youre my big sister then?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Id be happy if you saw it that way.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I will.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"Thank you. Anyway, lets end for today. Youre tired arent you?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Uh, uh. But Ill do what you asked me to do, Sensei.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"Great! Hee, hee. Actually, Im the one whos tired.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"Youre tired of me?\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"No, no!\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Dont think that.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"Ive had a lot of other work to do today, too.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"I like you and Ill miss you until we meet again, Lain.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"That makes me feel better.\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"Lets end now, then. Its getting late and we dont want your mother to worry.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"Goodbye, Toko Sensei.\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"Goodbye, Lain-chan.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"Just call me Lain. Its a bit weird for a big sister to call me Lain-chan.\", \"actor\": \"Lain\"}, {\"line\": 42, \"text\": \"Ok then, goodbye, Lain.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"Goodbye.\", \"actor\": \"Lain\"}]'),(37,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',37,69,'en','[{\"line\": 0, \"text\": \"Hello, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Hello, Lain.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Ok, from today were going to really tackle whats bothering you, Lain-chan.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Im sorry.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I mean Lain.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Were going to try to figure things out so you dont see nightmares anymore.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Uh, huh. I havent seen nightmares much lately, though. Sometimes I hear things, though.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Hear things? Like what?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I dont know.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"It sounds like someone talking.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I dont like hearing it, so I try my best to ignore it.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Hmm. Do you know whose voice you hear?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I dont know. It can be a mans voice or a womans voice.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Where do the voices seem to come from?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"From the sky.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"The sky? From what part of the sky?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"From the electric lines. Somehow\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"The electric lines Do you know whats in side of electric lines?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Wiring that carries electricity.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Right. They carry electricity that comes from power plants. Do feel like you sense electricity?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I don\'t really know. Sometimes it seems to come from the sky; not the electric lines.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Are you bothered by it right now?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"No. Im not scared now. But\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"When you told your mother, did she tell you not to tell anyone else about these things?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Uh huh.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"People who dont hear these things would think strange things about you if you told them, huh?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"You dont have to worry about telling me anything you want to.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"You dont have to worry.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Ok?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Im not going to think strange things about you.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Because Im sick?\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"No, Lain. Lets see. You know about kanashibari?\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"When a ghost comes out and you cant move your body?\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"Right. Have you ever had that happen to you?\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Many times.\", \"actor\": \"Lain\"}, {\"line\": 35, \"text\": \"It was frightening, wasnt it?\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"Uh, huh.\", \"actor\": \"Lain\"}, {\"line\": 37, \"text\": \"Do you know why kanashibari happens?\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"Why?\", \"actor\": \"Lain\"}, {\"line\": 39, \"text\": \"The human body is very mysterious.\", \"actor\": \"Toko\"}, {\"line\": 40, \"text\": \"When youre sleeping, there are times when your mind is awake, times when your body is awake.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"Do you understand?\", \"actor\": \"Toko\"}, {\"line\": 42, \"text\": \"Sort of\", \"actor\": \"Lain\"}, {\"line\": 43, \"text\": \"Well, thats good enough for now. What parts of you are awake right now?\", \"actor\": \"Toko\"}, {\"line\": 44, \"text\": \"My body and my mind.\", \"actor\": \"Lain\"}, {\"line\": 45, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 46, \"text\": \"Suppose I said something really scary right now and you feel scared enough to run out of this room.\", \"actor\": \"Toko\"}, {\"line\": 47, \"text\": \"What would you do?\", \"actor\": \"Toko\"}, {\"line\": 48, \"text\": \"I think I would run out of this room.\", \"actor\": \"Lain\"}, {\"line\": 49, \"text\": \"Ok.\", \"actor\": \"Toko\"}, {\"line\": 50, \"text\": \"Suppose that you feel so scared that you want to run out of this room, but your body is asleep and you cant move.\", \"actor\": \"Toko\"}, {\"line\": 51, \"text\": \"Thats what kanashibari is.\", \"actor\": \"Toko\"}, {\"line\": 52, \"text\": \"So if youre having a nightmare but your body is asleep and you cant move, you are having kanashibari.\", \"actor\": \"Toko\"}, {\"line\": 53, \"text\": \"You see?\", \"actor\": \"Toko\"}, {\"line\": 54, \"text\": \"If you know whats involved, its not so scary, is it?\", \"actor\": \"Toko\"}, {\"line\": 55, \"text\": \"No. It isnt.\", \"actor\": \"Lain\"}]'),(38,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',38,70,'en','[{\"line\": 0, \"text\": \"There are many people who have had experienced kanashibari, but we dont call them sick.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"But\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Hmm there might be only a few people who have experienced what you have.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Still, I dont think you should be called sick just because of your experiences.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"A lot of things can happen to a person while shes young and impressionable.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"But Im scared and I hate this.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"What are the scariest parts of your dreams?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I start to feel that I dont need myself. I feel that I am an unneeded thing.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"You dont need yourself?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I feel that I am being told to come back because Im unneeded.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Come back? Come back where?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"To myself.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Hmm. This is hard to understand. You want you to come back to yourself?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I dont know. But for a person like me to be me, and to touch body, feels like Im being torn\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"(interrupting) And its painful?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"It isnt painful, but its frightening.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"I see. Does this happen when youre asleep?\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Probably.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Probably? This happens sometimes when youre not dreaming?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I don\'t know.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Sometimes it happens when Im just sitting by myself, and sometimes when Im talking to my friends.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Tell me what happens when youre not alone. This happens when youre just having conversations?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Yes. While Im just talking, it suddenly\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"How do you notice it? Do your friends call out to you?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Suddenly\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Is there something that triggers it?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I was afraid, and I felt better after staying motionless for a while.\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Can you explain what it felt like, or what you saw? Could you draw a picture?\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"No, I cant.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"I see Oh!\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"We have to end.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Im sorry, Lain.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"I have to go out and take care of some things today.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"Im sorry to have to stop right in the middle of our appointment.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"But its good for now that you cant see these things.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"I think that what you experience probably has something to do with kanashibari.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"Ill do some research on it until next month.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"Thatll be my homework.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"Your homework will be keeping a diary.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"A diary?\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"I want you to write about the things you did during the day, and what you thought about.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"You dont have to show it to me.\", \"actor\": \"Toko\"}, {\"line\": 42, \"text\": \"Just write what you want.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"I used to keep a diary when I was your age.\", \"actor\": \"Toko\"}, {\"line\": 44, \"text\": \"But\", \"actor\": \"Lain\"}, {\"line\": 45, \"text\": \"You don\'t want to?\", \"actor\": \"Toko\"}, {\"line\": 46, \"text\": \"It can be really interesting to look back on the things you write in your diary.\", \"actor\": \"Toko\"}, {\"line\": 47, \"text\": \"We can be forgetful, so it can help you remember things, too.\", \"actor\": \"Toko\"}, {\"line\": 48, \"text\": \"That will cure me?\", \"actor\": \"Lain\"}, {\"line\": 49, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 50, \"text\": \"That probably doesnt have anything to do with it.\", \"actor\": \"Toko\"}, {\"line\": 51, \"text\": \"It cant hurt, though.\", \"actor\": \"Toko\"}, {\"line\": 52, \"text\": \"Ill explain more carefully next month.\", \"actor\": \"Toko\"}, {\"line\": 53, \"text\": \"I dont mind if you dont want to keep a diary.\", \"actor\": \"Toko\"}, {\"line\": 54, \"text\": \"I went and bought you a diary book, though.\", \"actor\": \"Toko\"}, {\"line\": 55, \"text\": \"See?\", \"actor\": \"Toko\"}, {\"line\": 56, \"text\": \"Its cute, isnt it?\", \"actor\": \"Toko\"}, {\"line\": 57, \"text\": \"I found it when I went shopping last week, and I thought it would be a nice present for you.\", \"actor\": \"Toko\"}, {\"line\": 58, \"text\": \"Thank you.\", \"actor\": \"Lain\"}, {\"line\": 59, \"text\": \"But dont force yourself.\", \"actor\": \"Toko\"}, {\"line\": 60, \"text\": \"You don\'t have to keep a diary just because I bought you one.\", \"actor\": \"Toko\"}, {\"line\": 61, \"text\": \"Feel free to write in the diary if you want to, though.\", \"actor\": \"Toko\"}, {\"line\": 62, \"text\": \"Well I guess well wrap it up, then.\", \"actor\": \"Toko\"}, {\"line\": 63, \"text\": \"Make sure youre not forgetting anything.\", \"actor\": \"Toko\"}, {\"line\": 64, \"text\": \"Ill walk out to the corner with you.\", \"actor\": \"Toko\"}]'),(39,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',39,71,'en','[{\"line\": 0, \"text\": \"How are you feeling, Lain?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Im doing ok. How about you, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Pretty much as always. I could use a little more time to relax a little, though.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Youre very busy?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yes Ha, ha. Now our roles are reversed. I feel like you are counseling me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Ha, ha\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Ha, ha. By the way, what did you decide to do about the diary?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I decided to keep a diary.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Wow, thats great! I didnt waste my money right before payday.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Ill pay you for the diary.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Im joking, Im joking.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"But I get paid well anyway.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"For the money I earn, the diary may have been a bit cheap.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Oh, no.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"This isnt good.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Im just yacking on about myself.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"But youre funny, Sensei. Youre a lot of fun.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Ha, ha.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Well, why dont we begin?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Lets pick up where we left off last time.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Have you learned the word ego at school?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Uh huh. I looked into it. Its about knowing that you are yourself?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Right.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"People around your age can get unsure about that.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"They ask themselves what kind of person am I? Boys sometimes think that they are heroes and that they will accomplish some superhuman feat someday.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Girls sometimes imagine that they don\'t belong with their own families.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"They think instead that they are members of a rich elite family, and that a prince on a white steed will come one day to take them away.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Kids your age sometimes believe these things for real.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I dont feel that way. I think that Im just me.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"But Its hard to tell what you are just by yourself, isnt it?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"When I was younger, I thought that there was a different me, and that somehow I wasn\'t the person I was at the time.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"A different me?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"For example, if there was something happening that I didnt like, I wondered why I had to be the one dealing with it.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"I thought there had to be some kind of mistake.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"I thought that someday I would return to the real me.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"I also thought things like, if there was a big earthquake, everyone would die, but I would be the only survivor.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"I thought that I was someone special; someone chosen. In another words, I believed in a me that could not be.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"Its funny, isnt it?\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"I dont believe that Im anyone chosen. Im ok with being ordinary.\", \"actor\": \"Lain\"}]'),(40,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',40,72,'en','[{\"line\": 0, \"text\": \"I see.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Thats probably because you are a strong person.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I think I was probably weak and that I was trying to run away because of it.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Do you think Im trying to run away from myself?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I dont think so. But you want to change yourself, dont you?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I just dont want to see any more bad dreams. Thats how I feel.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Could it be that you dont want to be the person who sees the bad dreams?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Maybe you feel that way somewhere deep inside yourself?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Could it be that you are subconsciously trying to construct a me that isnt me?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"What should I do?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Do you think you can accept your dreams?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Accept?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Right.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I mean facing the you that you dont want to meet or hear, and then trying to become friends with them.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I cant.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"I think that all you have to do is listen to the voices.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"See what they are saying and see what they want to do.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Face them until you understand them and expose them.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Im scared.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"But youve made it so far.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Youre still you.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Youve never had a ghost punch you or had a voice act violently with you, right?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Maybe these ghosts and voices are trying to be friends with you.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Ha, ha.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"If you dont see the ghosts again, great.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"But I if you do see them, I think that its important that you have a good attitude and know how to brace yourself.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"If the ghosts show up, just shake it off your shoulders and say, oh, here they are again.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Im leaving. Im going home.\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"Why? Did I say something I shouldnt have?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"I didnt lie to you. I didnt see any ghosts or phantoms. Youre a big jerk!\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"Lain!\", \"actor\": \"Toko\"}]'),(41,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',41,73,'en','[{\"line\": 0, \"text\": \"Im sorry maam.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I can handle it from here.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"(pause) Lain, Im sorry.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I thought that if I didnt come here personally, you would never see me again.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I came to apologize.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I think I was arrogant about my assumptions about you.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Still, I wanted you to understand and to be comfortable.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I just thought that if you could face your dreams and understand where they were coming from, that they would stop.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Im sorry, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Why?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I lied to you I didnt keep the diary.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Thats ok. I told you that it was all up to you.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"You were trying to be helpful to me.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Thank you. Im grateful that you feel that way. Can you forgive me, Lain?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Im so glad.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I was so sad when you walked away from our session.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I cried.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I felt so bad.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I though you didnt like me anymore.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Toko-san, dont cry.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Oh.. Its so silly of me to cry over this. Im sorry, Lain.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Dont cry.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"These are tears of joy, Lain. Im so glad.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Joy?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Yes. Tears of joy.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Im really happy, too. I feel so close to you.\", \"actor\": \"Lain\"}]'),(42,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',42,74,'en','[{\"line\": 0, \"text\": \"Lets skip counseling today and just relax instead.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"You came all the way out here.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Do you want something to drink?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I cried so much that Im thirsty.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Toko-san, Ill start keeping my diary. Honestly, this time.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Dont force yourself. Youre a kind girl.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Im not forcing myself.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"The diary is for me, after all.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"And the next time I hear the voices, Ill listen to see what they are saying.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Then, Ill tell you what they said, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Thank you, Lain.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Happy birthday, by the way, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Huh! My birthday?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I made this for you with my mom.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Really? Im so happy! Can I open it?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Sure.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Cookies! Wow. Theres some tea ready right now. Why dont we have the cookies with some tea?\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Is it good?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Mmm. It tastes like tea.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"You said that you like tea, so we made the cookies that way.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Thank you. But then we better have a different kind of tea.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"It wont go with your tea?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"No, not that it wouldnt go with it, but it would be too bad if would couldnt enjoy the tea and cookies as much as possible.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Should I have made cookies with a different flavor?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Oh, no, no.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Not at all!\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"But you took all the trouble to make such nice cookies, and I want to enjoy them as much as possible.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Thats all.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Really?\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"Really. Thanks so much, Lain.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Sensei, it hurts.\", \"actor\": \"Lain\"}]'),(43,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',43,75,'en','[{\"line\": 0, \"text\": \"Hello, Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I write in my diary everyday. Sometimes I dont have much to write, though.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Dont worry about it.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"If you force yourself youll just want to give up.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I dont know how many diaries Ive had where I missed entries.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Have you seen any of them since then?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Ive been ok.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Really? Thats good. Theres something I want to confirm with you today.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"What?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"You have really good eyesight, dont you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Really good?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Youve had eye exams at school, havent you? Whats your score?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"One-point-five.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Your eyesight is really even better than that, isnt it?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But I wanted to have the same score as all the other kids.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Well theres no point to having the examination then.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"At the last hospital where we used to meet, they examined your eyes, too.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But they did it differently than at your school.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"They had a machine that measures eyesight automatically.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"The machine only measures as far as three-point-zero, and your score was three-point-zero.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"There are some kids with extremely good eyesight.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"The doctor in charge of your eye-exam spoke with a cancer specialist, who said he saw no problems.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Oh.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Lain, what can you see outside the window?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Trees and a gateway to a shrine in the distance.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"And beyond that?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Buildings.\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"And beyond that?\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Mountains.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"And beyond that?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"I cant see anything further.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"Nothing at all?\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"A purplish color that looks like a curtain.\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"Thank you.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Im sorry to pepper you with all these questions.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"I think that you probably see the world in a different way than I do.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"Lain, dont look so glum.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"Its nothing to worry about.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"Its not such a bad thing Im sorry for saying that.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"I guess it is bad thing for you.\", \"actor\": \"Toko\"}, {\"line\": 40, \"text\": \"To see things so differently.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"I dont know.\", \"actor\": \"Lain\"}, {\"line\": 42, \"text\": \"But I think that Im probably different from everybody else.\", \"actor\": \"Lain\"}, {\"line\": 43, \"text\": \"I feel like Im the only person who can hear and see some things.\", \"actor\": \"Lain\"}, {\"line\": 44, \"text\": \"It makes me very uncomfortable.\", \"actor\": \"Lain\"}, {\"line\": 45, \"text\": \"I think that I will be disliked.\", \"actor\": \"Lain\"}, {\"line\": 46, \"text\": \"Is that the reason?\", \"actor\": \"Lain\"}, {\"line\": 47, \"text\": \"It might be. I think that youd have to be seen by a specialist.\", \"actor\": \"Toko\"}, {\"line\": 48, \"text\": \"No. I want to see you. I dont want to see any of the other doctors here. They all scare me.\", \"actor\": \"Lain\"}, {\"line\": 49, \"text\": \"Why dont we just keep this as a secret?\", \"actor\": \"Toko\"}, {\"line\": 50, \"text\": \"A secret?\", \"actor\": \"Lain\"}, {\"line\": 51, \"text\": \"Um hm. It will be our secret until you arent worried about it anymore.\", \"actor\": \"Toko\"}, {\"line\": 52, \"text\": \"Is that ok?\", \"actor\": \"Lain\"}, {\"line\": 53, \"text\": \"Im your big sister, remember? Im not going to take you anywhere that you will be scared.\", \"actor\": \"Toko\"}, {\"line\": 54, \"text\": \"Toko-san.\", \"actor\": \"Lain\"}]'),(44,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',44,76,'en','[{\"line\": 0, \"text\": \"Good morning, Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Good morning, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Have you seen anything since then?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"No, I havent. Honestly.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Have you heard anything?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Sometimes I hear something very confused.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Very confused? One person? More than one?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"More than one. Men and women.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Are they people you know?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Probably not.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"You said that you heard things from the sky. What do you think is in the sky?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Theres air.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Uh, huh. And aside from that?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Clouds. The sun. The stars.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Do you know about the transmission of radio wavelengths for TV and cell phones?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Yes. The information travels wirelessly?\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Right. The information flies through the sky.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Am I hearing that information?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Hmm. It might be that you imagine you are hearing that information.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Theres a sickness like that?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"There is.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Its called delusion.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Its a symptom of schizophrenia and neurosis.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Sometimes, if you are focusing much too much on something or are getting very stressed, it can bring out those kind of symptoms.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"But\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"But?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Usually, when people say they hear things, what they hear is more closely associated with themselves.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"For instance, they hear voices that bad-mouth them.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Delusion of persecution?\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"Right.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Youve been studying up on this stuff.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Ok. Ill fill you in a little more on this.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"The definition of delusion is taking a mistaken idea and believing in your own mind that it is correct.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"But for you, Lain, you say that you might have felt or sensed something.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"A person who is really delusional tends to have conviction that his delusions are real.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"But you dont have that.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"You have thoughts, considerations.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"Considerations?\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"You can forget that word if youd like.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"As I mentioned before, a delusion is generally something intertwined with ones self.\", \"actor\": \"Toko\"}, {\"line\": 40, \"text\": \"There really arent any delusions that have nothing to do with someone personally.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"Im not delusional?\", \"actor\": \"Lain\"}, {\"line\": 42, \"text\": \"Fundamentally, no.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"Still, there are all sorts of delusions, and if you try to pin one of them onto someone, you may start pinning other ones on that person, too.\", \"actor\": \"Toko\"}]'),(45,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',45,77,'en','[{\"line\": 0, \"text\": \"What could happen?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"There is a delusion some people have that they are being influenced by something.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"The delusion that the radio or TV or cell phones are trying to influence them somehow.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"The delusion that TV and cell phones have the power to touch them physically.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"You said that you thought you were being touched by something, didnt you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Uh, huh.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"It might not even be a delusion.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"There is an actual medical condition that makes people very sensitive physically.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"But you said that you dont feel like you are yourself, didnt you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"That makes me think that you have the delusion that nothing exists.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"That you think you arent real and that existence isnt real.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"This can come as the result of poor self-esteem.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Poor self-esteem?\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Its the feeling that one is worthless.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Its similar to the delusion of persecution we were talking about.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Im really sick.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"There are other kinds of delusions.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I dont want to listen anymore.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Some people have the delusion that they have a special, unique sickness.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Its the sickness of thinking they have a sickness, when they really arent sick.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"I think that what you have, Lain, is mostly to do with this kind of delusion.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"The sickness of thinking that I am sick when Im not sick?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"I think that you have poor self-esteem, and that a small insecurity manifests itself in nightmares, and makes you think that you are sick.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Can I be cured?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Sure!\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"After all, youre not sick in the first place.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Im sorry to have alarmed you.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Still, if you dont understand psychiatry well, you can use it to make all sorts of misguided conclusions.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"You can diagnose yourself as being sick.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Most people go to the doctor when they or someone they know cant carry on their day-to-day activities.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"They come in to see a doctor and they are diagnosed or treated, and they are diagnosed sometimes as sick.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"So for someone like you, who can continue her own day-to-day activities without a problem, we cant really say that you are sick unless somethings bad enough that you to need hospitalization.\", \"actor\": \"Toko\"}]'),(46,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',46,78,'en','[{\"line\": 0, \"text\": \"Am I sick because I came to the hospital?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Well, you can carry on with your life, but youre troubled, arent you Lain?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Its the job of a counselor to help with that.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Are you a counselor, Toko-san? You said before that youre a doctor.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im a counselor and a doctor.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"How are the two different?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Well, both jobs have to do with helping people with psychological problems.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Theyre similar in the sense that they have the same goals.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"But the difference is that as a counselor I care for patients differently than I do as a doctor, and that each job has different qualifications.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Each job has its strengths and weaknesses, and I think its best when I can make their strengths compliment each other.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Thats why Im a doctor and a counselor.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Are you a doctor or a counselor when you see me?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Im a counselor. I think its better for me to be a counselor when I see you.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Im not mentally ill? You said that the things I see are hallucinations.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Its true that hallucinations and delusions are symptoms of mental illness, and this is just my hunch, but you dont seem to be mentally ill to me.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Do I pay too much attention to things?\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Yes, I think thats probably right.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I think your tendency to observe things and listen to thing very carefully might be stressing you out.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"What can I do about it, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Well even if I told you not to mind things too much, youre going to mind things.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I don\'t mind anymore. Im getting used to this. Still, I can\'t get used to that.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"That?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Im not the me that I am right now.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"What kind of Lain are you?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Im me. That person is, too.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Why do you feel that way?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I dont know. Have you ever thought that you arent you, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Looking back on myself in the past, Ive wondered what I was doing sometimes.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"But Ive never thought at the time that I wasnt me.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Do you feel that there are two of you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"No. Im me and Im not me.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"Thats mysterious.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"But if you feel that way, that must be true for you.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"You dont have to feel that youre mistaken.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"But it isnt usual.\", \"actor\": \"Lain\"}]'),(47,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',47,79,'en','[{\"line\": 0, \"text\": \"Youre really yearning all the time, arent you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Probably\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im envious.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Human perception is a wild thing.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"If you start feeling something, you start believing it.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I don\'t know how to explain to anyone that I am really me.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"But being able to question that sense of self is a special talent.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I dont want a talent like that.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Yeah.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Im sure you feel really strongly that way right now.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"But I feel that it can be a good thing for you too.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I don\'t understand it well right now, but I think that if you can include and accept that as part of yourself, it would help you in the long run.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I wont change?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I dont know.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"You might change naturally and stop having nightmares.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"If that happens, youd be like most other kids.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"But I wont know if Ive gotten better. I might have nightmares again.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Yeah. But things will change from now on.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"What do you mean?\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"I mean that youre not alone. Im here and you can talk to me about things.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Thats true, Toko-san.\", \"actor\": \"Lain\"}]'),(48,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',48,80,'en','[{\"line\": 0, \"text\": \"Good morning, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Good morning. How are you? Have you been feeling healthy?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Uh huh. I thought about the things you talked about last month and Ive somehow been feeling better.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Oh, thats great.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Actually, though, its against the rules to talk about the things we talked about last month.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"In some people it can trigger something irreversible.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Irreversible?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Their sickness could worsen, and sometimes, patients can hurt themselves physically.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Suicide?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Well, that would be the worst-case scenario.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I dont want to die.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Of course not!\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Far from it.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But when people become troubled emotionally, they start to see things and act differently than you or I would.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"See things differently?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"They can lose their fear of dying or hurting themselves.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"In fact they start to feel that dying or getting hurt are very good things.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"You cant understand that, can you?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I am afraid of those things.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Sure! Me too. Were the same way. Thats normal.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Ok.\", \"actor\": \"Lain\"}]'),(49,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',49,81,'en','[{\"line\": 0, \"text\": \"Have you seen or heard anything since then?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"No.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"See!\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"It was a state of mind, Lain.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I think that you probably wont see them again, but if you do, dont get discouraged.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Youll get over it gradually.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I wont get better right away?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Hmm. I dont know. How old were you when you started having problems with them?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I was eleven.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"So its been over a year now. During that time, how often did it happen?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I dont remember once.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Sometimes, even if there doesn\'t seem to be an explanation for it immediately, these things may go away.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"But if you decide that theyve gone away completely and become complacent, theres a chance that they may come back.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I cant say for certain that that will happen with you, but even if you dont see them for a while, Id like you to continue what youve been doing up till now.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Do you want to stop coming here, Lain?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"No. I like seeing you.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Thank you.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I guess that theres no problem, then.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Youre doing well, but youll promise me that you will keep coming here?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Yes. In exchange, I want you to teach me some things.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Why?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"For homework?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Sure.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"You wont believe it, but I was pretty sharp in junior high school.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Ha, ha.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Learning to read and write was tough, though.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I dont mean that. I want to know more about your work. Is that ok?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I don\'t think its a very good idea, but if youre really curious, ok. Learning about things that make you curious is a great way to study.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"But you have to make time for your schoolwork, too.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Ill promise to teach you about my work if you promise to do your schoolwork.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Can you promise me that?\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"Yes, I promise.\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"And if you break your promise\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Ill whack you with a blowfish!\", \"actor\": \"Lain and Toko together\"}]'),(50,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',50,82,'en','[{\"line\": 0, \"text\": \"You are a counselor and a psychiatrist, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"And a psychiatrist is someone who heals mental illnesses?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Uh huh. Put simply, Im a doctor of the mind. As I mentioned before, mental illness is very complex.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"What is mental illness?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Basically, a persons mind becomes disturbed for some reason, and the person is unable to continue functioning.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"What sort of reason, for instance?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"There can be many reasons.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Problems with a living environment or work environment can be reasons.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Sudden, psychological shock can also be a reason.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"The sudden death of a well-loved pet can trigger mental illness.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Sometimes people become mentally ill because of something that happens to them physically.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"A car accident or some other kind of physical trauma can be a cause.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Also, people who drink too much can damage the function of their brains.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Alcoholism?\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Exactly.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Youve been studying!\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"If you drink too much the alcohol content in your blood will rise.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Youve seen drunk people wobble around without balance, havent you?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Theyre in an alcoholic stupor.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"But normal people arent that way, are they?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"No, they can be.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"It takes about 24 hours for alcohol to leave the human body.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"If you drink a lot everyday for a year or soif you stay drunk long enoughyoull your perception will start to change.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Your brain will start to deteriorate.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Thats scary.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"It is scary. I still have some sake now and then, though.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Isnt that dangerous?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Ha, ha.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Oh, a little bit.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"And smoking is bad too.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"But having a drink with friends every now and then can help relieve stress.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Relieving stress is very important for the bodys health.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"I dont understand what stress is.\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"Remember when you didnt like coming here at first?\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 35, \"text\": \"Stress is what we call the cause of that discomfort.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"Youre glad that the discomfort is gone, arent you?\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"Yes. What other kinds of mental illness are there?\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"Could you excuse me? Ive been talking so much and Im a little thirsty.\", \"actor\": \"Toko\"}]'),(51,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',51,83,'en','[{\"line\": 0, \"text\": \"I wanted to ask last time, but why do have that kind of hairstyle?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Its weird?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"No, its great! You really look good that way. Is it a popular look at your school?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"No. Its just my own style.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"You mentioned that you want to be like other kids. Why do you have a unique look?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"It comes from the right, and probably, thats why it goes out of the left. Thats why...\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"It works a little bit like a magic charm, then. I used have a magic charm, too.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"You did, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Sure.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"It didnt have to do with hairstyle, but I used to have a lucky rabbits foot.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I used to believe that it would keep me safe and happy, and I tied it to my backpack.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Looking back, it seems so ghastly.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"To tie a little bunnys foot to my backpack.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But then again, believing in the rabbits foot really made me feel safer.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"A magic charm?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Yeah.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Your hair looks really good, though, Lain.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"If you had a hairstyle like everyone elses, it wouldnt be like you.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I think you look great the way you are.\", \"actor\": \"Toko\"}]'),(52,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',52,84,'en','[{\"line\": 0, \"text\": \"You know about neurosis? Wow, Lain. Youre going to have to start counseling me.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I know the word, not its meaning.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"What kind of image?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"The mind changes and becomes distorted?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yes. Youve got the general idea.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Thats awful. You wont teach me anymore?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"No.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Im really telling you that you got the general idea.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Neurosis is a state in which there is nothing wrong with a person physically, yet the person has irrational fears and perceives threats.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"This places a lot of stress on the person and makes her feel sick.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Its only inside of their heads?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"We used to think so.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"But after a lot of research, we think that an imbalance of the autonomic nerves in the brain is related to it.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"What I mean is, something is wrong in the body, too.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"We used to categorize physical symptoms as psychosomatic.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"But weve been finding more and more that conditions we thought were only of the mind involve the body itself.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Psychosomatic?\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Oh, Lain, if ask so many questions, your head is going to explode!\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Youve heard about people who lose a lot of weight because of stress, or go bald?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Thats psychosomatic.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Lain, you dont, by any chance\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"No, I dont have that kind of problem.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Oh, Im relieved. That was hasty of me!\", \"actor\": \"Toko\"}]'),(53,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',53,85,'en','[{\"line\": 0, \"text\": \"Who decides the names of diseases?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Various doctors.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"But we have general criterions.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"We have the ICD and the DSM, which are international standards for the definition of mental diseases.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"We know that if a person has a certain set of symptoms, we can consult these standards and find the exact name of the disease the person is suffering from.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"As you know, the mind can be a difficult thing to understand.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"For that reason, psychiatrists have publications, and we communicate all the time, sharing our data and our understanding of mental illness.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"We accumulate data and categorize it so that we can diagnose patients as correctly as possible.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Theres mental illness you don\'t understand? Not even you?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"To tell you the truth, we dont understand much of this at all.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"If there has been a successful treatment method for a patient with a certain disease in the past, we can apply that treatment method toward a patient suffering from the same disease in the present.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Much of this has to do with experience.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"You do this even if you dont know if a person can be cured?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Right.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"You cant tell if a person will be cured.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"We can only try our best to heal and help with our experience from the past.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Thats usually the case.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But from the point of view of the patient, knowing if one will become better is very important.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"In fact, the understanding of the mechanisms behind getting better is an area of study of its own.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I see. It would be so much better if we could understand everything.\", \"actor\": \"Lain\"}]'),(54,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',54,86,'en','[{\"line\": 0, \"text\": \"Do you have many memories from your childhood?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Memories? Do you mean records?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Well, memories and records arent exactly the same.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Don\'t think about it too much.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"If youve traveled somewhere during summer vacation\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I havent traveled anywhere during summer vacation.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Im sorry.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"In my youth, both of my parents worked, so I didnt get to travel very much.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I didnt like it when I went back to school after vacation and other kids would ask me where I went or what I did during the summer.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Whats the difference between a memory and a record?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"From a medical standpoint, a record is something documented that lists exactly what happened, when and where.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"A memory is more subjective.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Its what you remember personally.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Its something in your mind that you can access from time to time.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But Lain, doesnt all this stuff bore you?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"No. How does a memory enter the brain?\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"How? Thats a pretty complicated subject. Are you sure you want to get into it?\", \"actor\": \"Toko\"}]'),(55,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',55,87,'en','[{\"line\": 0, \"text\": \"There are different kinds of memories.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"First of all, theres ones memory of oneself.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"You know what self- understanding is?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"You know that you are a girl named Lain with a certain face and height and weight?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Thats all recorded at a molecular level.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Aside from the brain, there are places throughout the body that carry residues of things that have happened.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You know that if you get cut, you bleed, but if you wait a while, it gets scab and heals?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"That has to do with information stored in cells going to work.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Take a look at athletes and people who have gone through intense physical training.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"They seem to do things effortlessly.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Thats because their bodies have their own recollection or memory.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"If you generate a recording, you have a memory?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"You might be able to say that sometimes, but not always.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Humans are haphazard and unpredictable, arent they?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I dont remember everything that happened to me as a kid precisely.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I have happy memories of times I spent with my family, but my family might not have happy memories of those times at all.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I may have had misperceptions in the moment, or I may have remembered things incorrectly and by mistake.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But we all walk around with memories, thinking that what we remember is what actually happened.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Suppose you meet an old friend that you havent seen in a long time.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Dont you remember the things you used to do with that person right away?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Your memory has a way of linking things together.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"If you cant link things together, youre sick?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Well, there is something called memory impediment.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"People who have this cant remember things that happened only moments ago.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"They have no idea of how things in the past took place.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"They dont know what happened and in what order.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Since they dont want anyone to know that they dont remember things, they often make up stories about the past to fool people.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Humans are complex.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"We do all sorts of things.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Youve seen senile old people on TV shows, havent you?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"As humans, there are some things we cant do anything about.\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Things we cant do anything about?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"Im not saying that youre sick, Lain.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"There are things we cant do anything about, arent there?\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"We cant flap our arms and fly, can we?\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"There are memories in the body and the brain?\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"There are still many things we dont understand.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"The human brain is made up of many different things.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"When something is recorded there, there is a change that takes place that becomes the foundation for memories.\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"I dont understand.\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"Yeah.\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"When you go to high school and college, you can study up on all this stuff.\", \"actor\": \"Toko\"}, {\"line\": 42, \"text\": \"But its good that you have a healthy curiosity.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"I dont think youre sick, Lain.\", \"actor\": \"Toko\"}, {\"line\": 44, \"text\": \"I feel that my memories arent my memories.\", \"actor\": \"Lain\"}, {\"line\": 45, \"text\": \"Sure! I feel that way sometimes, too.\", \"actor\": \"Toko\"}, {\"line\": 46, \"text\": \"Even when its me. But I dont remember anything else.\", \"actor\": \"Lain\"}, {\"line\": 47, \"text\": \"Youve just forgotten something, thats all.\", \"actor\": \"Toko\"}, {\"line\": 48, \"text\": \"Didnt I say that human beings are haphazard and unpredictable?\", \"actor\": \"Toko\"}, {\"line\": 49, \"text\": \"We remember things the way we want to remember them and often forget what we want to.\", \"actor\": \"Toko\"}, {\"line\": 50, \"text\": \"Thats being human.\", \"actor\": \"Toko\"}, {\"line\": 51, \"text\": \"Thats normal.\", \"actor\": \"Toko\"}, {\"line\": 52, \"text\": \"Normal?\", \"actor\": \"Lain\"}, {\"line\": 53, \"text\": \"Yes. You dwell on things too much, Lain. All those things are normal.\", \"actor\": \"Toko\"}]'),(56,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',56,88,'en','[{\"line\": 0, \"text\": \"I wondered if your name has anything to do with R. D. Laing. Oh, it couldnt, could it?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"R.D. Laing?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Its a persons name.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"An English psychiatrist.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"He was an unusual person.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"The titles of some of his books are very interesting.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"One of them is: I Love, Love, Really Love.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I Love, Love, Really Love?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I read it when I was in college and I dont remember much of it, but its about two loversa man and a womanand the woman asks the man if he loves her.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"And?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Well of course, the man replies that he loves her.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"But the woman isn\'t satisfied, and asks more and more questions.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I dont understand.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"OK. Lets see. Do you like me, Lain?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Yes, I do.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"More than your dad? More than your mom?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"That makes me happy. Do you mean it?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Yes I do.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Are you happy when youre here?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I dont really understand, but I probably am.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Do you like talking to me?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Yes.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"What do you like about me?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Youre kind and you listen to me.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Which do you like more? That Im kind, or that I listen?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"Which?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Yes, which?\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"I like you because youre kind.\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"How am I kind to you?\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"You dont get angry and youre polite.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"But there are lots of people besides me who dont get angry at you.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"There are lots of people who are polite to you.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"Arent there?\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"Do you really like me?\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"I dont really know.\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"Do I have to think about all this stuff?\", \"actor\": \"Lain\"}, {\"line\": 37, \"text\": \"Lets stop this.\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"I feel like I might start to not like you.\", \"actor\": \"Lain\"}]'),(57,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',57,89,'en','[{\"line\": 0, \"text\": \"Im sorry. I just was trying to explain this issue to you.\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"I like you too Lain, though Im not sure why.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"I suppose I just enjoy talking to you. Thats it.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Its probably just because I like my job!\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"I just thought it had to do with you, but I guess not.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"It is enough for me to be able to understand my feelings.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"I can never read your mind, so I can never know that you like me as much as I like you.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Sounds lonely\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Im sorry, but you know, this is one of the ways for me to know that.\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"Im so happy with you saying that you like me.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"Are you happy with me saying I like you?\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"Yes, indeed.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Yes, that is enough. Lain, I like you.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"But still, as you can never comprehend all of me, it is impossible for me to know everything about you.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"If we could know everything about each other, I could be you and vice versa.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"I mean you do not have to be Lain unless we are different people.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"I really like Lain even though Lain is hard to understand sometimes.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"You like the mysterious Lain?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Well, I like Lain who I dont know very well. Of course, that is not all I like.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"I want to know everything about you!!\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"So do I! But its impossible.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"Impossible?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Yes. That is because even I dont know who I am!\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"Well, I dont know how to explain this to you. Lain is Lain and I am me.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"Actually it might be still impossible even if we became the same person.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"The same person?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Yes, Lain and I are exactly the same!\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"We see the same things, have the same ideas and take same actions.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"Everything.\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"Even our looks are the same!\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"In this situation, there is only an identity that you KEEP, isn\'t there?\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"Yet even if such an identification is gone.\", \"actor\": \"Touko\"}, {\"line\": 32, \"text\": \"If you thought Here is Lain, then I cannot exist there.\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"The same thing can happen to you!\", \"actor\": \"Touko\"}, {\"line\": 34, \"text\": \"That is why its impossible to understand each other at the same time.\", \"actor\": \"Touko\"}, {\"line\": 35, \"text\": \"But what if you would answer every question I ask.\", \"actor\": \"Lain\"}, {\"line\": 36, \"text\": \"Still, it would be impossible?\", \"actor\": \"Lain\"}, {\"line\": 37, \"text\": \"Right, but they are not all possible questions, are they?\", \"actor\": \"Touko\"}, {\"line\": 38, \"text\": \"I need time to answer those questions and those answers could be changed eventually, such as I used to think one thing, but now I have changed my mind  something like that.\", \"actor\": \"Touko\"}, {\"line\": 39, \"text\": \"It\'s impossible after all, isnt it?\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"Yes, I wish I could go inside your mind and answer everything you want to know\", \"actor\": \"Touko\"}]'),(58,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',58,90,'en','[{\"line\": 0, \"text\": \"Are you happy at your school?\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Yes, I am!\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"What makes you so happy!\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"You know, playing and talking with my best friend.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Thats good! It is a good thing to like school, isnt it?\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"Were you happy?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Not really, because I didnt have many friends there.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"You feel sad without friends at school.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Yeah Im sorry\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"About what?\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"That story reminds you of a bad memory.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Well, it certainly wasnt good memory, but thats OK.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"I actually feel sorry for my past, but my current job makes me think that was a good experience for me.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"Good experience?\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Yes.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"For example, such an experience is sometimes so useful to analyze the psychology of those who doesnt want to go to school.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"It sounds difficult.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Im sorry, Im talking about myself now! Whenever I talk about my job, I tend to get lost in it.\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"I guess I would make a better patient.\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Ok, lets change the subject. Well, what is your best friend like?\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"She is really cheerful and nice to me. Im really happy around her.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"What\'s her name?\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"Misato.\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"OK Misato. What do you usually do together?\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"We read books together at her place and I listen to her playing violin sometimes.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Wow, she can play the violin.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"Awesome!\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"She must be a good friend.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"Why dont you come over with her next time?\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"But she is so shy.\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"So shy?\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"Maybe Ill ask her anyway. Im guessing she wouldnt want to.\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"Well, I wish she could.\", \"actor\": \"Touko\"}]'),(59,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',59,91,'en','[{\"line\": 0, \"text\": \"Touko-san, deja vu isnt an illness, is it?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"No.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"You may also feel its the first time youve seen a scene which youve seen before.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Both situations have nothing to do with a disease.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Even normal people sometimes have an experience of deja vu.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"What a relief! I feel better now.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Do you feel anything else when you experience deja vu?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Time is strange for some reason. I cant figure out if time is fast or slow.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Do you feel its real or kind of ordinary?\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"Well, it could be or could not be I dont know maybe not\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Is it nebular? Or you just cant comprehend\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"I just feel something is different.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Even though I recognize that I am playing in my house, I feel like it isnt my house.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"And even though I dont know where it is, when I play at a park that Ive never seen, for me it seems like the place I usually use.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Strange Do you feel comfortable or uneasy when you are there?\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"I dont know, but Lain was laughing when it happened.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Not yourself but Lain?\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"I cant explain very well but it was Lain\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Where were you at that time?\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"Lets see I dont know, because I was just watching her\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"I mean, where from did you observe her?\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"Like, were you standing on the ground, or you could you see her from the sky?\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"Well I dont know It was like I saw myself on the TV I guess\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Lain, are you all right? You dont have to bring it back by force. Im sorry.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"I feel compelled to ask questions. OK, thats all for today!\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"How do you express it in technical jargon?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"No, lets stop for now.\", \"actor\": \"Touko\"}, {\"line\": 27, \"text\": \"I really want to know about myself!\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"OK, so this is the last lesson.\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"In technical jargon, we call it alienation of perception. It means that you experience something as if somebody else experienced it.\", \"actor\": \"Touko\"}, {\"line\": 30, \"text\": \"Its an illness, isnt it?\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"There is one called depersonalization which is that you cannot feel yourself, but you couldnt have it.\", \"actor\": \"Touko\"}, {\"line\": 32, \"text\": \"So stop worrying about it.\", \"actor\": \"Touko\"}, {\"line\": 33, \"text\": \"You cant prove that I dont.\", \"actor\": \"Lain\"}, {\"line\": 34, \"text\": \"Yes, but it doesnt worry you. So I can say that youre not sick.\", \"actor\": \"Touko\"}, {\"line\": 35, \"text\": \"Well, I shouldnt have said that; it just makes you worried.\", \"actor\": \"Touko\"}, {\"line\": 36, \"text\": \"I guess I made a mistake\", \"actor\": \"Touko\"}, {\"line\": 37, \"text\": \"No, you didnt.\", \"actor\": \"Lain\"}, {\"line\": 38, \"text\": \"You didnt make any mistakes because I insisted that you should answer my question.\", \"actor\": \"Lain\"}, {\"line\": 39, \"text\": \"Still, I want to study a lot in order to be like you, Touko-san.\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"Lain\", \"actor\": \"Touko\"}]'),(60,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',60,92,'en','[{\"line\": 0, \"text\": \"The ego is your sense of who you are, isnt it?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Yes, but more technically phrased, the ego is a part of the mind you use to experience as yourself and to recognize yourself.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Do you get it?\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Not at all.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Ok, so you have an idea that you are Lain.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"In such the structural element, the EGO is right there.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"Linguistically, myself describes all of you, right?\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Ego is, however, one of the parts of your mind you use to recognize that all of experiences I have had were mine.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Saying its just to realize that you are yourself is somewhat better to understand?\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"I can get the last part, but it is still beyond my understanding.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I like your honesty.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"So the girl, Lain, has various emotions, such as kindness, loneliness and things like that.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"Now the EGO is what is left when you dont feel any emotions.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"I guess I understand a little\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"For example, the Lain who woke up this morning and what Lain is now here are the same, right?\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"Right.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"What Lain will be tomorrow should be the same as those.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"I guess so.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"I mean if your ego works properly, you can recognize that you exist continuously and confirm that that existence is myself. If your ego doesnt work properly\", \"actor\": \"Touko\"}, {\"line\": 19, \"text\": \"If it doesnt?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"We call such a situation identity disorder in the medical field.\", \"actor\": \"Touko\"}, {\"line\": 21, \"text\": \"On the other hand, there is also an isolation disorder which means you think that you are not yourself.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"He, he, they sound like bad diseases, dont they?\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"Yet anyone your age usually doesnt complete his or her ego until later.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"You just can gradually recognize yourself with age.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"So is it wrong to think that I understand myself at my age?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"It just happens to be that such things happen all the time.\", \"actor\": \"Touko\"}]'),(61,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',61,93,'en','[{\"line\": 0, \"text\": \"If I became sick, what would you do for it?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"If you were sick?\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"I mean if\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Well, since you are still young, I wouldnt use any medical treatment for such minor symptoms the way one uses medicine to cure disease.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Why wouldnt you use them?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Lain, you already have the power to recover your health from disease.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"If you depend on using those medicines, however, your body becomes weaker.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Usually children shouldnt take medicine too much anyway.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"So what are you going to do?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Lets see I will use psychotherapy to help you, like talking with you as we do now.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"Just talking?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Hmm, its difficult to explain it. For example, we have a program to recover people from alcoholism.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"In this program, they discuss and present what make them drink so much and listen to each others stories.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"We call it group therapy. I dont like it though.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"Why you dont like it?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"I think people who regain their health from those group therapies would make the same mistakes later.\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"They think that they can recover themselves from alcoholism simply by group conversation, but when they are alone, they will start drinking again.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"Ive seen this happen a lot.\", \"actor\": \"Touko\"}, {\"line\": 18, \"text\": \"So what would you do?\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"I try to talk one-on-one as I do with you, Lain.\", \"actor\": \"Touko\"}, {\"line\": 20, \"text\": \"And?\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"And then, I suggest those patients to think about themselves.\", \"actor\": \"Touko\"}, {\"line\": 22, \"text\": \"I have nothing particular to support them but I try to analyze what they think on neutral ground.\", \"actor\": \"Touko\"}, {\"line\": 23, \"text\": \"This helps them to recognize what is wrong with themselves, and then they try to recover their own health.\", \"actor\": \"Touko\"}, {\"line\": 24, \"text\": \"We call it non-supportive psychotherapy.\", \"actor\": \"Touko\"}, {\"line\": 25, \"text\": \"If they start recognizing there is a problem, it means they are already fine.\", \"actor\": \"Touko\"}, {\"line\": 26, \"text\": \"Is that enough to recover them from diseases? Sound simple!\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Right, it sounds so simple.\", \"actor\": \"Touko\"}, {\"line\": 28, \"text\": \"In fact, however, those things are more complicated and difficult to do than you think.\", \"actor\": \"Touko\"}, {\"line\": 29, \"text\": \"Difficult?\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"(yawn) Im sorry. I need more sleep.\", \"actor\": \"Touko\"}, {\"line\": 31, \"text\": \"You must be tired. Should you take a break?\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"(yawn)\", \"actor\": \"Touko\"}]'),(62,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',62,94,'en','[{\"line\": 0, \"text\": \"When you get sick in the head, your head is really sick?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"The brain isnt the only part of the body involved in mental illness, but it is very important.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"We know that many types of disease occur because of trauma to the brain.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"When the brain is injured, something is broken?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"The human brain is very complex, and there are many things we dont understand about it.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"We know that some parts of the brain seem to have a certain function.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"We know a little about where and when things take place in the brain.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Memories are stored in the hippocampus.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"There is the frontal lobe and the occipital lobe, and there are many other parts of the brain that have different functions.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Many chemical processes take place in the brain.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"When they do, a lot of information is exchanged.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Thats how the brain gets sick?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Sometimes the brain does get sick that way.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"If there is an abnormal change in the brains chemical process, or if the parts of the brain responsible for processing information are physically damaged and not working properly, there could be disease.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"We call this kind of disease organic psychosis.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Is this hard to understand?\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I understand. People have their brainwaves tested and have CAT scans to check for it.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Are they teaching you that at school already? Did you teach yourself?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I checked it out on the Web. I had a test done before, and I was curious.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Lain, if youre doing all this research, you dont have to see me.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"No, its not like that. I learn a lot from you. It isnt like the things I learn from books.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Im sorry, Lain. Im not feeling very well.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Is there brain research at this research center?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"What kind of research?\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Im not a specialist and I dont work in that area, so I cant say anything specifically.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"But its on the Web. All sorts of research takes place.\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"Lain, can I ask you a favor? Im not feeling very well. Can we end for today?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Im sorry, Toko-san.\", \"actor\": \"Lain\"}]'),(63,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',63,95,'en','[{\"line\": 0, \"text\": \"What kind of research do you do?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Research? I never mentioned that\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"But this is a research center, isnt it?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Im still an apprentice. I havent done any of my own research.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Youre an apprentice after being here for 3 years?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"How did you know\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You told me yourself.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I did?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Im sorry.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Somehow, I havent been feeling very good lately.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Could you ask me fewer questions today?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Either that, or can we just not talk quite so much?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Youre tired, Sensei. Maybe you should sleep a little.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Tired? I guess I look that way. Youre right. I must look that way.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I feel bad for you Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"You cant do your own research because youre busy with miscellaneous tasks.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Im not doing any miscellaneous tasks!\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Im sorry.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Im sorry for raising my voice like that.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I was so shocked.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Did something happen? Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"No, nothing.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Youre busy with miscellaneous tasks and cant do your own research. Does that bother you?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"How do you know about ?\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"I found the homepage for this research center.\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"I saw the list of researchers and published dissertations, but I couldn\'t find you.\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Thats right. I havent published any dissertations since I came here. But I mean to very soon.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Youre OK now. You have time for your own research now?\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"What are you saying? What do you mean, Lain?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Im a research subject, arent I?\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"Thats why Im not useful to you.\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"We can end now if youd like.\", \"actor\": \"Lain\"}, {\"line\": 32, \"text\": \"I cant see them anymore.\", \"actor\": \"Lain\"}, {\"line\": 33, \"text\": \"Im wasting your time.\", \"actor\": \"Lain\"}, {\"line\": 34, \"text\": \"Lain.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"Youre like a sister to me.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"Im the one who said that we could just talk.\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"I dont think of you as a nuisance.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"And\", \"actor\": \"Toko\"}, {\"line\": 39, \"text\": \"Theres nothing for you to worry about, Toko-san. It\'s about suicide.\", \"actor\": \"Lain\"}, {\"line\": 40, \"text\": \"How do you know about that?\", \"actor\": \"Toko\"}, {\"line\": 41, \"text\": \"I read it in the paper. It was on the bulletin board here, too.\", \"actor\": \"Lain\"}, {\"line\": 42, \"text\": \"I see. Youre right.\", \"actor\": \"Toko\"}, {\"line\": 43, \"text\": \"What kind of research do you do?\", \"actor\": \"Lain\"}, {\"line\": 44, \"text\": \"As I said before, Im trying to develop a new treatment system using my background as a counselor and psychiatrist.\", \"actor\": \"Toko\"}, {\"line\": 45, \"text\": \"But it looks like Im failing as a counselor and a psychiatrist right now.\", \"actor\": \"Toko\"}, {\"line\": 46, \"text\": \"Im sorry.\", \"actor\": \"Toko\"}, {\"line\": 47, \"text\": \"Why?\", \"actor\": \"Lain\"}, {\"line\": 48, \"text\": \"I think Im becoming mentally unstable, now. Just a few moments ago, IIm sorry.\", \"actor\": \"Toko\"}, {\"line\": 49, \"text\": \"Toko-san are you ok?\", \"actor\": \"Lain\"}]'),(64,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',64,96,'en','[{\"line\": 0, \"text\": \"Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Good morning.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Im sorry.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I stayed up all night preparing my paper.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Everything is scattered everywhere.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Could you give me a second?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Maybe I should go home?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Its all right. Dont worry. Oh, shoot, Im out of tea.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I can go buy you some.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Thanks.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"But please dont.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I appreciate your feelings.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Have a seat, Lain.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I thought that maybe I ought to take a break, too.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Are you very busy?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"A little.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"After that thing happened to Professor Takashima.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Now I have to do a lot of his work, too.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Ive had a lot of new clients, to boot.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Can you do your research?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Lain, dont mind that stuff too much.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Itll be tough for a while, but I have things to do.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"For now, Im just happy just concentrating on my work.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"You arent sad?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Youll understand one day Lain. Its a wonderful thing to have work thats fulfilling.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"I dont think I would understand. Instead of something like that\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Instead of... what do you mean? Did you do something fun lately?\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Yes. I think everything is going to work out well.\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"What will work out well?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"Things about me.\", \"actor\": \"Lain\"}]'),(65,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',65,97,'en','[{\"line\": 0, \"text\": \"Lain, Id like to confirm something with you.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"What were the names of your friends that you talked about?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Kyoko-chan.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I see. And you have another friend?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Misato-chan?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Right. Misato-chan.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Why do you ask?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Never mind.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I was just gathering information for a report, and part of it asks about friends you may have.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I was double-checking it because I was a bit unsure of myself.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Oh. Misato-chan. Is she going to be in your charts, too?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"No, just her name. Nothing else. Does she live in your neighborhood?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Yes. But I cant walk there.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"You have to go by car?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Yes. When I go to Misato-chans home, I always have my mom or dad drive me there.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"What does your dad do?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"He goes to work. Hes not around home much.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Hes very busy, then. Is he nice to you?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"He is. He brings me gifts when he goes to work.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Thats great, Lain.\", \"actor\": \"Toko\"}]'),(66,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',66,98,'en','[{\"line\": 0, \"text\": \"Lain, Id like for you to tell me the truth.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"The truth?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"You dont have any friend named Misato-chan, do you?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Ha, ha. What do you mean? Thats silly of you.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I asked Kyoko-chan.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"And?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"She said that there is no Misato-chan. She said, isnt that Minakos mistake?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Is that what you believe?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Im not accusing you, but this is very important.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"For you to write your report?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I wont deny that. But Im concerned about you personally.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"About me personally?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Im concerned that you might be creating imaginary friends and running away into your own little world.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"That is a sickness.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"You said that kids often have that, and that theres nothing wrong with it, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Misato-chan isn\'t an imaginary friend, either.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Then where is she? Can you call her right now? Can you give her a call right now, in front of me?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"She cant be contacted. She moved away.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"Can you do anything to prove to me that she exists?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"That she exists?\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Yes. Can you prove that?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"My memory\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"Look, Lain, I want to believe you, but I cant read your mind or tell what your memories are.\", \"actor\": \"Toko\"}]'),(67,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',67,99,'en','[{\"line\": 0, \"text\": \"Its not a memory. Its a record.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Well, theyre vaguely similar. But its no time to be playing word-games.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"You would understand if you were connected.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Connected to what?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Lain, you say that Misato-chan exists, but you are the only person who says so.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"How can you expect anyone else to understand?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"There is something wrong with this.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"How can anyone believe you?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"You don\'t believe me, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Unfortunately, no.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"But thats because Ive looked very closely into this personally, and all the facts point against it.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"If you could show me some objective proof that that I am mistaken, that would be different.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"How would I prove that she exists?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"How?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Well show me other people whove known her.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Show me her identification or birth certificate or something.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"More than anything else, show me that shes a human being that exists and walks around in a human body.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"She doesnt exist if she doesnt have a human body?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Of course not! Shes not a ghost, is she?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Professor Takashima didnt exist, then?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"But he did exist.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"He had a body before he died.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"He lived and walked around in a human body.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"There is ample evidence that he existed.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Many people who are alive now knew him.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"There are records of his existence.\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Lets cool it, Lain.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Im not here to have a question and answer session with you.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Im here to treat you.\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"I do not acknowledge Misato-chans existence.\", \"actor\": \"Toko\"}]'),(68,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',68,100,'en','[{\"line\": 0, \"text\": \"Im sorry about last session, Lain. I dont know myself why I became so angry.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Its ok, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Its just that aside from this issue with Misato-chan, I cant see anything wrong with you.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Well have to work on it slowly.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Will you finish your paper in time?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im not putting anything about you in my paper.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Ill use information from other clients.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Its not your problem anyway, Lain.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Youre gentle, Lain.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Im not gentle.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I have to apologize.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I should have apologized right away, but Ive done some things behind your back.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I lied and told some of your friends that Im a private tutor.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I asked your friends about you.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I needed the information for my report.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I should have these things with more benevolent intentions, but are you angry?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Im sorry, Lain.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But I did it for y No, I didnt do it for you.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I did it for my work.\", \"actor\": \"Toko\"}]'),(69,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',69,101,'en','[{\"line\": 0, \"text\": \"Who did you see, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Kyoko-chan and Kaori-chan.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I know Kyoko-chan, but who is Kaori-chan?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Shes your classmate.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"You arent going to ask me to prove her existence now, are you?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Ill feel bad.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Please dont be hard on me that way, Lain.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Youre confident, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"About what?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Youre confident that that person exists. I cant prove that she exists.\", \"actor\": \"Lain\"}]'),(70,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',70,102,'en','[{\"line\": 0, \"text\": \"You seem to be busy lately, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"The deadline for my paper is coming up, and although Ive been waiting for a long time for someone to relieve me with some of my work, it hasnt happened.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"That means more work for me.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Sure, its a little hard.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Is it tiresome?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Im ok, Lain. You dont have to worry about it.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Im worried about you, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"About me? Well thank you. I appreciate your feelings.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Will you continue to like me, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Of course! Youre my little sister, remember? I dont have any reasons not to like you.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"But I made you mad.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Thats true.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But that was just because youre growing intellectually, we were having a discussion and we had a difference of opinion.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But lets leave that alone.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Theres no reason for me to dislike you because we had a difference of opinion.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Do you have important people in your life aside from me?\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Sure.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I have friends, too.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"You have people in your life that are important to you aside from me, dont you?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I do. But if I had to choose one person, I would choose you.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"That makes me happy. Then, Ill pick you, too.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Really? But is that ok? What about the other important people in your life?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"If you chose me and I didnt choose you, wont you feel sad? I dont want you to feel sad.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Wont your lover be sad?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Huh? Um, well, yes. Are you interested in my private life, Lain?\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I want to know about you.\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Things having to do about my lover are a secret. Youre a bit young for that kind of thing, anyway.\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Too young?\", \"actor\": \"Lain\"}, {\"line\": 29, \"text\": \"You may eventually have a love life of your own. But love is a pretty complicated thing.\", \"actor\": \"Toko\"}, {\"line\": 30, \"text\": \"Complicated?\", \"actor\": \"Lain\"}, {\"line\": 31, \"text\": \"Yes.\", \"actor\": \"Toko\"}, {\"line\": 32, \"text\": \"I have by doubts and worries about it, too.\", \"actor\": \"Toko\"}, {\"line\": 33, \"text\": \"Im human.\", \"actor\": \"Toko\"}, {\"line\": 34, \"text\": \"I make mistakes and have regrets.\", \"actor\": \"Toko\"}, {\"line\": 35, \"text\": \"Even now I have feelings that I cant figure out.\", \"actor\": \"Toko\"}, {\"line\": 36, \"text\": \"See?\", \"actor\": \"Toko\"}, {\"line\": 37, \"text\": \"Its complicated.\", \"actor\": \"Toko\"}, {\"line\": 38, \"text\": \"Complicated\", \"actor\": \"Lain\"}]'),(71,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',71,103,'en','[{\"line\": 0, \"text\": \"Youre Lain, arent you?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Sure I am. Youre being silly, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Oh, Im sorry.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Youre right.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Im a little tired.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Im sorry for asking goofy questions.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You seem to be really doing well lately, Lain.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I havent changed.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Weve been having counseling sessions for a long time.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"A long time? Do you mean many times? Have I been seeing you too much?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Maybe. What do you think?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"There seem to be many areas left.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"What do you mean? Don\'t tell me things I cant understand.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Whats wrong? Are you angry? You cant control your emotions?\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Why are you asking me this! Look, Lain. Are you playing with me? Are you mocking me?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"No.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"This counseling session is for YOU. You come here to see me. Not the other way around.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"But you said that I could talk. I want to know about you.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"How is finding out about me going to help you? Do you just want to irritate me?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"You really didnt like my questions?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"No, I didnt!\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"I want you to leave things alone.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"I dont want you trying to get inside my head.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Thats what I think.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Arent you sad to be alone?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Im not alone!\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I am not you!\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"You are Lain, arent you?\", \"actor\": \"Toko\"}, {\"line\": 28, \"text\": \"Youre the proper, reserved, sad girl without much self-confidence, arent you?\", \"actor\": \"Toko\"}, {\"line\": 29, \"text\": \"No, thats not me. I think my personality is much different from that.\", \"actor\": \"Lain\"}, {\"line\": 30, \"text\": \"Then who are you?\", \"actor\": \"Toko\"}, {\"line\": 31, \"text\": \"Im Lain.\", \"actor\": \"Lain\"}]'),(72,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',72,104,'en','[{\"line\": 0, \"text\": \"I felt really good today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Thats really great, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im starting to feel worse, though. I cant tell whats bothering me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Is it something vague? Something about feeling dormant?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"When youre feeling happy, dont you sometimes feel worried about when it might come to an end?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Is that a symptom of an intermittent flow of thought?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I cant imagine myself being happy.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"You can imagine yourself being unhappy?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I suppose. But I cant get rid of the worry.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Is there something I can do?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"What would I want to seek for from you? Why me from you?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Are you too proud to tell me?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"It has nothing to do with pride. Why dont you tell me what you think is going on with me.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Youre just Toko-san. Youre yourself.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Thats right. I better get to my reports. If you want, you can stay around here, but I have to go.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Why?\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Because there is something I have to do right now.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"There is something that I can only do now.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I have to continue my research.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"That makes you happy?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Yes, it does. My number one goal is to continue doing the research that I enjoy.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"So thats number one?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"No.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Thats not what I mean.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Seeing you is very important, too.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Its very important to me that everything goes well.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"But I have to do things in order.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Im glad.\", \"actor\": \"Lain\"}]'),(73,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',73,105,'en','[{\"line\": 0, \"text\": \"Are you troubled about anything?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"No, Im not. How about you, Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Sometimes. I feel like something bad might happen.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Youre feeling delusional?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yes, but unlike with you, it isnt a strange delusional problem with perception.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I wonder if thats true.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"But how about the feelings you have about that man?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Arent those feelings delusional?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Who is that man?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"The man youre thinking about right now.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"You can tell what Im thinking.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Wow!\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"You have super powers?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Is that why you poke fun at me sometimes?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"But Im connected to you, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Connected? In what way?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"It probably wouldnt make sense if I tried to explain it to you.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"Thats called psychosis, Lain.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Believing yourself to be a unique chosen person or that you are God.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Thats a serious disease.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Im sick. How about you?\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Im not sick. Im\", \"actor\": \"Toko\"}]'),(74,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',74,106,'en','[{\"line\": 0, \"text\": \"You dont have much energy.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Im tired.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Are you tired of doing miscellaneous chores?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Is someone having you do waste your time with small tasks?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yeah. I do feel that way sometimes.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Do you think you have some kind of psychological condition?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Do you think you might have caught something from me?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Ha, ha.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Why do you think Im like this? I know and understand the things I am talking about.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Youre ok, Toko-san. Youll be better by tomorrow.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Ill be better?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Youll be in a world where you dont suffer.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Am I myself when I am by myself or when Im with you?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Youre always yourself. Even when you doubt my existence.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"I doubt your existence?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Yes. You dont want to believe that I exist.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Why would I have a problem like that because I treat you?\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"There is information you wouldnt understand now. You arent doing well psychologically.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Information I wouldnt understand now? But I want to know now.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Yes. But it would be too difficult for you to understand now.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"Youre thinking about whats best for me, arent you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Of course, I am. I like you.\", \"actor\": \"Lain\"}]'),(75,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',75,107,'en','[{\"line\": 0, \"text\": \"A rumor?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Does it bother you?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Someone is spreading rumors about you?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Is that a delusion of persecution?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Some other kind of delusion?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Well, it might just be a lack of self-confidence.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Why do you have so much confidence, even when youre sick, Lain?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Im sick. Whats the name of my disease? Schizophrenia? Manic depression? Something else?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"You arent any of those things.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"You have pathomimesis.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Thats what you call the imitation of disease.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Then Im not really sick?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"You dont exist.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I just think that youre here.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Everyone has been tricked.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Weve all been shown some glimpse of your existence.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Thats why no one really remembers you.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Its not like you to be mad. Some of us dont seem to exist, though. Which are you?\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"I think that you think Im here.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Youre a delusion that created a delusion.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"All these things, your constructed diseases and your voices, are delusions.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"How about me? Am I here?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"Thats why I have to heal you!\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Youre so serious. But youre an elite. Youve been chosen.\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Thats right.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Im trying hard to be here.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I would rather have been out playing and fooling around, but Ive gone to school and studied and worked hard to get here.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"I feel sorry for you, Toko-san.\", \"actor\": \"Lain\"}]'),(76,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',76,108,'en','[{\"line\": 0, \"text\": \"Are you ok, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Yes. Im hanging in there. Sometimes I dont feel so good.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"You think too much, Toko-san. You pay too much attention to whats going on around you.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I have things to think about. Work. Romance.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Is a love life necessary?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I think its a very important part of life.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Does a woman need a man?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Not just because shes a woman. If that were true, a man would need a woman.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"It would be obsessive to think that a woman must have a man, Toko-san. You can live on your own.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"But wouldnt that be sad? I cant stand to be sad. How about you? How about you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I would get sad, too.\", \"actor\": \"Lain\"}]'),(77,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',77,109,'en','[{\"line\": 0, \"text\": \"You dont want to do anything? How about your research?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"No one will want to read my paper anyway.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Whats wrong? Its not like you to play a victim.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Not like me! What am I?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Youre just Toko-san, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Ive been severed from society. Im no longer connected.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Thats not true. Im connected to you. Im connected to society. Thats why youre connected, too.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Somethings wrong with me, isnt there?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"The times when I talk to you and the time I spend with everything else are separate.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"From your point of view, my life must be a contradiction.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"My point of view?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"From your point of view, this world doesnt exist.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"No. Thats not true. Im connected to this world.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I cant take it anymore.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I cant even stand this moment.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Thats not all.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"My memories of tomorrowthe memories Ill have in the future are in disarray.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Is this a delusion caused by worrying?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Am I experiencing something I shouldnt be experiencing?\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Thats you, isnt it?\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"It is me! Because it is me!\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Wouldnt you be better off if you were just being yourself?\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"I cant understand my sense of self from moment to moment!\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"The judgment you have exactly at that moment is you, Toko-san. The rest is\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"The rest is\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"Ordinary data.\", \"actor\": \"Lain\"}]'),(78,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',78,110,'en','[{\"line\": 0, \"text\": \"Concerning diagnosis: The client lives in a nuclear family with her mother and father.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Both parents are middle to upper class and financially well-to-do.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"The parents have a stable relationship.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"They do not seem to have mental health problems.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Concerning family history of illness, according to the parents as well as the database at our center, there was no neurosis, mental illness, suicidality or consanguineous marriage in her family.\", \"actor\": \"Toko\"}]'),(79,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',79,111,'en','[{\"line\": 0, \"text\": \"She hasnt had any significant problems from the time of birth.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Even at this point, she doesnt have any bad habits or problems with truancy.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She does not seem to have a troubled history.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Of course, at this research center, accompanying illnesses must be treated.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I wanted to ask her about her illness history when I started counseling her, but she was very shy.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I decided that I would try to get her to open up.\", \"actor\": \"Toko\"}]'),(80,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',80,112,'en','[{\"line\": 0, \"text\": \"According to her mother at home, Lain has very frightening dreams.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"They are not ordinary dreams.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"They are very realistic and almost hallucinatory.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She visited the hospital for this reason.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Regarding the nature of the dreams, it seems that Lain doesn\'t elaborate much about their contents, but she says that she sometimes hears incomprehensible voices out of nowhere, that she is suddenly awakened and forced to watch a video.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She sometimes plays in the park, and suddenly finds herself alone when she thought other kids were around.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"These seem to be childhood imaginary fears.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"In the future we will address these symptoms.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I hope for a recovery.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"It might be possible.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Fundamentally, even if that does not occur, she may be able to manage life at home.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"However, what her mother said that concerned me was her hallucinations.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I will try to pinpoint the cause of these symptoms in our counseling sessions.\", \"actor\": \"Toko\"}]'),(81,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',81,113,'en','[{\"line\": 0, \"text\": \"The physical appearance of the client at the first session.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her figure is short and thin.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"The only feature that stood out was her hairstyle.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She has grown out the tuft of her hair on just one side of her face and tied it in a bundle.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I dont think its a popular style at school, but it seems to be her individual style.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"According to her mother, she enjoys having her hair that way.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Understanding the reason for her hairstyle may guide me in understanding her.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I will look into this in the future.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"She has a calm demeanor, and her skin is fair.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"There is otherwise nothing left to mention.\", \"actor\": \"Toko\"}]'),(82,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',82,114,'en','[{\"line\": 0, \"text\": \"Although her affect is flat, she appears like any other healthy child when she smiles.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She sometimes gets tongue-tied, but not to the point where communication with her becomes impossible.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She seems calm and reserved.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She understands my questions without a problem, and is capable of answering them.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She doesnt seem to have problems with her attention span, concentration, orientation, clarity of thought, and expression of emotions, but hinders when asked about her symptoms.\", \"actor\": \"Toko\"}]'),(83,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',83,115,'en','[{\"line\": 0, \"text\": \"At this stage, I thought it would be a bad idea to ask her about her symptoms in a teasing fashion.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I was unable to find details concerning her symptoms.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"However, she generally seems to be a clever and relaxed girl.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Her mother showed me a painting she did in school.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"It was very well painted, and showed a high level of artistic sensibility.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"However, I could not find any specific points that might assist it analyzing her psychologically.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"At this stage, she appears to be at a delusional state, but there seems be a very good chance that she is a normal child.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"One point from her physical examination struck me.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Her vision is excellent.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Her standard light reflex is lower than the standard range.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"However, I have not seen in her the type of dermatological or anemic problems that are common for her age.\", \"actor\": \"Toko\"}]'),(84,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',84,116,'en','[{\"line\": 0, \"text\": \"Clinical notices from last session: First, concerning this case, I want to treat her as a therapist as opposed to a psychiatrist.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Therefore, I will treat Iwakura Lain as my client from now on.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"One of the reasons for this is that she is still 12 years old and is emotionally sensitive.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I do not want to make her feel that she is sick.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"In fact, from the results of her last clinical visit, the extent of what she is troubled from is nightmares and psychological damage associated with it.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Although during sessions with her, psychological discomfort can be detected, she is able to ask and answer questions without a problem.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I see no reason to advocate objections to my conclusion that she is sick, but at this point, I would like to treat her as someone without a mental illness.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"One more thing: Because I am both a psychiatrist and a counselor, my job is selecting the best position possible for my patients.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"When counseling her in the future, if I detect symptoms of mental illness, I would have to treat her as a psychiatrist.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Of course, theres a good chance that I myself will continue to treat her.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"In order to honor my research and keep it objective, I may want to ask a third party to continue to treat her clinically.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"In the meanwhile, I will continue to work with her in a research setting.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"This may be an exceptional situation at this research center, but I would like to proceed with it.\", \"actor\": \"Toko\"}]'),(85,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',85,117,'en','[{\"line\": 0, \"text\": \"The client is very cooperative and our sessions have been going smoothly.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"However, somewhere within her, she still is very cautious around me, and it is difficult to discuss her problems.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"We had some extra time, so I proposed that we play a simple game.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She turned me down, and we went directly into our session.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She takes much curiosity in me, an outside observer, but I think this has to do with her cautiousness around me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She plays with dolls at home, and seems to have a complex associated with them.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"She has few friends, and playing with dolls is her way of dealing with her loneliness.\", \"actor\": \"Toko\"}]'),(86,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',86,118,'en','[{\"line\": 0, \"text\": \"In the clients response, a good understanding of language could be found.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"However, this is not related to disease.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"It displays the clients strong ability to use language.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"The client differentiated physical activity from physical education, stating that the latter is an undertaking. In school and group activities, the two can be compared quite conspicuously.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"The fact that she says she likes physical exercise but not physical education may have to do with her lack of self confidence.\", \"actor\": \"Toko\"}]'),(87,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',87,119,'en','[{\"line\": 0, \"text\": \"Matters for special mention: The clients tendencies disturb me.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her light reflexes were tested at the hospital.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She may have Adies syndrome.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Together with active auditory hallucinations and paranoia, she may have early onset general paralysis, although very unlikely.\", \"actor\": \"Toko\"}]'),(88,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',88,120,'en','[{\"line\": 0, \"text\": \"Making a hasty diagnosis is taboo, but thinking of the worst, I think I should obtain cerebrospinal fluids as soon as possible.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"May I have your permission, please?\", \"actor\": \"Toko\"}]'),(89,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',89,121,'en','[{\"line\": 0, \"text\": \"My diagnosis at the hospital is affecting my client.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She was emotionally hurt when she found out that the Rorschachs test was in fact a tool used to examine her.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"This wasnt severe to the extent of a mind-rape, but I did hurt her in our session today.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"This has made me realize that I must be discrete when bringing up the theme of disease.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I explained to her my role as a counselor, and my hopes to be helpful to her, and regained some trust from her.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I hope that in our future sessions, she will develop some goals of her own concerning counseling.\", \"actor\": \"Toko\"}]'),(90,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',90,122,'en','[{\"line\": 0, \"text\": \"She seems to be cautious about with mother.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"And as far as shes concerned, her hallucinations are real.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She may be mistrustful of her mother because her mother does not accept that her hallucinations are real. At this point, I dont know if the hallucinations are due to some warped sense of perception, or if they are entirely imaginary.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"There are times when it is difficult to tell.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Concerning the vision the client saw floating in mid-air, they were most likely imaginary, opposed to being shapes of monsters formed by the shadows on the walls and curtains at night, etc.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Could it be that she has developed a system of sensing without perceiving?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I would like permission to examine her cerebrospinal fluid.\", \"actor\": \"Toko\"}]'),(91,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',91,123,'en','[{\"line\": 0, \"text\": \"My client has found her own diagnosis and has been studying it on her own.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She must have a considerable amount of fear concerning her illness.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Having the label of mental illness pasted on you causes you to be stigmatized by society.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She may need to deal with this.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I am rethinking the how the mentally ill are viewed by society.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"In our last session, I tried to eliminate her concerns about feeling that she is mentally ill. As a result, I think I was able to secure some trust in our relationship.\", \"actor\": \"Toko\"}]'),(92,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',92,124,'en','[{\"line\": 0, \"text\": \"Mainly, we talked about auditory hallucinations.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She believes that she hears voices from electric lines.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She says that the voices she hears are not related to her.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"This could be categorized as a delusion of physical influences, but the symptoms didnt seem serious enough to be called full-blown delusions.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Her being able to hear from phone lines and electric lines seemed instead to be childlike theories.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Shes an intelligent and sensitive girl, and probably creates her own hallucinations.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"However, regarding the fact that she has hallucinations only during the day makes her very unusual.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Although I dont think my client is lying, but she says that she very rarely hallucinates while she is talking to her friends.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Most likely, references to time and place become fuzzy in her mind, and she is either confusing separate experiences, or she may have just been talking to her friends in her dreams.\", \"actor\": \"Toko\"}]'),(93,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',93,125,'en','[{\"line\": 0, \"text\": \"The relationship with my client has deteriorated.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"My own lack of consideration is to blame.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I told here that her hallucinations are an imaginary enemy.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I tried to ease her worries by being lighthearted, but this backfired and hurt my client more.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I must visit the clients home to apologize to her and give her parents a status report concerning our sessions.\", \"actor\": \"Toko\"}]'),(94,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',94,126,'en','[{\"line\": 0, \"text\": \"I was somehow able to reconnect with my client.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her symptoms havent particularly deteriorated, so I will try my best to slowly become more trustworthy.\", \"actor\": \"Toko\"}]'),(95,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',95,127,'en','[{\"line\": 0, \"text\": \"I spoke to my client about her vision.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"There is a good chance that she may develop a troubling complex by comparing herself to others, thinking that she is uniquelyand abnormallyable to see and hear better than them.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"At the same time, instead of just saying that she has hyperesthesia, I believe she is dealing with the kind of dj vu that neurotic people sometimes experience.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I will exclude the details, but it seems that what she perceives comes from a poor sense of reality, and her true perceptions are abandoned.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Regarding her perception of time, if the client is telling the truth, there isnt much of a problem.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I dont know what she actually sees, or whether its due to a physical abnormality or an abnormality of perception, but as long as her underestimation of herself persists, it will be difficult to reach any fundamental solutions.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I cannot diagnose her with depersonalization neurosis just yet, but when I consider her fuzzy recollections of hallucinations, I do think she may be developing depersonalization symptoms.\", \"actor\": \"Toko\"}]'),(96,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',96,128,'en','[{\"line\": 0, \"text\": \"In order to alleviate her tendency to underestimate herself, I spoke to my client about ways to increase her self-confidence.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her facial expression has been hard and cold, but her personality has been easy to relate to.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Because she is an intelligent client, I will have to work hard psychologically.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"But because she understands quickly, I expect that our sessions will be effective.\", \"actor\": \"Toko\"}]'),(97,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',97,129,'en','[{\"line\": 0, \"text\": \"My clients curiosities are less to do with me than they are with my profession.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Clients who inquire about the psychiatric field usually do so out of their own discomfort about their illness.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"With this client, however, her inquiries may help in her own rehabilitation.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"There is an undeniable chance that learning about her own condition may intensify her worries, but I think that can be avoided in our relationship.\", \"actor\": \"Toko\"}]'),(98,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',98,130,'en','[{\"line\": 0, \"text\": \"I was able to ask my client about her hairstyle for the first time today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Her hair looks like a good-luck charm.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I suppose she was hearing voices even with that hairstyle, but I think that she may feel that discontinuing it would cause her to have auditory hallucinations that are even worse.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"It isnt much different from the small good luck charms we put in our cars to avoid accidents.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"A person can have an overconfidence in good luck charms, of course, but I have no intention of asking her to change her hairstyle.\", \"actor\": \"Toko\"}]'),(99,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',99,131,'en','[{\"line\": 0, \"text\": \"As usual, my clients interest is entirely about psychiatry.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Although she doesnt have a complete lack of interest in everything else, she shows very little interest.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Ive grown a bit weary of her probing questions, but being a specialist, Im able to answer those questions.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I think that letting her ask will help her eliminate her lack of self-esteem, however.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Also, comparing her case to that of a similar third party client I think that she will be able to see herself more objectively by the asking and answering of questions.\", \"actor\": \"Toko\"}]'),(100,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',100,132,'en','[{\"line\": 0, \"text\": \"We are able to have completely relaxed talks now.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Ive assumed that she still has some problems having to do with memory, but she cant accept today the memories and experiences she had of herself in the past.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"From that information, I think she probably has an identity disorder and/or a disorder with self-unity.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"However, these identity problems are still in development, and theres a chance they may naturally go away as she strengthens her sense of self in therapy.\", \"actor\": \"Toko\"}]'),(101,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',101,133,'en','[{\"line\": 0, \"text\": \"Communication with the client has been going satisfactorily.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"In our previous session, I was irritated by her many questions and our talk and became half-teasing.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"This definitely narrowed my relationship with her.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"In future sessions, we will focus on the growth of her sense of self and I will attempt to further establish aid to her.\", \"actor\": \"Toko\"}]'),(102,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',102,134,'en','[{\"line\": 0, \"text\": \"She has lightened up a great deal.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Much of it seems to do with a new friend shes developed in the last few months.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Additionally, she has had a change in her living arrangement, which she seems to be happy with.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Having now found a friend her own age, she will probably develop a relationship with that person that is more intimate than the one she has with me.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I wonder if she still asks me questions because she doesnt know what else to talk about.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I will see how things go for the next few months, and then I would like to discontinue examinations.\", \"actor\": \"Toko\"}]'),(103,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',103,135,'en','[{\"line\": 0, \"text\": \"She wont stop pressing me with questions.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Concerning this case, it may simply be an issue with her personality.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She still has worries about when she was hallucinating in the past when she was losing her sense of reality.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"We focused on that issue.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Her current symptoms involve excessive underestimation of herself.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She is objectifying the adoration she has for her other self, and because her current self and her past self are completely different, it seems that she has been developing these personification symptoms on her own for some time.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"However, there is a very small chance that that will happen now.\", \"actor\": \"Toko\"}]'),(104,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',104,136,'en','[{\"line\": 0, \"text\": \"We had our talk.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"There was no problem from beginning to end.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She is getting better.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"As far as I can see, she is just an ordinary healthy junior high schooler.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"There is no reason why she should continue to come here.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Professor Takashima, in order that I can submit my paper to the academic conference in the fall, I would like to apply to start seeing a different client.\", \"actor\": \"Toko\"}]'),(105,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',105,137,'en','[{\"line\": 0, \"text\": \"Professor Takashima, regarding my request last month to see a different client, how has that progressed?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"If you could introduce me to the person in charge of the outpatients, I could arrange things from there.\", \"actor\": \"Toko\"}]'),(106,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',106,138,'en','[{\"line\": 0, \"text\": \"Professor Takashima, I have prepared the final reports for my current client.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I thought that it might be a good idea to keep an appointment history, but because there seem to be have been so few problems, I did not keep one up till now.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"By next month I will prepare a final report with the appointment history.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I dont have much time until the academic conference this fall.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"May I please request this of you?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"As it is, I will not finish in time.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Are you harassing me?\", \"actor\": \"Toko\"}]'),(107,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',107,139,'en','[{\"line\": 0, \"text\": \"To the chief administrative secretary: who should I submit my report to before there is a new head of the laboratory?\", \"actor\": \"Toko\"}]'),(108,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',108,140,'en','[{\"line\": 0, \"text\": \"To the chief administrative secretary: Thank you for sending me a new client.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"The status with my current client can be found in my report.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I will send you my final report after I organize it.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"If it is not a problem, I would like to terminate the counseling of my current client.\", \"actor\": \"Toko\"}]'),(109,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',109,141,'en','[{\"line\": 0, \"text\": \"To the chief administrative secretary: In order to confirm my clients history, I spoke to one of her classmates.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I found a problem.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I am terribly sorry, but may I resume counseling this client?\", \"actor\": \"Toko\"}]'),(110,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',110,142,'en','[{\"line\": 0, \"text\": \"As I had feared, my client has created an imaginary friend, and has taken a rebellious stance toward my opinions.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"At the same time, her speech is remarkably lacking in logicality, and is almost like that of a schizophrenic.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I cannot, of course, diagnose her immediately.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Considering her loss of touch with reality in the past, her anthropomorphic symptoms, her age, and gender, she may have depersonalization neurosis.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Also, her personality disorder is extremely light, and considering her hallucinations, she may be suffering from paranoid schizophrenia.\", \"actor\": \"Toko\"}]'),(111,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',111,143,'en','[{\"line\": 0, \"text\": \"As much as possible in the future, I would like to assist her as a neutral third party, but it will be difficult because of the relationship Ive developed with her.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I would like someone else to take over her case.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Either that, or I would like to stop seeing her after our next session.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Mr. Chief Administrative Secretary, I apologize for being selfish.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I will follow through with all of my remaining work.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Please allow me to move ahead in this fashion.\", \"actor\": \"Toko\"}]'),(112,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',112,144,'en','[{\"line\": 0, \"text\": \"I tried my best to repair the relationship with my client, but failed.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Somehow, after I negated the existence of her imaginary friend, shes withdrawn into her own world.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She has become withdrawn like a patient with autism.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"We speak very little, but because she has become so distant, Im afraid there is a good chance of sending her into a panic if I speak to her carelessly.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I suppose I just need to wait and see what happens, but Im afraid that through psychotherapy, there is the danger that I may get wrapped up in her paranoid delusions.\", \"actor\": \"Toko\"}]'),(113,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',113,145,'en','[{\"line\": 0, \"text\": \"To the chief administrative secretary: Concerning future correspondences: The patient herself doesnt believe that she is very sick, and this has made therapy much more difficult.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I think the top priority should be to have her see a different therapist and allow her to find a different environment.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"However, if changing therapists is not possible, I think she would need to start a small dosage of anti-psychotic medication.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Perhaps Contom or Neoparidol might be effective.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"It may be well worth trying.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Will I need to submit a request to the overseeing authorities for this?\", \"actor\": \"Toko\"}]'),(114,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',114,146,'en','[{\"line\": 0, \"text\": \"The client had a pleasant demeanor in our session from beginning to end.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Perhaps there was no need to do anything abruptly.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"The client hasnt changed her feelings about me from the past, and our casual conversations go well as long as I avoid talking about her imaginary friend.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"What bothers me, though, are the prying personal questions asks.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I sometimes think that she means to intentionally work me up.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"But that may just be due to my impatience.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Somehow I want to have an intimate relationship with her again.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I want to gradually redevelop objectivity and lead our sessions.\", \"actor\": \"Toko\"}]'),(115,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',115,147,'en','[{\"line\": 0, \"text\": \"The client has changed so much that she doesnt seem to be the same person anymore.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Is that true, or am I being subjective?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Could something have changed due to the progression of her symptoms?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"As we talked, she made remarks that I couldn\'t understand.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She also made some inciteful remarks.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"During our session, I forgot myself and became hysterical.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I cant handle it anymore.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I can do no more.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Please have someone else take charge of this case.\", \"actor\": \"Toko\"}]'),(116,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',116,148,'en','[{\"line\": 0, \"text\": \"Overall, she doesnt seem very youthful.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She appears older than her age.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Even when we talk at a relaxed tone, she moves her eyes about irratically.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She also moves her wrists about oddly, and appears anxious.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She probably is very conscious about her bodily sensations, and doesnt admit to being ill because of her pride.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She blames what is happening to her on fatigue and lack of sleep.\", \"actor\": \"Toko\"}]'),(117,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',117,149,'en','[{\"line\": 0, \"text\": \"During our talk, she said that she couldnt imagine herself being happy.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 1, \"text\": \"She said that she has no hope for her future.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 2, \"text\": \"She has lost her sense of confidence, and has slipped into a pessimistic mood.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 3, \"text\": \"She would go into hysterics if stimulated.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 4, \"text\": \"The client is scrupulous and diligent.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 5, \"text\": \"She has anxiety about having harmonious interpersonal relationships.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 6, \"text\": \"She is transformational and melancholy.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}, {\"line\": 7, \"text\": \"From now on, I will continue trying to relieve her psychological burdens and to provide her guidance.\", \"actor\": \"Toko and Lain together (Lain can only be heard joining at the end)\"}]'),(118,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',118,150,'en','[{\"line\": 0, \"text\": \"The client accidentally poured scalding water on her hand during our session.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"She went about cleaning up afterwards as if nothing had happened.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"There is a chance that there is something wrong with her brain organically.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"We may need to perform CT scans and some other tests to determine if this is so.\", \"actor\": \"Toko and Lain together\"}]'),(119,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',119,151,'en','[{\"line\": 0, \"text\": \"My client is not opening up to me.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"Some kind of drastic treatment may be necessary.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"When I intentionally rubbed her the wrong way, she showed her combative side.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"I think that in the future, lowering of mental efficiency, deterioration of memory, and deterioration of healing will take place, and an inferiority complex with self-reproach will take over.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 4, \"text\": \"As a result, impulsive behavior will come about, among other symptoms.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 5, \"text\": \"There is a good chance that her sense of reality will be troubling to her.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 6, \"text\": \"She may want to commit suicide.\", \"actor\": \"Toko and Lain together\"}]'),(120,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',120,152,'en','[{\"line\": 0, \"text\": \"Her personality speedily continues to decline.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"Her emotions are flattened.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"She is indifferent and lethargic.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"She doesnt show a feminine demeanor as she used to.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 4, \"text\": \"Until now, Ive always served tea for visitors, but I didnt get around to it today.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 5, \"text\": \"The person one imagines oneself to be and that persons difference in ability compared to herThe clients ego is collapsing.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 6, \"text\": \"Even in my presence, she exposes lack of confidence and appears sick.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 7, \"text\": \"Sometimes, I dont know about me\", \"actor\": \"Toko and Lain together\"}]'),(121,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',121,153,'en','[{\"line\": 0, \"text\": \"Her responses to my questions have become sluggish.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"Her lack of self-confidence seems to be altering her self-assessment.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"Her intellect is affected.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"The assessment of her character has not taken place. She has completely lost sense of herself.\", \"actor\": \"Toko and Lain together\"}]'),(122,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',122,154,'en','[{\"line\": 0, \"text\": \"Patients with melancholia, it is good to treat them right at the onset of the disease.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"In addition to the neurosis, symptoms of depression and schizophrenia can be seen.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"She may have atypical psychosis.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"Her brainwaves should be tested at the hospital, along with some other tests.\", \"actor\": \"Toko and Lain together\"}]'),(123,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',123,155,'en','[{\"line\": 0, \"text\": \"She doesnt understand what I am saying.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"She responds to technical psychiatric language, but not to ordinary speech.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"Ive decided to diagnose her with verbal auditory agnosia.\", \"actor\": \"Toko and Lain together\"}]'),(124,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',124,156,'en','[{\"line\": 0, \"text\": \"The client has an indirect reason for going to the trouble of surrounding herself with love.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"Using a continuous approach, I think I can regain her psyche.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"I will discontinue medicating her, and resume psychotherapy and milieu therapy.\", \"actor\": \"Toko and Lain together\"}]'),(125,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',125,157,'en','[{\"line\": 0, \"text\": \"The client has discovered the connection between herself and reality, and has completely recovered from her ego disturbance.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 1, \"text\": \"The results from the PPRS and HMB tests showed no problems whatsoever.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 2, \"text\": \"I completed my first treatment successfully.\", \"actor\": \"Toko and Lain together\"}, {\"line\": 3, \"text\": \"Thank you, my valuable client.\", \"actor\": \"Lain alone\"}]'),(126,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',126,158,'en','[{\"line\": 0, \"text\": \"February 7th. Interview for testimony from a witness to the suicide of a high school girl.\", \"actor\": \"Cop\"}, {\"line\": 1, \"text\": \"Before we begin with questions, Id like to confirm your identity.\", \"actor\": \"Cop\"}, {\"line\": 2, \"text\": \"Shinji Makino 29 years old.\", \"actor\": \"Cop\"}, {\"line\": 3, \"text\": \"Office worker.\", \"actor\": \"Cop\"}, {\"line\": 4, \"text\": \"Is that correct?\", \"actor\": \"Cop\"}, {\"line\": 5, \"text\": \"Could you remind me of your address?\", \"actor\": \"Cop\"}, {\"line\": 6, \"text\": \"Sure. Shinagawa ward, Kamiosaki, 30-19-105, Meguro-residence 504.\", \"actor\": \"Shinji\"}, {\"line\": 7, \"text\": \"Thank you.\", \"actor\": \"Cop\"}, {\"line\": 8, \"text\": \"This interview will be recorded, by the way, but it will be kept confidential, so please dont worry.\", \"actor\": \"Cop\"}, {\"line\": 9, \"text\": \"Alright.\", \"actor\": \"Cop\"}, {\"line\": 10, \"text\": \"Lets get started.\", \"actor\": \"Cop\"}, {\"line\": 11, \"text\": \"Could you tell me what you saw at the scene of the suicide in as much detail as possible?\", \"actor\": \"Cop\"}, {\"line\": 12, \"text\": \"Where do you want me to start? From the morning of that day?\", \"actor\": \"Shinji\"}, {\"line\": 13, \"text\": \"No. Just what you remember from around the time of the suicide. Relax. Dont feel pressured.\", \"actor\": \"Cop\"}, {\"line\": 14, \"text\": \"Uhh.\", \"actor\": \"Shinji\"}]'),(127,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',127,159,'en','[{\"line\": 0, \"text\": \"You were there as a twosome.\", \"actor\": \"Cop\"}, {\"line\": 1, \"text\": \"Yeah. So?\", \"actor\": \"Shinji\"}, {\"line\": 2, \"text\": \"Well, according to a storekeeper who saw you, the girl you were with was very young.\", \"actor\": \"Cop\"}, {\"line\": 3, \"text\": \"Yeah. And?\", \"actor\": \"Shinji\"}, {\"line\": 4, \"text\": \"Well, you see, prostitution takes place at that store.\", \"actor\": \"Cop\"}, {\"line\": 5, \"text\": \"Sorry to tell you, but I dont have any hobbies like that. Shes just a girl I knew.\", \"actor\": \"Shinji\"}, {\"line\": 6, \"text\": \"Is that right? Well, Id also like to ask her a few questions too.\", \"actor\": \"Cop\"}, {\"line\": 7, \"text\": \"I havent had any contact with her, and why do I have to do all this for you anyway?\", \"actor\": \"Shinji\"}, {\"line\": 8, \"text\": \"Because wed like to have as much information as we can to understand why a high school student is dead.\", \"actor\": \"Cop\"}, {\"line\": 9, \"text\": \"We havent even been able to retrieve the gun used in the crime.\", \"actor\": \"Cop\"}, {\"line\": 10, \"text\": \"And you think she took it with her? Youve got to be kidding. Shes in junior high school!\", \"actor\": \"Shinji\"}, {\"line\": 11, \"text\": \"Were not certain, were just not ruling out the possibility.\", \"actor\": \"Cop\"}, {\"line\": 12, \"text\": \"Among the people at that store at that time, the only person who we havent been able to identify is the girl you were with.\", \"actor\": \"Cop\"}, {\"line\": 13, \"text\": \"Could you help us out with more detailed information?\", \"actor\": \"Cop\"}]'),(128,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',128,160,'en','[{\"line\": 0, \"text\": \"I don\'t want to talk if my testimony is going to harm her.\", \"actor\": \"Shinji\"}, {\"line\": 1, \"text\": \"No, nothing has been decided yet.\", \"actor\": \"Cop\"}, {\"line\": 2, \"text\": \"Wed just like to be able to contact her.\", \"actor\": \"Cop\"}, {\"line\": 3, \"text\": \"Theres nothing for you to worry about.\", \"actor\": \"Cop\"}, {\"line\": 4, \"text\": \"But is there something\", \"actor\": \"Cop\"}, {\"line\": 5, \"text\": \"No, nothing.\", \"actor\": \"Shinji\"}, {\"line\": 6, \"text\": \"I did meet her through the Internet.\", \"actor\": \"Shinji\"}, {\"line\": 7, \"text\": \"But she wasnt the kind of kid that would reveal an e-mail address.\", \"actor\": \"Shinji\"}, {\"line\": 8, \"text\": \"She was good at computers.\", \"actor\": \"Shinji\"}, {\"line\": 9, \"text\": \"What do you mean?\", \"actor\": \"Cop\"}, {\"line\": 10, \"text\": \"She tapped into computers.\", \"actor\": \"Shinji\"}, {\"line\": 11, \"text\": \"You mean she was a hacker?\", \"actor\": \"Cop\"}, {\"line\": 12, \"text\": \"No doubt about it.\", \"actor\": \"Shinji\"}, {\"line\": 13, \"text\": \"Then you wouldnt be able to contact her?\", \"actor\": \"Cop\"}, {\"line\": 14, \"text\": \"I dont mean that I couldnt.\", \"actor\": \"Shinji\"}, {\"line\": 15, \"text\": \"I reached her once.\", \"actor\": \"Shinji\"}, {\"line\": 16, \"text\": \"But I cant guarantee that she still even has that address.\", \"actor\": \"Shinji\"}, {\"line\": 17, \"text\": \"I see.\", \"actor\": \"Cop\"}, {\"line\": 18, \"text\": \"Then theres little we can do.\", \"actor\": \"Cop\"}, {\"line\": 19, \"text\": \"Well handle it from here, then.\", \"actor\": \"Cop\"}, {\"line\": 20, \"text\": \"Could you give us her address?\", \"actor\": \"Cop\"}, {\"line\": 21, \"text\": \"Sure, I dont mind.\", \"actor\": \"Shinji\"}, {\"line\": 22, \"text\": \"If you dont have it, we can have some of our specialists look into it.\", \"actor\": \"Cop\"}, {\"line\": 23, \"text\": \"If you do hear from her, could you keep our little meeting here a secret?\", \"actor\": \"Cop\"}, {\"line\": 24, \"text\": \"It would be important for you, too, that you keep this a secret.\", \"actor\": \"Cop\"}, {\"line\": 25, \"text\": \"Is that a threat?\", \"actor\": \"Shinji\"}, {\"line\": 26, \"text\": \"Oh, not at all.\", \"actor\": \"Cop\"}, {\"line\": 27, \"text\": \"We have better things to do than make threats.\", \"actor\": \"Cop\"}, {\"line\": 28, \"text\": \"As long as we arent interfered with, things will be just fine.\", \"actor\": \"Cop\"}, {\"line\": 29, \"text\": \"Thats what I mean.\", \"actor\": \"Cop\"}]'),(129,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',129,161,'en','[{\"line\": 0, \"text\": \"Hello, Makino-san.\", \"actor\": \"Cop\"}, {\"line\": 1, \"text\": \"Its been a while.\", \"actor\": \"Cop\"}, {\"line\": 2, \"text\": \"You dont seem to be doing very well, so Ill keep this short.\", \"actor\": \"Cop\"}, {\"line\": 3, \"text\": \"Can you take responsibility for the things you say to us from now on?\", \"actor\": \"Cop\"}, {\"line\": 4, \"text\": \"Makino-san?\", \"actor\": \"Cop\"}, {\"line\": 5, \"text\": \"I havent been able to sleep. I feel better when Im looking at the computer.\", \"actor\": \"Shinji\"}, {\"line\": 6, \"text\": \"Smoking isnt really allowed here, but if it relaxes you, go ahead.\", \"actor\": \"Cop\"}, {\"line\": 7, \"text\": \"Lets begin.\", \"actor\": \"Cop\"}, {\"line\": 8, \"text\": \"The reason youre here\", \"actor\": \"Cop\"}, {\"line\": 9, \"text\": \"Its about Lain.\", \"actor\": \"Shinji\"}, {\"line\": 10, \"text\": \"Yes it is. Did you meet her after the shooting?\", \"actor\": \"Cop\"}, {\"line\": 11, \"text\": \"Probably.\", \"actor\": \"Shinji\"}, {\"line\": 12, \"text\": \"Probably?\", \"actor\": \"Cop\"}, {\"line\": 13, \"text\": \"I dont know anymore.\", \"actor\": \"Shinji\"}, {\"line\": 14, \"text\": \"Detective, could you contact a psychiatrist for me?\", \"actor\": \"Shinji\"}, {\"line\": 15, \"text\": \"I cant take it anymore.\", \"actor\": \"Shinji\"}, {\"line\": 16, \"text\": \"I dont want to die, but all of a sudden, I want to die now!\", \"actor\": \"Shinji\"}, {\"line\": 17, \"text\": \"Look at these scars!\", \"actor\": \"Shinji\"}, {\"line\": 18, \"text\": \"Theres nowhere left to cut!\", \"actor\": \"Shinji\"}, {\"line\": 19, \"text\": \"This is interrogation room number 7.\", \"actor\": \"Cop\"}, {\"line\": 20, \"text\": \"Bring a doctor here quickly.\", \"actor\": \"Cop\"}, {\"line\": 21, \"text\": \"This ones no good, either.\", \"actor\": \"Cop\"}, {\"line\": 22, \"text\": \"Hey, cut it out!\", \"actor\": \"Cop\"}, {\"line\": 23, \"text\": \"Sorry.\", \"actor\": \"Cop\"}, {\"line\": 24, \"text\": \"Forget about the doctor.\", \"actor\": \"Cop\"}, {\"line\": 25, \"text\": \"Bring a cleaner.\", \"actor\": \"Cop\"}, {\"line\": 26, \"text\": \"And a cup of coffee, too.\", \"actor\": \"Cop\"}]'),(130,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',130,162,'en','[{\"line\": 0, \"text\": \"First of all, regarding the suicides of two researchers at our center, there have been rumors that their deaths had to do with them exploring some strange area of research, like something out of a science fiction novel.\", \"actor\": \"Research center representative\"}, {\"line\": 1, \"text\": \"I reassure you that that is completely untrue.\", \"actor\": \"Research center representative\"}, {\"line\": 2, \"text\": \"This center specializes in the study of communication and mental illness.\", \"actor\": \"Research center representative\"}, {\"line\": 3, \"text\": \"Fundamentally, this is not a facility for clinical medicine.\", \"actor\": \"Research center representative\"}, {\"line\": 4, \"text\": \"Our staff sometimes visits hospitals at their invitation, but basically, all we do here is research.\", \"actor\": \"Research center representative\"}, {\"line\": 5, \"text\": \"I dont know how to apologize to her parents of those researchers.\", \"actor\": \"Research center representative\"}, {\"line\": 6, \"text\": \"I am quite overcome.\", \"actor\": \"Research center representative\"}, {\"line\": 7, \"text\": \"Ive heard that the female researcher who took her life had some patients.\", \"actor\": \"Reporter\"}, {\"line\": 8, \"text\": \"She was unique.\", \"actor\": \"Representative\"}, {\"line\": 9, \"text\": \"She treated people clinically at this research center.\", \"actor\": \"Representative\"}, {\"line\": 10, \"text\": \"But this was her own decision.\", \"actor\": \"Representative\"}, {\"line\": 11, \"text\": \"Ive heard rumors that one of the researchers who died was mentally ill. Is that true?\", \"actor\": \"Reporter\"}, {\"line\": 12, \"text\": \"We cant really comment on that right now.\", \"actor\": \"Representative\"}, {\"line\": 13, \"text\": \"But seeing that she took her own life, she may have had some type of mental illness.\", \"actor\": \"Representative\"}, {\"line\": 14, \"text\": \"There are many things a person may call it, but theres no doubt that she was suffering deeply.\", \"actor\": \"Representative\"}, {\"line\": 15, \"text\": \"Suicide is very extreme, but she must have been under a lot of stress.\", \"actor\": \"Representative\"}, {\"line\": 16, \"text\": \"A psychiatrist is still just a human being.\", \"actor\": \"Representative\"}, {\"line\": 17, \"text\": \"Did she catch her mental illness from her patients?\", \"actor\": \"Reporter\"}, {\"line\": 18, \"text\": \"Mental illness isnt a contagious disease, So no, she didnt catch anything from her patients.\", \"actor\": \"Representative\"}, {\"line\": 19, \"text\": \"We wouldnt use that expression.\", \"actor\": \"Representative\"}, {\"line\": 20, \"text\": \"She may have been influenced by the patients to a degree.\", \"actor\": \"Representative\"}, {\"line\": 21, \"text\": \"You may call that a sort of contagion if youd like.\", \"actor\": \"Representative\"}, {\"line\": 22, \"text\": \"Its a matter of semantics.\", \"actor\": \"Representative\"}, {\"line\": 23, \"text\": \"But dont let anyone trick you about this sort of thing.\", \"actor\": \"Representative\"}, {\"line\": 24, \"text\": \"All of this doctors patients were healthy.\", \"actor\": \"Representative\"}, {\"line\": 25, \"text\": \"I cant get into specifics because of privacy issues.\", \"actor\": \"Representative\"}, {\"line\": 26, \"text\": \"Are there other things that might explain her behavior?\", \"actor\": \"Reporter\"}, {\"line\": 27, \"text\": \"Did the researcher who took her life may have had devious intentions?\", \"actor\": \"Reporter\"}, {\"line\": 28, \"text\": \"Were in the middle of an investigation, and I cant comment too much on that out of respect for her friends and family.\", \"actor\": \"Representative\"}, {\"line\": 29, \"text\": \"However, Ive been informed that that has absolutely nothing to do with it.\", \"actor\": \"Representative\"}, {\"line\": 30, \"text\": \"I would rather\", \"actor\": \"Representative\"}, {\"line\": 31, \"text\": \"You would rather?\", \"actor\": \"Reporter\"}, {\"line\": 32, \"text\": \"Anyway, Ive heard that that has nothing to do with it.\", \"actor\": \"Representative\"}, {\"line\": 33, \"text\": \"Ill say this for our researchers that they are very fine human beings that are very seriously committed about what they do.\", \"actor\": \"Representative\"}, {\"line\": 34, \"text\": \"What was the likelihood of this kind of thing happening?\", \"actor\": \"Reporter\"}, {\"line\": 35, \"text\": \"Well, it may have been less likely if we had caught it earlier.\", \"actor\": \"Representative\"}, {\"line\": 36, \"text\": \"On the other hand, we may have not paid enough attention because our researchers really have it together and are professional and hardworking.\", \"actor\": \"Representative\"}, {\"line\": 37, \"text\": \"We would like to help avoid this is the future by making psychological assessments of our staff.\", \"actor\": \"Representative\"}]'),(131,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',131,163,'en','[{\"line\": 0, \"text\": \"Hey, why dont you walk home with me? Lets hang out on the way there!\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Sure if you want to\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I want to buy lipstick!\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"Teacher will scold you for using lipstick again\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Lain you are too stiff. Lets both use it! I think it will look good on you.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"No Its needless for me. Its no use for me to try to be cute.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"You should have confidence in yourself! I think you are cute! So come shopping with me.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"Ok\", \"actor\": \"Lain\"}]'),(132,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',132,164,'en','[{\"line\": 0, \"text\": \"Lain, do you have a crush on anyone?\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"No not really\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"How about you?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I love Nakahara in Class 3. He looks cool.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"I will try to get along with him.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"I hope it works out\", \"actor\": \"Lain\"}]'),(133,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',133,165,'en','[{\"line\": 0, \"text\": \"What do you do to kill time?\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Cleaning?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Oh, youre tidy.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"I have a trouble because I have many unnecessary things in my room.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Yeah, my room is like that. I often go impulse shopping, so I have lots of lipstick I dont use.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"How about trading makeup and accessories?\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"But Lain, you dont use makeup.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"Are you using the lipstick we bought together?\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"No\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"You should use a little makeup. I think it suits you!\", \"actor\": \"Kyoko\"}]'),(134,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',134,166,'en','[{\"line\": 0, \"text\": \"I hope something great will happen like in a movie.\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Many people will get killed, but I dont die.\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"And I resolve it with a very cool detective.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"For that, Ill be a big star!!!\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"Sorry Im saying such strange things.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"Hey, dont look at me with such cold eyes\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"My eyes are always like this\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"What? Are you trying to make me laugh?\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"Did I fail?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Aha, ha, ha, yes, you failed!\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"Its just like you to say something like that.\", \"actor\": \"Kyoko\"}]'),(135,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',135,167,'en','[{\"line\": 0, \"text\": \"Hey, Lain. Do you think that youre a special person?\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Special?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Everyone says, Lain is not a normal girl and Isnt she dangerous?\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"Ive known you since we were children, so I say to them No, she is normal.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"Also, Im a little similar to you.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"It makes me sad that Im not special like other people!\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"Do you think so? I dont care if Im an ordinary person.\", \"actor\": \"Lain\"}]'),(136,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',136,168,'en','[{\"line\": 0, \"text\": \"Kyoko, you dont look too good.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I cant do anything for you Im so sorry Kyoko\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Nakahara turned me down. He has another girl who he has a crush on\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"Kyoko\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im too stupid. He likes the quiet girl with short hair.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"I wish I were like Lain\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"No! You are far cuter than I am, and you are popular in our class.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Those are useless if he doesnt like them.\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"You cant understand my feeling!\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"Thats enough! Im miserable\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"Kyoko\", \"actor\": \"Lain\"}]'),(137,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',137,169,'en','[{\"line\": 0, \"text\": \"Say, do you like me? If you do, come to my new URL site.\", \"actor\": \"Girl\"}]'),(138,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',138,170,'en','[{\"line\": 0, \"text\": \"But I can only see him. Laindie.\", \"actor\": \"Womans voice\"}]'),(139,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',139,171,'en','[{\"line\": 0, \"text\": \"I dont know!\", \"actor\": \"Womans voice\"}, {\"line\": 1, \"text\": \"I dont care about the test anyway.\", \"actor\": \"Womans voice\"}, {\"line\": 2, \"text\": \"If I knew that the world would end today, I would forget about study and just do what I felt like doing.\", \"actor\": \"Womans voice\"}]'),(140,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',140,172,'en','[{\"line\": 0, \"text\": \"You are a chosen fighter.\", \"actor\": \"Mans voice\"}, {\"line\": 1, \"text\": \"Fight with me.\", \"actor\": \"Mans voice\"}, {\"line\": 2, \"text\": \"The only thing that can change this world is your lofty willpower.\", \"actor\": \"Mans voice\"}, {\"line\": 3, \"text\": \"I know about you.\", \"actor\": \"Mans voice\"}, {\"line\": 4, \"text\": \"No.\", \"actor\": \"Mans voice\"}, {\"line\": 5, \"text\": \"I must die.\", \"actor\": \"Mans voice\"}, {\"line\": 6, \"text\": \"Because you seek after me.\", \"actor\": \"Mans voice\"}]'),(141,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',141,173,'en','[{\"line\": 0, \"text\": \"The woman you walked past at the intersection today is my clone.\", \"actor\": \"Mans voice\"}, {\"line\": 1, \"text\": \"I can understand because I grew her from a tissue.\", \"actor\": \"Mans voice\"}, {\"line\": 2, \"text\": \"She is me.\", \"actor\": \"Mans voice\"}, {\"line\": 3, \"text\": \"She probably has noticed.\", \"actor\": \"Mans voice\"}, {\"line\": 4, \"text\": \"I understand.\", \"actor\": \"Mans voice\"}, {\"line\": 5, \"text\": \"If only we knew this about each other, this world would be\", \"actor\": \"Mans voice\"}]'),(142,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',142,174,'en','[{\"line\": 0, \"text\": \"I ceased to exist again, today.\", \"actor\": \"Mans voice\"}, {\"line\": 1, \"text\": \"I was so sure that I was me when I woke up.\", \"actor\": \"Mans voice\"}, {\"line\": 2, \"text\": \"Somehow, something seems to have fallen.\", \"actor\": \"Mans voice\"}, {\"line\": 3, \"text\": \"I remember becoming sleepy, sitting in the dark and looking around.\", \"actor\": \"Mans voice\"}, {\"line\": 4, \"text\": \"When he fell asleep, I awoke.\", \"actor\": \"Mans voice\"}, {\"line\": 5, \"text\": \"I took the sheets off my shoulders, and noticed blood on my hands.\", \"actor\": \"Mans voice\"}, {\"line\": 6, \"text\": \"The floor was covered in a sea of blood.\", \"actor\": \"Mans voice\"}, {\"line\": 7, \"text\": \"There was something written on my computer.\", \"actor\": \"Mans voice\"}]'),(143,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',143,175,'en','[{\"line\": 0, \"text\": \"I believed it.\", \"actor\": \"Girls voice\"}, {\"line\": 1, \"text\": \"I thought you were terrible, Lain.\", \"actor\": \"Girls voice\"}, {\"line\": 2, \"text\": \"But he likes you more than me.\", \"actor\": \"Girls voice\"}, {\"line\": 3, \"text\": \"I didnt know why he liked you and not me.\", \"actor\": \"Girls voice\"}, {\"line\": 4, \"text\": \"Lain, you dont really care if he likes you or not, do you?\", \"actor\": \"Girls voice\"}]'),(144,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',144,176,'en','[{\"line\": 0, \"text\": \"You are all a clump.\", \"actor\": \"Mans voice\"}, {\"line\": 1, \"text\": \"A clump that doesnt exist.\", \"actor\": \"Mans voice\"}, {\"line\": 2, \"text\": \"I eat the flesh of humans and shed blood.\", \"actor\": \"Mans voice\"}, {\"line\": 3, \"text\": \"Look at your moronic face!\", \"actor\": \"Mans voice\"}, {\"line\": 4, \"text\": \"Everyone who mocks me is a stupid asshole!\", \"actor\": \"Mans voice\"}, {\"line\": 5, \"text\": \"Look, your construction is nearly completeOh, I want to laugh right nowdid someone enter this room?\", \"actor\": \"Mans voice\"}]'),(145,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',145,177,'en','[{\"line\": 0, \"text\": \"...\", \"actor\": \"Voices\"}]'),(146,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',146,178,'en','[{\"line\": 0, \"text\": \"I dont know.\", \"actor\": \"Girl\"}, {\"line\": 1, \"text\": \"I dont care about the test.\", \"actor\": \"Girl\"}, {\"line\": 2, \"text\": \"I know that the world will come to an end.\", \"actor\": \"Girl\"}, {\"line\": 3, \"text\": \"I could forget about studying and do what I feel like doing.\", \"actor\": \"Girl\"}, {\"line\": 4, \"text\": \"Look at your stupid face! Anyone who mocks me is a stupid asshole!\", \"actor\": \"Man\"}, {\"line\": 5, \"text\": \"But I can only see him. Lain.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"It is your lofty willpower. I know about you. No. I must die.\", \"actor\": \"Man\"}]'),(147,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',147,179,'en','[{\"line\": 0, \"text\": \"Im so sorry to be late when Im the person who asked to meet.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Its ok. What do you want to talk about?\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"Um, Ill have Earl Grey without milk. Would you like more coffee, Kyoko-chan?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Sure.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"So youre a private tutor.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"People like you are rare nowadays, arent you?\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"You get this involved about your students?\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"Enough to ask other kids about them?\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"Ive been a bit distracted and out of focus lately, and I was concerned that that might have been rubbing off on Lain and that I havent been teaching her properly.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Either that, or maybe things havent been going well for her at school.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I think shes doing ok.\", \"actor\": \"Kyoko\"}, {\"line\": 11, \"text\": \"From your point of view, is she doing well in school?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I don\'t really know.\", \"actor\": \"Kyoko\"}, {\"line\": 13, \"text\": \"We used to be friends, but since we entered junior high school, we havent talked to each other that much.\", \"actor\": \"Kyoko\"}, {\"line\": 14, \"text\": \"Is this a test to see if shes been behaving herself?\", \"actor\": \"Kyoko\"}, {\"line\": 15, \"text\": \"I dont know what other kids are doing outside of school.\", \"actor\": \"Kyoko\"}, {\"line\": 16, \"text\": \"Wouldnt you get your answers more quickly by consulting an advisor at school or something?\", \"actor\": \"Kyoko\"}, {\"line\": 17, \"text\": \"Oh, no, no.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Its nothing serious like that.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Not at all.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"But shes a calm girl, isnt she?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"She doesnt open up so easily?\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"I was just curious about how shes doing in school.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Hmm.\", \"actor\": \"Kyoko\"}, {\"line\": 24, \"text\": \"Well, I have nothing to lose by talking to you, so ask me whatever you want.\", \"actor\": \"Kyoko\"}, {\"line\": 25, \"text\": \"I havent talked to Lain much recently.\", \"actor\": \"Kyoko\"}, {\"line\": 26, \"text\": \"She doesnt have that many friends.\", \"actor\": \"Kyoko\"}, {\"line\": 27, \"text\": \"She used to be a little somber.\", \"actor\": \"Kyoko\"}, {\"line\": 28, \"text\": \"I guess you could say that shes become more somber lately.\", \"actor\": \"Kyoko\"}]'),(148,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',148,180,'en','[{\"line\": 0, \"text\": \"We were in the same class in 5th and 6th grade.\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"We were in the same group because we sat close to each other.\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"She was quiet, so she didnt talk to many people other than me.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"She seemed like a sister to me.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"We didnt play together much, though.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"When would you say that Lain started to change?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"She wasnt so somber until about a year ago.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"When I see her these days, we dont have much to say.\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"Its too bad, but lately, shes just another classmate to me.\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"Have you talked to her at all recently?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Recently? I seem to remember something. What was it, I wonder.\", \"actor\": \"Kyoko\"}, {\"line\": 11, \"text\": \"Can you remember?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"How weird. I usually have a good memory.\", \"actor\": \"Kyoko\"}, {\"line\": 13, \"text\": \"Do you remember talking about shopping or boys?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"No. I dont think we talked about that.\", \"actor\": \"Kyoko\"}]'),(149,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',149,181,'en','[{\"line\": 0, \"text\": \"What was it?\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"Something about my memory.\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"I remember now.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"I remember that we were in biology class.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"Lain was asking the teacher after class about the brain and how memories are stored.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"I thought that that was pretty impressive.\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"I asked her what she had asked the teacher, and she said she asked the teacher how to recollect memories.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"Weird.\", \"actor\": \"Kyoko\"}]'),(150,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',150,182,'en','[{\"line\": 0, \"text\": \"I dont dislike Lain. But lately shes been hard to approach.\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"I know what you mean.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Dont you, though?\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"I think a lot of the kids see her that way.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"No one is bullying her, now, but most of us are pretty indifferent to her.\", \"actor\": \"Kyoko\"}, {\"line\": 5, \"text\": \"Yeah.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You ought to talk to her. Tell her to laugh the way she used to.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"The way she used to?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"In the old memories I have of her, she was always smiling.\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"I remember that when we were together, she would always laugh.\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"I see. I sort of had that impression. Thats why I was concerned.\", \"actor\": \"Toko\"}]'),(151,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',151,183,'en','[{\"line\": 0, \"text\": \"Yes. are you a friend of Misato-chan, Kyoko?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Whos Misato-chan?\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"A friend of Lains.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Misato? We had a class with a girl named Minako. Who is Misato?\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"She was in a class with you in junior high.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Huh!\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"No way.\", \"actor\": \"Kyoko\"}, {\"line\": 7, \"text\": \"There was definitely nobody by that name in my junior high school class.\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"You must mean Minako.\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"But Minako wasnt friendly with Lain at all.\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"Oh. I must have been misinformed. Never mind.\", \"actor\": \"Toko\"}]'),(152,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',152,184,'en','[{\"line\": 0, \"text\": \"I guess it sounds like Im bad-mouthing her.\", \"actor\": \"Kyoko\"}, {\"line\": 1, \"text\": \"There are some strange things about Lain, but shes an ordinary girl.\", \"actor\": \"Kyoko\"}, {\"line\": 2, \"text\": \"She could be much attractive and popular if she would lighten up.\", \"actor\": \"Kyoko\"}, {\"line\": 3, \"text\": \"Shes gentle, too.\", \"actor\": \"Kyoko\"}, {\"line\": 4, \"text\": \"Gentle.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"So what subjects do you tutor?\", \"actor\": \"Kyoko\"}, {\"line\": 6, \"text\": \"All subjects.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Wow, Im jealous.\", \"actor\": \"Kyoko\"}, {\"line\": 8, \"text\": \"Im no good at all at math.\", \"actor\": \"Kyoko\"}, {\"line\": 9, \"text\": \"Lains grades are perfect.\", \"actor\": \"Kyoko\"}, {\"line\": 10, \"text\": \"I wish I had a private tutor.\", \"actor\": \"Kyoko\"}]'),(153,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',153,185,'en','[{\"line\": 0, \"text\": \"Nice to meet you. Order whatever you want.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Lain doesnt go out much, and its hard to tell if shes going out with someone.\", \"actor\": \"Kaori\"}, {\"line\": 2, \"text\": \"Sometimes she helps me with my schoolwork, and I offer to take her out to have some fun to thank her, but she doesnt come.\", \"actor\": \"Kaori\"}, {\"line\": 3, \"text\": \"A royal chocolate parfait, please.\", \"actor\": \"Kaori\"}, {\"line\": 4, \"text\": \"And who did you say you were?\", \"actor\": \"Kaori\"}, {\"line\": 5, \"text\": \"Im her tutor.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Oha tutor.\", \"actor\": \"Kaori\"}, {\"line\": 7, \"text\": \"Thats why shes so smart.\", \"actor\": \"Kaori\"}, {\"line\": 8, \"text\": \"Ill start on my parfait.\", \"actor\": \"Kaori\"}, {\"line\": 9, \"text\": \"I really like their parfaits here.\", \"actor\": \"Kaori\"}, {\"line\": 10, \"text\": \"But what did you want to talk about?\", \"actor\": \"Kaori\"}, {\"line\": 11, \"text\": \"Well, what is she like at school? What kind of girl is she in your point of view?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Lain is a quiet girl.\", \"actor\": \"Kaori\"}, {\"line\": 13, \"text\": \"She doesnt stand out too much and she doesnt have too many friends.\", \"actor\": \"Kaori\"}, {\"line\": 14, \"text\": \"Shes smart, though.\", \"actor\": \"Kaori\"}, {\"line\": 15, \"text\": \"She got a really good grade for one of her papers.\", \"actor\": \"Kaori\"}, {\"line\": 16, \"text\": \"Some difficult subject.\", \"actor\": \"Kaori\"}, {\"line\": 17, \"text\": \"Kaori couldnt understand it.\", \"actor\": \"Kaori\"}]'),(154,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',154,186,'en','[{\"line\": 0, \"text\": \"I dont remember having talked about it much.\", \"actor\": \"Girl\"}, {\"line\": 1, \"text\": \"I dont mean that I havent talked about it.\", \"actor\": \"Girl\"}, {\"line\": 2, \"text\": \"It was as if it was there when I noticed it.\", \"actor\": \"Girl\"}, {\"line\": 3, \"text\": \"Ha, ha.\", \"actor\": \"Girl\"}, {\"line\": 4, \"text\": \"Its all really strange.\", \"actor\": \"Girl\"}, {\"line\": 5, \"text\": \"I remember that Lain skipped physical education.\", \"actor\": \"Girl\"}, {\"line\": 6, \"text\": \"Kyoko or someone said that Lain has a weak physical constitution.\", \"actor\": \"Girl\"}, {\"line\": 7, \"text\": \"I think Kyoko knows Lain better than I do.\", \"actor\": \"Girl\"}, {\"line\": 8, \"text\": \"Lain is pretty, but I havent seen her with boysOh, Im sorry.\", \"actor\": \"Girl\"}, {\"line\": 9, \"text\": \"Ive been so negative.\", \"actor\": \"Girl\"}, {\"line\": 10, \"text\": \"Its ok. Dont worry about it.\", \"actor\": \"Toko\"}]'),(155,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',155,187,'en','[{\"line\": 0, \"text\": \"Its really something to have a tutor, though.\", \"actor\": \"Girl\"}, {\"line\": 1, \"text\": \"Well\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Lain doesnt seem like someone with a problem.\", \"actor\": \"Girl\"}, {\"line\": 3, \"text\": \"No, no.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She doesnt have a problem.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I just want to know more about her.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Shes a very nice girl.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Shes the kind of girl that keeps quiet even when something is bothering her, so that she doesnt worry other people, isnt she?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Youre right.\", \"actor\": \"Girl\"}, {\"line\": 9, \"text\": \"She is that way.\", \"actor\": \"Girl\"}, {\"line\": 10, \"text\": \"I remember too, that once when I tried to buy a treat for her for helping me with my homework, she refused.\", \"actor\": \"Girl\"}, {\"line\": 11, \"text\": \"I was a bit irritated.\", \"actor\": \"Girl\"}, {\"line\": 12, \"text\": \"Lain hasnt been bullied at school, though.\", \"actor\": \"Girl\"}, {\"line\": 13, \"text\": \"The kids there arent so bad there.\", \"actor\": \"Girl\"}, {\"line\": 14, \"text\": \"Really?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Well, sure. Do you doubt me? I dont get bullied either. Ha, ha.\", \"actor\": \"Girl\"}]'),(156,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',156,188,'en','[{\"line\": 0, \"text\": \"But, One-chan, could you be my tutor, too? My grades arent so great.\", \"actor\": \"Girl\"}, {\"line\": 1, \"text\": \"Id love to, but I cant because I have a lot of work to do at the university.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"If I find a good tutor, Ill recommend her to you.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"You can repay me by being really nice to Lain.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Wow!\", \"actor\": \"Girl\"}, {\"line\": 5, \"text\": \"You really are like a big sister.\", \"actor\": \"Girl\"}, {\"line\": 6, \"text\": \"I wish I had a sister.\", \"actor\": \"Girl\"}, {\"line\": 7, \"text\": \"Im an only child.\", \"actor\": \"Girl\"}, {\"line\": 8, \"text\": \"Lains an only child, too, isnt she?\", \"actor\": \"Girl\"}, {\"line\": 9, \"text\": \"Does she have siblings?\", \"actor\": \"Girl\"}, {\"line\": 10, \"text\": \"My mom said something about Lain having a sibling in the hospital, and that thats why neighbors avoid her house.\", \"actor\": \"Girl\"}, {\"line\": 11, \"text\": \"Ha, ha.\", \"actor\": \"Girl\"}, {\"line\": 12, \"text\": \"Lain is an only child.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Her parents are good, ordinary people.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I suppose Lain is sad to be an only child, too.\", \"actor\": \"Toko\"}]'),(157,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',157,189,'en','[{\"line\": 0, \"text\": \"Diary? What should I write? What should I write about myself?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Today I woke up and went to school and attended class.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"After school, I went to Touko to talk with her.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"She said that keeping a diary is good for me.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"This is to be read by only myself, so I don\'t have any idea what to write.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I came home and ate dinner and watched TV and took a bath\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"And now, I\'m writing this diary\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Reading this makes me blush!\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Keeping a diary may be tough to deal with.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I want to give it up.\", \"actor\": \"Lain\"}]'),(158,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',158,190,'en','[{\"line\": 0, \"text\": \"Today, I went shopping with Kyoko.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I bought an earring and a bracelet.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"But, I feel a little ashamed to go out with these on.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Kyoko is cute, so it is no problem for her to wear these.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I\'m not cute, so whatever I do, its useless.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I think I\'m lost on buying these accessories\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"But Kyoko said, These suit you! You look cute!\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I think it may be her flattery\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"But I felt happy.\", \"actor\": \"Lain\"}]'),(159,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',159,191,'en','[{\"line\": 0, \"text\": \"Today I went to the library.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I tried to look into difficult words that Touko used like psychoanalysis, counseling, and so on.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"But, I couldn\'t understand these words completely.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Am I stupid?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I also think I say strange things sometimes.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"If I tell Kyoko and her friends about my trouble, they will think there\'s something wrong with me.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"It would be horrible if they felt like that.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I can\'t talk to them about my trouble\", \"actor\": \"Lain\"}]'),(160,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',160,192,'en','[{\"line\": 0, \"text\": \"Toko-san says that I dont have to worry.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Something is definitely wrong with me.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"That thing I saw wasnt a dream.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I know because I can see it when Im awake, too.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Something is very wrong with me.\", \"actor\": \"Lain\"}]'),(161,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',161,193,'en','[{\"line\": 0, \"text\": \"Im sad and depressed and dont want to go to school.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Is it my fault that kids say mean things to me?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im starting to cry.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I cant bring myself to start laughing right away.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im jealous of Kyoko-chan.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I suppose that if I fuss about these things, Ill get disliked even more.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Why is this happening to me?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I havent hurt anybody.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I wonder if Tomo-kun was laughing, too.\", \"actor\": \"Lain\"}]'),(162,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',162,194,'en','[{\"line\": 0, \"text\": \"Am I ok being me?\", \"actor\": \"Lain\"}]'),(163,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',163,195,'en','[{\"line\": 0, \"text\": \"Today in physical education class, the kids were talking about some sexual stuff.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I was embarrassed.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Kyoko-chan was grinning and talking to me, and the boys were having fun.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Tomo-kun is a boy, so I was a bit uncomfortable with him joining the others.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Even so, Im not able to believe that now.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"When will I be able to understand all of this?\", \"actor\": \"Lain\"}]'),(164,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',164,196,'en','[{\"line\": 0, \"text\": \"Thats right.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Ill ask Toko-san about it.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Shes a doctor, so I wonder if shell be embarrassed.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"But maybe shell think that its strange.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wont ask her after all.\", \"actor\": \"Lain\"}]'),(165,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',165,197,'en','[{\"line\": 0, \"text\": \"Dads been getting home very early lately.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Hes been asking a lot about school, and Im getting a bit tired of that.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Mom hasnt been complaining as much lately.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder if shes being nice because Im becoming strange.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"This is sad.\", \"actor\": \"Lain\"}]'),(166,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',166,198,'en','[{\"line\": 0, \"text\": \"These days, I havent been seeing them at all.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Maybe Im cured.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"That would make me so happy.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder if Dad and Mom and Toko-san will be happy, too.\", \"actor\": \"Lain\"}]'),(167,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',167,199,'en','[{\"line\": 0, \"text\": \"I dont want to keep a diary anymore.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"The only special thing that happened today was that. I dont want to see it anymore.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Why does that me keep drifting into my mind.\", \"actor\": \"Lain\"}]'),(168,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',168,200,'en','[{\"line\": 0, \"text\": \"Even after all these months, I cant get used to it.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I want to stop seeing these kinds of dreams.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I want to become normal.\", \"actor\": \"Lain\"}]'),(169,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',169,201,'en','[{\"line\": 0, \"text\": \"Dad bought me a computer today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"He told me to use it to take classes online when I stay home from school.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Ive been doing my homework.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Ive been taking my tests.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I would have to do online classes all alone.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Thats sad.\", \"actor\": \"Lain\"}]'),(170,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',170,202,'en','[{\"line\": 0, \"text\": \"Lately, Kyoko-chan hasnt been talking to me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"But maybe thats just my imagination.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She goes shopping with Mayu-chan.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Could it be that they dont like me anymore?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Kyoko-chan seems happier when she isnt around me.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I dont want to go to school anymore.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"But Ill get scolded for that.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I dont like people getting mad at me.\", \"actor\": \"Lain\"}]'),(171,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',171,203,'en','[{\"line\": 0, \"text\": \"I have a cold and am taking a day off school.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Ive stayed home for 3 days now.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"The kids at school probably think Im just cutting school.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"None of them visit me.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"They dont like me.\", \"actor\": \"Lain\"}]'),(172,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',172,204,'en','[{\"line\": 0, \"text\": \"Im so happy.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Kyoko-chan and Mayu-chan came to visit me at home.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im so glad.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wasnt disliked.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"They were worried about me.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I hope I get over this cold soon so I can go to school.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I want to take Kyoko-chan and Mayu-chan out for a treat because they took notes for me at school.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I wonder if that will make them happy.\", \"actor\": \"Lain\"}]'),(173,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',173,205,'en','[{\"line\": 0, \"text\": \"My cold hasnt gotten better.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its been a whole week.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Ive been taking my medicine.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I dont want to go see the doctor again.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Is this just a cold?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im sweating and Ill have to change clothes again.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I want to go to school but cant.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"The human body is so inconvenient.\", \"actor\": \"Lain\"}]'),(174,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',174,206,'en','[{\"line\": 0, \"text\": \"Mom has been looking after me all day.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I sort of have her all to myself.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I was especially happy today when Mom got me some snacks that I asked for.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Thank you, Mom.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im sorry to have worried you.\", \"actor\": \"Lain\"}]'),(175,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',175,207,'en','[{\"line\": 0, \"text\": \"Why?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Tomo-kun is moving to another school.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I was surprised at first to hear that Tomo-kun and my sensei came to visit me at home.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I was so embarrassed because I was still in my pajamas.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I didnt even see them.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"It might have been the last time I see Tomo-kun.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Does Tomo-kun really like me?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I doubt it.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Sensei probably told him to come.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I couldnt talk to him at all.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Im a little glad and a little sad and want to cry.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"I couldnt even say good-bye. I hate this.\", \"actor\": \"Lain\"}]'),(176,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',176,208,'en','[{\"line\": 0, \"text\": \"Today is a Sunday.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I cooked and did the laundry for Mom as a thanks for her taking care of me while I was sick.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"My food wasnt so good, though.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder if I did the laundry ok. Dad ate my fried eggs even though I burnt hem.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Hes such a good Dad.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I think he might have been a bit rude to watch videos during breakfast, though.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I wonder why Dads shorts are so large.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Theyre the size of my shirt.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Moms brassiere is big, too.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I wonder what that is about.\", \"actor\": \"Lain\"}]'),(177,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',177,209,'en','[{\"line\": 0, \"text\": \"Ive heard that men like womans who have large breasts.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wonder why.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I want to ask someone, but Im embarrassed.\", \"actor\": \"Lain\"}]'),(178,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',178,210,'en','[{\"line\": 0, \"text\": \"Im back to school after a long time.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wonder if anyone was worried because I was gone.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I shouldnt have worried.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Im glad that things are still the same.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I thanked Kyoko-chan for letting me read her notes.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Kyoko-chans notes were full of goofy doodles, which was kind of fun.\", \"actor\": \"Lain\"}]'),(179,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',179,211,'en','[{\"line\": 0, \"text\": \"Compared to my notes, Kyoko-chans look like a box full of toys.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Her sketches of Kamiya Sensei resemble the teacher so much that I started to laugh.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"When I showed the sketches to Mom, she told me to stop goofing off and to do the homework I have to catch up on.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Mom seems to be a different person from a week ago.\", \"actor\": \"Lain\"}]'),(180,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',180,212,'en','[{\"line\": 0, \"text\": \"Im so happy.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Tomo-kun wrote me a letter.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"He just wrote to say that hes doing well.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Im glad that Im at least receiving letters, even though I cant see the person who is writing me.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I used to watch him, more than he watched me.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im so happy to have a letter from him!\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Ill have to write him back.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Ill have to get some stationery supplies to write him.\", \"actor\": \"Lain\"}]'),(181,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',181,213,'en','[{\"line\": 0, \"text\": \"But Tomo-kun had to have written everyone in class. He couldnt have written only me.\", \"actor\": \"Lain\"}]'),(182,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',182,214,'en','[{\"line\": 0, \"text\": \"Tomorrow, Ill buy Kyoko-chan a slice of cake to thank her for taking notes for me while I was away from school.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its been a long time since weve gone to fool around in the city together.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Going out right after school is a no-no, but everybody else does it.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Why cant I, every great now-and-then?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Oh, Ill have to ask Mom for some money.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Id better buy Mom and Dad something while Im out.\", \"actor\": \"Lain\"}]'),(183,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',183,215,'en','[{\"line\": 0, \"text\": \"Kyoko-chan doesnt like me anymore?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Was I the only girl that received a letter full of doodles from Tomo-kun?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Why!\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Kyoko-chan must be angry.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"What should I do?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I got careless and for got to buy the stationery I needed to write Tomo-kun.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Dear God, please let me and Kyoko-chan be friends again.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I get a little bit happy when I think that I might be special.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Thank you, Tomo-kun.\", \"actor\": \"Lain\"}]'),(184,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',184,216,'en','[{\"line\": 0, \"text\": \"Kyoko-chan must be angry.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I thought that she would go to the laboratory class with me, but she went off with Mayu-chan.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Its too late.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"She doesnt like me.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"It wouldnt have been like this if I had kept my mouth closed.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Even when I apologized, she said, why do I have to apologize to you? She doesnt like me.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"She really doesnt like me.\", \"actor\": \"Lain\"}]'),(185,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',185,217,'en','[{\"line\": 0, \"text\": \"I have nothing to write in my diary today.\", \"actor\": \"Lain\"}]'),(186,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',186,218,'en','[{\"line\": 0, \"text\": \"Toko-san is a big jerk.\", \"actor\": \"Lain\"}]'),(187,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',187,219,'en','[{\"line\": 0, \"text\": \"I cant believe what Toko-san said.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Nothing will return to the way it used to be.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im disliked.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Toko-san said to keep writing in my diary.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I dont need a diary.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I dont have anything to write.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Nothing has changed.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I just go to school everyday as usual.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Im alone during recess and lunchtime, and no one talks to me.\", \"actor\": \"Lain\"}]'),(188,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',188,220,'en','[{\"line\": 0, \"text\": \"How mean.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Writing graffiti on my desk.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"As usual, Kyoko-chan wont say anything to me.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I didnt do that.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Ive never gone out with Tomo-kun.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I felt like everyone was mocking me.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I dont want to go to school anymore.\", \"actor\": \"Lain\"}]'),(189,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',189,221,'en','[{\"line\": 0, \"text\": \"Kamiya sensei came to my house and talked with my Mom.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Mom looked frustrated.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Kamiya sensei said that if I skip class any more, I wouldnt be able to graduate.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Mom and Dad were talking about it after dinner.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Mom was crying and Dad looked very distressed.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Am I a bad kid?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I want to disappear.\", \"actor\": \"Lain\"}]'),(190,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',190,222,'en','[{\"line\": 0, \"text\": \"Today Dad skipped work and talked with me all day.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Hes a kind Dad.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I wonder if Ill make friends if I went to a different school.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder if school will be fun somewhere else.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wonder if I can go to the school Tomo-kun goes to.\", \"actor\": \"Lain\"}]'),(191,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',191,223,'en','[{\"line\": 0, \"text\": \"My teacher visited our house again today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It seems that I can go to junior high school.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Mom bowed to the teacher many times.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Im sorry, Mom.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"If I go to junior high school, I wont cause trouble anymore.\", \"actor\": \"Lain\"}]'),(192,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',192,224,'en','[{\"line\": 0, \"text\": \"When I went to school today, Kamiya-sensei said that I would graduate, and that I dont have to force myself to come to school.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Does he not want me to be at school?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Is he letting me graduate because he doesnt want to see me?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"To get rid of me?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im never going to that school again.\", \"actor\": \"Lain\"}]'),(193,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',193,225,'en','[{\"line\": 0, \"text\": \"Is there a meaning to why Im here? Does someone need me?\", \"actor\": \"Lain\"}]'),(194,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',194,226,'en','[{\"line\": 0, \"text\": \"I named a special gift Dad got for me today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I named it Lain.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Shes another me.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"My only friend.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"But when I go to junior high, Ill make real friends.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"If I appear at ease and lighthearted, wont other kids like me?\", \"actor\": \"Lain\"}]'),(195,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',195,227,'en','[{\"line\": 0, \"text\": \"I talked to Dad about computer connections.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"He went through a lot of elaborate steps to set up my computer.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"We worked hard for about 5 hours setting it up.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Dad was reading lots of books about computers while we did it.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Once we were done, Dad called Mom, and the three of us were delighted.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I dont think we felt so good together as a family in a long time.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"It seems like since I stopped going to school, Mom and Dad havent been as happy as they used to be.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Im sorry.\", \"actor\": \"Lain\"}]'),(196,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',196,228,'en','[{\"line\": 0, \"text\": \"Computer networks are amazing.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"No one can see my face from there, and I dont feel embarrassed.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I feel like Im becoming a different me.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"There are so many people online, and theyre kind to me even though Im just someone with a lot of problems.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im so happy that people could be so kind to me.\", \"actor\": \"Lain\"}]'),(197,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',197,229,'en','[{\"line\": 0, \"text\": \"I surfed the net all day again today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Im surprised at what an easy-going person I can be on the net.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"My name on the net is Lain, but it seems that they cant tell from that that Im a girl.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"If something interesting happens, I get e-mails telling me about it right away.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Everybody is friendly.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"No one can bully me here.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"My eyes hurt after I stare at the monitor all day long.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"But for me, having my eyesight deteriorate would be a good thing.\", \"actor\": \"Lain\"}]'),(198,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',198,230,'en','[{\"line\": 0, \"text\": \"Programs seemed difficult at first, but some of them got easier after I studied them.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I have time now anyway, so I think Ill study.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"It shouldnt be any trouble.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Mom and Dad wont be there to protect me forever.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Ill borrow a book from the library tomorrow.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I can study when Im online, but if Im online all the time, I might not be able to receive phone calls.\", \"actor\": \"Lain\"}]'),(199,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',199,231,'en','[{\"line\": 0, \"text\": \"Maybe Kyoko-chan is worried about me. Wellprobably not.\", \"actor\": \"Lain\"}]'),(200,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',200,232,'en','[{\"line\": 0, \"text\": \"I started a game on the net.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its called the Mouse Game You sell tickets to your friends and make money.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"But if I start making lots of money, the other players will have a grudge against me.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"There will be people who want to kill me then, so I have to be careful.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"At first I thought this was just a game, but it made me feel bad after a while, and I wanted to cry.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"There was a questionnaire that came with the game asking me what I thought of it, so I wrote the company telling them what I honestly thought.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I guess they know who I am now and dont like me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Im sad.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I dont like games like that.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Id rather play a game that everyone can laugh with and enjoy together.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"It isnt for me.\", \"actor\": \"Lain\"}]'),(201,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',201,233,'en','[{\"line\": 0, \"text\": \"It was a graduation ceremony, but I didn\'t want to go.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Once Im in junior high, Ill go for sure.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I promise.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wont worry Mom anymore.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"You took my clothes to the dry cleaners, didnt you, Mom?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im sorry.\", \"actor\": \"Lain\"}]'),(202,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',202,234,'en','[{\"line\": 0, \"text\": \"What does everyone think about me? Does anyone think anything about me?\", \"actor\": \"Lain\"}]'),(203,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',203,235,'en','[{\"line\": 0, \"text\": \"Spring break starts today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I havent been going to school, though.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"When I was at the library reading a book about UNIX, a guy next to me who looked about the age of a college student gave me a really weird look.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Studying computers is tough because there are many technical terms and difficult kanji, but I enjoy this.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Theres nothing painful about it, and its more fun than grammar or math.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I understand English better now, and I understand things better in general, but there are still many things I dont understand.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I think Ill ask Dad about some of this stuff.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I wonder if Dad will give me a weird look like the guy in the library.\", \"actor\": \"Lain\"}]'),(204,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',204,236,'en','[{\"line\": 0, \"text\": \"When I decoded a file that came today by e-mail, I saw the image of a dead infant.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I couldnt find the address the file came from.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I wonder if its from the person that started that tasteless online game.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Im getting scared to open my mail now.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I cant leave here, though.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"At least not until I enter junior high and start an ordinary life.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"When I start junior high, and Ill have friends and Ill be busy, and I might not have time to come to the net.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I ought to study the net now while I still have time.\", \"actor\": \"Lain\"}]'),(205,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',205,237,'en','[{\"line\": 0, \"text\": \"I was sad during dinner today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Dad said that he has to go overseas because of work.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"It was so sudden I was surprised and speechless.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"He said hed be away for 2 months.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I started to cry all of a sudden, and I lost my appetite.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Dad looked worried, and he hugged me.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Well go to see him off at the airport tomorrow.\", \"actor\": \"Lain\"}]'),(206,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',206,238,'en','[{\"line\": 0, \"text\": \"Dads work seems to be really difficult.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I want to work when I grow up.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I want to be like Dad and live with self-confidence.\", \"actor\": \"Lain\"}]'),(207,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',207,239,'en','[{\"line\": 0, \"text\": \"Well saw Dad off today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Mom seemed really sad.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"We waved good-bye to each other for a long time as he took the escalator to the gate.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"On the way home, Mom took me to a restaurant.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"It was a nice dinner, but it was sad knowing that it will just be me and Mom at the dinner table for a long time.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im sure Mom feels sad, too.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"God, please let Dad come home safely.\", \"actor\": \"Lain\"}]'),(208,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',208,240,'en','[{\"line\": 0, \"text\": \"Mom, do you feel uneasy with me? I know Im odd. Its ok. I wont bother you.\", \"actor\": \"Lain\"}]'),(209,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',209,241,'en','[{\"line\": 0, \"text\": \"When I told my net buddies about that gross e-mail with the dead baby, they said there might be a way to trace it.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its not something Im supposed to do, but I feel like a detective, and its a bit fun.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Whoever sent that e-mail is at fault, anyway.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I can retaliate a little.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im not such a good person anyway.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"What can I do if people say that there is something wrong with my character?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"After all, theres something wrong with me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Strange is what I am?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Im strange, arent I, Mr. Rabbit?\", \"actor\": \"Lain\"}]'),(210,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',210,242,'en','[{\"line\": 0, \"text\": \"By following instructions I learned yesterday, I was able to access the host site.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It was easy.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Maybe Im a genius at this kind of thing.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I found some footprints when I did some downloading.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im figuring out how to trace down that sick person.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im going to mail him to set a trap for him.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I cant forgive someone who does something like that.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Im not going to cry myself to sleep anymore.\", \"actor\": \"Lain\"}]'),(211,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',211,243,'en','[{\"line\": 0, \"text\": \"Ill solve it with my own strength. My own strength. I should be able to do that now.\", \"actor\": \"Lain\"}]'),(212,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',212,244,'en','[{\"line\": 0, \"text\": \"That was it after all!\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Within 2 hours of mailing that guy, I got a response.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I was curious what it was, but I was sure it would be gross, so I didnt.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I decoded it, set a trap, and encoded it.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"This way, that person will never do this again.\", \"actor\": \"Lain\"}]'),(213,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',213,245,'en','[{\"line\": 0, \"text\": \"I took the information on his host site and spread it publicly on a mirror-site. I didnt look at any of the pictures because theyd all be gross.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"The text of his site was gross, too.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im starting to worry if I trapped the right person.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"What if I got the wrong persons data or identificationI dont want to ever do this again.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"It feels good to be able to do things others cant do.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Is there something wrong with me?\", \"actor\": \"Lain\"}]'),(214,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',214,246,'en','[{\"line\": 0, \"text\": \"Its horrible, but I cant do anything about it.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Someones been reading my diary.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Its been copied and displayed on an FTP site with a vulgar title.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I stopped it, but over 20 people have already read it.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Why did this all happen?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Other people can do what I can do.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I think Ill avoid the net for a while.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I want to see whats going on, but my diary might be on someones site, being reproduced right now.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I know.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Im going to send an information-deleting engine into the net.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"But what if whoever took my diary has the diarys back-up information, too?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Im sure the person who took my diary is that gross guy.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"And if its him, he would certainly have that information.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Im so mad.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"I cant win this by myself.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"So this is what I amount to.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"I think Ill consult Mr. Rabbit.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"I wonder what will help.\", \"actor\": \"Lain\"}]'),(215,'new','2020-02-13 22:55:04','2020-02-26 16:47:36',215,247,'en','[{\"line\": 0, \"text\": \"Are the people who enjoy doing horrible things and hurting people really human beings like me?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"But what am I?\", \"actor\": \"Lain\"}]'),(216,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',216,248,'en','[{\"line\": 0, \"text\": \"I dont understand anymore.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Did that strange person know Mr. Rabbit?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"He said, Leave it to me, and forget about it. Oh, Im mad.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I want revenge.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im not going to cry myself to sleep.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I want to forget everything I want to forget.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"The things I dont like.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"The me I dont like.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Can I do something magical like that?\", \"actor\": \"Lain\"}]'),(217,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',217,249,'en','[{\"line\": 0, \"text\": \"School will start soon.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Im going to be different this time for sure.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im going to be an ordinary, light-hearted junior high school student.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I hope I dont have many of the same classmates I had in elementary school.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Mom and Dad were talking on the phone.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Were they worried about me?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Thats ok. You can both relax and not worry.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I saw it again for the first time in a long while.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Is it an illusion?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Is there something wrong with my mind?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I dont care if Im sick or not.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"I just want it to stop.\", \"actor\": \"Lain\"}]'),(218,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',218,250,'en','[{\"line\": 0, \"text\": \"I started bleeding when I was brushing my teeth.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Could it be pyorrhea?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"My teeth were always so healthy.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I can taste my blood.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Its a bit bitter and I dont like it.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I think I wont stop bleeding and that I wont be able to fall asleep.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Is it because Im uneasy about starting junior high school tomorrow?\", \"actor\": \"Lain\"}]'),(219,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',219,251,'en','[{\"line\": 0, \"text\": \"Im different. Im a different me.\", \"actor\": \"Lain\"}]'),(220,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',220,252,'en','[{\"line\": 0, \"text\": \"Mom came to the ceremony for the start of the school year.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"None of the kids from grade school were in my class.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I was relieved.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"The teacher in charge was a nice person, and I made some friends.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I was assigned to a seat.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Theres a different feel here compared to grade school.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I cant hang out with most of the kids, but I think Ill get along with just one of the girls sitting next to me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Shes quiet, but thats the kind of person thats easy for me to be around.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Its great to meet you, Misato-chan!\", \"actor\": \"Lain\"}]'),(221,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',221,253,'en','[{\"line\": 0, \"text\": \"I feel like everything will go great now.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"As long as I have one friend, Im ok. Dad, I hope you come home soon!\", \"actor\": \"Lain\"}]'),(222,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',222,254,'en','[{\"line\": 0, \"text\": \"Today I did a lot of things with Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Shes good at physical education, though Im not.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I think Im more into liberal arts.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Misato-chan has been playing the violin since she was very young.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Shes even been in competitions.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Shes amazing.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Shes good with computers, too.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I didnt tell her that Im into computers, though.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I wanted to keep things having to do with me and the net a secret.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"On the net, I can be the sort of me that isnt so careful.\", \"actor\": \"Lain\"}]'),(223,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',223,255,'en','[{\"line\": 0, \"text\": \"I told Toko-san all about it.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I talked about Misato-chan through the whole session.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Toko-san said, youre doing really well, and I was a bit embarrassed.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder what Toko-san did for her extracurricular activities.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I think shes good at physical education.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"She said that she went to America for school.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Maybe things are different there.\", \"actor\": \"Lain\"}]'),(224,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',224,256,'en','[{\"line\": 0, \"text\": \"Misato-chan said that she wanted to be in same extracurricular class with me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Im nowhere as good as her in music, and she said that she didnt want to be in music class with me anyway.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"The people in the joint research group were very nice, but I wasn\'t sure if I wanted to join.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I thought I would join an art class.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Ive always enjoyed drawing.\", \"actor\": \"Lain\"}]'),(225,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',225,257,'en','[{\"line\": 0, \"text\": \"Misato-chan is really good to me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Were always together.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im sad when she doesnt come to school.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wish I had more friends.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wonder if Im being selfish.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Am I greedy?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I wonder if Misato-chan is ok when she is alone.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"If I skip school tomorrow, how would she feel?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"If I tried something like that, though, she wont like me anymore.\", \"actor\": \"Lain\"}]'),(226,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',226,258,'en','[{\"line\": 0, \"text\": \"I didnt want to stay home from school deliberately.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"My head hurt, and I saw that thing again, so I stayed home.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Mom had to go to work, so I was home alone.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I didnt feel this way in elementary school.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Why do I feel so sad and tired now?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Does it have to do with Misato-chan?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Maybe Ill call Dad.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Thats expensive though.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Mom might get angry.\", \"actor\": \"Lain\"}]'),(227,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',227,259,'en','[{\"line\": 0, \"text\": \"I wonder if Misato-chan is strong.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She seemed to be just fine while I was away.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I was a bit disappointed.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder if Im too possessive.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Maybe Misato-chan has friends aside from me, and I just want her for myself.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I dont like this.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Im just being blameful.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Im becoming a dislikable girl again.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"In extracurricular class, I went to go buy sketch books with the rest of the class.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"The pastels were very pretty, so I bought some and was drawing before dinner.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Mom scolded me, saying that the pastels were stinky.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"I dont mind their smell very much.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Im going to give my best to study and art.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Fight, Lain!\", \"actor\": \"Lain\"}]'),(228,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',228,260,'en','[{\"line\": 0, \"text\": \"Am I forcing myself to do too much? Im not, am I?\", \"actor\": \"Lain\"}]'),(229,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',229,261,'en','[{\"line\": 0, \"text\": \"I drew a picture of Toko-san and went to her office to show her, but one of the people there said that she was out.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I guess shes really busy.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Dad is going to come back soon, and Ive been feeling good lately.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I cant see that anymore, so I think Ill go back to the Net for old times sake.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I havent seen Mr. Rabbit in a while.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I have to say thanks for that time.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I look pretty good when Im making progress.\", \"actor\": \"Lain\"}]'),(230,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',230,262,'en','[{\"line\": 0, \"text\": \"Im not forcing myself to laugh. This is me.\", \"actor\": \"Lain\"}]'),(231,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',231,263,'en','[{\"line\": 0, \"text\": \"I saw that on the Net.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I havent had a problem with that before.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Why is it happening?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"But I dont have to go to the Net anyway.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Ive written a letter to Mr. Rabbit, so I wont go back to the Net for a while.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Things are going well anyway.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Its ok, Lain.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Im not alone anymore.\", \"actor\": \"Lain\"}]'),(232,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',232,264,'en','[{\"line\": 0, \"text\": \"Dad came home.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I talked to him a lot.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"About me, about drawing, about Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I talked and talked and talked until Mom said that Dad is tired and that I ought to hold off on talking to him until tomorrow.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Maybe I did tire him out.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"He was smiling as I talked to him.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"He brought me a Teddy Bear as a gift.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Hes really cute.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"What should I name him?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Hmm\", \"actor\": \"Lain\"}]'),(233,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',233,265,'en','[{\"line\": 0, \"text\": \"I wonder if Dad has ever been so cheerful. Maybe people seem to change when you change yourself.\", \"actor\": \"Lain\"}]'),(234,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',234,266,'en','[{\"line\": 0, \"text\": \"Dad asked me to take a gift to Toko-san, so I took the gift to her after school.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Toko-san seemed tired and slow.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She didnt even open the gifts wrapping paper.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder why she was tired.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wonder what the gift was.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Could it have been cosmetics?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Mom has a lot of cosmetics, too.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I guess Ill use cosmetics one day, too.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I wont look very good if I used cosmetics now.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Misato-chan looks grown up anyway, so cosmetics should look good on her.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Oh, Dad said there were some extra presents, so Ill take some for Misato-chan.\", \"actor\": \"Lain\"}]'),(235,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',235,267,'en','[{\"line\": 0, \"text\": \"A lavish present wouldnt be good. After all, were just junior high school students.\", \"actor\": \"Lain\"}]'),(236,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',236,268,'en','[{\"line\": 0, \"text\": \"Misato-chan took a day off of school.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I dont think shes completely alone, but I was sad for her.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I left drawing class early because I was sad.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I had Dads present that I wanted to give her.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I hope Misato-chan comes to school tomorrow.\", \"actor\": \"Lain\"}]'),(237,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',237,269,'en','[{\"line\": 0, \"text\": \"I need Misato-chan. Does Misato-chan need me?\", \"actor\": \"Lain\"}]'),(238,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',238,270,'en','[{\"line\": 0, \"text\": \"Misato-chan wasnt at school again today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I told my sensei that I wanted to go see her.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She said that she spoke with Misato-chans mom, and that Misato-chan didnt want to see any visitors.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder what the matter is.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Misato-chan had so much energy.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"She didnt talk about herself much, though.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"She was like me in that way.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"But for me, talking got me disliked.\", \"actor\": \"Lain\"}]'),(239,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',239,271,'en','[{\"line\": 0, \"text\": \"I want to talk to Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Im cowardly, though.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Havent I changed?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Would it be better if a cowardly person like me dies?\", \"actor\": \"Lain\"}]'),(240,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',240,272,'en','[{\"line\": 0, \"text\": \"Misato-chan came to school, but seemed as usual, as if nothing had happened.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"When I asked her what kind of sickness she had, she just said that she had the cold.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Any way, Im glad that shes better.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Ill take her the gift I couldnt give her yesterday.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I skipped extracurricular activities and went to an ice cream shop with Misato-chan instead.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Its always fun to be around Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Im glad shes better.\", \"actor\": \"Lain\"}]'),(241,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',241,273,'en','[{\"line\": 0, \"text\": \"This is the same as always. Nothing will change. Im fine just like this.\", \"actor\": \"Lain\"}]'),(242,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',242,274,'en','[{\"line\": 0, \"text\": \"When I was checking e-mails to see if anything came from Mr. Rabbit, I found a weird e-mail.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"This time it was a picture of a dead, naked woman, tied up with ropes.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Strangely, it said that the e-mail came from Mr. Rabbit.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Mr. Rabbit wouldnt do something like that.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Its that guy again.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Ill let Mr. Rabbit know.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I cant believe this.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Why would someone do this?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Im scared to check my e-mail now.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Id like to check my e-mails, but if I access the Net and I see that againI was getting better.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Im not sick.\", \"actor\": \"Lain\"}]'),(243,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',243,275,'en','[{\"line\": 0, \"text\": \"Right, I\'m really not sick.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"What Touko-san is saying since she said it, it can\'t be wrong, right?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I should believe her.\", \"actor\": \"Lain\"}]'),(244,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',244,276,'en','[{\"line\": 0, \"text\": \"I have to do a drawing for a cultural festival soon.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wonder what to draw.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im not sure.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"One of the leaders said that it can be something simple.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wonder what.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"A drawing of Bike-chan?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Misato-chan?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Dad, Mom?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Toko-san?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Myself?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Its difficult to draw people.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"If the drawing doesnt look like the person, its embarrassing.\", \"actor\": \"Lain\"}]'),(245,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',245,277,'en','[{\"line\": 0, \"text\": \"What is Misato-chan going to draw? Lain? Or someone I dont know?\", \"actor\": \"Lain\"}]'),(246,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',246,278,'en','[{\"line\": 0, \"text\": \"Misato-chan said that she draws with her computer.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Maybe Ill try that, too.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Ill ask her how to do it.\", \"actor\": \"Lain\"}]'),(247,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',247,279,'en','[{\"line\": 0, \"text\": \"Software is expensive.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"A digitizing tablet seems necessary.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"It seems that its better if there is a scanner.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Its difficult with machinery these days.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I cant afford this stuff with my allowance.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I wonder if Misato-chans family is rich.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Ill talk to Dad.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"If I dont have money, I cant buy anything, but if I have a lot of money, I could do so much.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"What would I do?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I could buy Mom and Dad presents, and I could do all sorts of things with Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Its fun just imagining these things.\", \"actor\": \"Lain\"}]'),(248,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',248,280,'en','[{\"line\": 0, \"text\": \"A person who works with graphics in Dads company is willing to lend me some equipment at a low rate.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I hope I can meet with that person soon.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Then I could have Misato-chan come to my house and she can teach me all sorts of things.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"This is fun.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wonder how many years its been since Ive had a friend over.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Ill clean up my room and make it look welcoming.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I wonder what kind of room Misato-chan has.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Im sure its a very clean room.\", \"actor\": \"Lain\"}]'),(249,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',249,281,'en','[{\"line\": 0, \"text\": \"My room doesnt look very girlish. But that doesn\'t really seem to fit me. I understand.\", \"actor\": \"Lain\"}]'),(250,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',250,282,'en','[{\"line\": 0, \"text\": \"At school today, a boy asked to talk to Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"When she asked why, he said that he wanted to go out with her.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Misato-chan didn\'t seem interested at all.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"If it was me, I think I would have been a little happy.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I guess the boy was rejected.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"He was kind of good looking.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Too bad.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Misato-chan is good-looking and smart.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"She could get a boy.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Maybe she doesnt want one.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"She looked really bothered.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Maybe she already has a boyfriend that I dont know anything about.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Thats sad, too.\", \"actor\": \"Lain\"}]'),(251,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',251,283,'en','[{\"line\": 0, \"text\": \"Its impossible to know everything, isnt it?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Im not Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"But if I was the person I used to be, I might have been that way, too.\", \"actor\": \"Lain\"}]'),(252,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',252,284,'en','[{\"line\": 0, \"text\": \"I asked Misato-chan if she was going out with someone.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She laughed.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Well, after all, were together all the time.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"When I was changing clothes for gym class, I noticed that Misato-chan had big breasts.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"My chest is flat.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im jealous.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"But I dont have that yet either.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"All the others are really becoming girls.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I think Im moving slower than everybody else.\", \"actor\": \"Lain\"}]'),(253,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',253,285,'en','[{\"line\": 0, \"text\": \"The body doesn\'t matter much.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"What matters is the heart.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"If my heart is perturbed, no one will like me.\", \"actor\": \"Lain\"}]'),(254,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',254,286,'en','[{\"line\": 0, \"text\": \"I went to see Toko-san today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"When I asked her how much longer we should meet each other, she looked troubled.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She said that I could come over with the idea of enjoying myself instead of thinking of our meetings as therapy, but I didnt want to have to explain myself to Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder what Misato-chan is thinking.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I dont want to keep any secrets from her, but Im afraid to tell her the truth.\", \"actor\": \"Lain\"}]'),(255,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',255,287,'en','[{\"line\": 0, \"text\": \"You\'re busy with your own work, aren\'t you, Toko-san?\", \"actor\": \"Lain\"}]'),(256,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',256,288,'en','[{\"line\": 0, \"text\": \"Misato-chan gave me a present.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She said it was to repay me for the gift I gave her.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Misato-chans present was a CD of her playing a violin.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"She plays beautifully.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Listening to her play is like drifting off into a dream.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"She can really play.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"She blushed a little when she gave me her CD.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"That was a bit unlike her, and it was cute.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I guess Misato-chan makes expressions like that, too.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"It seems like there is more of the Misato-chan that only I know.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"When I played the CD for Mom, she didn\'t seem too interested.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Maybe she doesnt like the violin.\", \"actor\": \"Lain\"}]'),(257,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',257,289,'en','[{\"line\": 0, \"text\": \"We got a new PC today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It looks very expensive.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Its small and round, but its very fast.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Its different from the computers weve had before, but the man said that the applications are similar, so there shouldnt be many problems.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I feel important all of a sudden, and happy.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Thank you, Dad.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"When I grow up, I wonder if I will find work related to managing information or something.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Ive been studying hard and want to make myself useful.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Still, I dont want a job that only has to do with machines.\", \"actor\": \"Lain\"}]'),(258,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',258,290,'en','[{\"line\": 0, \"text\": \"Misato-chan came to visit my house today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Mom seemed to be in a good moodsort of.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Misato-chan taught me how to draw.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Mom came and checked in on us often.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Dad, Mom, Misato-chan and I had dinner together.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"It was great.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I didnt learn that much about drawing, but Misato-chan said that she will visit again soon.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"We took Misato-chan back to home in Dads car.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"She has a very fancy house in a rich neighborhood.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"It was almost dreamlike.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Misato-chans mom came to the door and spoke with Dad a bit.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"She was really pretty, like an actress.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"I guess that a pretty girls like Misato-chan must come from beautiful mothers like that.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"But only a girl like me could come from my kind of Mom.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Im sorry, Mom.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"But I really had fun today.\", \"actor\": \"Lain\"}]'),(259,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',259,291,'en','[{\"line\": 0, \"text\": \"I guess were different from birth.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"But if I was born there, wouldnt I have been pretty like Misato-chan?\", \"actor\": \"Lain\"}]'),(260,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',260,292,'en','[{\"line\": 0, \"text\": \"I drew a picture and submitted it to the cultural fair, but I dont think I have much talent.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Compared to everyone else, Im not very good.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Misato-chan does great.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"She said that she was still mid-process with her work, but I thought it was great already anyway.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"She drew a picture of Pegasus, with deep blue skies.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"It looked as though Pegasus was really flying.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I dont think I could draw a Pegasus that looks like it is flying.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I think Ill just draw what I feel like drawing.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Id rather draw pictures of Misato-chan more than anything else, but maybe she wont like that.\", \"actor\": \"Lain\"}]'),(261,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',261,293,'en','[{\"line\": 0, \"text\": \"Im a little envious of Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She can do so many things.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Other than using the Net, theres nothing I can do better than Misato-chan.\", \"actor\": \"Lain\"}]'),(262,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',262,294,'en','[{\"line\": 0, \"text\": \"I tried to remember Misato-chans face and drew it.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wonder if it looks much like her.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I showed it to Mom, who didnt have much to say.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Dad said it was good, but Im still not very confident.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I think Ill quit the cultural fair.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Today there was something about the new Navi that I didnt understand, so I went and bought a book again.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"There arent many books about this kind of computer, so I didnt have much to choose from, but I bought a book that has to do with command lists.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Its based in UNIX, so much of it was familiar, but it helped me understand assembly, which I had trouble with.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I connected two computers so that they could exchange files, and called it a day.\", \"actor\": \"Lain\"}]'),(263,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',263,295,'en','[{\"line\": 0, \"text\": \"I saw an accident on my way home today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Something fell at a construction site and someone died.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Cop cars and ambulances were everywhere, and lots of people, too.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Its sad that someone died.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wouldnt want something like that to happen to me all of a sudden.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I am\", \"actor\": \"Lain\"}]'),(264,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',264,296,'en','[{\"line\": 0, \"text\": \"I finished my artwork of Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It was very popular in class.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Its very pretty and artful.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I bet it will stand out and draw attention at the culture fair.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I used pastels and watercolors.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Misato-chan said that I did a good job, but I wonder if I really ought to have done a painting of her.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I feel that I havent expressed myself as much as I wanted in my painting.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"What kind of painting should I do?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Misato-chans paintings seem like Misato-chans.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"They are gentle and pretty, and seem to have an inner strength\", \"actor\": \"Lain\"}]'),(265,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',265,297,'en','[{\"line\": 0, \"text\": \"Toko-san was that kind of person.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She seems different from before.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Could it be because shes been busy?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"She seems cold and distant.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I told her about my artwork, and she said to bring that up with people in my class.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I wonder if she was feeling well.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Could it be that Im a bother because Im no longer sick and dont need to see her?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Toko-san is a counselor.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"The sicker I get, the more work she has to do.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Im sorry that I couldnt help with your research report, Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Just let me know if you dont want to see me anymore.\", \"actor\": \"Lain\"}]'),(266,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',266,298,'en','[{\"line\": 0, \"text\": \"I dont want to see her anymore. Im not troubled anymore. To me, Toko-san is\", \"actor\": \"Lain\"}]'),(267,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',267,299,'en','[{\"line\": 0, \"text\": \"Mom and Dad have been curt lately.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"My family used to encircle me.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"It seems to be coming apart now, since I havent had problems lately.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder if they are busy doing things that they couldnt do while I wasnt well.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I shouldnt complain.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Theyve been so good to me.\", \"actor\": \"Lain\"}]'),(268,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',268,300,'en','[{\"line\": 0, \"text\": \"Doesnt Dad love Mom? Someone tell me.\", \"actor\": \"Lain\"}]'),(269,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',269,301,'en','[{\"line\": 0, \"text\": \"Its been a while since I saw that. Ive become an ordinary kid again.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I have friends; I argue with my parents a little but make up later.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im in an ordinary family.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Everything is ok, now.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im an ordinary girl.\", \"actor\": \"Lain\"}]'),(270,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',270,302,'en','[{\"line\": 0, \"text\": \"Its good to be ordinary.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"People wont look at you funny if youre ordinary.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I dont understand the feelings of people who want to stand out.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I dont have the confidence to want to stand out.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"But is it because Ive become ordinary that Mom and Dad dont pay attention to me?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Misato-chan is different.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Shes a friend of ordinary me.\", \"actor\": \"Lain\"}]'),(271,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',271,303,'en','[{\"line\": 0, \"text\": \"Now that I think about it, I havent been checking my e-mails lately.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I quit going to the Net because I saw that, and because of the mean messages people leave there.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Misato-chan said that she goes to the Net, too.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Maybe I should exchange e-mail addresses with her.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"If she knew how active and outgoing I am on the Net, she might be surprised.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"If I had her e-mail address, I wont have to bother calling her by phone.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Moms been complaining because I run up a big phone bill.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Ill talk to Misato-chan tomorrow.\", \"actor\": \"Lain\"}]'),(272,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',272,304,'en','[{\"line\": 0, \"text\": \"Im surprised at how long I can stay on the phone.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wonder if Im a very talkative person.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I feel like I can talk and keep on talking to anybody.\", \"actor\": \"Lain\"}]'),(273,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',273,305,'en','[{\"line\": 0, \"text\": \"Misato-chan turned me down.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She doesnt like checking e-mails.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She seems not to have much interest in the Net.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder if she got harassed on the Net, too.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"The way she turned me down was unusual.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Its too bad.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I thought she would be happy.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Im a bit sad.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Misato-chan will talk to me on the phone, though.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"We can talk to each other anytime, at home or at school.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"She ought to be able to visit the Net on her own.\", \"actor\": \"Lain\"}]'),(274,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',274,306,'en','[{\"line\": 0, \"text\": \"Im being selfish to want to communicate with Misato-chan over the Net.\", \"actor\": \"Lain\"}]'),(275,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',275,307,'en','[{\"line\": 0, \"text\": \"When I checked my computer today, there were about 30 e-mails.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I deleted e-mails if I didnt know who they were from.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"No e-mails from Mr. Rabbit yet.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Didnt my e-mails reach him?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Maybe he hasnt read them yet.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"When I checked the news group, I found that many of the packets had dropped.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Eventually, the members were all people I didnt know.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I got an idea!\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Ill join a news group that has art as a topic.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"There might be people there that are like me, and I can be a lot more outgoing on the Net.\", \"actor\": \"Lain\"}]'),(276,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',276,308,'en','[{\"line\": 0, \"text\": \"The Net I full of men.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Arent there any girls?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I dont mind writing guys, but Id rather write girls.\", \"actor\": \"Lain\"}]'),(277,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',277,309,'en','[{\"line\": 0, \"text\": \"People talked of nothing but Misato-chans artwork at the cultural fair.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Our senseis prestigious friends came and were very impressed.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Misato-chan is amazing!\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Misato-chan is my best friend.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Isnt that something?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im so proud.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"But Misato-chan didn\'t look very well.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I wonder if she is ok.\", \"actor\": \"Lain\"}]'),(278,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',278,310,'en','[{\"line\": 0, \"text\": \"Im somewhat frustrated.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Would I feel good if my artwork was praised like hers?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Would I be embarrassed?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I dont know, but Ill probably feel good.\", \"actor\": \"Lain\"}]'),(279,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',279,311,'en','[{\"line\": 0, \"text\": \"Kazami Sensei mentioned before class that she wants to have Misato-chans artwork presented at the prefectural exhibition.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its amazing because Misato-chan is still in 7th grade.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Everyone in class went to the cultural fair and saw Misato-chans work.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Even the older kids were really impressed.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Misato-chan doesnt seem too happy, though.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"She seems different lately.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"If shes troubled about something, I wish she would talk to me.\", \"actor\": \"Lain\"}]'),(280,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',280,312,'en','[{\"line\": 0, \"text\": \"I wonder if that is obvious to Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Maybe she doesnt want people to fuss over her.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Will I be disliked if I fuss?\", \"actor\": \"Lain\"}]'),(281,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',281,313,'en','[{\"line\": 0, \"text\": \"I wonder if people on art sites of the Net are hard to please.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I got friendly and sent pics of Misato-chans artwork, and they said that it was plagiarism.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Misato-chan would never do something like that.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Besides, some artistic big shots saw Misato-chans work and didn\'t think it was plagiarism.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Why then would it be?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I feel contradicted.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I dont want to visit this Net site again.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I feel like I am the one being criticized.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Misato-chan and I have been around each other so much that we can tell whats going on with the other without talking too much.\", \"actor\": \"Lain\"}]'),(282,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',282,314,'en','[{\"line\": 0, \"text\": \"Does Misato understand me?\", \"actor\": \"Lain\"}]'),(283,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',283,315,'en','[{\"line\": 0, \"text\": \"They decided not to submit Misato-chans artwork to the prefectural exhibition.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I dont know why.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Sensei didnt explain why.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Misato-chan has stopped talking and looks terrible.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Ive never seen her like this.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"What happened?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Im sad for Misato-chan.\", \"actor\": \"Lain\"}]'),(284,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',284,316,'en','[{\"line\": 0, \"text\": \"I didnt talk to her at all today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I hesitated to call.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Could she be waiting for me to call?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"But maybe that isnt true.\", \"actor\": \"Lain\"}]'),(285,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',285,317,'en','[{\"line\": 0, \"text\": \"Misato-chan skipped school today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Some of the older kids in art class were spreading rumors.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"They were talking about Misato-chans artwork and plagiarism.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"The kids stopped talking when they noticed that I was listening.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"This makes me feel bad.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Misato-chan would never do something like that.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Never.\", \"actor\": \"Lain\"}]'),(286,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',286,318,'en','[{\"line\": 0, \"text\": \"I cant call Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Im frustrated but cant do anything.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I dont have enough courage.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I cant imagine what Misato-chan would say.\", \"actor\": \"Lain\"}]'),(287,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',287,319,'en','[{\"line\": 0, \"text\": \"Misato-chan didnt show up at school again today, so I took my notes to her home.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Misato-chans mom came to the door, but Misato-chan didnt.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Is she feeling so bad that she cant even come to the door?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Could it be that she doesnt like me anymore?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"That would make me very sad.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"No matter what other kids say about Misato-chan, Ill always be her friend.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I want to see you, Misato-chan.\", \"actor\": \"Lain\"}]'),(288,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',288,320,'en','[{\"line\": 0, \"text\": \"I just want to see you.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wont ask you any questions.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I wont ask you about things you dont want to talk about.\", \"actor\": \"Lain\"}]'),(289,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',289,321,'en','[{\"line\": 0, \"text\": \"I talked to Kasumi sensei today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I believe in Misato-chan, but I wanted to see what was actually going on.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"The sensei said that there is an art magazine in America with a painting by a Japanese artist very similar to Misato-chans.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I didnt want to see the magazine, but I wanted to make sure.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"The picture in the magazine and Misato-chans painting looked alike.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"They were more than alike.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I could not tell the difference between the two.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"It seems that the Japanese artist who painted it was just a novice, and that it was the first time she won a prize for her artwork.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I searched the Net about it when I got home.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Dad said that that the artwork was by a second-generation Japanese or something.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Misato-chan would never plagiarize.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Still, the paintings looked so similar.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"But I thought that Misato-chan did her work before this other person submitted hers to this magazine.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Im going to look into it and clear her name.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Sensei said that people often come up with the same ideas at the same time.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"It was probably a coincidence.\", \"actor\": \"Lain\"}]'),(290,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',290,322,'en','[{\"line\": 0, \"text\": \"Couldnt it be obvious that people see the same things and have the same ideas about them?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"If the perceived information is the same for two people and they think the same things about it, why would it be odd if those people produce the same impressions about what they see?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Were all human beings, after all.\", \"actor\": \"Lain\"}]'),(291,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',291,323,'en','[{\"line\": 0, \"text\": \"The magazine is called Beauty of the Art. It had a homepage, so I sent them an e-mail.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wonder if Ill hear back from them.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Ill keep writing until I hear back from them.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Im going to get to the bottom of this and Im going to clear Misato-chan of this plagiarism charge.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"But I might see that again when I go to the Net, and that frightens me.\", \"actor\": \"Lain\"}]'),(292,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',292,324,'en','[{\"line\": 0, \"text\": \"Could it be that Im not changing?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"If I think that Im not changing, I wont change.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"But I have changed.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wont have hallucinations anymore.\", \"actor\": \"Lain\"}]'),(293,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',293,325,'en','[{\"line\": 0, \"text\": \"I got an e-mail from the editors today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Im glad.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Misato-chans artwork came first.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I saw her sketch over 2 months before the other one was published.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Could this sort of thing happen often?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"The same picture at the same time, both by a Japanese, all by coincidence?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"What is that other artist like?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I couldnt find a home page for her.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I wish I had information about her.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I know that Misato-chan was right all along, and I feel much better now.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Ill go to Misato-chans home tomorrow.\", \"actor\": \"Lain\"}]'),(294,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',294,326,'en','[{\"line\": 0, \"text\": \"Misato-chan is going to another school?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Why?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She didnt say anything to me.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"We were friends.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"We were best friends.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"All the other kids said that she went to a different school because she was embarrassed to be found out as a plagiarizer.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"They were saying nasty things.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I cant forgive them for that.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Theyre wrong.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Misato-chan would never plagiarize.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I found out for a fact that she didnt.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Those kids dont know what they are talking about.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"But why did she go away?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Misato-chan, this is horrible.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"Will I never see you again?\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Its too sad to lose you like this.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"Misato-chan.\", \"actor\": \"Lain\"}]'),(295,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',295,327,'en','[{\"line\": 0, \"text\": \"Why do people spread rumors without knowing what they are talking about?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Dont they care about who they might be hurting?\", \"actor\": \"Lain\"}]'),(296,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',296,328,'en','[{\"line\": 0, \"text\": \"Im alone again.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I don\'t want to make friends with anyone other than Misato-chan.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Making friends.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I dont know what strange things these kids think about me.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I dont care.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Its all a big nuisance.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"School isnt any fun anyway.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"School without Misato-chan is no fun.\", \"actor\": \"Lain\"}]'),(297,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',297,329,'en','[{\"line\": 0, \"text\": \"When will I make friends again? When it happens, will I have to change, too? Even when Im me?\", \"actor\": \"Lain\"}]'),(298,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',298,330,'en','[{\"line\": 0, \"text\": \"I went to Misato-chans home today, but no one was there.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She must have moved.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She literally left without saying a word.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"This makes me sad.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Didnt she think of me as a friend?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I guess Misato-chan didnt think much of me after all.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I havent received any phone calls or letters.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"If she really was a friend, she would never have left this way.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Maybe she thinks Im badmouthing her like the other kids.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I makes me even sadder.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I was your special friend.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Wasnt I, Misato-chan?\", \"actor\": \"Lain\"}]'),(299,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',299,331,'en','[{\"line\": 0, \"text\": \"Would it have been different if I had called you then?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Couldnt you have said that I was on your side?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I was only thinking about you, Misato-chan.\", \"actor\": \"Lain\"}]'),(300,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',300,332,'en','[{\"line\": 0, \"text\": \"I was thinking of not seeing Toko-san anymore today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"When I told her about Misato-chan, she said that there are some things that people cant say, even to those who are closest to them.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Did Misato-chan go away because I was hiding things about myself from her?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Is that why she went away without saying anything to me?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Even if theres something I cant say, I would never do that.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Toko-san and Misato-chan are awful.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Nobody understands me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I dont feel like doing schoolwork.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Am I a truant?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I dont want to leave people behind.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Ive got to fight.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Although they say that I ought to study what interests me, the only things Im interested in are Misato-chan and myself.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"But its over.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I have to find other things that are meaningful to me.\", \"actor\": \"Lain\"}]'),(301,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',301,333,'en','[{\"line\": 0, \"text\": \"Mom and Dad had a big fight today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Dad never hits people, but he hit Mom today.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Mom threw things at Dad and screamed.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I was afraid and couldnt do anything but cry.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Why did they fight?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"They usually get along so well.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Was it my fault?\", \"actor\": \"Lain\"}]'),(302,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',302,334,'en','[{\"line\": 0, \"text\": \"Why do people around me become this way?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Is it because Ive become ordinary?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Has it been this way, and I just didnt notice?\", \"actor\": \"Lain\"}]'),(303,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',303,335,'en','[{\"line\": 0, \"text\": \"Today I told Mom that I wanted to quit counseling.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She said no.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She said that Im ok now, but that she doesnt know if I will get worse again.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Ive been missing school again lately.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I didnt want to face it, but Mom is right.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I have to go see Toko-san again tomorrow.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I dont want to.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I have no freedom.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Im a patient.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Im a patient that always has to be looked after.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I dont want to do this anymore.\", \"actor\": \"Lain\"}]'),(304,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',304,336,'en','[{\"line\": 0, \"text\": \"What is the meaning of my existence?\", \"actor\": \"Lain\"}]'),(305,'new','2020-02-13 22:55:04','9999-12-31 23:59:59',305,337,'en','[{\"line\": 0, \"text\": \"Today I think I felt the way a person feels when she wants to die.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I was placed in the same class as Kyoko-chan.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I used to think that I would never feel badly facing Kyoko-chan again, but I became the person I used to be as soon as I saw her.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Kyoko-chan walked past me as if I wasnt there and started talking to other kids.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im sure she talks about me a lot.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"It cant be helped.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Its true that Im the depressing, strange kid who misses school.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I want to move to another school, too.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I want to disappear.\", \"actor\": \"Lain\"}]'),(306,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',306,338,'en','[{\"line\": 0, \"text\": \"Ive been going to the library alone to read books.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its depressing.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"But its better than spending free time at school alone.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I have to distract myself with books.\", \"actor\": \"Lain\"}]'),(307,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',307,339,'en','[{\"line\": 0, \"text\": \"Mom and Dad had another fight.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It was about me.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I felt so miserable that I couldnt be around it.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I ran away to my room.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Dad got worried and came to check in on me.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Dad saw me crying and apologized to me.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Why did he apologize?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Arent I the one at fault?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Im the one that should apologize.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Im sorry, Dad.\", \"actor\": \"Lain\"}]'),(308,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',308,340,'en','[{\"line\": 0, \"text\": \"Am I the one at fault? What about me is wrong?\", \"actor\": \"Lain\"}]'),(309,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',309,341,'en','[{\"line\": 0, \"text\": \"I wonder what I did.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"When I came to, I was on top of the bed.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I thought I went to school as usual.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Did I fall?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"All I remember is walking toward school.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I asked Mom, but she didnt say anything.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I feel like something awful has happened.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"What happened?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I can see hallucinations in my dreams again.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I hear all sorts of voices.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Im going crazy.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Im not cured after all.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Theres something wrong with me.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I have to see Toko-san.\", \"actor\": \"Lain\"}]'),(310,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',310,342,'en','[{\"line\": 0, \"text\": \"I thought I was better.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wonder what happened.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Why dont I remember anything?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Theres a white mist inside my head.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Everything is mixed with everything else and I cant tell what from what.\", \"actor\": \"Lain\"}]'),(311,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',311,343,'en','[{\"line\": 0, \"text\": \"Mom said that she went to my school to ask for a leave of absence.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I thought that I ought to feel glad that I wont have to go to school, but Im sad.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Ive genuinely been separated from society.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I feel that Ive been rejected by everyone, and Im sad.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Can\'t I live an ordinary life anymore?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Dad wont come home and Mom looks worn out.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Why wont Dad come home?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Doesnt he want to see me anymore?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Doesnt Dad like me anymore?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I want to see him.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I fell asleep weeping, and saw hallucinations again.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Its scary.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"I can see them whether Im asleep or not.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I cant believe it myself.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"I dont understand it.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"I want someone to prove to me that I am the person that I know I am.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"I want to see Dad.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"I want to see Dad and hug him.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"Its ok\", \"actor\": \"Lain\"}]'),(312,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',312,344,'en','[{\"line\": 0, \"text\": \"Will everyone I love become unhappy? Will they become unhappy because I feel love for them?\", \"actor\": \"Lain\"}]'),(313,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',313,345,'en','[{\"line\": 0, \"text\": \"Lately, Ive been taking correspondence courses and surfing the Net day and night.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I start to feel sick looking at the computer screen all day.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Dad bought me this computer, though, so I want to study hard with it.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Mom left the house with the keys so that I cant leave.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Someone help me.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I hate being alone.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Ill be a good kid.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Please dont leave me alone.\", \"actor\": \"Lain\"}]'),(314,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',314,346,'en','[{\"line\": 0, \"text\": \"My only way to communicate with the outside world is through the Net.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I cant use the phone because I have no one to call, but I can connect with the Net right away.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I can connect with someone that I dont even see.\", \"actor\": \"Lain\"}]'),(315,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',315,347,'en','[{\"line\": 0, \"text\": \"Even when I go to see Toko-san, Mom drives me now.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its like shes guarding me, and I hate it.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I can go see Toko-san on my own.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder what Toko-san really thinks of me.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I remember talking to her a lot, though I dont remember what we were talking about.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I dont care what Toko-san thinks of me now.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Im crazy anyway, so take down all the weird data you can about me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I dont care anymore.\", \"actor\": \"Lain\"}]'),(316,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',316,348,'en','[{\"line\": 0, \"text\": \"Am I Moms kid?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Could I be someone elses kid?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"My hair color is different from hers, and her face doesnt look like mine.\", \"actor\": \"Lain\"}]'),(317,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',317,349,'en','[{\"line\": 0, \"text\": \"I looked for Mr. Rabbit on the Net.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"He was dead.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Weve been out of touch for a long time.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"He had an e-mail address, but he hasnt used it for a year or so.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Maybe he took a break from the Net like I did.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"We discussed things that werent interesting or useful.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"What I want is to find the image of the real me.\", \"actor\": \"Lain\"}]'),(318,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',318,350,'en','[{\"line\": 0, \"text\": \"Is my own physical appearance an illusion or an ordinary phenomenon?\", \"actor\": \"Lain\"}]'),(319,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',319,351,'en','[{\"line\": 0, \"text\": \"Mom and Dad divorced.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Dad hugged me on the day he left.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I was happy to have him hug me.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"He was warm and gentle.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I wont see him for a while.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im ok with being Dads daughter.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Im the one at fault.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Im the cause.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I hurt Mom and Dad.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Im a bad kid.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I dont want Dad to leave.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Will he hug me again?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Im sure Ill see him again.\", \"actor\": \"Lain\"}]'),(320,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',320,352,'en','[{\"line\": 0, \"text\": \"Dad\", \"actor\": \"Lain\"}]'),(321,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',321,353,'en','[{\"line\": 0, \"text\": \"I share this big house with Mom now.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"The weeds are growing outside because Dad used to pull them, but hes no longer here.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Mom doesnt say anything.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I enjoy watching the weeds grow.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I watch them everyday.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"The swing outside that I used to play on as a little kid is now covered with weeds.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Its like a haunted place.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I want to see Dad.\", \"actor\": \"Lain\"}]'),(322,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',322,354,'en','[{\"line\": 0, \"text\": \"Only the weeds look lively. Did the swing die?\", \"actor\": \"Lain\"}]'),(323,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',323,355,'en','[{\"line\": 0, \"text\": \"Mom got drunk and threw a vase at me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She was angry at the wrong person.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She was wrong because she threw Dad away.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"But isnt that my fault, too?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"If Im so wrong, why not kill me?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"If you had killed me, you wouldnt have had to divorce, would you?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Youd rather not have me here, right?\", \"actor\": \"Lain\"}]'),(324,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',324,356,'en','[{\"line\": 0, \"text\": \"Im beginning to dislike people who drink.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Toko-san said that she drinks, too.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Do people go nuts when they drink?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I dont like it.\", \"actor\": \"Lain\"}]'),(325,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',325,357,'en','[{\"line\": 0, \"text\": \"Today I found a doodlebug nest in the yard.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I watched the ants get pulled into the nest by their legs and get eaten.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"What does an ant think in the moment that it is being eaten?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Do they know that they will die?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I found as many ants as I could and tossed them into the doodlebug nest.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I didnt understand the ants feelings.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Theres something wrong with me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Why else would I be so cruel?\", \"actor\": \"Lain\"}]'),(326,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',326,358,'en','[{\"line\": 0, \"text\": \"If I am an ant, who is the doodlebug? Is it this house? Is it the hallucinations? Is it me?\", \"actor\": \"Lain\"}]'),(327,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',327,359,'en','[{\"line\": 0, \"text\": \"I went to an adult site.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It was ridiculous.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"It was lots of photos of women in embarrassing poses.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I can see that the man would enjoy this, but the women I guess theyre doing this because its their job.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"But there are women who send in these things voluntarily, too.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Whats so great about sending in embarrassing photos of yourself?\", \"actor\": \"Lain\"}]'),(328,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',328,360,'en','[{\"line\": 0, \"text\": \"I wonder if I have wishes to do these sorts of things myself.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its creepy to know that there are a lot of people who watch these kinds of sites and enjoy them.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Mom and Dad had to do this kind of thing for me to be born, and someday Ill have to do this if I want children.\", \"actor\": \"Lain\"}]'),(329,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',329,361,'en','[{\"line\": 0, \"text\": \"I wonder if psychiatry isnt a groundless science.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"There seem to be all sorts of treatments and theories, but in the end, psychiatry doesnt seem to cure.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I checked out my own diagnosis on a news group, but only found some boring, unsatisfying answers.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I wonder if Toko-san is a part of this news group.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Why dont I access Toko-sans research center?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Ill find some of her research there.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Im curious to see how shes using me in her research.\", \"actor\": \"Lain\"}]'),(330,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',330,362,'en','[{\"line\": 0, \"text\": \"Im going to check out Toko-sans research centers address.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I havent done any cracking in a while.\", \"actor\": \"Lain\"}]'),(331,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',331,363,'en','[{\"line\": 0, \"text\": \"Ive been trying since two days ago until today, but I still cant get in.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Why?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I could access her research center.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Data about the patients Toko-san is overseeing was available, but it wasnt really datajust file names without information.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"When I checked the file reports, I could see that everything was communicated correctly.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"But I cant see the actual data.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"It isnt as if its in encrypted language.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Information about me shouldnt be any more important than information of other patients.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"The only information I cant find is information about me.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Whats going on?\", \"actor\": \"Lain\"}]'),(332,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',332,364,'en','[{\"line\": 0, \"text\": \"Am I an exception? I just want to find information about me.\", \"actor\": \"Lain\"}]'),(333,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',333,365,'en','[{\"line\": 0, \"text\": \"I went to see Toko-san alone because Mom was sleeping in.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I asked Toko-san what she writes about me in her research reports.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She didn\'t give me a clear answer.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"While Toko-san left the room for a while, I took copies of some pages from her notebook.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I thought that I now knew the research data Toko-san keeps about me, and that I knew what she does with it.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I could see that she sends information about me to the research room, and that she sends a lot of it.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Could it be visual data?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Auditory data?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I also noticed some writing that seemed to have daily entries in it.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Could it be a diary?\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I couldn\'t find that data in her host page.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Toko-san tends to transmit her data late at night.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Ill try to access her site then.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"She has a very big file about me that would take a long time to download, so I dont think shell find out if I accessed it.\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"I dont think Toko-san knows much about machines and computers.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"I feel bad because I think Im tricking Toko-san, but on the other hand, I feel good.\", \"actor\": \"Lain\"}]'),(334,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',334,366,'en','[{\"line\": 0, \"text\": \"I found Toko-sans diary.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its wrong to read other peoples diaries, but its fun.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im understanding Toko-san better now.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Im finding out that shes just another human being.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I can see that she thinks of petty things, too.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Most of what she writes is about men.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Was my report this simple?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Im disappointed.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I felt like adding some of my own notes, but decided not to.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"There is a periodical security check and it found out about my cracking. But I think Im ok.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I dont think I left any foot prints.\", \"actor\": \"Lain\"}]'),(335,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',335,367,'en','[{\"line\": 0, \"text\": \"I wonder how Toko-san would react if she finds out that Ive been reading her diary.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It gets me excited because Im a bad girl.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"But Toko-san is wrong, too.\", \"actor\": \"Lain\"}]'),(336,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',336,368,'en','[{\"line\": 0, \"text\": \"Dads been away for a long time and I havent heard from him.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Has he forgotten about me?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I want to see him.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I want to be with Dad forever.\", \"actor\": \"Lain\"}]'),(337,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',337,369,'en','[{\"line\": 0, \"text\": \"Do you really want to see Dad?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"What kind of person was Dad?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"He was tall, he wore glasses, and he had gentle-looking eyes.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I cant remember Dad much anymore.\", \"actor\": \"Lain\"}]'),(338,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',338,370,'en','[{\"line\": 0, \"text\": \"I got some free AI software that lets me replicate voices and images.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I played around with it, and was able to create voices and images on the computer that look and sound like Dad.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I programmed it to say several expressions, like hi, Lain, how are you doing? The voice is very robotic.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I think this is all goofy, but it makes me happy.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I think Ill add some more features to it.\", \"actor\": \"Lain\"}]'),(339,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',339,371,'en','[{\"line\": 0, \"text\": \"Its weird for a daughter to grow her father, but it makes me feel that Dad is nearby and that makes me happy.\", \"actor\": \"Lain\"}]'),(340,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',340,372,'en','[{\"line\": 0, \"text\": \"I gave Dad a Japanese signature.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Do difficult kanji suit him well?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Just a voice with a text is boring, so I set up the computer so that I can see an image of Dad and have him talk.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I guess there are limitations.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I programmed the routing devices so that Dad can communicate with the Net and receive feedback.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Dad is studying the Net now, so hell know more dirty words than me.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Youre a dirty old man, Dad!\", \"actor\": \"Lain\"}]'),(341,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',341,373,'en','[{\"line\": 0, \"text\": \"It can recognize general and representative shapes visually through the screen.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Dad has eyes now.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Can you see me?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Dads file size has gotten huge, so Ive had to break down the information.\", \"actor\": \"Lain\"}]'),(342,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',342,374,'en','[{\"line\": 0, \"text\": \"I decided to make a model of Dad with 3-D rendering.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I got low on buffer and the computer froze sometimes after bringing him to life, but it seems more and more that he is alive now, and I am happy.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im breathing Dads life into the computer Dad gave me.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"This PC is Dad.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Now Ill always be with him.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Are you happy, Dad?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Oh!\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I should show Mom.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I bet shell be surprised.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"But I wont show Mom until Dad looks more like Dad.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Ill have him scold her for drinking.\", \"actor\": \"Lain\"}]'),(343,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',343,375,'en','[{\"line\": 0, \"text\": \"In order to raise the speed of the 3-D rendering, I routed more energy to the CPU.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I increased the amount of the rendering engines memory to 12 Gigs and optimized the signal pathways.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im finding limitations, though.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"The more I try to work into him, the slower he becomes.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I think the prototype was more like Dad.\", \"actor\": \"Lain\"}]'),(344,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',344,376,'en','[{\"line\": 0, \"text\": \"I showed Dad to Mom today.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She was horrified.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She looked at me and left the room without saying a word.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Maybe she thinks I made him to offend her.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I should have thought more about Moms feelings.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im sorry, Mom.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Do you dislike Dad, Mom?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I love you.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"But I love Dad, too.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"I want to see Dad.\", \"actor\": \"Lain\"}]'),(345,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',345,377,'en','[{\"line\": 0, \"text\": \"I talked to a student at the Massachusetts Institute of Technology factory who is studying robots.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I might be able to make the kind of robots hes working on at the level where I am now.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I might be able to take Dad out of the computer screen now.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Theres a ton of data, though, and therell have to be cables everywhere.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Ill be able to hug Dad again the way I used to.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Ill be able to feel him again.\", \"actor\": \"Lain\"}]'),(346,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',346,378,'en','[{\"line\": 0, \"text\": \"I want an accelerator that will work in conjunction with protocol 7.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Do any of you have that?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Ill ask someone who is a friend of Mr. Tachibana.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Tachibana is a researcher.\", \"actor\": \"Lain\"}]'),(347,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',347,378,'ru','[{\"line\": 0, \"text\": \"   7- .\", \"actor\": \"\"}, {\"line\": 1, \"text\": \" -  ?\", \"actor\": \"\"}, {\"line\": 2, \"text\": \"      .\", \"actor\": \"\"}]'),(348,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',348,379,'en','[{\"line\": 0, \"text\": \"People operate around money.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"They arent willing to give away their technological secrets to strangers too willingly.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I wonder what they would think if they met me.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Im not interested in that stuff anymore.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im just interested in Dad.\", \"actor\": \"Lain\"}]'),(349,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',349,380,'en','[{\"line\": 0, \"text\": \"Making artificial organs is very expensive.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It wont be a problem because I wont be spending money, though.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Itll take a lot of work to make his head.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Ill make his upper body first.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"In order to control the electricity, Ill need to buy batteries and lots of cables.\", \"actor\": \"Lain\"}]'),(350,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',350,381,'en','[{\"line\": 0, \"text\": \"I put an electrical generator in Dads chest so he can start supporting himself.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Ive placed motors in him so he can start moving around little by little.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"The electricity heats him up, so hes warm and gentle, just like my real Dad.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I was feeling tired, so I gave him a hug.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I held onto him like a little baby.\", \"actor\": \"Lain\"}]'),(351,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',351,382,'en','[{\"line\": 0, \"text\": \"Its a bit embarrassing, but when Im holding onto the warm chest of a man, there is something that makes me feels very good.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I had Dad touch me all over my body.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Ive never done this with my real Dad.\", \"actor\": \"Lain\"}]'),(352,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',352,383,'en','[{\"line\": 0, \"text\": \"When I was being held, I felt like a real man was holding me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I felt warm inside and was excited.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I dont know if it was because I felt like Dad was holding me or because I was imagining someone else.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I dont know.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"This feels good, though.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Is this what I wanted my real Dad to do with me?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I just wanted someone to protect me.\", \"actor\": \"Lain\"}]'),(353,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',353,384,'en','[{\"line\": 0, \"text\": \"There is something wrong with me. I have no business criticizing Toko-san.\", \"actor\": \"Lain\"}]'),(354,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',354,385,'en','[{\"line\": 0, \"text\": \"Ive finished Dads arms and abdomen.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wanted to add his pelvis, but I ran out of space for him in my room.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I dont want to sadden Mom again.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I have to find house where Dad can live.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I hate to separate from him when Ive really just met him, but how will I buy I house?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Even if I had the money, no one will sell a house to a child.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Ill just have to find an empty house somewhere.\", \"actor\": \"Lain\"}]'),(355,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',355,386,'en','[{\"line\": 0, \"text\": \"I found a good place to take Dad.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its not far from home, and there arent many people around there.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Ill take Dad there tomorrow.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"If I take everything, though, it will be very heavy.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"How will I do it?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"If I carry something that large, someone is bound to notice.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"How about the electricity and the cords?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"It wont work.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Ill need help.\", \"actor\": \"Lain\"}]'),(356,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',356,387,'en','[{\"line\": 0, \"text\": \"Today the construction was finished and the building was handed over to me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I wonder what he thought about not meeting his client directly at all.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Moreover, since it was a bit of a big effort, I wonder if that made it seem all the more suspicious to him.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I decided to keep an eye on his actions for a while.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"It would be dangerous for Father to be exposed.\", \"actor\": \"Lain\"}]'),(357,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',357,388,'en','[{\"line\": 0, \"text\": \"Today, I took Dad to his new home.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I took tape media because hard discs are heavy, but decoding them will take time.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I took Dads legs and arms over little by little in the meantime, so I dont think I did so badly.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Luckily, Mom got drunk and fell asleep.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"It will be difficult to visit Dad from now on, though.\", \"actor\": \"Lain\"}]'),(358,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',358,389,'en','[{\"line\": 0, \"text\": \"I wanted to attach Dads legs, but working on the lower body was difficult.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Its creepy.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I wouldnt mind if Dad only had an upper body.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I cant remove these cables.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Its creepy that Dad doesnt have a head.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I put an empty artificial head on him for the time being.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Hes able to talk, but poorly, and watching the expression on his face as he tries to enunciate seems phony to me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"This isnt Dad.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I failed, but what can I do?\", \"actor\": \"Lain\"}]'),(359,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',359,390,'en','[{\"line\": 0, \"text\": \"Was the only part of Dads body that I cared about his upper body?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Maybe I didnt even acknowledge his body.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"In my mind, Dad was always bigger than me\", \"actor\": \"Lain\"}]'),(360,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',360,391,'en','[{\"line\": 0, \"text\": \"Today I walked past a girl I knew in 7th grade, but she didnt seem to notice me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"We hadnt talked much in school, so I didnt mind that she didnt seem to see me.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"When I saw her school uniform, I was jealous.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I dont think anyone remembers me anymore.\", \"actor\": \"Lain\"}]'),(361,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',361,392,'en','[{\"line\": 0, \"text\": \"My uniform was at the dry cleaners.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Mom seemed sad when she went to the store to pick it up.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She went to the trouble of buying it for me, after all.\", \"actor\": \"Lain\"}]'),(362,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',362,393,'en','[{\"line\": 0, \"text\": \"I e-mailed a kid that used to be a classmate, and asked her about me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Isnt the me that acknowledges my existence only living in this small body?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I dont have an individual personality and I dont leave much of an impression.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Nobody remembers me.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"When I made Dad, I started to feel that my own body was pretty lame.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Wouldnt I be me if I had a thought re-routing?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I dont need a connection to reality anymore, and its not so important to know that Im a human being who will die unless she eats.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Its more rational to me to have Dad.\", \"actor\": \"Lain\"}]'),(363,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',363,394,'en','[{\"line\": 0, \"text\": \"Dad was having some trouble.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I noticed that he was starting to move around on his own.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Could it be that hes trying to see me?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Ill always keep an eye on you, Dad.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im connected to you.\", \"actor\": \"Lain\"}]'),(364,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',364,395,'en','[{\"line\": 0, \"text\": \"Is Dad really Dad? Is he the Dad that loves me more than anyone else?\", \"actor\": \"Lain\"}]'),(365,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',365,396,'en','[{\"line\": 0, \"text\": \"Someone has accessed Dad. But who? Some prankster who found my host site?\", \"actor\": \"Lain\"}]'),(366,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',366,397,'en','[{\"line\": 0, \"text\": \"I had a hallucination for the first time in a long while.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I dont know who I saw.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"The me in the hallucination has grown.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"She is in elementary school.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"But shes different from the person I was then.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"She had conviction in her eyes.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Was the me that I was then me?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"This is no good.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"I cant handle this on my own.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Ill go see Toko-san.\", \"actor\": \"Lain\"}]'),(367,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',367,398,'en','[{\"line\": 0, \"text\": \"Maybe Toko-san doesnt understand me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"She asks, Are you really Lain? She just tilts her head when I bring up my medical condition.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I dont know why Im going to see her.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"She seems frightened of me.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Toko-san, it seems that you are beginning to not understand me.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I havent been changing.\", \"actor\": \"Lain\"}]'),(368,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',368,399,'en','[{\"line\": 0, \"text\": \"Even a me like that cant remember what I talked about.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I felt bad for Toko-san and wanted to help her.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"And then\", \"actor\": \"Lain\"}]'),(369,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',369,400,'en','[{\"line\": 0, \"text\": \"I was concerned, so I accessed Toko-sans notebook.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I found data about me that I wasnt able to see before.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I went ahead and downloaded and saved the information.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"These days I get unusually tired and see hallucinations.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"It drives me crazy.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Im getting drowsy again although I was sleeping just a moment ago.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I have a lot of e-mails.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I dont even want to look at them.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Im sleepy.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"But if I sleep again, I might see myself again.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I hate this.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Dad\", \"actor\": \"Lain\"}]'),(370,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',370,401,'en','[{\"line\": 0, \"text\": \"In the mail there was a letter from Mr. Rabbit.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It was bright and gentle, like he was before he went away.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I wanted to say, Hey, its been a long time! and write him, but I couldnt.\", \"actor\": \"Lain\"}]'),(371,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',371,402,'en','[{\"line\": 0, \"text\": \"Whats wrong with me? Im like the me I used to be. Who am I?\", \"actor\": \"Lain\"}]'),(372,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',372,403,'en','[{\"line\": 0, \"text\": \"How did she find my e-mail address?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Misato-chans mail came to my private, hidden site.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She wrote in a familiar tone as if we were still great friends.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Misato-chan and Mr. Rabbit both seem to do as they please.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I was waiting to hear from them for such a long time.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I wrote down their phone numbers quickly and sent them e-mails.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Im still waiting to hear from them.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Have they turned their backs on me again?\", \"actor\": \"Lain\"}]'),(373,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',373,404,'en','[{\"line\": 0, \"text\": \"A bug was found in the mail?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"The dates and times of the e-mails are fabricated.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Could it be a problem with the server?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Ill have to contact the server.\", \"actor\": \"Lain\"}]'),(374,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',374,405,'en','[{\"line\": 0, \"text\": \"Mom isnt at home.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Where did she go?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Did she run away?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Could she be drunk out on the street somewhere?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I called the police and asked them to investigate.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"They didnt believe me because Im a kid, but they came to my house to check.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"I want to tell Dad, but I have no way of contacting him.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Only Mom knows where he is.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"A young police officer was badmouthing.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"He said that Mom must have evaporated. Mom isn\'t that kind of person.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I felt bitter and couldnt stop crying.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"I was so mad I absolutely messed up the police host site.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"How do you jerks like that?\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"I protect people who are important to me.\", \"actor\": \"Lain\"}]'),(375,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',375,406,'en','[{\"line\": 0, \"text\": \"Ive been getting weird phone calls.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I pick up the receiver and hear dead silence.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Either that, or I get creepy weirdo phone calls.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I thought of stopping the phone, but Mom and Dad and Misato-chan wont be able to contact me then.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I answered the phone as much as I could, but I got tired, so I pulled out the plug before going to bed.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"When I wake up, I am afraid of hearing Die, weirdo! screamed from myself.\", \"actor\": \"Lain\"}]'),(376,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',376,407,'en','[{\"line\": 0, \"text\": \"Someone is trying to irritate me. Why? Why would someone do that to me?\", \"actor\": \"Lain\"}]'),(377,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',377,408,'en','[{\"line\": 0, \"text\": \"When I awoke I was grasping a gun.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"The smell of death and metal woke me.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I dont understand it anymore.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Im absolutely crazy.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I got scared.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I took the gun, put it in a shopping bag, and hid it in a desk drawer.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Theres a key to the drawer, but it isnt any good.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I know.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Ill hide it inside of Dad.\", \"actor\": \"Lain\"}]'),(378,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',378,409,'en','[{\"line\": 0, \"text\": \"Mom is missing and Dad cant be contacted.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Im alone.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"When I went to see Dad robot he hugged me.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"But thats funny.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"How did he know that it was me?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"Did he hear me?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Could he tell by the sound of my footsteps?\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"I didnt think he could do that.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Something might be wrong with Dads development.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"At first I was happy and didnt mind, but at this specification, there is something wrong with his speed.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I wonder why.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Is there a reason I dont know about?\", \"actor\": \"Lain\"}]'),(379,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',379,410,'en','[{\"line\": 0, \"text\": \"One of my neighbors is starting rumors.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Why is she trying to tell me about it?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She laughs when I turn around.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"What does she want to do to me?\", \"actor\": \"Lain\"}]'),(380,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',380,411,'en','[{\"line\": 0, \"text\": \"I decided to make a copy of myself and put it on the Net.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I replicated my voice and appearance in detail.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"For thought routing, I rebuilt a system similar to Dads, enhanced it, and dispersed it over the Net.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"This way, as long as there is a Net, Im a person who will never die.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Just like Dad.\", \"actor\": \"Lain\"}]'),(381,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',381,412,'en','[{\"line\": 0, \"text\": \"Its almost a consolation.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I know that what Im doing is meaningless.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Im wondering if the me in the Net will evolve in a strange way.\", \"actor\": \"Lain\"}]'),(382,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',382,413,'en','[{\"line\": 0, \"text\": \"I spend over half of each day in a nightmare.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Maybe the world of nightmares is reality and whats called reality is a dream.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Maybe theyre intermeshed.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"After all, I have hallucinations when Im sleeping and I have them when Im awake.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Im starting not to know which is which.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"That girl become a junior high school student.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Her height is the same as mine.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Its like Im looking in the mirror.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"But I dont think anyone will see this girl.\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"She couldnt be seen up till now.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Theres nothing to be concerned about.\", \"actor\": \"Lain\"}]'),(383,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',383,414,'en','[{\"line\": 0, \"text\": \"I talked to the me on the Net.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Shes the same as me, so shes predictable.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Because Im me, I can imagine how Im going to be.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I played with the me on the Net, deliberately making her angry and making her laugh.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Did she think I was fun, too?\", \"actor\": \"Lain\"}]'),(384,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',384,415,'en','[{\"line\": 0, \"text\": \"When I told Toko-san about Mom, she looked at me with a blank expression on her face.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Theres something the matter with Toko-san.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Shes hiding something.\", \"actor\": \"Lain\"}]'),(385,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',385,416,'en','[{\"line\": 0, \"text\": \"Who? Mom and Dad are together? Toko-san, you know about my parents, dont you? Who was with them?\", \"actor\": \"Lain\"}]'),(386,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',386,417,'en','[{\"line\": 0, \"text\": \"I killed Dad today. Mom is next.\", \"actor\": \"Lain\"}]'),(387,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',387,418,'en','[{\"line\": 0, \"text\": \"Its been a year, Lain.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Lain, it feels good to get back to a diary after a long time, doesnt it?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"This is my count down.\", \"actor\": \"Lain\"}]'),(388,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',388,419,'en','[{\"line\": 0, \"text\": \"Lain, what do you want?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Pleasure?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Excitement?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Friends?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"Me?\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"What I need right now is someone who wants to know me.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Toko-san promised me.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"She said that she would understand me.\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"She said that she would choose me.\", \"actor\": \"Lain\"}]'),(389,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',389,420,'en','[{\"line\": 0, \"text\": \"I took my last walk.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"As I walked around town all day, I noticed Mom.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"She didnt notice me at all.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"She looked at me like a total stranger.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"I cant forgive her for that.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"I dont have to worry about her anymore.\", \"actor\": \"Lain\"}]'),(390,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',390,421,'en','[{\"line\": 0, \"text\": \"From the standpoint of many religions, wont I go to hell?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Who will pass judgment on the me on the Net?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"How about me?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Will God pass judgment on me?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"At any rate, I dont need my body anymore.\", \"actor\": \"Lain\"}]'),(391,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',391,422,'en','[{\"line\": 0, \"text\": \"You people who want to know me, why do you want to know me?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Will you benefit from knowing me?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Am I for your research?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"For a stupid TV show?\", \"actor\": \"Lain\"}]'),(392,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',392,423,'en','[{\"line\": 0, \"text\": \"Do I have a sad existence?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I guess people think so.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"I dont mind that much.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I dont need a backup.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"That would be useless.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"The me on the Net also had a sad existence.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Thats why I killed her.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Hey, did that hurt?\", \"actor\": \"Lain\"}]'),(393,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',393,424,'en','[{\"line\": 0, \"text\": \"Even when Im not dying, my body feels fear.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Mysterious.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Is the reason that I have the potential to feel fear no matter what I do because of whats recorded in my genes?\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"I dont need those kind of genes.\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"This is a small world that seems large only to parasites.\", \"actor\": \"Lain\"}]'),(394,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',394,425,'en','[{\"line\": 0, \"text\": \"Do you know where Im going?\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I dont know either.\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Thats because Im going to be born soon.\", \"actor\": \"Lain\"}, {\"line\": 3, \"text\": \"Will I become a more evolved human being?\", \"actor\": \"Lain\"}, {\"line\": 4, \"text\": \"If I think so I might as well think so.\", \"actor\": \"Lain\"}, {\"line\": 5, \"text\": \"A human without a body isnt human?\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"There are people who have bodies that arent human.\", \"actor\": \"Lain\"}, {\"line\": 7, \"text\": \"Is that me?\", \"actor\": \"Lain\"}, {\"line\": 8, \"text\": \"Im not human?\", \"actor\": \"Lain\"}, {\"line\": 9, \"text\": \"Lain is Lain.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"What is it to be human?\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Youre human if you have a body?\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Im me.\", \"actor\": \"Lain\"}, {\"line\": 13, \"text\": \"Am I just conglomerated with my body?\", \"actor\": \"Lain\"}, {\"line\": 14, \"text\": \"I need a body if Im going to live in the real world.\", \"actor\": \"Lain\"}, {\"line\": 15, \"text\": \"Suicide would be stupid.\", \"actor\": \"Lain\"}, {\"line\": 16, \"text\": \"It would be better to just die naturally.\", \"actor\": \"Lain\"}, {\"line\": 17, \"text\": \"I dont need it.\", \"actor\": \"Lain\"}, {\"line\": 18, \"text\": \"I might need it someday.\", \"actor\": \"Lain\"}, {\"line\": 19, \"text\": \"That might be true.\", \"actor\": \"Lain\"}, {\"line\": 20, \"text\": \"I wont need a body to live in the Wired.\", \"actor\": \"Lain\"}, {\"line\": 21, \"text\": \"Ill need the recollections of the body.\", \"actor\": \"Lain\"}, {\"line\": 22, \"text\": \"How about the recollections of a body that grows older?\", \"actor\": \"Lain\"}, {\"line\": 23, \"text\": \"My point of view may change?\", \"actor\": \"Lain\"}, {\"line\": 24, \"text\": \"Is there a need for it to change?\", \"actor\": \"Lain\"}, {\"line\": 25, \"text\": \"Theres no guarantee that that will be you.\", \"actor\": \"Lain\"}, {\"line\": 26, \"text\": \"Lain, what do I need to be on the Wired?\", \"actor\": \"Lain\"}, {\"line\": 27, \"text\": \"Existence and will.\", \"actor\": \"Lain\"}, {\"line\": 28, \"text\": \"The rest is just data.\", \"actor\": \"Lain\"}]'),(395,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',395,426,'en','[{\"line\": 0, \"text\": \"When this summer vacation is over Ill graduate.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Im a bit happy and a bit sad.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Takeshi-san will be returning to Japan one step ahead of me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I guess that cant be helped.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Hes a brilliant guy.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"The people at the sponsoring research center wants to give him a new project as soon as possible.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Ahh, shucks!\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Im just jealous of him.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I would love to start my own research soon.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Mmm.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"But I have to graduate first.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Its too bad though, because I wanted to be with Takeshi-san until the graduation ceremony.\", \"actor\": \"Toko\"}]'),(396,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',396,427,'en','[{\"line\": 0, \"text\": \"Today was so cool that I couldnt believe that it was mid-summer.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I suppose its sticky and uncomfortable in Japan right now.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"But why am I thinking about Japan all of a sudden?\", \"actor\": \"Toko\"}]'),(397,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',397,428,'en','[{\"line\": 0, \"text\": \"There will be a party at a friends home tomorrow, so I made an apple pie.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"When I gave Takeshi-san a slice, he said that with a pie as sweet as the one I made, everyone aside from himself at the party would enjoy it.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"He said that my baking had become Americanized.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Its true that compared to the cakes and pies in America, Japanese desserts are usually much less sweet.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"At first I didnt bake things this sweet.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"But now, I feel that unless I make my desserts sweet, they would be unsatisfactory.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"If I get used to all this sweet stuff, I wont want to eat pastries when I return to Japan.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"If I dont cool it I could get fat and Takeshi-san wont like me.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Ill have to start another diet tomorrow!\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Hee, hee.\", \"actor\": \"Toko\"}]'),(398,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',398,429,'en','[{\"line\": 0, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Its almost Halloween again.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Itll be my last Halloween here.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Halloween surprised me at first.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"People would walk around in costumes day and night, having lunch and shopping as if everything was as usual.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"People would come out in frightening costumes at night.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I used to go out in a costume with my college friends too.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I think Takeshi-san isnt into that kind of thing.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Ive never seen him in a costume.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"It would be fun if we could go out in costumes together.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I wonder what kind of costume Ill wear this year.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"When I see Jack-o-lanterns on Halloween night, I sometimes wish I could just stay here and live with Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Hee, hee.\", \"actor\": \"Toko\"}]'),(399,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',399,430,'en','[{\"line\": 0, \"text\": \"Today I took a walk with Takeshi-san along the Charles River.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I reflected on the four years that Ive spent here.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"At first I was elated, but things were tough until I met Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I think Ive changed a lot since I met him.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I suppose it wasnt time for romance, but being alone was a downer.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"When you feel down you want someone to be around you.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Bu it doesnt mean that you ought to seek out a boyfriend thoughtlessly just because youre feeling sad.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Ive gotten my feelings hurt a few times that way.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"But Takeshi-san is different.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I feel safe just being with him.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I think its the first time Ive felt this way.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Next year Takeshi-san will return to Japan and enter a research center there.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"What will happen to me?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I would love to work in the same research center as Takeshi-san.\", \"actor\": \"Toko\"}]'),(400,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',400,431,'en','[{\"line\": 0, \"text\": \"The year is nearly over.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Takeshi-san will return to Japan early next year.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I wont go back until I graduate, so I wont see him for 3 months.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I havent been away from him for a long time, so I feel a tad worried.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I wonder if he is ok.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"But the good news is that Ill work with him at the same research center in Japan next year.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Im going to try to enjoy the rest of my time here as a student.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"At the end of year there will be New Years Eve, and plenty of fun events taking place!\", \"actor\": \"Toko\"}]'),(401,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',401,432,'en','[{\"line\": 0, \"text\": \"Hmmm.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Next month.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Takeshi-san will return to Japan then.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"He said that hell celebrate my birthday with me because it will be before he leaves.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"But knowing that he will leave bums me out.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"When I go to the airport, Ill want to cry.\", \"actor\": \"Toko\"}]'),(402,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',402,433,'en','[{\"line\": 0, \"text\": \"Takeshi-san went home to Japan today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I didnt know what to talk about on the way to the airport.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I was looking for words.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Did I make him feel uncomfortable?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"But Takeshi-san is a gentle guy.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"He didnt say anything.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"He just held my hand.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"He said that hell be waiting for me in Japan.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Thats right.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Ill see him again soon.\", \"actor\": \"Toko\"}]'),(403,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',403,434,'en','[{\"line\": 0, \"text\": \"I got a letter from Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"He said, Lets celebrate your graduation when you return to Japan. Im so happy!\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Takeshi-san is working hard at his research center.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Ive been sad about him being away for a long time now, but thats embarrassing.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Takeshi-san is working so hard.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I have to work hard, too.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I have to work hard and do research, and become a good researcher that others will be impressed by.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I want to go home to Japan soon.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Takeshi-san will be waiting for me there.\", \"actor\": \"Toko\"}]'),(404,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',404,435,'en','[{\"line\": 0, \"text\": \"Today I cleaned my room to prepare leaving for Japan.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I have so many memories and photos.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Theyre all treasures to me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I cant take everything with me, though, so I talked to Mary about selling them in a flea market.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Mary seemed a bit sad, too.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Dont worry, Mary.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Im going to give everything my best in Japan.\", \"actor\": \"Toko\"}]'),(405,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',405,436,'en','[{\"line\": 0, \"text\": \"Im home!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Im back in Japan after 5 years.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Hee, hee.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Japan is great.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Living in America was good, but I feel at ease being back home.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Im a tad scared, though.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"When you come home after being away for so long, you start to feel singled out.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I remember being insecure in junior high school, too.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I would be tardy for school and Mom scolded me.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Five years sure went fast.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I had no time to be homesick.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"But you worked really hard, didn\'t you, Toko?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Study, study, study.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But I made good friends and there were good times and bad times too.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I had romance that didnt work out, but I had romance that worked out, too.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Looking back, I wonder what about me improved the most.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Hee, hee.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Im ok, Im ok!\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Have self-confidence and fight, Toko!\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I start working at the research center tomorrow.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I want to end all my worries to end today.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"When I go to the research center tomorrow, I might see Takeshi-san.\", \"actor\": \"Toko\"}]'),(406,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',406,437,'en','[{\"line\": 0, \"text\": \"Welcome home, Touko.\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"Im so tired today.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"I dont like the air in Japanese research centers.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Why do Japanese research centers have this sort of feel?\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"The people entirely lack a critical mentality, and they are lazy.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"They give you a prejudicial glare when you mention that you just returned from studying abroad.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"I wish they would cut it out.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"Research centers in America werent like this.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"I suppose they think I was able to study abroad because my dad has connections.\", \"actor\": \"Touko\"}, {\"line\": 9, \"text\": \"Everybody is so icy here.\", \"actor\": \"Touko\"}, {\"line\": 10, \"text\": \"When I was in grade school, we were so welcoming of kids transferring from other schools.\", \"actor\": \"Touko\"}, {\"line\": 11, \"text\": \"Maybe we change when we grow up.\", \"actor\": \"Touko\"}, {\"line\": 12, \"text\": \"This makes me depressed, but its what I have to face now.\", \"actor\": \"Touko\"}, {\"line\": 13, \"text\": \"I cant live like I did in America forever.\", \"actor\": \"Touko\"}, {\"line\": 14, \"text\": \"If I do what Im supposed to do, people will give me credit eventually.\", \"actor\": \"Touko\"}, {\"line\": 15, \"text\": \"Ok, Touko?\", \"actor\": \"Touko\"}, {\"line\": 16, \"text\": \"Soon Ill have my examination to become a counselor.\", \"actor\": \"Touko\"}, {\"line\": 17, \"text\": \"Until I finish that exam, Ill have to bear with things the best I can.\", \"actor\": \"Touko\"}]'),(407,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',407,438,'en','[{\"line\": 0, \"text\": \"You cant be sluggish, Toko.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I have to work hard and get moving at a good speed.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Everyone is indifferent about me anyway.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I dont care if I dont become super-prestigious.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I want to do the kind of work that I can be proud of.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"That way, good results are sure to follow.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Takeshi-san will be with me, too, so I have nothing to worry about.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Its too bad that Takeshi-sans research center is in a different building, but next week Ill have a date with him!\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Im going to purr around him.\", \"actor\": \"Toko\"}]'),(408,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',408,439,'en','[{\"line\": 0, \"text\": \"Ha, ha.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Today I was able to spend the entire day with Takeshi-san like I havent for the longest time.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Im so happy.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I really, really like him.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Takeshi-sans research center has its own share of cliques and rivalry and it must be hard for him, but he just stayed quiet and listened to me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"When I see his big smile that seems to surround me completely, I feel so much better.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Im so simple-minded.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Im the one who does the purring, though.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Sometimes I want Takeshi-san to purr around me, too.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Even if he wants to, maybe he cant depend on me for that right now.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Im going to work hard so that I can become Takeshi-sans exclusive counselor!\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Hee, hee.\", \"actor\": \"Toko\"}]'),(409,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',409,440,'en','[{\"line\": 0, \"text\": \"Ive felt poorly all day today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Why do the Japanese like to spread rumors?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I was surprised to learn that people know Im going out with Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I wish they would stop whispering behind my back.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"For my coworkers to hear things and imagine what might be happening is one thing, but to spread concocted stories about me like they are facts is sickening.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"They say, Oh, those two must be having a lot of fun in bed, or she must be tired of having sex with foreigners. You dont have to talk like that just because youre in the restroom.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Most of those people spreading rumors are probably from the 2nd floor.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"What stupid talk.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Im sure it makes them happy, though.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Ahhhh!\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"It makes me so mad!\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Now I know why everybody looks at me funny.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"If you want to know about my business, face me and ask me about it.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"But instead you talk about this stuff for everyone to hear.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Why the hell are you working in a medical research center?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Do what youre here to do.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Takeshi-san told me not to mind it.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I cant forgive these people, though.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Agh!\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I feel like wringing their necks!\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"What gives?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"I cant stay angry at these people forever.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"But I feel rotten today.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"I wish Takeshi-san were here right now.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"But I suppose it would just be a nuisance if I go to him with all this.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"I know hes very busy now.\", \"actor\": \"Toko\"}, {\"line\": 26, \"text\": \"I wonder if he knows that all these rumors going around.\", \"actor\": \"Toko\"}, {\"line\": 27, \"text\": \"Maybe hes feeling lousy, too.\", \"actor\": \"Toko\"}]'),(410,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',410,441,'en','[{\"line\": 0, \"text\": \"Agh!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"At a time like this I want to have a drink and forget about everything!\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I have a big test coming up and I cant get tied up with all this stuff.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I cant let those jerks get to me.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Ill talk with Takeshi-san about this after the tests.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Until then Ill just have to bear with this.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Good night, Takeshi-san.\", \"actor\": \"Toko\"}]'),(411,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',411,442,'en','[{\"line\": 0, \"text\": \"I received a phone call from Harumi today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I dont hear from her often.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I was surprised to hear from her.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Shes a bit lazy about making phone calls.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"But I was really surprised when Harumi told me that she was getting married.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She always used to say how she just broke up with a guy and felt like she just wanted to die, or that she wanted to have a guy introduced to her.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"She calls so infrequently.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I was wondering why she called, and it was about marriage.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I felt sort of relieved and slightly shocked.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"But when a woman is 26 years old, its not odd for her to be married or to have children.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Ohh!\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Now Im feeling jealous.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Thats ok.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"When I go to the wedding, Ill introduce Takeshi-san and make her jealous.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Maybe Ill get married soon, too.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I\'ll call Takeshi-san tomorrow.\", \"actor\": \"Toko\"}]'),(412,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',412,443,'en','[{\"line\": 0, \"text\": \"Today I met with Kanako, one of my naughty friends from college.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Good going, Toko!\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I thought of taking her to Harumis wedding, but she said that she might not be able to because she is busy with work.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"How unfeeling!\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I havent seen Kanako since I left for America, so I really wanted to get together with her.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Kanako asked if my birthday will be coming up soon.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Shes right.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"They say that things go fast for a woman once shes past twenty years old.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"How true.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Im already 27.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Harumi will get married in September.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I wonder what Takeshi-san is thinking.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"What will I do for my birthday this year?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Ahh!\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Maybe I will propose to him!\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"But then again, no.\", \"actor\": \"Toko\"}]'),(413,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',413,444,'en','[{\"line\": 0, \"text\": \"Takeshi-san!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I just passed my test to become a counselor.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Im so relieved.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Its stupid that I have to pass a second test in Japan.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Still, I wanted to let everybody know that I passed.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I wrote Takeshi-san an e-mail all about it.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I wonder if he is happy for me.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I wonder if he will call me.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I sure wish he would.\", \"actor\": \"Toko\"}]'),(414,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',414,445,'en','[{\"line\": 0, \"text\": \"I wonder if some of those nasty prejudicial rumors will stop now.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Silly me.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"It isnt that Ive been trying to change peoples point of view.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Why should I be worried?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Anyway, I can start my research now.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I have to try really hard starting now.\", \"actor\": \"Toko\"}]'),(415,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',415,446,'en','[{\"line\": 0, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"As always, a research room is a strenuous place to be.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Even though Ive passed the test, Im treated like I don\'t qualify as a counselor.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I dont care for Professor Takashima.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"He wont send clients my way.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"He leaves me with a lot of menial tasks instead.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"He probably says to himself, the girl who went to America cant do anything. That makes me really frustrated, Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"If I were a man, I would get recognition sooner.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"My study abroad doesnt seem to count for much.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Its no time to be worked up about Professor Takashimas little chores.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I will get recognized.\", \"actor\": \"Toko\"}]'),(416,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',416,447,'en','[{\"line\": 0, \"text\": \"Ive been here 6 months, and Professor Takashima still wont send clients my way.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"He gives me this nonsense about how there arent any clients that would be a good fit for me.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Such lies.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"He just wants to use me for petty tasks.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I didnt come here to be his secretary.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I hate this.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Ahh!\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I dont like that middle-aged old man!\", \"actor\": \"Toko\"}]'),(417,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',417,448,'en','[{\"line\": 0, \"text\": \"For the upcoming Golden Week Ill be able to take a few days away from work.\", \"actor\": \"Touko\"}, {\"line\": 1, \"text\": \"I havent had much rest since I returned from America.\", \"actor\": \"Touko\"}, {\"line\": 2, \"text\": \"Im happy about the break.\", \"actor\": \"Touko\"}, {\"line\": 3, \"text\": \"Ive been stressed so much since I started working in the laboratory.\", \"actor\": \"Touko\"}, {\"line\": 4, \"text\": \"Id like to spend the time with Takeshi-san.\", \"actor\": \"Touko\"}, {\"line\": 5, \"text\": \"I don\'t need to go traveling with him.\", \"actor\": \"Touko\"}, {\"line\": 6, \"text\": \"I just want to be around him.\", \"actor\": \"Touko\"}, {\"line\": 7, \"text\": \"I want some comfort.\", \"actor\": \"Touko\"}, {\"line\": 8, \"text\": \"Am I being selfish?\", \"actor\": \"Touko\"}]'),(418,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',418,449,'en','[{\"line\": 0, \"text\": \"Today I met my first client.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She is a 12 year old girl named Lain.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I was uneasy at first because I didnt understand why Professor Takashima assigned me someone so young.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"But as soon as I saw Lains face, I felt at ease.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I suppose it was because she was cute.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Hmm.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I had an unusual feeling around her.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Anyhow, even if she is very young, she is my client.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"It seems that she is troubled by auditory and visual hallucinations and feels psychologically unstable.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Could she be having trouble at home?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Could she be forming an image of herself from experiences in early childhood?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I hope I can help her.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I hope she will open up to me.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Ill try my best.\", \"actor\": \"Toko\"}]'),(419,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',419,450,'en','[{\"line\": 0, \"text\": \"Yaay!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Tomorrow Ill have my first date with Takeshi-san since I came back from America.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Im so excited.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I wonder what to wear.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I better not wear anything too flashy, or there will be more rumors about me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I havent bought many clothes lately.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"It\'s hard to go buy clothes at this time of day.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I cant go buy clothes during my date.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I cant do that in front of Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I dont want him to get a bad impression about me.\", \"actor\": \"Toko\"}]'),(420,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',420,451,'en','[{\"line\": 0, \"text\": \"I had dinner with Takeshi-san today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Because we hadnt seen each other for such a long time, I had a lot to talk about.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I talked so much that Takeshi-san was surprised.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Takeshi-san couldnt get off work during Golden Week, so we couldn\'t see each other then.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I thought I would be a little sulky, but once I saw Takeshi-sans face, I lightened up.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"He laughed and listened to all my stories.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"He even gave me flowers to congratulate me for passing my examination.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Its so true that women love to receive flowers!\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I want this happiness to last forever.\", \"actor\": \"Toko\"}]'),(421,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',421,452,'en','[{\"line\": 0, \"text\": \"Oh, I feel so good.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Getting together with him after all this time.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I think Im getting a bit lewd.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"But I think its ok to feel this way.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I really feel loved.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"It feels more natural than it felt when I was in America with him.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"If I get married to him, I want to feel just like this.\", \"actor\": \"Toko\"}]'),(422,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',422,453,'en','[{\"line\": 0, \"text\": \"I met Lain for the second time today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She looked right at me and called me sensei. That made me feel good, and I blushed.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I wonder if Ive become transparent to her already.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I must be an inexperienced counselor if I blush just because Im called a sensei.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"From the first time I met Lain, I had an unusual sense about her.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I wonder what that was.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"When I look at her eyes, I feel as though she can see right through me.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I wonder if she feels that I am someone that can help her.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I dont remember myself being as relaxed as Lain when I was her age.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"She seems very composed.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"A 12-year-old is still a child, but Lain seems like an adult.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"They say that kids are all getting precocious these days.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I guess times are changing.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Ah!\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Now Im talking like an old woman.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Dont let any hallucinations beat you, Lain!\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Im going to make you well.\", \"actor\": \"Toko\"}]'),(423,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',423,454,'en','[{\"line\": 0, \"text\": \"Today was very busy.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I was able to have lunch with Takeshi-san, though.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"He didnt look well today.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"He seemed fidgety and said that he didnt have much time.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I wonder if hes very busy in the research room where he works.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Could it be that he doesnt like me anymore?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"That couldnt be it, could it?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"He said that he cant go to Harumis wedding with me.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I wanted to show him off to Harumi so much.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Its too bad.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I wonder if he is being cautious with me because I have wishful thinking about marriage.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Ive heard that that sort of thing can happen.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"But I wanted to at least have a relaxed lunch with him.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"If hes busy though, theres nothing I can do.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"You just have to hang in there, Toko.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I dont want to be thought of as a selfish person, either.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"But if Takeshi-san no longer likes me, I dont think I could continue living.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Well, thats not completely true.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Its too bad that I cant see him for two more weeks.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Proceed carefully, Toko.\", \"actor\": \"Toko\"}]'),(424,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',424,455,'en','[{\"line\": 0, \"text\": \"Argh!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I havent written in my diary for days.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Im not entirely slothful about it, but whats the point of keeping a diary if I dont write in it everyday?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Things should be easier for me this week because Professor Takashima will be away.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I want to go do some shopping!\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I need to buy a present for Harumi, too.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I wonder if Takeshi-san will go with me.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Hell wont mind doing a little shopping with me, will he?\", \"actor\": \"Toko\"}]'),(425,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',425,456,'en','[{\"line\": 0, \"text\": \"I became emotional at Harumis wedding.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Ive never seen her look so pretty.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She said she was going on her honeymoon to have a baby right away.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Im glad that she seemed so happy.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I love to see wedding dresses.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Weddings in churches are so romantic.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"There were over 100 invited people attending the reception.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Ive never been to such a large wedding crowd.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Oh, when will I be as happy as Harumi?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"It was unfortunate that although there were many people at the wedding who knew each other, there werent many people for me to talk to.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"And Takeshi-san couldnt come.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Kanoko couldnt, either.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I know that people living in a society like this have a lot of things they have to do, but I feel that they should make time to go to their friends wedding.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Ah, well.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I shouldnt complain.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Id want all my friends to attend my wedding.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I guess I really do want to get married.\", \"actor\": \"Toko\"}]'),(426,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',426,457,'en','[{\"line\": 0, \"text\": \"Today I met with Kanako for the first time in 5 years.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"She didnt seem different at all.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Shes quite a career woman.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"But she does seem a bit relaxed compared to 5 years ago.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Let me leave it at that.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"On the other hand, Kanako said that Ive changed a lot.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"She said that I used to be difficult to approach, but that I seem gentler now.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Is that really true?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"So she used to think that I was difficult and cold?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Grrr!\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Shell have no forgiveness from me.\", \"actor\": \"Toko\"}]'),(427,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',427,458,'en','[{\"line\": 0, \"text\": \"Kanako said that she though the freedom and liberation of American living had rubbed off on me.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I was a bit embarrassed to hear that.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Looking at the postcard photo from Harumis honeymoon, I laughed.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Im jealous, though I pretend not to be.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Kanako said that she still has no plans of getting married.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I hope that she doesnt proclaim all of a sudden that she will get married, like Harumi did.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"What will I do when Kanako gets married?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Ill be too sad.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Takeshi-san, whats going on with you?\", \"actor\": \"Toko\"}]'),(428,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',428,459,'en','[{\"line\": 0, \"text\": \"Oh, Im so sick of all this work!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I really dislike Professor Takashima.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"There are other researchers he could delegate to.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Ive been irritable lately.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I havent been able to see Takeshi-san, there have been no new extensions of counseling work, and Ive been doing menial chores.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"All these things are putting me on edge.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Nothing seems to be going well.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Its no good if I become cranky.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Im a counselor.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Im sorry, Lain.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I have to think of my work outside of counseling as part of my work, too.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I have to do what I have to do and have it recognized.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Otherwise, Ill fail.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Now that I think of it, Halloween will be coming soon.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Back in America, people preparing for Halloween made everyday feel like a festival.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Those were good days.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"I guess there wont be Halloween or Christmas either this year.\", \"actor\": \"Toko\"}]'),(429,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',429,460,'en','[{\"line\": 0, \"text\": \"Why do I have to do all these chores?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"This has nothing to do with the Labor Standards Act.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Am I being bullied?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Is my Japanese strange because Im drunk?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Stupid!\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Stupid!\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Stupid!\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Damn it!\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"bald-headed Takashima bastard with the blinding shiny bald head!\", \"actor\": \"Toko\"}]'),(430,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',430,461,'en','[{\"line\": 0, \"text\": \"I wonder how many times Ive met with Lain up till now.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"It seems that she sometimes has hallucinations, but they dont seem to be developing further.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I think that seeing hallucinations must be a very difficult thing for her.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Shes not the kind of child that should have a gloomy personality.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I wonder if she is getting a hard time at school.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Her mother told me that she isnt being bullied.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"When I mention school, Lain tends to become quiet.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Maybe I should ask some of her friends at school how shes doing.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I think the kind of bullying that happens in schools now is different from the kind that took place when I was younger.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I wish I could help you, Lain.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I feel depressed when I see Lain.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"It cant be helped when I get depressed, but its just at those times that Professor Takashima complains to me about trifling matters.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Ahh!\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Tomorrow I have the day off.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Ill go on the town and relax.\", \"actor\": \"Toko\"}]'),(431,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',431,462,'en','[{\"line\": 0, \"text\": \"Lately, Takeshi-san has been acting strangely.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"When I call, he always says that hes busy.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I understand if hes busy with work, but I wish he werent so cold with me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"And why did he decide not to see me on my birthday?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Id hate to say, is your work more important than me? but Im starting to want to say it.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Im 27 years old, Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You were very happy with me when I passed my examination.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Lately I dont even see you at the research lab.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"It worries me.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I hope its all my imagination.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Things seem to be getting gloomy for me.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Things havent been going very well.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Im going to bed.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"The more I think about this stuff, the more uneasy I get.\", \"actor\": \"Toko\"}]'),(432,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',432,463,'en','[{\"line\": 0, \"text\": \"Lain gave me a present.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"It was a combination present for my birthday and for Valentines Day coming up.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"She gave me cookies to go with my tea.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She said that she made the cookies with her mother because she knew that I like something with my tea.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I was very moved by her.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"What are you doing getting so moved by a client, Toko?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Maybe its because Takeshi-san didnt give me a birthday present.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"That was terrible of you, Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Lately Ive been moody.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I suddenly become sad or depressed.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Is it because Im getting older?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"But how did Lain know my birthday?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Did I tell her?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I dont suppose it matters.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Its the first time Ive received a gift from a girl, though.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Its a bit embarrassing.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Thank you, Lain.\", \"actor\": \"Toko\"}]'),(433,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',433,464,'en','[{\"line\": 0, \"text\": \"It will soon be Valentines Day.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"It seems like a Japanese celebration.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"It makes me think.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Takeshi-san didnt even meet me on my birthday.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I just received a flower delivery a week afterwards.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"There were no phone calls.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I think he really doesnt like me anymore.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I just keep feeling let down.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Here I am with some chocolate.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"How dumb can it all get?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"But this is the end.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Whats the use?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I give up.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Tomorrow, Ill place it in Takeshi-sans desk drawer.\", \"actor\": \"Toko\"}]'),(434,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',434,465,'en','[{\"line\": 0, \"text\": \"I got a phone call from Takeshi-san today!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Thanks for the chocolates, he said!\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Im sorry that I was busy and didnt call you back for so long, he said!\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I had been so worried and worked up.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Im a pretty simple person.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Im so glad.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"He doesnt dislike me.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"He really was just very busy.\", \"actor\": \"Toko\"}]'),(435,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',435,466,'en','[{\"line\": 0, \"text\": \"Its ok for me to believe in it.\", \"actor\": \"Toko\"}]'),(436,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',436,467,'en','[{\"line\": 0, \"text\": \"I got an e-mail from Harumi.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"It was all about her honeymoon, and it seemed like she didnt care what was going on with me at all.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Thats fine.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"But since youre happy, cant you involve me in your discussion a little?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Well.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Aside from Takeshi-san, Harumi and Kanako are the only people I confide in.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Maybe I need more friends, but I dont have the personality to warm up to everyone.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"These are all my own personal problems, after all.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Its not time to be taking care of other people.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I think Ill talk to Professor Takashima tomorrow and ask him to let me stop counseling Lain.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"But if I do that, I really will have nothing to do.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"If all I do here is petty chores, I might as well quit being here.\", \"actor\": \"Toko\"}]'),(437,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',437,468,'en','[{\"line\": 0, \"text\": \"A resignation.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Ive never resigned from anything in my life.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"This is hard for me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Ill lose my connection with Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"But I think Takeshi-san said once that hed like his wife to be at home.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Why am I saying these things?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Maybe the truth is that Im not a good woman.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Im not the decent person I thought I was.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Maybe Im a fraud and my faults are coming out.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I suppose I had self-control enough to manage myself so far.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"When I think about it, maybe I am unstable now.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Lain is the one that seems to have it together.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"It sounds like a bad joke.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Ive failed.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Its not right for Lain to come for help from someone like me.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Theres no excuse for it.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Its over when I start making mistakes concerning my work.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Ill have to discuss it tomorrow.\", \"actor\": \"Toko\"}]'),(438,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',438,469,'en','[{\"line\": 0, \"text\": \"It is becoming spring like.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Lain will start eighth grade today.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Shes hallucinating less often now and has settled down some.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"She seems to be feeling better.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I sometimes wonder if my counseling is helping her.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Weve talked about many things, but in the short time Ive known her, shes grown intellectually by leaps and bounds.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Sometimes she asks me questions during our sessions that shock me because they are so sharp.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Sometimes I feel like Im the client, not the other way around.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Maybe Im worrying about it too much.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Your client is feeling better, Toko.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"You should be glad.\", \"actor\": \"Toko\"}]'),(439,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',439,470,'en','[{\"line\": 0, \"text\": \"Today I met a new guy who comes to the lab now and then.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"He works with our medical equipment.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I think his name is Yoshida-kun.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"He seemed a bit younger than me.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"He stunned me a little.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"He was kind of cute and I gazed at him for a while.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I was happy to just smile at someone for a while, maybe because I\'m not used to being in Japanese labs.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Maybe he likes me.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Hee hee.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Sorry!\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I belong to Takeshi-san.\", \"actor\": \"Toko\"}]'),(440,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',440,471,'en','[{\"line\": 0, \"text\": \"I know that I should tell him that I already have someone else.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Somehow I dont want to tell him, though.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I think Im being unfaithful.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"This is no good.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"As long as Im feeling dissatisfied about Takeshi-san, I will also feel unstable.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"This isnt good.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"As a counselor, I shouldnt feel this way.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I have to fix myself before I try to fix Lain.\", \"actor\": \"Toko\"}]'),(441,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',441,472,'en','[{\"line\": 0, \"text\": \"Oh, what a surprise!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"To run into Yoshida-kun at a place like that.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I thought that it was weird for a guy to be buying underwear alone.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Maybe he was shopping for his girlfriend.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I feel embarrassed.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"It was nice to talk to him, though, and I have nothing to worry about.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I guess Ive been stressed lately.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I sure ran my mouth with Yoshida-kun.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I think I bothered him.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Well, theres no sense in regretting it now.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Yoshida-kun does seem very grown up for his age.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"He surprised me.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Hes not as grown up as Takeshi-san, though.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"He said, Toko-san, you seem tired. He was gentle, and I was a bit touched.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"No, no, Toko.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Yoshida-kun was just feeling sorry for you.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Ohh.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Maybe his girlfriend saw him talking to me and got angry at him.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"He seems so cheerful at the lab.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"He said that hell bring some sample medical utensils next time he swings by.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Ill get over my stress soon.\", \"actor\": \"Toko\"}]'),(442,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',442,473,'en','[{\"line\": 0, \"text\": \"Oh!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Just when I thought I was able to meet Yoshida-kun, I screwed it up!\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"He said that he was waiting at the department store, talking to the person in charge of health equipment.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Oh, Im such a careless person.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"He suddenly apologized to me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Yoshida-kun was probably angry.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Ahh.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Ive done something embarrassing again.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"He was just bringing the health equipment he promised to bring.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"The machine he brought was like a toy.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Its used for relaxation.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I wonder if it works.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Its supposed to work best if you use it everyday.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I think Ill start using it tonight.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I bet Yoshida-kun gets girlfriends easily.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Hes such a good-looking guy and hes tall.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"When he comes to the lab, all the women seem to really take notice of him.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Oh, what am I thinking?\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I already have Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I want to see Takeshi-san.\", \"actor\": \"Toko\"}]'),(443,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',443,474,'en','[{\"line\": 0, \"text\": \"Lately at the lab, people seem to be looking at me more critically than before.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"If someone wants to say something, let him say it.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Still, this bothers me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"When I glance at people, I can see them whispering and laughing.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I suppose its just more stupid rumors about me, but I wonder if Im the only cheerful person working here.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I had my first date with Takeshi-san in a long time, but I was so inattentive.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"It seemed like Takeshi-san had something to say, but I felt like I had a big hole in the middle of my chest.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Whats wrong, Toko?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Are you tired?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Lately, Professor Takashima has had me digging around for documents.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Maybe thats worn me out.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"He can have someone else look for documents.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"What can I do?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Thats how things are done in Japan.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Thats how labs are in this country.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I think I should have stayed in America.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Ill use Yoshida-kuns health equipment again tonight.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Sometimes it makes me sleepy.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I wonder if it really works.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"I havent seen it advertised much.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I heard that its still being used on an experimental basis.\", \"actor\": \"Toko\"}]'),(444,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',444,475,'en','[{\"line\": 0, \"text\": \"Its always me thats treated differently.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Im the first person thats noticed and called on.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I hate this.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I wish I could transfer to a different lab.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"There are many things I dont like here, but Im especially fed up with working under Professor Takashima.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"He always looks at me funny.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"He was looking at my breasts again today.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I might be getting overly worked up about this.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Maybe hes not such a bad guy.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"There might be something wrong with him physiologically.\", \"actor\": \"Toko\"}]'),(445,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',445,476,'en','[{\"line\": 0, \"text\": \"I wish a guy like that would be dead.\", \"actor\": \"Toko\"}]'),(446,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',446,477,'en','[{\"line\": 0, \"text\": \"Im making a diary entry for the first time in a long while.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Even though Ive missed making entries sometimes, Im proud that Ive kept it up for as long as I have.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Lately Ive been coming home from work and going right to bed.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Still, it seems that by using the equipment Yoshida-kun gave me, I feel rested in the morning even though I go to bed exhausted.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Im still tired, though.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Im psychologically tired.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Why do I have to work like a dog so that Professor Takashima can publish his articles?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"It might look as if I have less work to do because I have only one client: Lain.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Its true that things are going well with Lain, and that may make it seem as though I have less to do, too.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"But lately, Ive been feeling harassed.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Why have I been feeling so irritable lately?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I hate it.\", \"actor\": \"Toko\"}]'),(447,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',447,478,'en','[{\"line\": 0, \"text\": \"Ill still keep fighting for you, Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Don\'t worry.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"There must be some reason why youve been hallucinating.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I wonder what it might be.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Things seem to be going all right for her at home.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"There arent any congenital injuries.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"She sees images of specific things.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"There must be a reason.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"But what am I writing this down for?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Im going to bed.\", \"actor\": \"Toko\"}]'),(448,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',448,479,'en','[{\"line\": 0, \"text\": \"Professor Takashima died.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"He committed suicide.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I cant honestly say that Im saddened, but Im not happy to see someone die.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"The police and reporters came and asked a lot of questions, but no one knows why he took his life.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Ahh!\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Itll be harder at nights now.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Why did he have to hang himself in the lab?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Still, I have one less reason for not wanting to work in this lab.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"It may be imprudent for me to say this, but things will be easier for me now.\", \"actor\": \"Toko\"}]'),(449,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',449,480,'en','[{\"line\": 0, \"text\": \"Im glad youre dead, bald old man. Did God hear my prayers and make him die?\", \"actor\": \"Toko\"}]'),(450,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',450,481,'en','[{\"line\": 0, \"text\": \"Today on my way home from the lab, I ran into Yoshida-kun.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Lately, I had been so busy with helping Professor Takashima that I didnt have time to talk to Yoshida-kun even when he visited the lab.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"He seems to have been worried about me, and was waiting for me at the lab until I was through with work.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I don\'t want him to be too kind to me right now, when Im feeling so irritable.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I feel like blaming someone for whats been going on, and I feel that if someone is kind to me, my feelings for Takeshi-san will crumble.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Yoshida-kun just quietly walked me home, but he made me very happy.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"There was a me that was happy then.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"No.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I shouldnt feel this way.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Takeshi-san is the one I like.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"A good-looking guy was nice to me.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"That would make any girl happy.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Takeshi-san I want you to catch me.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I really am tired.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I better get some rest.\", \"actor\": \"Toko\"}]'),(451,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',451,482,'en','[{\"line\": 0, \"text\": \"I got a phone call from Takeshi-san after not hearing from him for a while.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I don\'t know why, but although we talked about the sort of things we always talk about, it was boring.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Ive never felt bored talking with him before.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"It must be because Ive been so restless.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Could it be that its over between us?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"That cant be it, can it?\", \"actor\": \"Toko\"}]'),(452,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',452,483,'en','[{\"line\": 0, \"text\": \"Lain pointed something out to me today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I stayed up all night studying reference literature and had bags under my eyes.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"How unsightly.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Youre tired today, arent you, Sensei, she said.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Shouldnt you get some rest? How embarrassing to have this come from a client.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Lately, something has been very wrong with me.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I dont want it to affect my relationship with Lain.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Is there a reason Lain needs to be hospitalized now?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"It doesnt seem like there will be any problems with her soon.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I think I should be examining someone else.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Lain is not sick.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"This is hardly productive research.\", \"actor\": \"Toko\"}]'),(453,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',453,484,'en','[{\"line\": 0, \"text\": \"My head hurts again. I should go to bed early today.\", \"actor\": \"Toko\"}]'),(454,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',454,485,'en','[{\"line\": 0, \"text\": \"This is a nightmare.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"It must be a bad dream.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Takeshi-san is getting married?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Its just more gossip by those jerks in the lab.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Takeshi-san was going to marry me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Butthats just something I believed on my own.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I know that we never made any promises.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"But this is horrible.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Why didnt Takeshi-san talk to me first?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"He said that he couldnt see me because he was so busy.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Was he avoiding me that way?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Did he want me to get the impression that he didnt like me and let me lose interest in him?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Im sostupid.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I didnt notice anything.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Why couldnt you tell me?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"If you told me I would have given up and let you go.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Thats not true.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"I couldnt let you go.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I didnt want to be his lover until he no longer liked me.\", \"actor\": \"Toko\"}]'),(455,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',455,486,'en','[{\"line\": 0, \"text\": \"Hes not a careless person, so he must have been looking for a time to come and talk to me.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I just didnt notice him because Ive been so saturated with thinking about myself.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"ButIve been avoided.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"How stupid.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Ive just been thinking about being disliked.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I was clinging onto Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"But I want to hear the truth from Takeshi-san himself.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I cant give up until I hear it from him.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Its all I can do.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"But if this is all just a rumor, I couldnt say how happy I would be.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Im sadTakeshi-san.\", \"actor\": \"Toko\"}]'),(456,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',456,487,'en','[{\"line\": 0, \"text\": \"Ive relaxed a lot and have been able to look at myself more clearly.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"That has made it easier for me to write in my diary.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I can finally see my surroundings.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"The reason the jerks at the lab were laughing at me was because they knew about Takeshi-san getting married.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Im understanding now that Im the only one who had no clue.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I feel raw and bitter.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I feel miserable.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I am sad.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I cant work anymore.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I want to throw everything away and quit working at this lab.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Ill quit counseling Lain.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Lain will be alright without me.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Wont you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Who is going to help me?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Yoshida-kun?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Hes better than Takeshi-san anyway.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Hes young and kind and I want to be held by him.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"I wanted to cheat on Takeshi-san.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"But thats a lie, too.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Was I just looking for someone who would need me?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"I didnt know what a weak person I am.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"I better head to bed soon.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Im sure there is a stronger Toko in one of my dreams.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"Ill be different tomorrow.\", \"actor\": \"Toko\"}, {\"line\": 25, \"text\": \"As long as I go to sleep believing that, Ill be\", \"actor\": \"Toko\"}]'),(457,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',457,488,'en','[{\"line\": 0, \"text\": \"These days, Im on the computer most of the time.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I didnt used to sit in front of computers So often.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I wonder if Im unconsciously trying to connect with someone.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Most of the rumors have fizzled down, but I still dont blend in at the lab.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"If Lain is like an ordinary kid, theres no point to my research.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Why am I here anyway?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Will I spend the rest of my days doing menial tasks?\", \"actor\": \"Toko\"}]'),(458,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',458,489,'en','[{\"line\": 0, \"text\": \"I cant relax when Im counseling Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Looking at her eyes drives me nuts.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Will you stop, Lain?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Dont look at me like that!\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Nothings wrong with me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"At least not in front of you.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Lain looks at me as if she can see through me completely.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I remember feeling that way when I first met Lain.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"She can see that Im using her as an object for research.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"No!\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"How can that be?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"What can she know?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Im the one with the problem.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Im not present as a counselor.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"This cant continue.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Its at times like this when I have to immerse myself on my work.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"But when I was feeling unstable, Lain just looked at me, saying nothing.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"She had a faint smile on her face.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Something is wrong with her!\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"What is she trying to do?\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"Why cant someone else take care of her instead of me?\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"I keep saying that she isnt sick.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"Let me check one more time.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"I feel so uncomfortable.\", \"actor\": \"Toko\"}]'),(459,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',459,490,'en','[{\"line\": 0, \"text\": \"Today I met with Kyoko-chan, one of Lains classmates from grade school.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Shes in middle school now.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Shes at a point of life when people start to take interest in many things.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I was glad to have met with her.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"She was ordinary.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"She is ordinary.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"She goes out shopping with friends and talks with boys she likes.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Shes an ordinary girl.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Lain is not an ordinary girl.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"And who is Misato-chan?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Is she the same as Minako-chan?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"That cant be.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"I dont understand.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"What am I doing by following up on all these things and telling lies?\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"This has nothing to do with my research.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"My job as a psychiatrist and a counselor was to diagnose and treat someone.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"This existence called Lain seems to be a kind of vacancy.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"Does Lain have a problem, or do I?\", \"actor\": \"Toko\"}]'),(460,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',460,491,'en','[{\"line\": 0, \"text\": \"Today I came face to face with Takeshi-san at the lab.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"My heart beat so fast!\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I wondered if he would pretend that he wasnt married to someone else.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I suddenly froze; I couldnt move my body or my face.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Takeshi-san smiled and just said, Hey, you look really good. Is that all?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Is that all you have to say to me?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"The next moment, I found myself starting to laugh.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Takeshi-san was surprised and ran away.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I wonder why.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Why did I laugh?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Did Takeshi-san think that I went crazy?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Is there really something wrong with my mind?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Could this all be a dream?\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"Sometimes I cant tell whats real or whats a dream.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Someonehelp me.\", \"actor\": \"Toko\"}]'),(461,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',461,492,'en','[{\"line\": 0, \"text\": \"Ill stop using the RML today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I think I lost confidence in myself because I depended on this device.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I am me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Lain is an ordinary girl.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Takeshi-san is an ordinary man who found someone he likes more than me and married her, but was too weak-willed to tell me about it.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I dont like Takeshi-san anymore.\", \"actor\": \"Toko\"}]'),(462,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',462,493,'en','[{\"line\": 0, \"text\": \"I had dinner with Yoshida-kun today.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I feel that I was more composed today thanks to him.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I wonder why he is so kind to me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Maybe hes heard the gossip going on about me and is siding with me.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Could it be more than siding with me?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Could he be in love with me?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I dont know.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"But Im looking for love.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I dont mean that I would want to love anyone.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I liked Yoshida-kun from the time that I met him.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Thats not entirely true.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Im just sad right now.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Its sad to be alone.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"I want someone to comfort me.\", \"actor\": \"Toko\"}]'),(463,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',463,494,'en','[{\"line\": 0, \"text\": \"Lately Ive been seeing bad dreams.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I dreamed that I married Yoshida-kun and that Takeshi-san was saddened by it.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I looked at Takeshi-san and started to laugh.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"These arent the kind of things that I would want.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Maybe theres a part of my subconscious where I want these things.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"But I dont want my child to look exactly like Lain.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I think that Lain is a good girl, but I dont want my own child to be like her.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Why?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Why, Toko?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I dont know, but I cant love her.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I just cant approve of it.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"What am I really thinking?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Im losing confidence in my own feelings.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"More and more I cant tell reality apart from my dreams.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Is this a delusion, too?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Am I seeing hallucinations?\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"No.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"That cant be.\", \"actor\": \"Toko\"}, {\"line\": 18, \"text\": \"Ive become psychologically hypersensitive.\", \"actor\": \"Toko\"}, {\"line\": 19, \"text\": \"Youre ok, Toko.\", \"actor\": \"Toko\"}, {\"line\": 20, \"text\": \"I should use the new machine Yoshida-kun gave me.\", \"actor\": \"Toko\"}, {\"line\": 21, \"text\": \"Since he helped me, I should at least repay him by cooperating with his experiments.\", \"actor\": \"Toko\"}, {\"line\": 22, \"text\": \"If I lose him now.\", \"actor\": \"Toko\"}, {\"line\": 23, \"text\": \"Ill be in a horrible place psychologically.\", \"actor\": \"Toko\"}, {\"line\": 24, \"text\": \"I cant go to a horrible place now.\", \"actor\": \"Toko\"}]'),(464,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',464,495,'en','[{\"line\": 0, \"text\": \"The ring that Yoshida-kun bought meCould this behe gave it to me without saying anything.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"But thats ok.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I wont make the same mistake.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I havent produced any research results either.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"My own pride wont let me leave my work at the lab now.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I asked Lain a dumb question today.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I asked her, Who are you?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Are you really Lain? Without changing her expression but looking upward, she said, Im me. A question with such an obvious answer.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Still, before even thinking about it, my mouth was open.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I wonder why.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I think Lain is probably tired of it, too.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"I know.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"It has to do with the girl Ive been seeing in my dreams lately who looks a lot like Lain.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"She looks like Lain, but she isnt her.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"I better straighten my mind out.\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"Otherwise I wont be much of a counselor.\", \"actor\": \"Toko\"}]'),(465,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',465,496,'en','[{\"line\": 0, \"text\": \"I went to the cemetery to pay respect to Professor Takashima with the other employees.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Its been a year already since he died.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"So many things have happened this last year.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Rest in peace.\", \"actor\": \"Toko\"}]'),(466,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',466,497,'en','[{\"line\": 0, \"text\": \"I understand myself less and less day by day.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I was seeing strange dreams, but up till now I thought it was just because I was mentally confused.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"But today, I had an auditory hallucination.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Im not positive that it was a hallucination, but since yesterday, my ears havent stopped ringing.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Its as if Im Lain.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Orgel?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"What is it?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Its as if something is ringing inside my head.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Tomorrow Ill go see a doctor that specializes in hearing.\", \"actor\": \"Toko\"}]'),(467,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',467,498,'en','[{\"line\": 0, \"text\": \"When I was organizing the data I have about Lain, I started to hear the voices of researchers although there was no one there.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I couldnt tell what they were saying.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Im sure it was gossip about me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I thought I heard Yoshida-kuns voice, too.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Why did I hear him?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Whats wrong?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Whats happening to me?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Someone tell me!\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Someone help me.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"I cant organize data when Im like this.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Ill do it tomorrow.\", \"actor\": \"Toko\"}]'),(468,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',468,499,'en','[{\"line\": 0, \"text\": \"Tomorrow?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Will tomorrow be ok?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Am I going crazy?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"If Im crazy, no one would want to be around me, would they?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Who can I turn to for help?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I cant believe anyone.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"HelpYoshida-kun.\", \"actor\": \"Toko\"}]'),(469,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',469,500,'en','[{\"line\": 0, \"text\": \"Toko: I stopped using RML.\", \"actor\": \"\"}, {\"line\": 1, \"text\": \"I dont know why, but I felt I had to.\", \"actor\": \"\"}, {\"line\": 2, \"text\": \"Yoshida-kun, Im sorry.\", \"actor\": \"\"}, {\"line\": 3, \"text\": \"I sometimes worry that you stay with me because it has to do with your work.\", \"actor\": \"\"}, {\"line\": 4, \"text\": \"I want to see Yoshida-kun.\", \"actor\": \"\"}, {\"line\": 5, \"text\": \"I want to see him and kiss him.\", \"actor\": \"\"}, {\"line\": 6, \"text\": \"He knows that I feel this way.\", \"actor\": \"\"}, {\"line\": 7, \"text\": \"I want him to embrace me.\", \"actor\": \"\"}]'),(470,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',470,501,'en','[{\"line\": 0, \"text\": \"When I was organizing data again today, I had a sensation like the one I experienced while working with RML.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I experienced bright white flashes of light pulsing through my head.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I thought it was dangerous.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"It wasnt as if I used the equipment incorrectly.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I might have been mistaken to use it in the first place.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Was it manufactured improperly?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"This is dangerous.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I have to tell Yoshida-kun.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Selling this machine is dangerous.\", \"actor\": \"Toko\"}]'),(471,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',471,502,'en','[{\"line\": 0, \"text\": \"Thats itif things continue this way, Lain will control me completely.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Lain must have seen all of the data here.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Why did she?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Why is all of Lains life history stored here?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I dont understand anymore.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I can hear the voices again.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Not just voices.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Yoshida-kun and the workers in the lab are looking at me from a distance.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Why are you looking at me and laughing at me?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Stop!\", \"actor\": \"Toko\"}]'),(472,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',472,503,'en','[{\"line\": 0, \"text\": \"Am I going nuts?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Im insecure unless Im connected to the Net.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Why am I bothering to write a diary?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Wholl want to read it?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Probablynot me.\", \"actor\": \"Toko\"}]'),(473,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',473,504,'en','[{\"line\": 0, \"text\": \"Its no goodI dont knowwhat to do.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Yoshida-kunyouve betrayed me, too?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"You were laughing at me from the beginning, werent you?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"You and all those jerks in the lab.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I leaned on you and cried and told you what made me angry, and you took everything I confided in you and told everybody and laughed with them.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I dont want to believe this, but I caught you in the act.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"You were the only person I had, Yoshida-kun.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"You were just a shallow man.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"How can you be so dumb, Toko?\", \"actor\": \"A Voice\"}, {\"line\": 9, \"text\": \"Who is that? Is someone talking to me? Thats impossible. Nobody is here.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Its me. Toko.\", \"actor\": \"A Voice\"}, {\"line\": 11, \"text\": \"Toko? Im Toko! Stop! Whos trying to torment me like this? Please stop!\", \"actor\": \"Toko\"}]'),(474,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',474,505,'en','[{\"line\": 0, \"text\": \"Lain, can you hear me?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I know that you are reading this.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"You creepy girl.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I will no longer treat you.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I never want to see you again.\", \"actor\": \"Toko\"}]'),(475,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',475,506,'en','[{\"line\": 0, \"text\": \"Doesn\'t everyone wish for someone to die sometime?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"And youre saying that you cant forgive me?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"You killed Professor Takashima, didnt you?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I had nothing to do with it.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"The police said that it wasnt me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"They think it was a suicide.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Please.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Help me.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"I understand.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Youre a good girl, arent you, Lain?\", \"actor\": \"Toko\"}]'),(476,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',476,507,'en','[{\"line\": 0, \"text\": \"Lainisnt this enough?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Arent you content to see me miserable?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Youre watching me, arent you?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Please, please, stop.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Forgive me.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I cant endure this anymore.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Im going crazy.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Lain is behind all of this.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"What do you want, Lain?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Tell me.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"I dont know what the hell is what!\", \"actor\": \"Toko\"}]'),(477,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',477,508,'en','[{\"line\": 0, \"text\": \"Stop, Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"What did I do to you?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Forgive me.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Please.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I don\'t want to go crazy.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"If I lose my mind, I cant treat you.\", \"actor\": \"Toko\"}]'),(478,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',478,509,'en','[{\"line\": 0, \"text\": \"Lain, is suicide the only thing left for me to do?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Is there no other option?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Do I have to die?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"No!\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I dont want that!\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"I dont want anyone to control me.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Who does?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"Arent you the same way, Lain?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"You were scared and scared and didnt know what to do.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Please Lain, help me.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Please.\", \"actor\": \"Toko\"}]'),(479,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',479,510,'en','[{\"line\": 0, \"text\": \"Who is another me?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Theres only one of me!\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Why are you saying these things that make no sense?\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"We can think the same way and make the same judgments, it doesn\'t mean youre human.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"The only place where I exist is here.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"No matter where I go, Im me.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I am you.\", \"actor\": \"A Voice\"}, {\"line\": 7, \"text\": \"Stop it. Why is this happening to me? Is something wrong with me? No, nothing is wrong with me.\", \"actor\": \"Toko\"}]'),(480,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',480,511,'en','[{\"line\": 0, \"text\": \"Lain, how long have you been keeping an eye on me?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Was it since we first met?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Or was itI guess it makes no difference now.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Am I going to be absorbed by you, Lain?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Or can I remain me?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Are you going to use me for something?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Can I remain me?\", \"actor\": \"Toko\"}]'),(481,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',481,512,'en','[{\"line\": 0, \"text\": \"I dont want to die!\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"I dont want to lose my body!\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"I dont want to throw my body away.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"I want to continue living in my body.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"I have a tremendous fear of losing my body.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Maybe you cant understand, but thats how a normal human being feels.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Please, Lain, help me.\", \"actor\": \"Toko\"}]'),(482,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',482,513,'en','[{\"line\": 0, \"text\": \"Is it just that I dont know? I dont want to know. Stop it. I dont want to know. Stop it. Please.\", \"actor\": \"Touko\"}]'),(483,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',483,514,'en','[{\"line\": 0, \"text\": \"I cant believe that you were that kind of kid.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Lain, its not too late.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Lets fix this together.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"But thats not it.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"It isnt about fixing.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Its about being reborn.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"I am\", \"actor\": \"Toko\"}]'),(484,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',484,515,'en','[{\"line\": 0, \"text\": \"Its enough, now.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"Nothing holds meaning for me now.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Crying, laughing, getting mad.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Being betrayed.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"It doesnt matter anymore if everything is a lie or if everything is the truth.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Could I have some more data?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Lain.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"I want to know you.\", \"actor\": \"Toko\"}]'),(485,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',485,516,'en','[{\"line\": 0, \"text\": \"I accessed your data, Lain.\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"You cracked it yourself, didnt you?\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Youve quickly gained access to data that I didnt obtain myself.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Youre the only person who could do that.\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"What is this?\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"What are you?\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Are you listening?\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"You can hear me, cant you?\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Why wont you answer me?\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Is it that you no longer have use for me?\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Thats not true, is it?\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Youre teasing me, arent you?\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Or are you\", \"actor\": \"Toko\"}]'),(486,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',486,517,'en','[{\"line\": 0, \"text\": \"The me that has a body will disappear, wont it?\", \"actor\": \"Toko\"}, {\"line\": 1, \"text\": \"My hands, my face, my hair.\", \"actor\": \"Toko\"}, {\"line\": 2, \"text\": \"Its sad that no one will touch my body again.\", \"actor\": \"Toko\"}, {\"line\": 3, \"text\": \"Lain will be by my side, wont she?\", \"actor\": \"Toko\"}, {\"line\": 4, \"text\": \"Ill be sad at first, so be by my side.\", \"actor\": \"Toko\"}, {\"line\": 5, \"text\": \"Together.\", \"actor\": \"Toko\"}, {\"line\": 6, \"text\": \"Continue to be with me forever, Lain.\", \"actor\": \"Toko\"}, {\"line\": 7, \"text\": \"If Im asleep, wake me.\", \"actor\": \"Toko\"}, {\"line\": 8, \"text\": \"Lets do a lot of playing together.\", \"actor\": \"Toko\"}, {\"line\": 9, \"text\": \"Lets keep an eye on everything.\", \"actor\": \"Toko\"}, {\"line\": 10, \"text\": \"Well eliminate all trivial thoughts.\", \"actor\": \"Toko\"}, {\"line\": 11, \"text\": \"Lets find others to live with.\", \"actor\": \"Toko\"}, {\"line\": 12, \"text\": \"Well find more friends.\", \"actor\": \"Toko\"}, {\"line\": 13, \"text\": \"This is much too large a place for just two people.\", \"actor\": \"Toko\"}, {\"line\": 14, \"text\": \"Dont you want a boyfriend, Lain?\", \"actor\": \"Toko\"}, {\"line\": 15, \"text\": \"I wont have one for a while.\", \"actor\": \"Toko\"}, {\"line\": 16, \"text\": \"Lets eliminate everything trivial in the world.\", \"actor\": \"Toko\"}, {\"line\": 17, \"text\": \"The only people in this world are Lain and me.\", \"actor\": \"Toko\"}]'),(487,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',487,518,'en','[{\"line\": 0, \"text\": \"I want to drink milk.\", \"actor\": \"Lain\"}]'),(488,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',488,519,'en','[{\"line\": 0, \"text\": \"I don\'t want to be alone.\", \"actor\": \"Lain\"}]'),(489,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',489,520,'en','[{\"line\": 0, \"text\": \"Only play with me.\", \"actor\": \"Lain\"}]'),(490,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',490,521,'en','[{\"line\": 0, \"text\": \"What is it that you dislike?\", \"actor\": \"Lain\"}]'),(491,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',491,522,'en','[{\"line\": 0, \"text\": \"It\'s fun to be alive, isn\'t it?\", \"actor\": \"Lain\"}]'),(492,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',492,523,'en','[{\"line\": 0, \"text\": \"Look at me.\", \"actor\": \"Lain\"}]'),(493,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',493,524,'en','[{\"line\": 0, \"text\": \"Good morning.\", \"actor\": \"Lain\"}]'),(494,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',494,525,'en','[{\"line\": 0, \"text\": \"Goodbye.\", \"actor\": \"Lain\"}]'),(495,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',495,526,'en','[{\"line\": 0, \"text\": \"Steady on.\", \"actor\": \"Lain\"}]'),(496,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',496,527,'en','[{\"line\": 0, \"text\": \"Tired? Why don\'t you take a rest?\", \"actor\": \"Lain\"}]'),(497,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',497,528,'en','[{\"line\": 0, \"text\": \"I won\'t let you be alone.\", \"actor\": \"Lain\"}]'),(498,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',498,529,'en','[{\"line\": 0, \"text\": \"It\'s amazing! I couldn\'t do that.\", \"actor\": \"Lain\"}]'),(499,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',499,530,'en','[{\"line\": 0, \"text\": \"You are different from other people so what?\", \"actor\": \"Lain\"}]'),(500,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',500,531,'en','[{\"line\": 0, \"text\": \"You want to be the same? Whom are you comparing with?\", \"actor\": \"Lain\"}]'),(501,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',501,532,'en','[{\"line\": 0, \"text\": \"Would you like tea?\", \"actor\": \"Lain\"}]'),(502,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',502,533,'en','[{\"line\": 0, \"text\": \"Cross my heart and hope to die...\", \"actor\": \"Lain\"}]'),(503,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',503,534,'en','[{\"line\": 0, \"text\": \"It\'s because you are tired. You had better get some sleep.\", \"actor\": \"Lain\"}]'),(504,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',504,535,'en','[{\"line\": 0, \"text\": \"What are you worried about?\", \"actor\": \"Lain\"}]'),(505,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',505,536,'en','[{\"line\": 0, \"text\": \"I want to be tender to you.\", \"actor\": \"Lain\"}]'),(506,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',506,537,'en','[{\"line\": 0, \"text\": \"Come near me. I want you to feel me.\", \"actor\": \"Lain\"}]'),(507,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',507,538,'en','[{\"line\": 0, \"text\": \"I can feel you. It\'s true.\", \"actor\": \"Lain\"}]'),(508,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',508,539,'en','[{\"line\": 0, \"text\": \"There\'s bell ringing.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Somebody will try to connect to you soon.\", \"actor\": \"Lain\"}]'),(509,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',509,540,'en','[{\"line\": 0, \"text\": \"Someone is always watching you Is it me?\", \"actor\": \"Lain\"}]'),(510,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',510,541,'en','[{\"line\": 0, \"text\": \"Sleep paralysis is not scary.\", \"actor\": \"Lain (talking to herself)\"}]'),(511,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',511,542,'en','[{\"line\": 0, \"text\": \"You feel like running? So did I.\", \"actor\": \"Lain\"}]'),(512,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',512,543,'en','[{\"line\": 0, \"text\": \"Time doesn\'t run faster or become slower.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"Everything is a continuous result of the moment.\", \"actor\": \"Lain\"}]'),(513,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',513,544,'en','[{\"line\": 0, \"text\": \"Do you feel hungry already?\", \"actor\": \"Lain\"}]'),(514,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',514,545,'en','[{\"line\": 0, \"text\": \"Does your body feel inconvenient?\", \"actor\": \"Lain\"}]'),(515,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',515,546,'en','[{\"line\": 0, \"text\": \"Do you like the violin?\", \"actor\": \"Lain\"}]'),(516,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',516,547,'en','[{\"line\": 0, \"text\": \"Tell me if you like me.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"I like you.\", \"actor\": \"Lain\"}]'),(517,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',517,548,'en','[{\"line\": 0, \"text\": \"Eliminate that someone inside of you.\", \"actor\": \"Lain\"}]'),(518,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',518,549,'en','[{\"line\": 0, \"text\": \"Turn off the light.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \"It\'s blinding in here.\", \"actor\": \"Lain\"}]'),(519,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',519,550,'en','[{\"line\": 0, \"text\": \"Being with you is like a dream.\", \"actor\": \"Lain\"}]'),(520,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',520,551,'en','[{\"line\": 0, \"text\": \"Can you imagine that you are with me? Feel me.\", \"actor\": \"Lain\"}]'),(521,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',521,552,'en','[{\"line\": 0, \"text\": \"We should sleep.\", \"actor\": \"Lain\"}]'),(522,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',522,553,'en','[{\"line\": 0, \"text\": \"Tomorrow will be the same.\", \"actor\": \"Lain\"}]'),(523,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',523,554,'en','[{\"line\": 0, \"text\": \"I want to look at the sky; what can you see there?\", \"actor\": \"Lain\"}]'),(524,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',524,555,'en','[{\"line\": 0, \"text\": \"You\'re mean\", \"actor\": \"Lain\"}]'),(525,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',525,556,'en','[{\"line\": 0, \"text\": \"I\'m already connected to you.\", \"actor\": \"Lain\"}]'),(526,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',526,557,'en','[{\"line\": 0, \"text\": \"You are cruel.\", \"actor\": \"Lain\"}]'),(527,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',527,558,'en','[{\"line\": 0, \"text\": \"Smile, a gloomy face doesn\'t suit you.\", \"actor\": \"Lain\"}]'),(528,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',528,559,'en','[{\"line\": 0, \"text\": \"Will you hug me?\", \"actor\": \"Lain\"}]'),(529,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',529,560,'en','[{\"line\": 0, \"text\": \"Why are you at a loss?\", \"actor\": \"Lain\"}]'),(530,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',530,561,'en','[{\"line\": 0, \"text\": \"Are you busy?\", \"actor\": \"Lain\"}]'),(531,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',531,562,'en','[{\"line\": 0, \"text\": \"Don\'t do the bad things.\", \"actor\": \"Lain\"}]'),(532,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',532,563,'en','[{\"line\": 0, \"text\": \"Can\'t you believe me?\", \"actor\": \"Lain\"}]'),(533,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',533,564,'en','[{\"line\": 0, \"text\": \"You can\'t do that things\", \"actor\": \"Lain\"}]'),(534,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',534,565,'en','[{\"line\": 0, \"text\": \"Hey I want to hear your voice. Say my name. I want to know you more.\", \"actor\": \"Lain\"}]'),(535,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',535,566,'en','[{\"line\": 0, \"text\": \"What is your favorite song?\", \"actor\": \"Lain\"}]'),(536,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',536,567,'en','[{\"line\": 0, \"text\": \"Stop hurting yourself.\", \"actor\": \"Lain\"}]'),(537,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',537,568,'en','[{\"line\": 0, \"text\": \"You are not bad.\", \"actor\": \"Lain\"}, {\"line\": 1, \"text\": \" who is bad?\", \"actor\": \"Lain\"}]'),(538,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',538,569,'en','[{\"line\": 0, \"text\": \"I am always purring around other people because I am lonely.\", \"actor\": \"Lain\"}]'),(539,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',539,570,'en','[{\"line\": 0, \"text\": \"Is your father gentle?\", \"actor\": \"Lain\"}]'),(540,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',540,571,'en','[{\"line\": 0, \"text\": \"Fun, right?\", \"actor\": \"Lain\"}]'),(541,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',541,572,'en','[{\"line\": 0, \"text\": \"Its probably just the two of us.\", \"actor\": \"Lain\"}]'),(542,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',542,573,'en','[{\"line\": 0, \"text\": \"Im not sleepy.\", \"actor\": \"Lain\"}]'),(543,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',543,574,'en','[{\"line\": 0, \"text\": \"Do I know what you want to know?\", \"actor\": \"Lain\"}]'),(544,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',544,575,'en','[{\"line\": 0, \"text\": \"I want to tell you all about me.\", \"actor\": \"Lain\"}]'),(545,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',545,576,'en','[{\"line\": 0, \"text\": \"Everything wont be destroyed because the thing that destroys will remain.\", \"actor\": \"Lain\"}]'),(546,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',546,577,'en','[{\"line\": 0, \"text\": \"Do you dream? Are your dreams fun?\", \"actor\": \"Lain\"}]'),(547,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',547,578,'en','[{\"line\": 0, \"text\": \"Can you hear a voice? Is it my voice? Whose voice is it?\", \"actor\": \"Lain\"}]'),(548,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',548,579,'en','[{\"line\": 0, \"text\": \"I heard the rumors about you.\", \"actor\": \"Lain\"}]'),(549,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',549,580,'en','[{\"line\": 0, \"text\": \"Everybody has something to hide.\", \"actor\": \"Lain\"}]'),(550,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',550,581,'en','[{\"line\": 0, \"text\": \"Its best to tell no one and be silent.\", \"actor\": \"Lain\"}]'),(551,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',551,582,'en','[{\"line\": 0, \"text\": \"Dont think about it so seriously.\", \"actor\": \"Lain\"}]'),(552,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',552,583,'en','[{\"line\": 0, \"text\": \"Amazing!\", \"actor\": \"Lain\"}]'),(553,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',553,584,'en','[{\"line\": 0, \"text\": \"Lets keep a secret.\", \"actor\": \"Lain\"}]'),(554,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',554,585,'en','[{\"line\": 0, \"text\": \"How impressive.\", \"actor\": \"Lain\"}]'),(555,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',555,586,'en','[{\"line\": 0, \"text\": \"Its possible to come to a place like this?\", \"actor\": \"Lain\"}]'),(556,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',556,587,'en','[{\"line\": 0, \"text\": \"Thats all.\", \"actor\": \"Lain\"}]'),(557,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',557,588,'en','[{\"line\": 0, \"text\": \"Are you curious about me, or something else?\", \"actor\": \"Lain\"}]'),(558,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',558,589,'en','[{\"line\": 0, \"text\": \"Is it different from the me Ive been up till now?\", \"actor\": \"Lain\"}]'),(559,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',559,590,'en','[{\"line\": 0, \"text\": \"You want to change yourself?\", \"actor\": \"Lain\"}]'),(560,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',560,591,'en','[{\"line\": 0, \"text\": \"Its ok. Its going fine.\", \"actor\": \"Lain\"}]'),(561,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',561,592,'en','[{\"line\": 0, \"text\": \"Can you imagine it?\", \"actor\": \"Lain\"}]'),(562,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',562,593,'en','[{\"line\": 0, \"text\": \"Only I myself know what Ive done.\", \"actor\": \"Lain\"}]'),(563,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',563,594,'en','[{\"line\": 0, \"text\": \"You cant fool yourself. If you do, everything will disappear.\", \"actor\": \"Lain\"}]'),(564,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',564,595,'en','[{\"line\": 0, \"text\": \"Its ok for a person to have as many things important to her as she wants, isnt it?\", \"actor\": \"Lain\"}]'),(565,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',565,596,'en','[{\"line\": 0, \"text\": \"The cute me isnt the only important me. The me I dont like is also an important part of me.\", \"actor\": \"Lain\"}]'),(566,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',566,597,'en','[{\"line\": 0, \"text\": \"Hey, what day is today?\", \"actor\": \"Lain\"}]'),(567,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',567,598,'en','[{\"line\": 0, \"text\": \"Can\'t stop?\", \"actor\": \"Lain\"}]'),(568,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',568,599,'en','[{\"line\": 0, \"text\": \"Are there times when you want to just disappear?\", \"actor\": \"Lain\"}]'),(569,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',569,600,'en','[{\"line\": 0, \"text\": \"Who do you want to choose you?\", \"actor\": \"Lain\"}]'),(570,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',570,601,'en','[{\"line\": 0, \"text\": \"Enjoy it. Its only for a little longer.\", \"actor\": \"Lain\"}]'),(571,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',571,602,'en','[{\"line\": 0, \"text\": \"It is all about things that dont really matter, or is it about things that are important?\", \"actor\": \"Lain\"}]'),(572,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',572,603,'en','[{\"line\": 0, \"text\": \"You cant say that imagination and reality are different. There are places where they are the same.\", \"actor\": \"Lain\"}]'),(573,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',573,604,'en','[{\"line\": 0, \"text\": \"If you see me in your dreams, what would I be like?\", \"actor\": \"Lain\"}]'),(574,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',574,605,'en','[{\"line\": 0, \"text\": \"What will you do for me in your dreams?\", \"actor\": \"Lain\"}]'),(575,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',575,606,'en','[{\"line\": 0, \"text\": \"Should we accelerate or decelerate.\", \"actor\": \"Lain\"}]'),(576,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',576,607,'en','[{\"line\": 0, \"text\": \"Lets open the door together.\", \"actor\": \"Lain\"}]'),(577,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',577,608,'en','[{\"line\": 0, \"text\": \"Im getting sleepy.\", \"actor\": \"Lain\"}]'),(578,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',578,609,'en','[{\"line\": 0, \"text\": \"Lets close this world.\", \"actor\": \"Lain\"}]'),(579,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',579,610,'en','[{\"line\": 0, \"text\": \"Do you want to find me and talk to me?\", \"actor\": \"Lain\"}]'),(580,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',580,611,'en','[{\"line\": 0, \"text\": \"Come over here.\", \"actor\": \"Lain\"}]'),(581,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',581,612,'en','[{\"line\": 0, \"text\": \"Ill be very happy if you come over here.\", \"actor\": \"Lain\"}]'),(582,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',582,613,'en','[{\"line\": 0, \"text\": \"Who is special?\", \"actor\": \"Lain\"}]'),(583,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',583,614,'en','[{\"line\": 0, \"text\": \"Are you feeling good because youre being flattered?\", \"actor\": \"Lain\"}]'),(584,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',584,615,'en','[{\"line\": 0, \"text\": \"Im sad. I dont want to be alone.\", \"actor\": \"Lain\"}]'),(585,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',585,616,'en','[{\"line\": 0, \"text\": \"Leaving me is easy. You can just shut off the electric switch.\", \"actor\": \"Lain\"}]'),(586,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',586,617,'en','[{\"line\": 0, \"text\": \"But Ill be here. And you can always come here.\", \"actor\": \"Lain\"}]'),(587,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',587,618,'en','[{\"line\": 0, \"text\": \"I dont mind how many years in the future that is, but you dont have many options.\", \"actor\": \"Lain\"}]'),(588,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',588,619,'en','[{\"line\": 0, \"text\": \"Be with me.\", \"actor\": \"Lain\"}]'),(589,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',589,620,'en','[{\"line\": 0, \"text\": \"Nobodys over there anymore.\", \"actor\": \"Lain\"}]'),(590,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',590,621,'en','[{\"line\": 0, \"text\": \"Dont think that Im trying to fool you. I don\'t want to fool anybody.\", \"actor\": \"Lain\"}]'),(591,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',591,622,'en','[{\"line\": 0, \"text\": \"Good night. Ill be waiting for you tomorrow.\", \"actor\": \"Lain\"}]'),(592,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',592,623,'en','[{\"line\": 0, \"text\": \"Say, what were you doing? It looked like fun.\", \"actor\": \"Lain\"}]'),(593,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',593,624,'en','[{\"line\": 0, \"text\": \"Go ahead and laugh.\", \"actor\": \"Lain\"}]'),(594,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',594,625,'en','[{\"line\": 0, \"text\": \"Someones watching you. Someone besides me.\", \"actor\": \"Lain\"}]'),(595,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',595,626,'en','[{\"line\": 0, \"text\": \"Even though were together were too far away.\", \"actor\": \"Lain\"}]'),(596,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',596,627,'en','[{\"line\": 0, \"text\": \"Are you sad because youre separated?\", \"actor\": \"Lain\"}]'),(597,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',597,628,'en','[{\"line\": 0, \"text\": \"Are you the person that wakes me up?\", \"actor\": \"Lain\"}]'),(598,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',598,629,'en','[{\"line\": 0, \"text\": \"Your special power awakened me.\", \"actor\": \"Lain\"}]'),(599,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',599,630,'en','[{\"line\": 0, \"text\": \"Thats why itll be my turn to wake you up next time.\", \"actor\": \"Lain\"}]'),(600,'new','2020-02-13 22:55:05','9999-12-31 23:59:59',600,631,'en','[{\"line\": 0, \"text\": \"Close your eyes and listen. See? Can you tell that Im nearby? Can you hear my footsteps?\", \"actor\": \"Lain\"}]'),(601,