(721,65,'jp','[{\"line\": 0, \"text\": \"何か楽しい話をしよっか、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"玲音ちゃんはお風呂とシャワーどっちが好き?\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"シャワー\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"そう、先生はお風呂のほうが好きだなー、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"でも先生のうちってユニットバスだからお風呂だと結構めんどくさいのよ、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"毎日遅くに帰ってからお風呂の支度するのがめんどくさくて、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"ついシャワーですましちゃうんだけどお休みの日は必ずお風呂にするの。\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"お風呂に入ると体中があったまるし気持ちよくならない?\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"お湯の中が怖いの\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"溺れちゃいそうで?\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"違うよ\", \"actor\": \"玲音\"}, {\"line\": 11, \"text\": \"じゃあ何が怖いの?\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"よくわからない、\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"でも体に何かが触れてるみたいだから\", \"actor\": \"玲音\"}, {\"line\": 14, \"text\": \"それは、お湯は少なくとも肌に触れてるけど\", \"actor\": \"柊子\"}]',0),
(722,66,'jp','[{\"line\": 0, \"text\": \"私変だから\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"ちょっと待って玲音ちゃん。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"玲音ちゃんは全然変な子じゃないのよ。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"子供の頃ってみんなそうなの、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"天井の木の模様が人の目に見えたり想像力が旺盛な子だったらみんなそういう経験は持ってるのよ\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"でも違う\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"玲音ちゃんは人と違う自分って嫌い?\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"よくわからない\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"いい?\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"人と違うから玲音ちゃんなのよ、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"玲音ちゃんはとっても想像力があってそれはねすごくいいことなの、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"だから自分のこと変な子なんて言っちゃダメ、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"先生は全然そんなこと思ってないよ\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"でもテストした\", \"actor\": \"玲音\"}, {\"line\": 14, \"text\": \"ごめんなさいでもね、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"はぁ、玲音ちゃんは困ってるんだよね、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"先生は玲音ちゃんを困らなくていいようにしてあげたいの、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"それにはね玲音ちゃんのこといろいろ知らなくちゃいけないの、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"でも玲音ちゃんが嫌がることはしたくない、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"玲音ちゃんの困ってることを解決するために先生ができるのは手助けだけなの、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"玲音ちゃんのことは玲音ちゃんにしかわからないでしょ、\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"玲音ちゃんが自分で解決するしかないの、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"先生が手助けするにも玲音ちゃんの協力が必要なの\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"私は何をすればいいの?\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"今は何もしなくていいわ、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"先生と玲音ちゃんは友達なんだから、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"ゆっくりゆっくり一緒に話をしていけば玲音ちゃんのこといろいろわかってくるから、\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"そしたら先生がこうしたほうがいいんじゃないかっていうから、\", \"actor\": \"柊子\"}, {\"line\": 28, \"text\": \"玲音ちゃんはそれを