(732,76,'jp','[{\"line\": 0, \"text\": \"おはよう玲音\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"おはようございます柊子さん\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"あれから何か見えたりした?\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"ううん見えないよホントだよ\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"何か聞こえた?\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"なんかごちゃごちゃ言って聞こえるときはあるけど\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"ごちゃごちゃ?\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"一人の人?\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"それとも大勢?\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"大勢、男の人も女の人も\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"誰か知ってる人っている?\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"たぶんいないと思う\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"玲音は空から聞こえるって言うけど空には何があると思う?\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"空気はあるよね\", \"actor\": \"玲音\"}, {\"line\": 14, \"text\": \"そうね、他には?\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"雲とか太陽とか星とか\", \"actor\": \"玲音\"}, {\"line\": 16, \"text\": \"携帯電話やテレビの電波って知ってる?\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"しってる、\", \"actor\": \"玲音\"}, {\"line\": 18, \"text\": \"無線で送られてくるんだよね\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"そう、空を飛んでくるの\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"その声が聞こえるの?\", \"actor\": \"玲音\"}, {\"line\": 21, \"text\": \"わたし\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"うーんそういうイメージから作り出されたものかもしれない\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"そういう病気があるの?\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"あるわ、妄想っていうの、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"神経症とか精神分裂病とかの症状で簡単に言ったたら気にしすぎたりストレスとかでそういう妄想を抱くこともあるわ、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"ただ\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"ただ?\", \"actor\": \"玲音\"}, {\"line\": 28, \"text\": \"たいていの場合自分にある程度関係したことが聞こえるの、\", \"actor\": \"柊子\"}, {\"line\": 29, \"text\": \"例えば自分の悪口とか\", \"actor\": \"柊子\"}, {\"line\": 30, \"text\": \"被害妄想?\", \"actor\": \"玲音\"}, {\"line\": 31, \"text\": \"そう、玲音は勉強しちゃったんだもんね、\", \"actor\": \"柊子\"}, {\"line\": 32, \"text\": \"いいわ勉強ついでに教えてあげる、\", \"actor\": \"柊子\"}, {\"line\": 33, \"text\": \"妄想の定義ってね、\", \"actor\": \"柊子\"}, {\"line\": 34, \"text\": \"内容が誤っていたりその考えの発生根拠が客観的でなかったり、\", \"actor\": \"柊子\"}, {\"line\": 35, \"text\": \"簡単に言うと間違ったことを自分の中ではそれが正しいって信じ込んでる状態なの、\", \"actor\": \"柊子\"}, {\"line\": 36, \"text\": \"玲音の感じだと自分がそう感じたかもしれないって言ってるけど確信してない状態はもうとは言わないの専門用語だけど念慮っていうの\", \"actor\": \"柊子\"}, {\"line\": 37, \"text\": \"念慮?\", \"actor\": \"玲音\"}, {\"line\": 38, \"text\": \"まあその言葉は忘れてくれてもいいわ、\", \"actor\": \"柊子\"}, {\"line\": 39, \"text\": \"さっきも言ったけどたいていの場合それは自分に結び付いたことなの、\", \"actor\": \"柊子\"}, {\"line\": 40, \"text\": \"あんまり関係ない妄想ってないのよ\", \"actor\": \"柊子\"}, {\"line\": 41, \"text\": \"妄想じゃない?\", \"actor\": \"玲音\"}, {\"line\": 42, \"text\": \"基本的にそうね、\", \"actor\": \"柊子\"}, {\"line\": 43, \"text\": \"ただ妄想にもいろいろ種類があって無理やり当てはめようとすると何とでも当てはまっちゃうの\", \"actor\": \"柊子\"}]',0),
(733,77,'jp','[{\"line\": 0, \"text\": \"当てはめると何になるの?\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"そうね、影響妄想って言って何かに影響されているって感じの妄想かな、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"ラジオやテレビや携帯電話に影響を受ける妄想加えてその影響で皮膚を触られる撫でられる電機が走るとか体に感じる幻覚を同時に起こすこともあるの、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"玲音は何かに触られてるって感じたことがあるって言ってたよね\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"うん\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"妄想じゃなくてもセネストパチーっていう病気も実際あるし、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"そう、それから玲音は自分が自分でないような気がするって言ってたわねそれは虚無妄想と言って自分が空虚で存在しない世界も存在しないという妄想にも当てはまるわ、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"これは自己を過小評価することから来る妄想ね\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"自己を過小評価?\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"自分のことをなんてつまらない人間なんだとか悲観的に考えることよ、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"さっきの被害妄想も同じ種類の妄想なの\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"いっぱい病気なんだ、\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"わたし\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"まだあるわ\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"もういいよ\", \"actor\": \"玲音\"}, {\"line\": 15, \"text\": \"心気妄想っていって自分に特別な病気があるって信じる妄想、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"自分が病気だと思っちゃう病気なの、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"ほんとは病気じゃないのに、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"先生はね玲音の問題の多くはこれじゃないかって思うの\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"ほんとは病気じゃない病気?\", \"actor\": \"玲音\"}, {\"line\": 20, \"text\": \"そう、自分に自信がなくてちょっとの不安が怖い夢と一緒になって病気って思いこんでる\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"治るの?\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"そうよ、だってホントは病気じゃないもの、\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"ごめんね脅かしちゃって、\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"でもね中途半端な知識で考えると精神医学はどうにでも取れちゃうのよ、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"病気に出来ちゃうの、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"たいていの人は本人や周りの人が普通に生活できなくて困ってお医者さんに来るから病気として治療するの、\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"その時にどうやって直したらいいかを判断する基準でもあるの、\", \"actor\": \"柊子\"}, {\"line\": 28, \"text\": \"だから玲音のように普通に生活できている人は病院に行かなかったら病気にはならない\", \"actor\": \"柊子\"}]',0),
(734,78,'jp','[{\"line\": 0, \"text\": \"病院に来たから病気になったの?\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"そうね、でも玲音は生活できるけど困ってるよね、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"それをなくすのはカウンセラーの仕事でもあるの\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"柊子さんはカウンセラーなの?\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"前はお医者さんだって\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"私はどちらの資格も持ってるの\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"何が違うの?\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"精神的な問題で困ってる人を助けるって点で本質的には何も違わないわ、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"ただ資格や治療方法が違ってるだけ、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"だけどそれぞれ長所や短所があってもうちょっとお互いのやり方を尊重しあえて一緒に治療できたらいいかなって思うの、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"そう思って先生は両方の資格を取ったの\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"いまは、私と会うときはどっち?\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"カウンセラーよ、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"玲音の場合はそれがいいって思ったの\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"私精神病じゃないの?\", \"actor\": \"玲音\"}, {\"line\": 15, \"text\": \"私が見えるあれは幻覚なんだって\", \"actor\": \"玲音\"}, {\"line\": 16, \"text\": \"確かに病気でそういった幻覚や妄想の症状が起こることはあるわ、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"でも、これは直観だけど玲音はそんな感じしないわ\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"私気にしすぎ?\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"ひょっとしたらそうかもね、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"見えすぎたり聞こえすぎたりすることが玲音にストレスを与えてるのかもしれないわ\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"どうすればいいの柊子さん\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"うーん気にしないでって言っても気になるものね\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"でももう気になってないよ、\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"慣れてきた、\", \"actor\": \"玲音\"}, {\"line\": 25, \"text\": \"でもあれは慣れない\", \"actor\": \"玲音\"}, {\"line\": 26, \"text\": \"あれって?\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"私が今の私じゃないの\", \"actor\": \"玲音\"}, {\"line\": 28, \"text\": \"どんな玲音なの?\", \"actor\": \"柊子\"}, {\"line\": 29, \"text\": \"私なの、その人も\", \"actor\": \"玲音\"}, {\"line\": 30, \"text\": \"どうしてそう思うの\", \"actor\": \"柊子\"}, {\"line\": 31, \"text\": \"よくわからない、\", \"actor\": \"玲音\"}, {\"line\": 32, \"text\": \"柊子さんは自分が自分だと思えないことってない?\", \"actor\": \"玲音\"}, {\"line\": 33, \"text\": \"うーん後から考えると私何考えてたんだろうってあるけどその時に自分じゃないって感じたことはないな、\", \"actor\": \"柊子\"}, {\"line\": 34, \"text\": \"玲音は自分が二人いるって思うの?\", \"actor\": \"柊子\"}, {\"line\": 35, \"text\": \"違う。\", \"actor\": \"玲音\"}, {\"line\": 36, \"text\": \"私は私でそうじゃないの\", \"actor\": \"玲音\"}, {\"line\": 37, \"text\": \"不思議ね、\", \"actor\": \"柊子\"}, {\"line\": 38, \"text\": \"でも玲音がそう感じるならそれはそうなのよ、\", \"actor\": \"柊子\"}, {\"line\": 39, \"text\": \"間違ってなんかないのよ\", \"actor\": \"柊子\"}, {\"line\": 40, \"text\": \"でも普通じゃない\", \"actor\": \"玲音\"}]',0),
(735,79,'jp','[{\"line\": 0, \"text\": \"玲音は普通にあこがれてるのね\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"たぶん\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"あたしはうらやましいわ、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"人間の感覚なんていい加減なものでそう感じたら思い込んじゃうものなのよ、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"私が私であるなんて絶対説明できないことでしょ、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"でもそのことに疑問を持てるってそれはそれで特別な能力なのよ\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"いらないよ、\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"そんなの\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"そうね、今はいらないって気持ちが強いと思うの。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"でもそれがきっと玲音にとっていいことも起こしてくれると思うのよ、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"よくわからないけどそれも含めて玲音なんだって自分で思えるようになった時きっと玲音の役に立つと思うわ\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"私はこのまま?\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"分からないわ、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"自然に変わっていくかもしれないしこのままもう怖い夢を見ないかも、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"そしたらもう玲音は普通の子と変わらないわ\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"でも治ったかわからない、\", \"actor\": \"玲音\"}, {\"line\": 16, \"text\": \"いつまた見るかもしれない\", \"actor\": \"玲音\"}, {\"line\": 17, \"text\": \"そうね、でもこれからは違うわ\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"違うって?\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"玲音は1人じゃないってこと、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"先生に言ってくれれば先生が相談に乗れるってこと\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"そうだね、\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"柊子さん\", \"actor\": \"玲音\"}]',0),
(736,80,'jp','[{\"line\": 0, \"text\": \"おはよう柊子さん\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"おはよう調子はどう、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"元気にしてた?\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"うん、先月なんかいろいろ柊子さんに聞いてなんとなくだけど楽な気分になれたの\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"そう、よかったわ、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"ほんとはああいう話をするのはいけないってされてることなの、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"余計に憶測して取り返しのつかない状態になるケースも実際にあるのよ\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"取り返しのつかない?\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"うーん病気が悪化しちゃったり、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"ひどいときには自分を傷つけたり\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"自殺?\", \"actor\": \"玲音\"}, {\"line\": 11, \"text\": \"まぁ最悪のケースだけどね\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"私死にたくない\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"もちろんよ、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"玲音はそんな状態なんか程遠いわ、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"でも人は精神的に病んでしまうと私たちと別な価値観で動いてしまうことがあるの\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"別な価値観?\", \"actor\": \"玲音\"}, {\"line\": 17, \"text\": \"命を失うことや体を傷付けることに恐怖を感じなくなったり、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"逆にそれが自分にとってすごくいいことだって思いこんじゃうの、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"そんなの理解できないでしょ?\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"私は怖い\", \"actor\": \"玲音\"}, {\"line\": 21, \"text\": \"ほら、玲音は私と一緒でしょ、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"普通なのよそれが\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"うん!\", \"actor\": \"玲音\"}]',0),
(737,81,'jp','[{\"line\": 0, \"text\": \"あれからは何か見えたりとか聞こえたりした?\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"ないよ\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"やっぱりねー気の持ちようなのよ玲音、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"多分もう見えないって思うけどまた見えてもがっかりしないで、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"ゆっくり治せばいいの\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"まだ治らないの\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"うーん分からないわ、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"玲音が初めてそうなったのはいくつの時からなの?\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"11歳の時\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"じゃあもう1年以上にもなるのね、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"その間に何回くらいそうなったの?\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"覚えてない、\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"いっぱい\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"こういうのって突然明らかな原因がなくても急に見なくなったりするの、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"ただ見なくなったからって安心しちゃうとまた見えちゃうケースもあるのよ、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"ああ、必ずしもそうなるとは言えないけどね、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"だから見なくなってもしばらくは今まで通り通ってもらったりしてるの、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"ねえ玲音はもうここには来たくたくない?\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"そんなことないよ、\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"柊子さんに会いたいって思うことあるもの\", \"actor\": \"玲音\"}, {\"line\": 20, \"text\": \"ありがと、\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"そしたら問題ないわね、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"なんにも問題ないけどしばらくはちゃんと先生のところに来るって約束してね\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"うん、その代わりいろいろ教えてほしいの\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"なーに宿題とか?\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"いいわよ、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"こう見えても先生は一応中学の時は学年でもトップクラスだったんだから、\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"でも国語はちょっとダメかな\", \"actor\": \"柊子\"}, {\"line\": 28, \"text\": \"違うの、先生のお仕事のこともっと知りたいの、\", \"actor\": \"玲音\"}, {\"line\": 29, \"text\": \"