(1085,254,'jp','[{\"line\": 0, \"text\": \"今日は美里ちゃんといろいろな部活を見学した。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"美里ちゃんは運動は得意みたいだけど、\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"私は運動はやっぱり駄目だから文科系かな?\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"美里ちゃんはバイオリンを子供のころからやっててコンクールとかも出ていたらしい、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"すごい。\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"パソコンもやっていたみたいだったけど私は言わなかった。\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"何だかネットのことは秘密にしておきたかったの。\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"だってネットでは気を使わない自分でいたいから、\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"やっぱり部活はバラバラになりそうかな\", \"actor\": \"玲音\"}]',0),
(1086,255,'jp','[{\"line\": 0, \"text\": \"柊子さんのところに行って報告してきた。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"美里ちゃんのことばっかり話しちゃった。\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"元気になったねって言われて照れ臭かった。\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"そういえば柊子さんは部活何やってたんだろう、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"運動とか得意そうだもんね。\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"確か柊子さんはアメリカにいたって言ってたから日本とは違うかもしれないな\", \"actor\": \"玲音\"}]',0),
(1087,256,'jp','[{\"line\": 0, \"text\": \"美里ちゃんが私と一緒の部活にしようって言ってた。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"音楽は美里ちゃんとレベルが違うし、\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"美里ちゃんも部活までは音楽をやりたくないって言ってたし、\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"郷土研究会の人たちが優しそうだったけど、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"ちょっと、\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"なんだかだし。\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"部活はやっぱり美術部にしようかな、\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"絵はむかしから好きだったから、\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"何とかやっていけるかな\", \"actor\": \"玲音\"}]',0),
(1088,257,'jp','[{\"line\": 0, \"text\": \"美里ちゃんって私のことすごく大事にしてくれる。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"いつも私たち一緒、\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"美里ちゃんが休んだりしたらすごく寂しいな。\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"もう一人くらい友達がいたらなぁ。\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"私って浮気性?\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"なんかずるい考えかただよね。\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"美里ちゃんは一人でも大丈夫なのかな、\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"明日休んでみたら美里ちゃんはどう思ってくれるかな。\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"そんなことして試したら嫌われるのは当たり前だよね\", \"actor\": \"玲音\"}]',0),
(1089,258,'jp','[{\"line\": 0, \"text\": \"別に本当に休もうとしたわけじゃないのに頭が痛くて、\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"アレが見えて結局休んだ。\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"お母さんも仕事に行って独りぼっちだった。\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"小学校の時は割と平気だったのに、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"なんでこんなにさみしくてつらいんだろう、\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"美里ちゃんのせいかな?\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"お父さんに電話してみようかな?\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"でも国際電話って高いから、\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"お母さん怒るかな?\", \"actor\": \"玲音\"}]',0),
(1090,259,'jp','[{\"line\": 0, \"text\": \"美里ちゃんは強いのかな。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"私が休んでも平気だったみたい。\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"ちょっとがっかり?\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"私って独占欲が強いのかな、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"もしかして私以外にも友達がいて私が独占してるだけ?\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"やだな、疑ってばっかり。\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"私また嫌な子になってる。\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"部活で先輩たちと一緒に、\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"画材屋さんにスケッチブックを買いに行った。\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"パステルがかわいかったんでクロッキーと一緒に買って、\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"晩御飯の前に絵をかいていたらお母さんに怒られた。\", \"actor\": \"玲音\"}, {\"line\": 11, \"text\": \"においが結構するみたい、\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"私はそんなに嫌いなにおいじゃないかな。\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"勉強も部活も頑張るぞ!\", \"actor\": \"玲音\"}, {\"line\": 14, \"text\": \"ファイト玲音!\", \"actor\": \"玲音\"}, {\"line\": 15, \"text\": \"なんちゃって\", \"actor\": \"玲音\"}]',0),
