(1097,266,'jp','[{\"line\": 0, \"text\": \"お父さんが柊子さんに、\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"ってお土産を渡すよう頼まれたから学校が終わって先生のところに行ってきた。\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"なんか柊子さん元気がなくてちょっと雰囲気が変わってた。\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"お土産の袋も明けなかったし、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"疲れているのかな?\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"何が入ってたんだろう、\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"化粧品かなぁ。\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"お母さんも化粧品とか高そうなバッグだったし。\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"私もいつか化粧とかするんだろうな。\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"この顔で化粧したら似合わないよね。\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"美里ちゃんとかは大人びた感じだからきっと似合うよね。\", \"actor\": \"玲音\"}, {\"line\": 11, \"text\": \"あ、お父さんがお土産余ったって言ってたから美里ちゃんの分ももらおう\", \"actor\": \"玲音\"}]',0),
