(1101,270,'jp','[{\"line\": 0, \"text\": \"今日も美里ちゃんが休んだ。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"先生に聞いてお見舞いに行こうって思ったら昨日美里ちゃんのお母さんが来て、\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"本人の希望でお見舞いはご遠慮させてくださいって言われたんだって。\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"どこが悪いんだろう、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"あんなに元気だったのに。\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"そういえば美里ちゃんってあんまり自分のこと話さないもんね。\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"私も一緒だけど。\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"私の場合は話したら嫌われちゃうもん。\", \"actor\": \"玲音\"}]',0),
(1102,271,'jp','[{\"line\": 0, \"text\": \"美里ちゃんにはホントは話したいんだよね、\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"でも私は臆病者だから、\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"変わってない?\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"それとも臆病な私は死んでしまったほうがいいの?\", \"actor\": \"玲音\"}]',0),
(1103,272,'jp','[{\"line\": 0, \"text\": \"美里ちゃんが学校に来たけど、\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"まるで何にもなかったかのように普通だった。\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"病気って何だったの?\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"って聞いたら、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"風邪だって言ってたけどとにかく治ってよかった。\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"明日渡せなかったお土産を持っていこう。\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"久しぶりだったから部活はお休みして帰りにアイスクリーム屋さんに行った。\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"やっぱり美里ちゃんがいると楽しい、\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"よかった元気になって。\", \"actor\": \"玲音\"}]',0),
