(1193,362,'jp','[{\"line\": 0, \"text\": \"柊子さんの研究室の端末のアドレス調べておこう。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"久しぶりにクラッキングしがいがあるかな\", \"actor\": \"玲音\"}]',0),
(1194,363,'jp','[{\"line\": 0, \"text\": \"一昨日から今朝までかかったけどどうしても入れなかった。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"なんで?\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"研究室まで入れるのに、\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"患者のデータで柊子さんの受け持ちのクランケのデータはあるのに?\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"ファイル名だけしかなくてデータがない、\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"ファイル情報を見てみると確実に更新されているのにデータが見えない、\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"暗号化されているわけでもないし、\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"私の情報がほかのクランケに比較して重要なわけなんかないのに、\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"私だけがいない、\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"どういうこと?\", \"actor\": \"玲音\"}]',0),
