(1445,433,'jp','[{\"line\": 0, \"text\": \"今日、タケシさんが日本に帰ってしまった。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"空港までの間、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"何を話したらいいかわからなくて言葉を探してしまった。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"タケシさん気を悪くしたかな。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"でもタケシさんは優しいよね、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"なんにも言わないでずっと手をつないでてくれた、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"日本で待ってるよって。\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"そ、また少ししたら日本でタケシさんに会える\", \"actor\": \"柊子\"}]',0),
(1446,434,'jp','[{\"line\": 0, \"text\": \"今日、タケシさんからエアメールが届いた。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"卒業のお祝いは日本でね。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"だって、うれしいー!\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"タケシさんも研究所で頑張ってるみたい。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"今まではタケシさんがいない寂しさに浸っていたけど、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"恥ずかしいわ私。\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"タケシさんがこんなに頑張ってるんだもん。\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"私も頑張らなきゃ。\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"頑張って研究しては約誰にも認められるような研究者になろ。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"はぁ早く日本に帰りたい。\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"タケシさんが待ってる、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"日本。\", \"actor\": \"柊子\"}]',0),
