(1448,436,'jp','[{\"line\": 0, \"text\": \"ただいま。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"5年ぶりの日本。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"やっぱり日本はいいなー、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"なーんて。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"アメリカ生活もよかったけどやっぱり日本に帰ってくると落ち着く。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"でも少し怖い。\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"海外で長く暮らして戻ってくると、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"なぜか自分だけ取り残されてるじゃないかって思ってしまう。\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"中学の時も不安だったなー。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"学校に行くのをぐずってお母さんに叱られたっけ、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"でも5年間なんてあっという間だったなー、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"ホームシックに掛かる暇なんかなかったし、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"とにかく一生懸命だったよね柊子、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"勉強勉強で、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"でもちゃんと友達もできたし、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"楽しいこともつらいことも、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"失恋もしたけど恋もしたもんね。\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"こうして振り返ると、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"何が一番自分の中で変わったかな、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"うふふふ、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"大丈夫大丈夫。\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"自信をもって、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"柊子ファイト。\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"明日から研究室に初出勤じゃないの、\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"頑張らなくちゃ、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"こんな不安は今日でおしまいにしないと、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"明日研究所に行ったらタケシさんに会えるかもしれないしね\", \"actor\": \"柊子\"}]',0),
