(1670,547,'ru','[{\"line\": 0, \"text\": \"Дай знать..., если я тебе нравлюсь.\", \"actor\": \"Lain\", \"comment\": \"\"}, {\"line\": 1, \"text\": \"Ты мне нравишься. \", \"actor\": \"Lain\", \"comment\": \"\"}]',10),
(1671,548,'ru','[{\"line\": 0, \"text\": \"Устрани того, кто внутри тебя.\", \"actor\": \"Lain\", \"comment\": \"\"}]',10),
(1672,549,'ru','[{\"line\": 0, \"text\": \"Выключи свет.\", \"actor\": \"Lain\", \"comment\": \"\"}, {\"line\": 1, \"text\": \"Сильно ослепляет.\", \"actor\": \"Lain\", \"comment\": \"\"}]',10),
(1673,651,'ru','[{\"line\": 0, \"text\": \"Улыбнись, хмурое лицо тебе не идет.\", \"actor\": \"Touko\", \"comment\": \"\"}]',10),
(1674,652,'ru','[{\"line\": 0, \"text\": \"Обнимешь меня?\", \"actor\": \"Touko\", \"comment\": \"\"}]',10),
(1675,550,'ru','[{\"line\": 0, \"text\": \"С тобой я будто во сне.\", \"actor\": \"Lain\", \"comment\": \"\"}]',10),
(1676,551,'ru','[{\"line\": 0, \"text\": \"Можешь представить себя со мной? Почувствуй меня.\", \"actor\": \"Lain\", \"comment\": \"\"}]',10),
(1677,552,'ru','[{\"line\": 0, \"text\": \"Пойдем спать.\", \"actor\": \"Lain\", \"comment\": \"\"}]',10),
(1678,654,'ru','[{\"line\": 0, \"text\": \"Ты просто не можешь этого сделать.\", \"actor\": \"Toko\", \"comment\": \"\"}]',10),
(1679,656,'ru','[{\"line\": 0, \"text\": \"Хватит причинять себе боль.\", \"actor\": \"Toko\", \"comment\": \"\"}]',10),
