(1683,556,'ru','[{\"line\": 0, \"text\": \"Я уже связана...с тобой.\", \"actor\": \"Lain\", \"comment\": \"\"}]',10),
(1686,657,'ru','[{\"line\": 0, \"text\": \"Я всегда \\\"мурлыкаю\\\" среди людей, из-за чувства одиночества.\", \"actor\": \"Toko\", \"comment\": \"\"}]',10),
