(679,'new','2020-03-16 10:14:14','2020-03-16 10:58:41',678,11,'ru','[{\"line\": 0, \"text\": \"Ты сказала, что мы можем навестить тебя после школы.\", \"actor\": \"Girl\"}, {\"line\": 1, \"text\": \"Правда?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Слушай, Лэйн!\", \"actor\": \"Girl2\"}, {\"line\": 3, \"text\": \"Ты играешь в игры всё время.\", \"actor\": \"Girl2\"}, {\"line\": 4, \"text\": \"Ты никогда не слушаешь во время занятий ... но ты всегда получаешь 100% на тестах.\", \"actor\": \"Girl2\"}, {\"line\": 5, \"text\": \"Это не так.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"Так! Так!\", \"actor\": \"Girl\"}, {\"line\": 7, \"text\": \"Лейн, твой папа классный, не так ли?\", \"actor\": \"Girl2\"}, {\"line\": 8, \"text\": \"Мы увидим его сегодня?\", \"actor\": \"Girl2\"}, {\"line\": 9, \"text\": \"Мне жаль.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"Я сегодня не могу.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Извините.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"Я уверена, что она свободна ...\", \"actor\": \"Girl\"}, {\"line\": 13, \"text\": \"Да...\", \"actor\": \"Girl2\"}]',0),
(680,'edit','2020-03-16 10:16:26','9999-12-31 23:59:59',3,11,'en','[{\"line\": 0, \"text\": \"You said we could visit you after school.\", \"actor\": \"Girl-1\"}, {\"line\": 1, \"text\": \"Really?\", \"actor\": \"Lain\"}, {\"line\": 2, \"text\": \"Listen, Lain!\", \"actor\": \"Girl-2\"}, {\"line\": 3, \"text\": \"You play games all the time.\", \"actor\": \"Girl-2\"}, {\"line\": 4, \"text\": \"You never listen during classes… and then you get 100% on tests.\", \"actor\": \"Girl-2\"}, {\"line\": 5, \"text\": \"It\'s not relevant.\", \"actor\": \"Lain\"}, {\"line\": 6, \"text\": \"It is! It is!\", \"actor\": \"Girl\"}, {\"line\": 7, \"text\": \"Lain, your dad is cool, isn\'t he?\", \"actor\": \"Girl-2\"}, {\"line\": 8, \"text\": \"Will we see him today?\", \"actor\": \"Girl-2\"}, {\"line\": 9, \"text\": \"I\'m sorry.\", \"actor\": \"Lain\"}, {\"line\": 10, \"text\": \"I really can\'t today.\", \"actor\": \"Lain\"}, {\"line\": 11, \"text\": \"Sorry.\", \"actor\": \"Lain\"}, {\"line\": 12, \"text\": \"I\'m sure she\'s free…\", \"actor\": \"Girl-1\"}, {\"line\": 13, \"text\": \"Yeah…\", \"actor\": \"Girl-2\"}]',0),
