(865,'edit','2020-10-03 15:23:20','9999-12-31 23:59:59',718,62,'jp','[{\"line\": 0, \"text\": \"こんにちは玲音ちゃん、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"元気だった?\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"うん、先生は\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"先生はもちろん元気よ、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"ちょっと疲れてるけど\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"つかれてるの?\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"そうね、これでも結構忙しいのよ\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"どのくらい?\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"うーんそうだなー、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"こうやって玲音ちゃんとゆっくりお話しするのが唯一の楽しみくらい、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"かな\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"本当?\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"本当よ、今日は何の話をしよっか、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"玲音ちゃんはなにをはなしたい?\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"先生のこと、\", \"actor\": \"玲音\"}, {\"line\": 15, \"text\": \"先月はあんまり聞けなかった\", \"actor\": \"玲音\"}, {\"line\": 16, \"text\": \"うーん、先生の話なんかより玲音ちゃんの話が先生は聞きたいんだけど、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"そうね、一方的に聞くのも平等じゃないわね、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"私が話したら今度は玲音ちゃんの番よ\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"うん\", \"actor\": \"玲音\"}, {\"line\": 20, \"text\": \"何から話せばいいかわからないけど米良柊子、\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"27歳、東京都世田谷区生まれ、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"橘総合研究所精神病理研究室ドクター、\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"こんなとこよ。\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"あ、これじゃ話になんないよね。\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"先生はね人の話を聞くのは慣れてるんだけど、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"自分の子と話すのはあんまり得意じゃないのよ、\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"玲音ちゃんのききたいことってなんかある?\", \"actor\": \"柊子\"}, {\"line\": 28, \"text\": \"どうやって先生になったの?\", \"actor\": \"玲音\"}, {\"line\": 29, \"text\": \"うん、大学ってところでいっぱい勉強して論文ってものを書いてそれが認められると博士号がもらえてそれで先生になれるの\", \"actor\": \"柊子\"}]',0),
(866,'edit','2020-10-03 15:23:20','9999-12-31 23:59:59',719,63,'jp','[{\"line\": 0, \"text\": \"大学ってどんなところ?\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"玲音ちゃんは今中学生よね、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"そこでもっと勉強したい人は高校へ行くでしょ、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"それでもまだ勉強したい人が行くところが大学なの。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"先生は勉強が好きなの?\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"昔は嫌いだったな、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"でも中学の時にアメリカに留学してちょっと考えが変わったかな\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"留学してたの?\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"すごい!\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"うーん、その頃は先生も親に勧められていっただけなの、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"ただ外国って日本と考え方や価値観も違っていてすごく勉強になったの、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"今思うと大変だったしはじめは不安だらけだったけど今は留学させてもらったことを感謝してるくらい、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"それと英語の勉強にもなったしね\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"すごいんだ柊子先生\", \"actor\": \"玲音\"}, {\"line\": 14, \"text\": \"そんなことないよ先生よりももっと勉強してる人なんていっぱいいるわよ、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"先生はどっちかといえば怠け者で割と遊んでたかな、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"だって勉強だけだとつまんないでしょ?\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"玲音ちゃんは勉強好き?\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"あんまり好きじゃない\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"どの教科が嫌いなの?\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"体育と国語\", \"actor\": \"玲音\"}, {\"line\": 21, \"text\": \"そっか、玲音ちゃんは運動が嫌いなのね\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"違うの、体育が嫌いなの\", \"actor\": \"玲音\"}, {\"line\": 23, \"text\": \"運動は好き?\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"うん\", \"actor\": \"玲音\"}, {\"line\": 25, \"text\": \"何の運動が好きなの?\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"鉄棒\", \"actor\": \"玲音\"}, {\"line\": 27, \"text\": \"すごーい、\", \"actor\": \"柊子\"}, {\"line\": 28, \"text\": \"先生は走るのは得意だったけど鉄棒はちょっとダメだったな、\", \"actor\": \"柊子\"}, {\"line\": 29, \"text\": \"ぐるぐる回ると目が回っちゃってダメなのよ\", \"actor\": \"柊子\"}, {\"line\": 30, \"text\": \"わたし、ぐるぐる回るの好きだよ\", \"actor\": \"玲音\"}, {\"line\": 31, \"text\": \"前転も後転も?\", \"actor\": \"柊子\"}, {\"line\": 32, \"text\": \"どっちも好き\", \"actor\": \"玲音\"}, {\"line\": 33, \"text\": \"で、何が嫌いなの?\", \"actor\": \"柊子\"}, {\"line\": 34, \"text\": \"(沈黙)\", \"actor\": \"玲音\"}, {\"line\": 35, \"text\": \"あ、答えたくないなら別にいいんだよ、\", \"actor\": \"柊子\"}, {\"line\": 36, \"text\": \"それより玲音ちゃんが好きなお勉強は、\", \"actor\": \"柊子\"}, {\"line\": 37, \"text\": \"ごめんなさい疲れた?\", \"actor\": \"柊子\"}, {\"line\": 38, \"text\": \"今日はちょっと早いけど終わりにしよっか\", \"actor\": \"柊子\"}]',0),
(867,'edit','2020-10-03 15:23:20','9999-12-31 23:59:59',720,64,'jp','[{\"line\": 0, \"text\": \"この間はごめんね。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"でもよかったー、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"先生嫌われちゃってもう玲音ちゃん来てくれないかなって心配してたの\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"ごめんなさい\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"玲音ちゃんが謝る必要なんて全然ないのよ悪いのは質問攻めにした先生、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"許してくれる?\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"ありがと、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"それでね、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"今日はちょっとこの絵を見て話しをしようかなって思ってるんだけど\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"それ、前の病院でやった\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"そう?\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"でも先生もこれで遊びたいの\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"何かのテストなんでしょ?\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"すごいわね玲音ちゃん知ってるの?\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"これはねロールシャッハテストっていうのよ、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"別にこれで何がテストできるってわけじゃないの、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"ちょっとしたお遊びって思ってくれればいいのよ\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"でも。\", \"actor\": \"玲音\"}, {\"line\": 18, \"text\": \"つまんない\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"うーんそうね、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"つまんなかったらそこでやめにしましょ、\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"それならいい?\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"うん\", \"actor\": \"玲音\"}, {\"line\": 23, \"text\": \"じゃあね、\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"これなんに見える?\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"しみ\", \"actor\": \"玲音\"}, {\"line\": 26, \"text\": \"うん、そうね、\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"でもなにかにたとえるとしたら?\", \"actor\": \"柊子\"}, {\"line\": 28, \"text\": \"先生は何に見えるの?\", \"actor\": \"玲音\"}, {\"line\": 29, \"text\": \"先生?\", \"actor\": \"柊子\"}, {\"line\": 30, \"text\": \"そうね、人の顔かな\", \"actor\": \"柊子\"}, {\"line\": 31, \"text\": \"怒ってる、\", \"actor\": \"玲音\"}, {\"line\": 32, \"text\": \"それとも泣いてる?\", \"actor\": \"玲音\"}, {\"line\": 33, \"text\": \"ちょっと怒ってるかな\", \"actor\": \"柊子\"}, {\"line\": 34, \"text\": \"つまんないよ\", \"actor\": \"玲音\"}, {\"line\": 35, \"text\": \"ねー玲音、\", \"actor\": \"柊子\"}, {\"line\": 36, \"text\": \"はあ、そう、\", \"actor\": \"柊子\"}, {\"line\": 37, \"text\": \"約束よね、\", \"actor\": \"柊子\"}, {\"line\": 38, \"text\": \"やめましょう\", \"actor\": \"柊子\"}]',0);
