(878,'edit','2020-10-03 15:23:21','9999-12-31 23:59:59',731,75,'jp','[{\"line\": 0, \"text\": \"こんにちは玲音\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"日記毎日つけてるよ、\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"あんまり書くことない日もあるけど\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"いいのよ無理しても続かないわ、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"先生なんて三日坊主で終わった日記帳が何冊あるかわからないもの、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"あれから見てない?\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"大丈夫だよ\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"そ、よかったわ、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"今日ね玲音に確認したいことがあるんだけど\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"なあに\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"玲音は目がすごくいいよね\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"すごく?\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"学校で視力検査ってやるでしょ、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"視力いくつ?\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"1.5\", \"actor\": \"玲音\"}, {\"line\": 15, \"text\": \"その下も見えてたでしょ、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"ほんとは\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"でも、みんなと同じがよかった\", \"actor\": \"玲音\"}, {\"line\": 18, \"text\": \"それじゃあ検査にならないわ、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"前の病院で目の検査もしてたの、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"それは学校とかでやるのと違って自動的にね視力がわかるやつなのよ、\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"3.0までしか計測できないんだけど玲音が答えたのは3,0だったの、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"ごくまれに極端に視力がいい子はいるし担当した先生は眼科のお医者さんに相談したけど問題はなさそうって言ってたわ\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"そうなんだ\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"玲音、窓の外に何が見える?\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"銀杏の木と遠くに門\", \"actor\": \"玲音\"}, {\"line\": 26, \"text\": \"その向こうは?\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"ビル\", \"actor\": \"玲音\"}, {\"line\": 28, \"text\": \"その向こうは?\", \"actor\": \"柊子\"}, {\"line\": 29, \"text\": \"山\", \"actor\": \"玲音\"}, {\"line\": 30, \"text\": \"その向こうは\", \"actor\": \"柊子\"}, {\"line\": 31, \"text\": \"見えない\", \"actor\": \"玲音\"}, {\"line\": 32, \"text\": \"なにも?\", \"actor\": \"柊子\"}, {\"line\": 33, \"text\": \"紫っぽくカーテンみたいな\", \"actor\": \"玲音\"}, {\"line\": 34, \"text\": \"ありがとう、\", \"actor\": \"柊子\"}, {\"line\": 35, \"text\": \"ごめんねなんかまた質問攻めにしちゃって、\", \"actor\": \"柊子\"}, {\"line\": 36, \"text\": \"そう、玲音には多分私と違う景色が見えてるんだね、\", \"actor\": \"柊子\"}, {\"line\": 37, \"text\": \"ほーらー暗くなっちゃだめだよ気にすることじゃないよ、\", \"actor\": \"柊子\"}, {\"line\": 38, \"text\": \"別にそんなに悪いことじゃ、\", \"actor\": \"柊子\"}, {\"line\": 39, \"text\": \"ごめん。\", \"actor\": \"柊子\"}, {\"line\": 40, \"text\": \"玲音にとっては悪いことなんだよね違ってることが\", \"actor\": \"柊子\"}, {\"line\": 41, \"text\": \"わからない、\", \"actor\": \"玲音\"}, {\"line\": 42, \"text\": \"でもたぶんみんなと違うんだって気がしてた、\", \"actor\": \"玲音\"}, {\"line\": 43, \"text\": \"聞こえるのも見えるのもいつも私だけ、\", \"actor\": \"玲音\"}, {\"line\": 44, \"text\": \"気味悪いみたい、\", \"actor\": \"玲音\"}, {\"line\": 45, \"text\": \"嫌われちゃうから、\", \"actor\": \"玲音\"}, {\"line\": 46, \"text\": \"それが原因?\", \"actor\": \"玲音\"}, {\"line\": 47, \"text\": \"かもしれない、\", \"actor\": \"柊子\"}, {\"line\": 48, \"text\": \"専門の先生に診てもらったほうがいいのかも\", \"actor\": \"柊子\"}, {\"line\": 49, \"text\": \"いや、私柊子さんがいい、\", \"actor\": \"玲音\"}, {\"line\": 50, \"text\": \"ここの先生ってみんな怖い\", \"actor\": \"玲音\"}, {\"line\": 51, \"text\": \"玲音…二人だけの秘密にしよう?\", \"actor\": \"柊子\"}, {\"line\": 52, \"text\": \"秘密?\", \"actor\": \"玲音\"}, {\"line\": 53, \"text\": \"うん玲音が気にしなくなるまで二人の秘密\", \"actor\": \"柊子\"}, {\"line\": 54, \"text\": \"いいの?\", \"actor\": \"玲音\"}, {\"line\": 55, \"text\": \"先生は玲音のお姉ちゃんなんだから玲音が怖がってるとこになんか玲音を渡せないって\", \"actor\": \"柊子\"}, {\"line\": 56, \"text\": \"柊子さん\", \"actor\": \"玲音\"}]',0),
(879,'edit','2020-10-03 15:23:21','9999-12-31 23:59:59',732,76,'jp','[{\"line\": 0, \"text\": \"おはよう玲音\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"おはようございます柊子さん\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"あれから何か見えたりした?\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"ううん見えないよホントだよ\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"何か聞こえた?\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"なんかごちゃごちゃ言って聞こえるときはあるけど\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"ごちゃごちゃ?\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"一人の人?\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"それとも大勢?\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"大勢、男の人も女の人も\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"誰か知ってる人っている?\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"たぶんいないと思う\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"玲音は空から聞こえるって言うけど空には何があると思う?\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"空気はあるよね\", \"actor\": \"玲音\"}, {\"line\": 14, \"text\": \"そうね、他には?\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"雲とか太陽とか星とか\", \"actor\": \"玲音\"}, {\"line\": 16, \"text\": \"携帯電話やテレビの電波って知ってる?\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"しってる、\", \"actor\": \"玲音\"}, {\"line\": 18, \"text\": \"無線で送られてくるんだよね\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"そう、空を飛んでくるの\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"その声が聞こえるの?\", \"actor\": \"玲音\"}, {\"line\": 21, \"text\": \"わたし\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"うーんそういうイメージから作り出されたものかもしれない\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"そういう病気があるの?\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"あるわ、妄想っていうの、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"神経症とか精神分裂病とかの症状で簡単に言ったたら気にしすぎたりストレスとかでそういう妄想を抱くこともあるわ、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"ただ\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"ただ?\", \"actor\": \"玲音\"}, {\"line\": 28, \"text\": \"たいていの場合自分にある程度関係したことが聞こえるの、\", \"actor\": \"柊子\"}, {\"line\": 29, \"text\": \"例えば自分の悪口とか\", \"actor\": \"柊子\"}, {\"line\": 30, \"text\": \"被害妄想?\", \"actor\": \"玲音\"}, {\"line\": 31, \"text\": \"そう、玲音は勉強しちゃったんだもんね、\", \"actor\": \"柊子\"}, {\"line\": 32, \"text\": \"いいわ勉強ついでに教えてあげる、\", \"actor\": \"柊子\"}, {\"line\": 33, \"text\": \"妄想の定義ってね、\", \"actor\": \"柊子\"}, {\"line\": 34, \"text\": \"内容が誤っていたりその考えの発生根拠が客観的でなかったり、\", \"actor\": \"柊子\"}, {\"line\": 35, \"text\": \"簡単に言うと間違ったことを自分の中ではそれが正しいって信じ込んでる状態なの、\", \"actor\": \"柊子\"}, {\"line\": 36, \"text\": \"玲音の感じだと自分がそう感じたかもしれないって言ってるけど確信してない状態はもうとは言わないの専門用語だけど念慮っていうの\", \"actor\": \"柊子\"}, {\"line\": 37, \"text\": \"念慮?\", \"actor\": \"玲音\"}, {\"line\": 38, \"text\": \"まあその言葉は忘れてくれてもいいわ、\", \"actor\": \"柊子\"}, {\"line\": 39, \"text\": \"さっきも言ったけどたいていの場合それは自分に結び付いたことなの、\", \"actor\": \"柊子\"}, {\"line\": 40, \"text\": \"あんまり関係ない妄想ってないのよ\", \"actor\": \"柊子\"}, {\"line\": 41, \"text\": \"妄想じゃない?\", \"actor\": \"玲音\"}, {\"line\": 42, \"text\": \"基本的にそうね、\", \"actor\": \"柊子\"}, {\"line\": 43, \"text\": \"ただ妄想にもいろいろ種類があって無理やり当てはめようとすると何とでも当てはまっちゃうの\", \"actor\": \"柊子\"}]',0),
(880,'edit','2020-10-03 15:23:21','9999-12-31 23:59:59',733,77,'jp','[{\"line\": 0, \"text\": \"当てはめると何になるの?\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"そうね、影響妄想って言って何かに影響されているって感じの妄想かな、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"ラジオやテレビや携帯電話に影響を受ける妄想加えてその影響で皮膚を触られる撫でられる電機が走るとか体に感じる幻覚を同時に起こすこともあるの、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"玲音は何かに触られてるって感じたことがあるって言ってたよね\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"うん\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"妄想じゃなくてもセネストパチーっていう病気も実際あるし、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"そう、それから玲音は自分が自分でないような気がするって言ってたわねそれは虚無妄想と言って自分が空虚で存在しない世界も存在しないという妄想にも当てはまるわ、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"これは自己を過小評価することから来る妄想ね\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"自己を過小評価?\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"自分のことをなんてつまらない人間なんだとか悲観的に考えることよ、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"さっきの被害妄想も同じ種類の妄想なの\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"いっぱい病気なんだ、\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"わたし\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"まだあるわ\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"もういいよ\", \"actor\": \"玲音\"}, {\"line\": 15, \"text\": \"心気妄想っていって自分に特別な病気があるって信じる妄想、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"自分が病気だと思っちゃう病気なの、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"ほんとは病気じゃないのに、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"先生はね玲音の問題の多くはこれじゃないかって思うの\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"ほんとは病気じゃない病気?\", \"actor\": \"玲音\"}, {\"line\": 20, \"text\": \"そう、自分に自信がなくてちょっとの不安が怖い夢と一緒になって病気って思いこんでる\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"治るの?\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"そうよ、だってホントは病気じゃないもの、\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"ごめんね脅かしちゃって、\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"でもね中途半端な知識で考えると精神医学はどうにでも取れちゃうのよ、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"病気に出来ちゃうの、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"たいていの人は本人や周りの人が普通に生活できなくて困ってお医者さんに来るから病気として治療するの、\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"その時にどうやって直したらいいかを判断する基準でもあるの、\", \"actor\": \"柊子\"}, {\"line\": 28, \"text\": \"だから玲音のように普通に生活できている人は病院に行かなかったら病気にはならない\", \"actor\": \"柊子\"}]',0),
(881,'edit','2020-10-03 15:23:21','9999-12-31 23:59:59',734,78,'jp','[{\"line\": 0, \"text\": \"病院に来たから病気になったの?\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"そうね、でも玲音は生活できるけど困ってるよね、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"それをなくすのはカウンセラーの仕事でもあるの\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"柊子さんはカウンセラーなの?\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"前はお医者さんだって\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"私はどちらの資格も持ってるの\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"何が違うの?\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"精神的な問題で困ってる人を助けるって点で本質的には何も違わないわ、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"ただ資格や治療方法が違ってるだけ、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"だけどそれぞれ長所や短所があってもうちょっとお互いのやり方を尊重しあえて一緒に治療できたらいいかなって思うの、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"そう思って先生は両方の資格を取ったの\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"いまは、私と会うときはどっち?\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"カウンセラーよ、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"玲音の場合はそれがいいって思ったの\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"私精神病じゃないの?\", \"actor\": \"玲音\"}, {\"line\": 15, \"text\": \"私が見えるあれは幻覚なんだって\", \"actor\": \"玲音\"}, {\"line\": 16, \"text\": \"確かに病気でそういった幻覚や妄想の症状が起こることはあるわ、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"でも、これは直観だけど玲音はそんな感じしないわ\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"私気にしすぎ?\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"ひょっとしたらそうかもね、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"見えすぎたり聞こえすぎたりすることが玲音にストレスを与えてるのかもしれないわ\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"どうすればいいの柊子さん\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"うーん気にしないでって言っても気になるものね\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"でももう気になってないよ、\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"慣れてきた、\", \"actor\": \"玲音\"}, {\"line\": 25, \"text\": \"でもあれは慣れない\", \"actor\": \"玲音\"}, {\"line\": 26, \"text\": \"あれって?\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"私が今の私じゃないの\", \"actor\": \"玲音\"}, {\"line\": 28, \"text\": \"どんな玲音なの?\", \"actor\": \"柊子\"}, {\"line\": 29, \"text\": \"私なの、その人も\", \"actor\": \"玲音\"}, {\"line\": 30, \"text\": \"どうしてそう思うの\", \"actor\": \"柊子\"}, {\"line\": 31, \"text\": \"よくわからない、\", \"actor\": \"玲音\"}, {\"line\": 32, \"text\": \"柊子さんは自分が自分だと思えないことってない?\", \"actor\": \"玲音\"}, {\"line\": 33, \"text\": \"うーん後から考えると私何考えてたんだろうってあるけどその時に自分じゃないって感じたことはないな、\", \"actor\": \"柊子\"}, {\"line\": 34, \"text\": \"玲音は自分が二人いるって思うの?\", \"actor\": \"柊子\"}, {\"line\": 35, \"text\": \"違う。\", \"actor\": \"玲音\"}, {\"line\": 36, \"text\": \"私は私でそうじゃないの\", \"actor\": \"玲音\"}, {\"line\": 37, \"text\": \"不思議ね、\", \"actor\": \"柊子\"}, {\"line\": 38, \"text\": \"でも玲音がそう感じるならそれはそうなのよ、\", \"actor\": \"柊子\"}, {\"line\": 39, \"text\": \"間違ってなんかないのよ\", \"actor\": \"柊子\"}, {\"line\": 40, \"text\": \"でも普通じゃない\", \"actor\": \"玲音\"}]',0),
(882,'edit','2020-10-03 15:23:21','9999-12-31 23:59:59',735,79,'jp','[{\"line\": 0, \"text\": \"玲音は普通にあこがれてるのね\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"たぶん\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"あたしはうらやましいわ、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"人間の感覚なんていい加減なものでそう感じたら思い込んじゃうものなのよ、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"私が私であるなんて絶対説明できないことでしょ、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"でもそのことに疑問を持てるってそれはそれで特別な能力なのよ\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"いらないよ、\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"そんなの\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"そうね、今はいらないって気持ちが強いと思うの。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"でもそれがきっと玲音にとっていいことも起こしてくれると思うのよ、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"よくわからないけどそれも含めて玲音なんだって自分で思えるようになった時きっと玲音の役に立つと思うわ\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"私はこのまま?\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"分からないわ、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"自然に変わっていくかもしれないしこのままもう怖い夢を見ないかも、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"そしたらもう玲音は普通の子と変わらないわ\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"でも治ったかわからない、\", \"actor\": \"玲音\"}, {\"line\": 16, \"text\": \"いつまた見るかもしれない\", \"actor\": \"玲音\"}, {\"line\": 17, \"text\": \"そうね、でもこれからは違うわ\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"違うって?\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"玲音は1人じゃないってこと、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"先生に言ってくれれば先生が相談に乗れるってこと\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"そうだね、\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"柊子さん\", \"actor\": \"玲音\"}]',0),
(883,'edit','2020-10-03 15:23:21','9999-12-31 23:59:59',736,80,'jp','[{\"line\": 0, \"text\": \"おはよう柊子さん\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"おはよう調子はどう、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"元気にしてた?\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"うん、先月なんかいろいろ柊子さんに聞いてなんとなくだけど楽な気分になれたの\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"そう、よかったわ、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"ほんとはああいう話をするのはいけないってされてることなの、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"余計に憶測して取り返しのつかない状態になるケースも実際にあるのよ\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"取り返しのつかない?\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"うーん病気が悪化しちゃったり、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"ひどいときには自分を傷つけたり\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"自殺?\", \"actor\": \"玲音\"}, {\"line\": 11, \"text\": \"まぁ最悪のケースだけどね\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"私死にたくない\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"もちろんよ、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"玲音はそんな状態なんか程遠いわ、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"でも人は精神的に病んでしまうと私たちと別な価値観で動いてしまうことがあるの\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"別な価値観?\", \"actor\": \"玲音\"}, {\"line\": 17, \"text\": \"命を失うことや体を傷付けることに恐怖を感じなくなったり、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"逆にそれが自分にとってすごくいいことだって思いこんじゃうの、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"そんなの理解できないでしょ?\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"私は怖い\", \"actor\": \"玲音\"}, {\"line\": 21, \"text\": \"ほら、玲音は私と一緒でしょ、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"普通なのよそれが\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"うん!\", \"actor\": \"玲音\"}]',0),
(884,'edit','2020-10-03 15:23:22','9999-12-31 23:59:59',737,81,'jp','[{\"line\": 0, \"text\": \"あれからは何か見えたりとか聞こえたりした?\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"ないよ\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"やっぱりねー気の持ちようなのよ玲音、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"多分もう見えないって思うけどまた見えてもがっかりしないで、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"ゆっくり治せばいいの\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"まだ治らないの\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"うーん分からないわ、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"玲音が初めてそうなったのはいくつの時からなの?\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"11歳の時\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"じゃあもう1年以上にもなるのね、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"その間に何回くらいそうなったの?\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"覚えてない、\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"いっぱい\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"こういうのって突然明らかな原因がなくても急に見なくなったりするの、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"ただ見なくなったからって安心しちゃうとまた見えちゃうケースもあるのよ、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"ああ、必ずしもそうなるとは言えないけどね、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"だから見なくなってもしばらくは今まで通り通ってもらったりしてるの、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"ねえ玲音はもうここには来たくたくない?\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"そんなことないよ、\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"柊子さんに会いたいって思うことあるもの\", \"actor\": \"玲音\"}, {\"line\": 20, \"text\": \"ありがと、\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"そしたら問題ないわね、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"なんにも問題ないけどしばらくはちゃんと先生のところに来るって約束してね\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"うん、その代わりいろいろ教えてほしいの\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"なーに宿題とか?\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"いいわよ、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"こう見えても先生は一応中学の時は学年でもトップクラスだったんだから、\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"でも国語はちょっとダメかな\", \"actor\": \"柊子\"}, {\"line\": 28, \"text\": \"違うの、先生のお仕事のこともっと知りたいの、\", \"actor\": \"玲音\"}, {\"line\": 29, \"text\": \"ダメ?\", \"actor\": \"玲音\"}, {\"line\": 30, \"text\": \"うーんあんまりよくないんだけど、\", \"actor\": \"柊子\"}, {\"line\": 31, \"text\": \"ま、でも玲音が興味持ってるんならいっかな、\", \"actor\": \"柊子\"}, {\"line\": 32, \"text\": \"興味があることを追求するのが正しい勉強でもあるしね、\", \"actor\": \"柊子\"}, {\"line\": 33, \"text\": \"ただ学校の勉強もちゃんとしないとダメよ、\", \"actor\": \"柊子\"}, {\"line\": 34, \"text\": \"それが約束できるなら先生は教えてもいいけど?\", \"actor\": \"柊子\"}, {\"line\": 35, \"text\": \"うん約束する\", \"actor\": \"玲音\"}, {\"line\": 36, \"text\": \"嘘ついたら?\", \"actor\": \"柊子\"}, {\"line\": 37, \"text\": \"ハリセンボン。\", \"actor\": \"双方\"}, {\"line\": 38, \"text\": \"うふふふ\", \"actor\": \"双方\"}]',0),
(885,'edit','2020-10-03 15:23:22','9999-12-31 23:59:59',738,82,'jp','[{\"line\": 0, \"text\": \"先生はカウンセラーで精神科医なんだよね、\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"精神科医って精神病を治す人でいいの?\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"そうね簡単に言えば心の医者さんね、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"前にも言ったけど精神病って概念はけっこう難しいの\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"精神病ってホントはどんな病気なの?\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"うーん、おおざっぱに言えば何かの原因で心が病んでしまって通常の生活が送れなくなったり困ってしまう病気かな\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"何かの原因、\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"たとえば?\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"いっぱいあるけど一つは心の問題、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"家庭環境や生活環境の問題でおかしくなることもあるし突発的な精神的ショックでなることもあるわ、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"例えば大事にしてたペットが死んじゃったりしてそうなるケースだってあるわ、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"それと体のどこかの障害がもとで発病することもあるの、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"単純に事故とかの外傷でそうなったりお酒の飲み過ぎで脳の機能がおかしくなったりね\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"アルコール中毒?\", \"actor\": \"玲音\"}, {\"line\": 14, \"text\": \"そう、さすがねお酒を飲んでばかりいると血液中にアルコールがいっぱいになるの、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"ほら酔っ払いのおじさんってフラフラしてるでしょ、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"あれはアルコールのせいで体の期間がマヒしてるの\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"でも普通の人はならないんだよね\", \"actor\": \"玲音\"}, {\"line\": 18, \"text\": \"そんなことないわ、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"アルコールが身体から抜けるには24時間くらいかかるの、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"毎日のんでいたら一年中アルコール漬けの身体になるわ、\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"ずーっとマヒしてると感覚そのものがおかしくなるの、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"具体的には脳の組織が機能しなくなったり\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"こわいね\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"怖いことよ、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"でも先生も時々お酒は飲むの\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"あぶなくない?\", \"actor\": \"玲音\"}, {\"line\": 27, \"text\": \"えへ、たぶんね、\", \"actor\": \"柊子\"}, {\"line\": 28, \"text\": \"タバコとかもそうだけどお酒を飲んで騒ぐってストレスを解消するのよ、\", \"actor\": \"柊子\"}, {\"line\": 29, \"text\": \"ストレスが解消されることは体にとってすごく大事なことなの\", \"actor\": \"柊子\"}, {\"line\": 30, \"text\": \"ストレスってよくわからない\", \"actor\": \"玲音\"}, {\"line\": 31, \"text\": \"昔玲音はここに来るのが嫌だったよね\", \"actor\": \"柊子\"}, {\"line\": 32, \"text\": \"うん\", \"actor\": \"玲音\"}, {\"line\": 33, \"text\": \"その嫌な気持ちを与えることをストレスっていうの、\", \"actor\": \"柊子\"}, {\"line\": 34, \"text\": \"なくなってよかったでしょ\", \"actor\": \"柊子\"}, {\"line\": 35, \"text\": \"うん、ほかに病気の種類は?\", \"actor\": \"玲音\"}, {\"line\": 36, \"text\": \"ちょっと待ってね、\", \"actor\": \"柊子\"}, {\"line\": 37, \"text\": \"なんかしゃべりすぎてのどが渇いちゃった\", \"actor\": \"柊子\"}]',0),
(886,'edit','2020-10-03 15:23:22','9999-12-31 23:59:59',739,83,'jp','[{\"line\": 0, \"text\": \"ねえ、前から聞きたかったんだけど玲音はどうしてそういう髪型にしてるの?\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"変だよね\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"あー、すごく似合ってるしかっこいいわ、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"中学とかではやってるの?\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"ちがう、私だけ\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"玲音はみんなと同じに普通になりたいって言ってたわよね、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"なんでそうしてるの?\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"右から入ってくるの、\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"たぶんそれで左から出てくの、\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"だから\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"そう、そうしてると落ち着けるからなの?\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"うん、おまじないみたいなものね、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"先生も昔やってたなー\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"柊子さんも?\", \"actor\": \"玲音\"}, {\"line\": 14, \"text\": \"うん、あ、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"髪型とかじゃないけど、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"ウサギの足のペンダントがあってね、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"それを持ってると幸せになれるって信じてカバンにぶら下げてたの、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"冷静に考えたらすごく不気味なことよね、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"あんな愛らしい動物の足をカバンにぶら下げて歩いてたなんて、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"でもそれを信じることで助かっていたって気もするんだ\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"おまじない\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"うん、でも玲音はすごく似合ってるわ、\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"玲音が普通の髪型になったらそのほうが玲音らしくないかもよ、\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"そのままにしておいたほうがきっといい\", \"actor\": \"柊子\"}]',0),
(887,'edit','2020-10-03 15:23:22','9999-12-31 23:59:59',740,84,'jp','[{\"line\": 0, \"text\": \"神経症も知ってるの?\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"んーすごいわね玲音、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"今度は玲音にカウンセリングしてもらおっかな\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"言葉だけだよ、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"意味なんてよく分からない\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"どんなイメージ?\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"神経が変になっておかしくなる病気?\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"うん、イイ線行ってるね\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"ひどい先生、\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"もう教えてくれないの?\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"んー違うのよ、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"ほんとにイイ線行ってるの本当は神経症って体の原因がなくて何かを怖がったり何かに脅迫されてるような気がすることでストレスを感じて病気になっちゃうものなの\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"心の中だけの病気?\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"うーん昔はそう考えられていたのでも研究してみたら自律神経系の不安定が伴うことが多くて、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"だから体の中の神経がおかしくなってるんじゃないかって考えもあるの、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"体にも影響が出るものを心身症って言って区別していたんだけどそうじゃないって定義されていたはずの神経症も実は体に関係していたってわかってきたの\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"心身症って?\", \"actor\": \"玲音\"}, {\"line\": 17, \"text\": \"あんまりいっぱい聞くと頭がパンクしちゃうわよ、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"ほらストレスで痩せちゃったり頭に剥げができちゃったりって聞いたことない?\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"あれのことよ。\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"っ、玲音まさか\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"違うよそんなのないよ\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"はーよかった、\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"口は禍の元ね\", \"actor\": \"柊子\"}]',0),
(888,'edit','2020-10-03 15:23:22','9999-12-31 23:59:59',741,85,'jp','[{\"line\": 0, \"text\": \"病名って誰が決めるの?\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"それぞれのお医者さんね、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"ただ一応の基準はあるのよ、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"ICDやDSセームっていう国際基準があってある程度こういう症状の時はきっとこんな病気だろうって、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"玲音が思っているように心の中はもともとよくわからないものだから、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"毎年のようにいろんな報告があって少しでも診断に役立てるようにデーターが集められて分類されていってるの\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"わからない?\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"先生でも\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"ホントのところ先生たちにも全くわからないことばかりなの、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"例えばあるケースにおいて以前こうしたら治ったからその方法をとろうとか結構経験的なものだったりもするの\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"治るかどうかもわからないのに?\", \"actor\": \"玲音\"}, {\"line\": 11, \"text\": \"そう、治るかどうかなんてわからないの、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"ただこうしたら治ったとかこうしたら危険が少ないとかそういうことでしかないケースが大半なの、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"でもね患者さんにとってもだけど基本的に治ることが大事でそのメカニズムを調べることは別の研究なのよ\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"そうなんだ、\", \"actor\": \"玲音\"}, {\"line\": 15, \"text\": \"すべてわかってしまえばいいのにね\", \"actor\": \"玲音\"}]',0),
(889,'edit','2020-10-03 15:23:22','9999-12-31 23:59:59',742,86,'jp','[{\"line\": 0, \"text\": \"玲音って子供の頃の思い出で記憶に残ってることってある?\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"記憶?\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"記録?\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"はは記憶と記録は違うわ、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"そんなに難しく考えないで、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"ほら夏休みにどこかに旅行したとか\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"旅行なんてしたことないよ\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"あぁごめんなさい、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"そうね先生も両親が共働きだったから子供のころは旅行に連れてってもらえなくて、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"学校で他の子たちがどこに行ったとか夏休みの話してると嫌だったわ\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"記憶は記録とどう違うの?\", \"actor\": \"玲音\"}, {\"line\": 11, \"text\": \"うーん医学的に言うとね記録は単純にいつどこで何をどうしたっていうデータでしょ、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"記憶はその個人が体験したことが記名され頭の中に保持されてもう一度呼び起こされるものって定義なの、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"ふふこんな話玲音にはつまらないでしょう?\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"ううん。\", \"actor\": \"玲音\"}, {\"line\": 15, \"text\": \"それで記憶って脳にどう入っているの?\", \"actor\": \"玲音\"}, {\"line\": 16, \"text\": \"どう?\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"うーんちょっと難しい話になっちゃうけど大丈夫?\", \"actor\": \"柊子\"}]',0),
(890,'edit','2020-10-03 15:23:22','9999-12-31 23:59:59',743,87,'jp','[{\"line\": 0, \"text\": \"記憶といってもいくつか種類があってね、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"まず玲音が玲音である記憶、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"ほら遺伝って言葉知ってるでしょ、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"玲音はこんな顔でこんな体型の子って、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"それは分子レベルで記録されてるの。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"脳以外でも体のあちこちに何らかの痕跡があって記録されてるの、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"それとね、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"体が傷ついたら血が出てしばらくするとかさぶたになるでしょ、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"それも細胞の組織の中に記録されてることが再生されるの。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"あとはね訓練すると運動選手とか無意識に体が先に動くっていうじゃない、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"それも身体か記憶してるってことなの\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"記録を再生すれば記憶なの?\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"うーんそうとも言えるけどそうじゃないこともあるわね、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"人間っていい加減でしょ。\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"先生も子供の時のことってしっかりとは覚えてないわ、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"家族で楽しかった思い出もひょっとしたらほかの人は楽しくなかったかもしれない、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"その瞬間の受け取り方の問題もあるし勘違いして記憶されてるかもしれない、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"ただその人の中では正確な記録として存在してるの、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"例えば久しぶりに会ったお友達を見ると昔の印象に残ってる断片的な出来事だけすぐに思い出せることってあるでしょ、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"それは自分の中にある記憶を結び付けて再生することも出来るからなの\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"結びつかないと病気なの?\", \"actor\": \"玲音\"}, {\"line\": 21, \"text\": \"そうね記憶障害って言ってその記憶が自分のものでなくなっちゃったり、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"たった今したことが思い出せなかったり、\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"過去に体験したことの時間的配列が狂ってしまってそれを自分でおかしくないように作り話でごまかしちゃったりするの。\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"人間って複雑な生き物だからそういうことも起こるの、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"ほらテレビドラマでおじいちゃんがぼけちゃってとか見たことあるでしょ。\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"人間である以上仕方がないことでもあるのよ\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"仕方がないこと?\", \"actor\": \"玲音\"}, {\"line\": 28, \"text\": \"玲音が病気とかそういうことじゃないのよ、\", \"actor\": \"柊子\"}, {\"line\": 29, \"text\": \"人間だから仕方がないことってあるでしょ?\", \"actor\": \"柊子\"}, {\"line\": 30, \"text\": \"人間は空は飛べないでしょ\", \"actor\": \"柊子\"}, {\"line\": 31, \"text\": \"脳や体の中に記憶があるの?\", \"actor\": \"玲音\"}, {\"line\": 32, \"text\": \"基本的にはまだまだ分かってないことも多いけど、\", \"actor\": \"柊子\"}, {\"line\": 33, \"text\": \"そうだな、\", \"actor\": \"柊子\"}, {\"line\": 34, \"text\": \"人間の脳ってねいろんな物質で構成されているの、\", \"actor\": \"柊子\"}, {\"line\": 35, \"text\": \"記録されるとその物質に何らかの変化があってそれが記録のもとになるの\", \"actor\": \"柊子\"}, {\"line\": 36, \"text\": \"わかんない\", \"actor\": \"玲音\"}, {\"line\": 37, \"text\": \"そうねもっと大きくなって高校や大学に行ったら勉強できるわ、\", \"actor\": \"柊子\"}, {\"line\": 38, \"text\": \"でも興味を持つことはいいことよ、\", \"actor\": \"柊子\"}, {\"line\": 39, \"text\": \"ただ玲音は多分病気なんかじゃないから\", \"actor\": \"柊子\"}, {\"line\": 40, \"text\": \"私、私の記憶が自分じゃない気がする\", \"actor\": \"玲音\"}, {\"line\": 41, \"text\": \"そういうこともあるわ、\", \"actor\": \"柊子\"}, {\"line\": 42, \"text\": \"もちろん先生にだって\", \"actor\": \"柊子\"}, {\"line\": 43, \"text\": \"私なのに、\", \"actor\": \"玲音\"}, {\"line\": 44, \"text\": \"でもそれ以外は何も思い出せない\", \"actor\": \"玲音\"}, {\"line\": 45, \"text\": \"忘れてるだけよ、\", \"actor\": \"柊子\"}, {\"line\": 46, \"text\": \"言ったでしょ玲音、\", \"actor\": \"柊子\"}, {\"line\": 47, \"text\": \"人間なんていい加減なものなの、\", \"actor\": \"柊子\"}, {\"line\": 48, \"text\": \"都合のいいように思ってたり忘れたりして思い込んでしまう生き物なの、\", \"actor\": \"柊子\"}, {\"line\": 49, \"text\": \"それが普通なのよ\", \"actor\": \"柊子\"}, {\"line\": 50, \"text\": \"普通?\", \"actor\": \"玲音\"}, {\"line\": 51, \"text\": \"そう、玲音は気にしすぎ、\", \"actor\": \"柊子\"}, {\"line\": 52, \"text\": \"普通のことよ\", \"actor\": \"柊子\"}]',0),
(891,'edit','2020-10-03 15:23:22','9999-12-31 23:59:59',744,88,'jp','[{\"line\": 0, \"text\": \"玲音の名前ってR.D.レインから取ったのかなーまさかね。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"RD玲音?\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"人の名前、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"イギリスの精神科のお医者さんでちょっと変わった人なの、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"翻訳されてる本のタイトルが面白いわよ『好き?\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"好き?\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"大好き?\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"』っていうの\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"『好き?\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"好き?\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"大好き?\", \"actor\": \"玲音\"}, {\"line\": 11, \"text\": \"』?\", \"actor\": \"玲音\"}, {\"line\": 12, \"text\": \"先生の大学の時に読んだんでもううろ覚えだけど確かね、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"恋人同士の男の人と女の人がいて、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"女の人が男の人に私のこと好きって聞くの\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"それで?\", \"actor\": \"玲音\"}, {\"line\": 16, \"text\": \"もちろん恋人だからすきすきっていうのよ、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"でも彼女は満足しなくてもっともっと質問してくるの\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"よくわからない\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"そうねー、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"じゃあ玲音は先生のこと好き?\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"うん好き\", \"actor\": \"玲音\"}, {\"line\": 22, \"text\": \"お父さんよりもお母さんよりも?\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"うん\", \"actor\": \"玲音\"}, {\"line\": 24, \"text\": \"うれしいなーほんと?\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"ほんとだよ\", \"actor\": \"玲音\"}, {\"line\": 26, \"text\": \"じゃあ玲音は私といると幸せ?\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"よく分からないけどたぶんそうだと思う\", \"actor\": \"玲音\"}, {\"line\": 28, \"text\": \"玲音は私と話をするの好き?\", \"actor\": \"柊子\"}, {\"line\": 29, \"text\": \"うん\", \"actor\": \"玲音\"}, {\"line\": 30, \"text\": \"私のどこが好き?\", \"actor\": \"柊子\"}, {\"line\": 31, \"text\": \"優しいところと玲音の話を聞いてくれるとこ\", \"actor\": \"玲音\"}, {\"line\": 32, \"text\": \"優しいのと話を聞いてくれるのどっちが好き?\", \"actor\": \"柊子\"}, {\"line\": 33, \"text\": \"どっちか?\", \"actor\": \"玲音\"}, {\"line\": 34, \"text\": \"そうどっちか\", \"actor\": \"柊子\"}, {\"line\": 35, \"text\": \"優しいから好き\", \"actor\": \"玲音\"}, {\"line\": 36, \"text\": \"やさしいってどういうとこが玲音に優しいの?\", \"actor\": \"柊子\"}, {\"line\": 37, \"text\": \"怒らないし親切だから\", \"actor\": \"玲音\"}, {\"line\": 38, \"text\": \"でも玲音に怒らない人もいっぱいいるわ、\", \"actor\": \"柊子\"}, {\"line\": 39, \"text\": \"玲音に親切にしてくれる人も私のほかにいるでしょ、\", \"actor\": \"柊子\"}, {\"line\": 40, \"text\": \"ほんとに玲音は私のこと好きなの?\", \"actor\": \"柊子\"}, {\"line\": 41, \"text\": \"よくわからなくなっちゃった。\", \"actor\": \"玲音\"}, {\"line\": 42, \"text\": \"そんなに考えなくちゃいけないの?\", \"actor\": \"玲音\"}, {\"line\": 43, \"text\": \"もうやめようよ私なんだか先生のこと嫌いになっちゃいそう\", \"actor\": \"玲音\"}]',0),
(892,'edit','2020-10-03 15:23:22','9999-12-31 23:59:59',745,89,'jp','[{\"line\": 0, \"text\": \"うふふ、ごめんなさい。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"ちょっと説明ついでにやってみたの。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"先生も玲音のこと大好きよ?\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"理由なんてどうでもいいの、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"玲音と一緒に話ができることが先生はうれしいの、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"ただそれだけ。\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"それは、でも、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"先生の一方的は思いなの。\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"玲音には関係がありそうで関係ないの、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"先生はそう思ってる自分が分かるだけでいいの、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"それ以上は結局わからないことでしょ?\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"玲音が先生の子と先生と同じように好きかなんて玲音の心の中を先生は見られないんだから。\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"なんだか、\", \"actor\": \"玲音\"}, {\"line\": 13, \"text\": \"寂しくなっちゃった。\", \"actor\": \"玲音\"}, {\"line\": 14, \"text\": \"ごめんなさいね。\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"でもそんなものだって解釈もあるの、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"でも私のこと好きって言ってくれる玲音に私は喜びを感じるのよ?\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"玲音は先生に好きって言われてうれしい?\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"うん、うれしい\", \"actor\": \"玲音\"}, {\"line\": 19, \"text\": \"そう、それだけでいいの。\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"先生は玲音を大事な友達だって思ってるの、\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"玲音のことをすべてわかることは不可能なのよ、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"玲音も先生のこと全部わからないでしょう?\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"もしすべてわかってしまったら、\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"先生は玲音で玲音は先生になっちゃうでしょ?\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"つまり同じ人間になっちゃったら玲音が玲音でいる必要がなくなっちゃうでしょう。\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"先生はわからないことがいっぱいあっても玲音が好きなの。\", \"actor\": \"柊子\"}, {\"line\": 27, \"text\": \"よくわからない玲音が好き?\", \"actor\": \"玲音\"}, {\"line\": 28, \"text\": 