(991,'edit','2020-10-03 15:23:27','9999-12-31 23:59:59',773,165,'jp','[{\"line\": 0, \"text\": \"なんか退屈よね、\", \"actor\": \"今日子\"}, {\"line\": 1, \"text\": \"玲音っていつも暇なとき何してるの?\", \"actor\": \"今日子\"}, {\"line\": 2, \"text\": \"掃除かな\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"へーきれい好きなんだ\", \"actor\": \"今日子\"}, {\"line\": 4, \"text\": \"いらないものが多くて仕方ないの\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"そうだよね、\", \"actor\": \"今日子\"}, {\"line\": 6, \"text\": \"私もアクセサリーとか衝動買いしちゃうし使ってないルージュとかめっちゃくちゃあるもん、\", \"actor\": \"今日子\"}, {\"line\": 7, \"text\": \"今度交換しようよ、\", \"actor\": \"今日子\"}, {\"line\": 8, \"text\": \"でも玲音って相変わらずすっぴんだよね、\", \"actor\": \"今日子\"}, {\"line\": 9, \"text\": \"前に一緒に買ったリップつけてる?\", \"actor\": \"今日子\"}, {\"line\": 10, \"text\": \"ううん\", \"actor\": \"玲音\"}, {\"line\": 11, \"text\": \"少しは化粧しなって、\", \"actor\": \"今日子\"}, {\"line\": 12, \"text\": \"似合うと思うよ\", \"actor\": \"今日子\"}]',0),
(992,'edit','2020-10-03 15:23:27','9999-12-31 23:59:59',774,166,'jp','[{\"line\": 0, \"text\": \"なんかすごいこと起こんないかなー映画みたいな大事件、\", \"actor\": \"今日子\"}, {\"line\": 1, \"text\": \"人が死んじゃったりして私は死なないの。\", \"actor\": \"今日子\"}, {\"line\": 2, \"text\": \"で、かっこいい刑事と事件解決しちゃっていきなりスターになっちゃったり、\", \"actor\": \"今日子\"}, {\"line\": 3, \"text\": \"ごめんちょっと妄想系だよね、\", \"actor\": \"今日子\"}, {\"line\": 4, \"text\": \"そんな冷たい目しなくていいじゃん。\", \"actor\": \"今日子\"}, {\"line\": 5, \"text\": \"わたし、いつもこんな目だよ\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"なにそれ、\", \"actor\": \"今日子\"}, {\"line\": 7, \"text\": \"笑かそうとしてんの?\", \"actor\": \"今日子\"}, {\"line\": 8, \"text\": \"すべってる?\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"アハハ、めちゃくちゃすべってる、\", \"actor\": \"今日子\"}, {\"line\": 10, \"text\": \"まあ玲音らしいけどさ\", \"actor\": \"今日子\"}]',0),
