(1120,'new','2020-10-03 15:23:32','9999-12-31 23:59:59',1127,296,'jp','[{\"line\": 0, \"text\": \"美里ちゃんの絵が完成した。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"美術部でも評判になっていた。\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"すごくきれいでもう芸術って感じ。\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"50号の油彩だから目立つし文化祭でもきっと目を引くだろうな。\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"わたしは10号のキャンバスにパステルで描いた上に水彩ペイントした美里ちゃんは上手ってほめてくれたけどやっぱり美里ちゃんの絵を描けばよかったかな。\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"絵でもどこか自分を出し切れてないって気がする。\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"私はどんな絵を描けばいいの?\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"美里ちゃんの絵は美里ちゃんって感じがするもの、\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"優しくてきれいなんだけど内面に強さを秘めていて…\", \"actor\": \"玲音\"}]',0),
(1121,'new','2020-10-03 15:23:32','9999-12-31 23:59:59',1128,297,'jp','[{\"line\": 0, \"text\": \"柊子さんてあんな人だっけ。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"なんか今までと違う気がする。\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"このごろ忙しいから?\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"なんかよそよそしいもの。\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"児湯も絵のことで相談したら絵のことなんて部活の人のきいてだなんて機嫌悪かったのかなぁ、\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"それとも、\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"私がもう治ってきたんで邪魔なのかな。\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"柊子さんはカウンセラーだもんね。\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"私がおかしくなればなるほど柊子さんの仕事は増えるんだもんね。\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"研究レポートの役に立てなくてごめんなさい。\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"そうだったらもう来なくていいって言ってくれればいいのに\", \"actor\": \"玲音\"}]',0),
