(1127,'new','2020-10-03 15:23:32','9999-12-31 23:59:59',1134,303,'jp','[{\"line\": 0, \"text\": \"そういえばずいぶんメールを見てないな、\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"ネットであれが見えたり意地悪なメールのせいでやめていたんだ、\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"美里ちゃんもネットをやっているっていったからアドレス交換しようかな私がネットだとだれと\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"でも仲良くなれるくらい活動的にしていることを知ったら美里ちゃんびっくりするかな、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"それにメールで連絡取れたら電話じゃなくても連絡取れるもんね、\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"このごろ玲音の長電話で電話代が大変だってお母さんが文句言ってくるんだもの、\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"明日美里ちゃんに話してみよ\", \"actor\": \"玲音\"}]',0),
(1128,'new','2020-10-03 15:23:32','9999-12-31 23:59:59',1135,304,'jp','[{\"line\": 0, \"text\": \"長電話している私なんだか自分でも不思議なくらい話せている、\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"私ってホントはおしゃべり?\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"この調子ならどんな人とでもいっぱい話が出来そう\", \"actor\": \"玲音\"}]',0),
(1129,'new','2020-10-03 15:23:32','9999-12-31 23:59:59',1136,305,'jp','[{\"line\": 0, \"text\": \"美里ちゃんに断られちゃった。\", \"actor\": \"玲音\"}, {\"line\": 1, \"text\": \"メール見るのめんどくさいんだって、\", \"actor\": \"玲音\"}, {\"line\": 2, \"text\": \"ネット自体にあんまり興味がなさそうだった。\", \"actor\": \"玲音\"}, {\"line\": 3, \"text\": \"美里ちゃんもメールで嫌な目にあったのかな、\", \"actor\": \"玲音\"}, {\"line\": 4, \"text\": \"ちょっといつもの違う感じで断ってきたもの、\", \"actor\": \"玲音\"}, {\"line\": 5, \"text\": \"何だか残念。\", \"actor\": \"玲音\"}, {\"line\": 6, \"text\": \"もっと喜んでくれると思ったのに、\", \"actor\": \"玲音\"}, {\"line\": 7, \"text\": \"ちょっと寂しかった。\", \"actor\": \"玲音\"}, {\"line\": 8, \"text\": \"でも美里ちゃんは電話で話してくれるんだもの、\", \"actor\": \"玲音\"}, {\"line\": 9, \"text\": \"学校でも家でも私たちはいつでも話ができるんだ、\", \"actor\": \"玲音\"}, {\"line\": 10, \"text\": \"せめてネットくらいはお互い一人でいたいんだよね\", \"actor\": \"玲音\"}]',0),
