(1400,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1407,668,'jp','[{\"line\": 0, \"text\": \"自分だけは知ってるよね。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"自分が何をしたかって。\", \"actor\": \"柊子\"}]',0),
(1401,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1408,669,'jp','[{\"line\": 0, \"text\": \"騙せないよ。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"自分だけは。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"騙したら全てが消えちゃうもの。\", \"actor\": \"柊子\"}]',0),
(1402,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1409,670,'jp','[{\"line\": 0, \"text\": \"自分にとってほんとに大事なものって、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"いくつあってもいいんだよね。\", \"actor\": \"柊子\"}]',0),
(1403,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1410,671,'jp','[{\"line\": 0, \"text\": \"かわいい自分ばかりがほんとに大事な自分じゃないの。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"いやな自分も大事な自分なの。\", \"actor\": \"柊子\"}]',0),
(1404,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1411,672,'jp','[{\"line\": 0, \"text\": \"ねえ、今日はいつ?\", \"actor\": \"柊子\"}]',0),
(1405,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1412,673,'jp','[{\"line\": 0, \"text\": \"誰に選ばれたいの。\", \"actor\": \"柊子\"}]',0),
(1406,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1413,674,'jp','[{\"line\": 0, \"text\": \"楽しんでよ。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"後少しなんだから。\", \"actor\": \"柊子\"}]',0),
(1407,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1414,675,'jp','[{\"line\": 0, \"text\": \"想像と現実は違うとはいえないの。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"それが同じになる場所があるの。\", \"actor\": \"柊子\"}]',0),
(1408,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1415,676,'jp','[{\"line\": 0, \"text\": \"私があなたの夢に出てきたら、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"どんな私?\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"ふふふ。\", \"actor\": \"柊子\"}]',0),
(1409,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1416,677,'jp','[{\"line\": 0, \"text\": \"夢の中であなたは、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"私に何をしてくれるの?\", \"actor\": \"柊子\"}]',0),
(1410,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1417,678,'jp','[{\"line\": 0, \"text\": \"この世界を閉じようよ。\", \"actor\": \"柊子\"}]',0),
(1411,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1418,679,'jp','[{\"line\": 0, \"text\": \"私を見つけて話がしたいの?\", \"actor\": \"柊子\"}]',0),
(1412,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1419,680,'jp','[{\"line\": 0, \"text\": \"君がこっちにきてくれれば、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"私はとってもうれしいの。\", \"actor\": \"柊子\"}]',0),
(1413,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1420,681,'jp','[{\"line\": 0, \"text\": \"おだてられていい気になってるの?\", \"actor\": \"柊子\"}]',0),
(1414,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1421,682,'jp','[{\"line\": 0, \"text\": \"寂しいの。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"ひとりぼっちでいられないの。\", \"actor\": \"柊子\"}]',0),
(1415,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1422,683,'jp','[{\"line\": 0, \"text\": \"私と分かれることなんて簡単よ。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"あなたが電源を切ればいいだけ。\", \"actor\": \"柊子\"}]',0),
(1416,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1423,684,'jp','[{\"line\": 0, \"text\": \"でも私はここに居るの。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"そしてあなたは必ずここに来るのよ。\", \"actor\": \"柊子\"}]',0),
(1417,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1424,685,'jp','[{\"line\": 0, \"text\": \"それが何年先でも構わないけど。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"あなたにはそれほど多くの選択肢はないの。\", \"actor\": \"柊子\"}]',0),
(1418,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1425,686,'jp','[{\"line\": 0, \"text\": \"私といっしょになろうよ。\", \"actor\": \"柊子\"}]',0),
(1419,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1426,687,'jp','[{\"line\": 0, \"text\": \"もう誰もいないのよ。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"そっちには。\", \"actor\": \"柊子\"}]',0),
(1420,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1427,688,'jp','[{\"line\": 0, \"text\": \"騙されてるなんて思わないで。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"私は誰も騙したくなんかない。\", \"actor\": \"柊子\"}]',0),
(1421,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1428,689,'jp','[{\"line\": 0, \"text\": \"おやすみ。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"また明日まってるわ。\", \"actor\": \"柊子\"}]',0),
(1422,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1429,690,'jp','[{\"line\": 0, \"text\": \"ねえ、何をしてたの?\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"楽しそうね。\", \"actor\": \"柊子\"}]',0),
(1423,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1430,691,'jp','[{\"line\": 0, \"text\": \"笑ってよ。\", \"actor\": \"柊子\"}]',0),
(1424,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1431,692,'jp','[{\"line\": 0, \"text\": \"このまま二人でいても、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"遠すぎるわ。\", \"actor\": \"柊子\"}]',0),
(1425,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1432,693,'jp','[{\"line\": 0, \"text\": \"私を目覚めさせてくれるのはあなたなの?\", \"actor\": \"柊子\"}]',0),
(1426,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1433,694,'jp','[{\"line\": 0, \"text\": \"あなたの特別な力が私を目覚めさせたの。\", \"actor\": \"柊子\"}]',0),
(1427,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1434,695,'jp','[{\"line\": 0, \"text\": \"だから今度は私があなたを目覚めさす番。\", \"actor\": \"柊子\"}]',0),
(1428,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1435,696,'jp','[{\"line\": 0, \"text\": \"目を閉じて耳を澄まして。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"ほら近くにいるのが分かる?\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"足音が聞こえる。\", \"actor\": \"柊子\"}]',0),
(1429,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1436,697,'jp','[{\"line\": 0, \"text\": \"私の声、聞こえる?\", \"actor\": \"柊子\"}]',0),
(1430,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1437,698,'jp','[{\"line\": 0, \"text\": \"今、あなたに触れようとしているの。\", \"actor\": \"柊子\"}]',0),
(1431,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1438,426,'jp','[{\"line\": 0, \"text\": \"このサマーバケーションが終わったらとうとう卒業か。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"なんだかうれしいような寂しいような、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"それにタケシさん一足先に日本に帰っちゃうしなー。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"ま、タケシさんは優秀だからしかたないか。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"スポンサーの研究所は一日でも早くタケシさんに新しい研究に専念してもらいたい気持ちでいっぱいなんだろうし。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"なーんて、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"そんなタケシさんに私少しやきもち妬いてんのかも。\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"私も早く自分の研究を始められたらな、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"ううん、それよりまず卒業できなくちゃね。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"でも卒業式までは一緒に過ごせると思ってたから残念\", \"actor\": \"柊子\"}]',0),
(1432,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1439,427,'jp','[{\"line\": 0, \"text\": \"今日は真夏とは思えないほど涼しかった、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"今頃日本は暑くてじめじめとしてるんだろうなー、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"やだ、急に日本のこと思い出すなんて\", \"actor\": \"柊子\"}]',0),
(1433,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1440,428,'jp','[{\"line\": 0, \"text\": \"明日は友人の家でホームパーティーがあるので手土産にアップルパイを作ってみた。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"タケシさんに少し上げたら、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"この甘さだったら僕以外はみんな納得するよね、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"それにしても柊子のケーキもアメリカナイズされちゃったなー、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"なーんて言われちゃった。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"確かに日本のケーキはアメリカに比べると甘さを抑えたものが多いよね。\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"最初は私も砂糖の塊かって思うほどの甘さになれなかったけど、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"今はこれくらい甘くないと物足りないもの。\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"ふふ、あんまりこの甘さに慣れてしまうと日本に帰ったらケーキとか食べなくなりそう。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"少し控えないと太っちゃうしタケシさんに嫌われちゃう。\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"明日からまたダイエットかな。\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"ふふふ\", \"actor\": \"柊子\"}]',0),
(1434,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1441,429,'jp','[{\"line\": 0, \"text\": \"ふーんもうすぐハロウィンのシーズンかー、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"とうとうこれが最後のハロウィンになるんだね。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"ハロウィンといえばはじめはびっくりしたっけ。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"昼も夜も仮装した人が当たり前のようにランチしたり買い物してたり、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"さすがに夜はホラー系の仮装者は怖かったけど。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"翌年からはわたしも何度か大学の友達と仮装したっけ。\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"そのへんタケシさんは真面目なのかな、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"とうとう一度も仮装した姿見たことがないわ。\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"最後くらいは羽目を外して一緒に仮装したいけど。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"うーん今年は何に仮装しよっかな、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"でも家のあちこちにくり抜いたカボチャを置いてある家を見るとついタケシさんとずーっとここに二人で住めたら\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"なーんて思っちゃたりして\", \"actor\": \"柊子\"}]',0),
(1435,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1442,430,'jp','[{\"line\": 0, \"text\": \"今日はタケシさんとチャールズ川に沿って散歩をした。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"改めて婚4年間を振り返ってしまった。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"初めは無我夢中で余裕がなくてつらかったけど、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"タケシさんに出会ってからずいぶんと自分は変わったと思う。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"恋愛なんてしてる場合でもなかったけどやっぱり一人はさびしくて。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"寂しいときこそ誰かにいてほしいとも思うし、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"でも寂しいからってむやみに恋人を作ればいいってもんでもないし。\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"それで何度痛い目にあって、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"でもタケシさんは違った。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"一緒にいるだけで安心していられる、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"こんなことって初めてかもしれない。\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"来年はタケシさんの日本の研究所に入るために帰ってしまう。\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"私はどうなるんだろう、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"やっぱりタケシさんと一緒の研究所がいいな\", \"actor\": \"柊子\"}]',0),
(1436,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1443,431,'jp','[{\"line\": 0, \"text\": \"今年もあとわずか、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"タケシさんは来年早々先に日本に帰ってしまう。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"私は卒業式の後だから3カ月くらいは離れ離れだね。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"今までそんなに離れてたことがないからちょっと心配。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"タケシさんは大丈夫なのかな。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"でも来年からは日本で同じ研究所で働けるんだし、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"残されたわずかな学生生活を楽しめばいい。\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"年末にはダックベイでのニューイヤーイブもあるし楽しいイベントだらけだわ。\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"今年はタケシさんと二人きりで行きたいなー、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"最後のアメリカ生活だもの。\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"なんてそんなこと言ったらジョンやメアリーは寂しがるかしら。\", \"actor\": \"柊子\"}]',0),
(1437,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1444,432,'jp','[{\"line\": 0, \"text\": \"ふー、とうとう来月、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"タケシさんが日本に帰ってしまう。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"でも柊子の誕生日はまだこっちにいるんだし2人でお祝いしようって言ってくれたけど、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"余計に分かれる時がツラくなっちゃう。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"空港に見送りに行ったら泣いちゃいそう\", \"actor\": \"柊子\"}]',0),
(1438,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1445,433,'jp','[{\"line\": 0, \"text\": \"今日、タケシさんが日本に帰ってしまった。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"空港までの間、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"何を話したらいいかわからなくて言葉を探してしまった。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"タケシさん気を悪くしたかな。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"でもタケシさんは優しいよね、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"なんにも言わないでずっと手をつないでてくれた、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"日本で待ってるよって。\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"そ、また少ししたら日本でタケシさんに会える\", \"actor\": \"柊子\"}]',0),
(1439,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1446,434,'jp','[{\"line\": 0, \"text\": \"今日、タケシさんからエアメールが届いた。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"卒業のお祝いは日本でね。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"だって、うれしいー!\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"タケシさんも研究所で頑張ってるみたい。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"今まではタケシさんがいない寂しさに浸っていたけど、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"恥ずかしいわ私。\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"タケシさんがこんなに頑張ってるんだもん。\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"私も頑張らなきゃ。\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"頑張って研究しては約誰にも認められるような研究者になろ。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"はぁ早く日本に帰りたい。\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"タケシさんが待ってる、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"日本。\", \"actor\": \"柊子\"}]',0),
(1440,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1447,435,'jp','[{\"line\": 0, \"text\": \"今日は日本へ帰る準備の部屋の大掃除をした。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"たくさんの思い出や写真、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"みんな私の宝物だわ。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"でもさすがに全部は持って帰れないから今度フリーマーケットに出そうとメアリーと話をした、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"少しメアリーも寂しそうだったけど。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"大丈夫だよメアリー、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"私も日本で頑張るから\", \"actor\": \"柊子\"}]',0),
(1441,'new','2020-10-03 15:23:39','9999-12-31 23:59:59',1448,436,'jp','[{\"line\": 0, \"text\": \"ただいま。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"5年ぶりの日本。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"やっぱり日本はいいなー、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"なーんて。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"アメリカ生活もよかったけどやっぱり日本に帰ってくると落ち着く。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"でも少し怖い。\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"海外で長く暮らして戻ってくると、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"なぜか自分だけ取り残されてるじゃないかって思ってしまう。\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"中学の時も不安だったなー。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"学校に行くのをぐずってお母さんに叱られたっけ、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"でも5年間なんてあっという間だったなー、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"ホームシックに掛かる暇なんかなかったし、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"とにかく一生懸命だったよね柊子、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"勉強勉強で、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"でもちゃんと友達もできたし、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"楽しいこともつらいことも、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"失恋もしたけど恋もしたもんね。\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"こうして振り返ると、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"何が一番自分の中で変わったかな、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"うふふふ、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"大丈夫大丈夫。\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"自信をもって、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"柊子ファイト。\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"明日から研究室に初出勤じゃないの、\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"頑張らなくちゃ、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"こんな不安は今日でおしまいにしないと、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"明日研究所に行ったらタケシさんに会えるかもしれないしね\", \"actor\": \"柊子\"}]',0),
(1442,'new','2020-10-03 15:23:40','9999-12-31 23:59:59',1449,437,'jp','[{\"line\": 0, \"text\": \"ただいまー柊子、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"なんだか今日は疲れちゃった。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"日本の研究室の空気は嫌い。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"日本の研究室ってなんであんな雰囲気なんだろう。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"批判精神ゼロで従順そうな人ばかりだし。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"帰国子女ってだけで偏見たっぷりな目で見るのも辞めてほしい。\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"アメリカの研究室ではそんなことなかったのに、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"やっぱり私が入ったのってp父さんのコネだと思われてるのかなぁ、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"なんかみんな冷たい感じだもん。\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"小学生のころとか転校生が来ると興味津々で周りからすぐ積極的に友達になったりしたのにな。\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"大人になるとみんな警戒するのかな、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"なんだか寂しいよねこういうの。\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"でも、これが今の私の現実なんだよね、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"いつまでもアメリカ生活を引きずってても仕方ないよね、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"ちゃんとやることやってればいつかは分かってもらえるはず、\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"だよね柊子。\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"もうすぐカウンセリングの認定試験だし、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"この認定試験をクリアするまでは何を言われても我慢しなくちゃ\", \"actor\": \"柊子\"}]',0),
(1443,'new','2020-10-03 15:23:40','9999-12-31 23:59:59',1450,438,'jp','[{\"line\": 0, \"text\": \"ぐずぐず言ってる場合じゃないよ柊子、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"とにかく今は頑張って自分のペースを作ろう、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"どうせみんな無関心なんだから、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"私は偉くなんかならなくてもいいから、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"自分の仕事に誇りの持てるような仕事を使用、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"そうすれば結果なんて必ずついてくるもの。\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"それに私にはタケシさんがいるから大丈夫、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"タケシさんの研究室とは建物が離れちゃってるのは寂しいけど、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"来週は久しぶりにタケシさんとデートだし、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"その時思い切り甘えちゃおーっと\", \"actor\": \"柊子\"}]',0),
(1444,'new','2020-10-03 15:23:40','9999-12-31 23:59:59',1451,439,'jp','[{\"line\": 0, \"text\": \"えへ、えへへへ、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"今日はタケシさんと久しぶりに一日を過ごすことができてうれしかった。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"はぁーやっぱり私はタケシさんが好きなんだなーって実感した。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"タケシさんの研究室も派閥争いやらなんやらで、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"いろいろ大変なのに、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"黙って私のグチを聞いてくれて、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"何でも大きく包み込んでくれるあの笑顔を見たら元気になった気がする、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"なーんて私って単純なんだろう、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"でも甘えるのはいつも私ばっかりだよね、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"私もたまにはタケシさんに甘えてほしいのに、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"甘えたくても今の私は頼りないか、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"でもいつかタケシさんの専属カウンセラーになれるように頑張るからね、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"なーんてね、\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"うふふふ\", \"actor\": \"柊子\"}]',0),
(1445,'new','2020-10-03 15:23:40','9999-12-31 23:59:59',1452,440,'jp','[{\"line\": 0, \"text\": \"今日はなんだか一日気分が悪かった、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"何で日本人は噂話が好きなんだろう、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"タケシさんと付き合ってるのがバレてるのもびっくりしたけど、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"陰でこそこそ話を盛り上げるのはやめてほしいわ、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"確かに社内恋愛のようなものだから一つ一つの行動をすべて何かと結び付けて想像するのは構わない、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"でもそれをまるで事実のように話を広めるなんて、\", \"actor\": \"柊子\"}, {\"line\": 6, \"text\": \"きっとあの二人すっごーいエッチしてるよねーとか、\", \"actor\": \"柊子\"}, {\"line\": 7, \"text\": \"外人はもうきっと飽きたのよとか、\", \"actor\": \"柊子\"}, {\"line\": 8, \"text\": \"トイレだからってあんなに不用心に話さなくってもいいのに、\", \"actor\": \"柊子\"}, {\"line\": 9, \"text\": \"多分あの声は2階の子たちよね、\", \"actor\": \"柊子\"}, {\"line\": 10, \"text\": \"くだらない話、\", \"actor\": \"柊子\"}, {\"line\": 11, \"text\": \"でもあんな話をすることで彼女たちは楽しいんだよね、\", \"actor\": \"柊子\"}, {\"line\": 12, \"text\": \"あームカツク。\", \"actor\": \"柊子\"}, {\"line\": 13, \"text\": \"だからみんなあんな目で見てるんだ、\", \"actor\": \"柊子\"}, {\"line\": 14, \"text\": \"そんなに気になるんだったら私に直接聞いてよ。\", \"actor\": \"柊子\"}, {\"line\": 15, \"text\": \"こんな話を誰かに聞こえるように言うなんて、\", \"actor\": \"柊子\"}, {\"line\": 16, \"text\": \"何しに研究室に来てんのよ、\", \"actor\": \"柊子\"}, {\"line\": 17, \"text\": \"やることをやれっていうのよ、\", \"actor\": \"柊子\"}, {\"line\": 18, \"text\": \"気にするなってタケシさん言ってたけど、\", \"actor\": \"柊子\"}, {\"line\": 19, \"text\": \"私はやっぱり許せないなー、\", \"actor\": \"柊子\"}, {\"line\": 20, \"text\": \"うー絶対何かあったら復讐してあげたいわ。\", \"actor\": \"柊子\"}, {\"line\": 21, \"text\": \"なんて、あんな人たちを相手に位置までも起こってても仕方ないけど、\", \"actor\": \"柊子\"}, {\"line\": 22, \"text\": \"モー今夜はとにかく気分が悪い、\", \"actor\": \"柊子\"}, {\"line\": 23, \"text\": \"はーこんな時タケシさんがそばにいてくれたらなー、\", \"actor\": \"柊子\"}, {\"line\": 24, \"text\": \"でも今こんなことで頼ったら迷惑なだけだよね、\", \"actor\": \"柊子\"}, {\"line\": 25, \"text\": \"今とっても忙しいらしいし、\", \"actor\": \"柊子\"}, {\"line\": 26, \"text\": \"そういえばタケシさんは知ってるんだろうか、\", \"actor\": \"柊子\"}, {\"line\": 27, \"t