(1525,'new','2020-10-03 15:23:42','9999-12-31 23:59:59',1532,827,'jp','[{\"line\": 0, \"text\": \"なんか実験の代償で私に付き合ってくれるって、\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"不安になるの。\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"吉田君に会いたい、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"会ってキスしてほしい。\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"私の気持ちはもうわかってるくせに、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"早く私を受け入れてほしい\", \"actor\": \"柊子\"}]',0),
(1526,'new','2020-10-03 15:23:42','9999-12-31 23:59:59',1533,828,'jp','[{\"line\": 0, \"text\": \"無意識に危ないと思った。\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"間違った使い方をしたわけでもないのに、\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"使っていたのが間違いなのかも、\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"試作機のトラブル?\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"危ないよ吉田君に相談しなくちゃ。\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"こんなの販売したらトラブルのもとだよ。\", \"actor\": \"柊子\"}]',0),
(1527,'new','2020-10-03 15:23:42','9999-12-31 23:59:59',1534,829,'jp','[{\"line\": 0, \"text\": \"何のために?\", \"actor\": \"柊子\"}, {\"line\": 1, \"text\": \"どうして玲音の生まれた時からのデータがあるの?\", \"actor\": \"柊子\"}, {\"line\": 2, \"text\": \"あぁ、もうわからない。\", \"actor\": \"柊子\"}, {\"line\": 3, \"text\": \"また声が聞こえるもう声だけじゃない、\", \"actor\": \"柊子\"}, {\"line\": 4, \"text\": \"吉田君や研究員たちが私を遠くから見てる、\", \"actor\": \"柊子\"}, {\"line\": 5, \"text\": \"どうして私を見て笑ってるのもうやめて!\", \"actor\": \"柊子\"}]',0),
